INSERT INTO `quest_template_locale` VALUES (32307, 'koKR', '죽은 언어를 읽는 자', '대도시에 있는 상급 흑마법사에게 고서를 가져가야 합니다.', '대도시의 상급 흑마법사 중에서 이 고서에 대해 아는 자를 찾아봐야 합니다.', '', '', '', '', '', '', 0);
