INSERT INTO `quest_template_locale` VALUES (32500, 'ruRU', 'Поклонение огню', 'Поклонитесь костру праздника Огненного солнцеворота на вершине Кунь-Лай.', 'Праздник Огненного солнцеворота – время поклонения стихиям. Огонь разжигает страсти в нашей душе. Сколько раз эти страсти приводили к открытой агрессии... Сейчас же, находясь на этой земле, мы направляем свои эмоции так, чтоб выразить почтение стихии огня.$b$bМы поклонимся пламени, как того требуют традиции. Желаешь ли ты присоединиться к нашему чествованию огня, $n?', 'Поклонитесь костру праздника Огненного солнцеворота на вершине Кунь-Лай.', '', '', '', '', '', 0);
