INSERT INTO `quest_template_locale` VALUES (32520, 'deDE', 'Konkurrierende Magie', 'Tötet Arkanwirker Jor\'guva.', 'Meine Späher haben diesseits von Za\'Tual einen gefährlichen Zandalari entdeckt. Ich brauche jemand Fähigen, der dafür sorgt, dass er beseitigt wird. Traut Ihr Euch das zu?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'esES', 'Magia conflictiva', 'Mata al tejearcos Jor\'guva.', 'Mis exploradores han descubierto a un peligroso Zandalari en este lado de Za\'Tual. Necesito a alguien capaz de acabar con él. ¿Quieres encargarte tú?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'esMX', 'Magia conflictiva', 'Mata al tejearcos Jor\'guva.', 'Mis exploradores han descubierto a un peligroso Zandalari en este lado de Za\'Tual. Necesito a alguien capaz de acabar con él. ¿Quieres encargarte tú?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'frFR', 'Compétition de magie', 'Tuez le Tisse-arc Jor’guva.', 'Mes éclaireurs ont repéré un dangereux zandalari de ce côté de Za’Tual. Je cherche une personne capable de nous en débarrasser. Vous sentez-vous à la hauteur de la tâche ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'itIT', 'Magie rivali', 'Uccidi il Mistico Arcano Jor\'guva.', 'I miei esploratori hanno trovato un pericoloso Zandalari da questa parte di Za\'tual. Ho bisogno di qualcuno abbastanza abile da occuparsi di lui. Pensi di esserne all\'altezza?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'koKR', '위험한 마술사', '대마술사 조르구바를 처치해야 합니다.', '내 정찰대가 자투알쪽에서 위험한 잔달라를 발견했소. 유능한 실력을 갖춘 누군가가 그자를 처치해줬으면 좋겠군. 그대가 이 임무를 맡아주겠소?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'ptBR', 'Magia adversária', 'Mate o Tece-arcos Jor\'guva.', 'Meus batedores localizaram um perigoso Zandalari deste lado de Za\'Tual. Preciso de alguém capaz de despachá-lo. Você está à altura da tarefa?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'ruRU', 'Магия против магии', 'Убейте чародея Джор\'гуву.', 'Мои разведчики засекли опасного зандалара на нашей стороне За\'Туала. Я ищу добровольца, который вызовется его ликвидировать. Ты возьмешься за эту работу?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'zhCN', '魔法竞赛', '消灭织奥者琼古瓦。', '我的探子们在赞图尔的这一侧发现了一个危险的赞达拉巨魔。我需要有人解决掉他。你能完成这项任务吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32520, 'zhTW', '魔法對決', '殺死織秘者鳩谷瓦。', '我的斥候在札突爾的這一側看見一位危險的贊達拉，我需要有能力的人去除掉他。你願意接下這個任務嗎?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'deDE', 'Feindlicher Übergriff', 'Tötet Nurkala, den Kriegsanführer der Zandalari.', 'Meine Späher haben an der Nordküste einen Kriegstrupp der Zandalari entdeckt. Ich brauche jemand Fähigen, der dafür sorgt, dass er beseitigt wird. Traut Ihr Euch das zu? ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'esES', 'Fuerzas invasoras', 'Mata a Nurkala, líder de guerra Zandalari.', 'Mis exploradores han descubierto un destacamento de guerra Zandalari en la playa que hay al norte. Necesito a alguien capaz de acabar con ellos. ¿Quieres encargarte tú?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'esMX', 'Fuerzas invasoras', 'Mata a Nurkala, líder de guerra Zandalari.', 'Mis exploradores han descubierto un destacamento de guerra Zandalari en la playa que hay al norte. Necesito a alguien capaz de acabar con ellos. ¿Quieres encargarte tú?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'frFR', 'Une troupe envahissante', 'Tuez Nurkala, le chef de guerre zandalari.', 'Mes éclaireurs ont repéré une troupe de guerriers zandalari sur la plage nord. Il me faut quelqu’un capable de nous en débarrasser. Vous sentez-vous à la hauteur de la tâche ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'itIT', 'Forza opprimente', 'Uccidi Nurkala, il Signore della Guerra Zandalari.', 'I miei esploratori hanno trovato un gruppo armato di Zandalari sulla spiaggia settentrionale. Ho bisogno di qualcuno abbastanza abile da occuparsi della faccenda. Pensi di esserne all\'altezza?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'koKR', '적군의 침략', '잔달라 전쟁 지도자인 누르카라를 처치해야 합니다.', '내 정찰대가 북쪽 해안에서 잔달라 공격 부대를 발견했소. 유능한 실력을 갖춘 누군가가 그자를 처치해줬으면 좋겠군.$B$B그대가 이 임무를 맡아주겠소?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'ptBR', 'Força de invasão', 'Mate Nurkala, o Líder Guerreiro dos Zandalari.', 'Meus batedores localizaram um grupo de guerreiros Zandalari na praia norte. Preciso de alguém capaz de despachá-lo. Você está à altura da tarefa?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'ruRU', 'Вторжение', 'Убейте Нуркала, военного вождя зандаларов.', 'Мне доложили, что на северном побережье замечен зандаларский военный отряд. Я ищу добровольца, который вызовется его уничтожить. Ты возьмешься за эту работу?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'zhCN', '渗透力量', '消灭赞达拉战争首领奴卡拉。', '我的探子们在北部海滩上发现了一支赞达拉作战小队。我需要有人解决掉他们。你能完成这项任务吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32521, 'zhTW', '外力入侵', '殺死贊達拉戰爭領袖奴凱拉。', '我的斥候在北方海灘看見贊達拉的戰士。我需要一位有能力的人去除掉他。你願意接下這個任務嗎?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'deDE', 'Imposante Bedrohung', 'Tötet Horgak den Versklaver.', 'Meine Späher haben unterhalb des zentralen Turms einen Anführer der Mogu entdeckt. Er muss sterben, bevor er einen Angriff gegen unsere Stellung anzettelt.$B$BKönnt Ihr Euch für mich darum kümmern?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'esES', 'Una amenaza imponente', 'Mata a Horgak el Esclavista.', 'Mis exploradores han localizado a un líder mogu bajo la torre central. Hay que matarlo antes de que organice una ofensiva contra nosotros.$B$B¿Puedes encargarte de ello por mí?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'esMX', 'Una amenaza imponente', 'Mata a Horgak el Esclavista.', 'Mis exploradores han localizado a un líder mogu bajo la torre central. Hay que matarlo antes de que organice una ofensiva contra nosotros.$B$B¿Puedes encargarte de ello por mí?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'frFR', 'Une menace impressionnante', 'Tuez Horgak l’Esclavagiste.', 'Mes éclaireurs ont repéré un chef mogu sous la tour centrale. Il doit être tué avant d’avoir pu monter une offensive contre notre position.$B$BPouvez-vous vous charger de ça pour moi ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'itIT', 'Una grande minaccia', 'Uccidi Horgak lo Schiavista.', 'I miei esploratori hanno trovato un capo dei Mogu sotto la torre centrale. Deve essere eliminato prima che riesca a organizzare un\'offensiva contro la nostra posizione.$B$BPuoi occupartene tu per me?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'koKR', '눈앞에 닥친 위기', '노예사냥꾼 호르각을 처치해야 합니다.', '내 정찰대가 중앙탑 아래에 있는 모구 지휘관을 발견했소. 그자가 우리 위치에 공세를 가하기 전에 처치해야 하오.$B$B그대가 이 임무를 맡아주겠소?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'ptBR', 'Ameaça iminente', 'Mate Horgak, o Escravizador.', 'Meus batedores localizaram um líder mogu sob a torre central. Alguém precisa matá-lo antes que ele lance uma ofensiva contra nossa posição.$b$bVocê pode cuidar disso para mim?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'ruRU', 'Серьезная угроза', 'Убейте Хограка Поработителя.', 'Мои разведчики обнаружили командира могу у подножия центральной башни. Его нужно убить, пока он не организовал атаку на наши позиции.$B$BТы возьмешься за эту работу?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'zhCN', '危机四伏', '消灭奴役者霍嘎克。', '我的探子们在中央高塔底下发现了一位魔古族首领。得赶在他袭击我们的据点前将他消灭。$B$B你能替我完成这项任务吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32522, 'zhTW', '重大威脅', '殺死『奴役者』霍賈克。', '我的斥候在中央高塔下發現了一個魔古首領。在他對我們的陣地發動攻擊之前，我們得殺掉他。$B$B你願意幫我辦這件事嗎?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'deDE', 'Die Bestienpferche', 'Findet Späherhauptmann Elsia in den Bestienpferchen.', 'Wir müssen diesen Bestien, die die Zandalari gegen unser Lager auffahren, irgendetwas entgegensetzen. Ich habe Späherhauptmann Elsia zu den Bestienpferchen geschickt; zusammen mit einigen Kriegsmagiern, die für Deckung sorgen.$b$bGeht nach Osten und sucht sie in der Nähe des Mauerdurchbruchs. Helft ihr, die Zahl der Reiterei und Belagerungseinheiten einzuschränken, die gegen uns aufgebracht werden können.$b$bSie sind durch Magie getarnt, haltet also Ausschau nach dem Schimmer ihrer Tarnzauber.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'esES', 'El Cerco de las Bestias', 'Encuentra a la capitana de exploradores Elsia en El Cerco de las Bestias.', 'Necesitamos hacer frente a las bestias que están utilizando los Zandalari para atacar nuestro campamento. He enviado a la capitana de exploradores Elsia a El Cerco de las Bestias con algunos magos de guerra a modo de protección.$b$bVe y reúnete con ella al este de aquí, cerca de las grietas del muro. Ayúdala a diezmar la caballería enemiga y a cercar a las bestias que están mandando para atacarnos.$b$bEstán ocultos, así que busca el resplandor de sus hechizos mágicos de invisibilidad.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'esMX', 'El Cerco de las Bestias', 'Encuentra a la capitana de exploradores Elsia en El Cerco de las Bestias.', 'Necesitamos hacer frente a las bestias que están utilizando los Zandalari para atacar nuestro campamento. He enviado a la capitana de exploradores Elsia a El Cerco de las Bestias con algunos magos de guerra a modo de protección.$b$bVe y reúnete con ella al este de aquí, cerca de las grietas del muro. Ayúdala a diezmar la caballería enemiga y a cercar a las bestias que están mandando para atacarnos.$b$bEstán ocultos, así que busca el resplandor de sus hechizos mágicos de invisibilidad.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'frFR', 'Les enclos à bêtes', 'Trouvez le Capitaine des éclaireurs Elsia dans les Enclos à bêtes.', 'Nous devons contrer les bêtes dont les Zandalari se servent pour harceler notre campement. J’ai envoyé le capitaine des éclaireurs Elsia dans les Enclos à bêtes, accompagnée de quelques mages de guerre pour la couvrir.$b$bAllez la retrouver à l’est d’ici, près des brèches dans la muraille, et aidez-la à affaiblir la cavalerie et les bêtes de siège qui ont été levées contre nous.$b$bIls seront camouflés, alors cherchez le scintillement de leurs sorts de dissimulation.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'itIT', 'Recinti delle Bestie', 'Trova il Capitano degli Esploratori Elsia ai Recinti delle Bestie.', 'Dobbiamo trovare un modo per contrastare le bestie che gli Zandalari stanno utilizzando per attaccare il nostro accampamento. Ho inviato il Capitano degli Esploratori Elsia ai Recinti delle Bestie, insieme ad alcuni Maghi da Guerra per fornire copertura.$b$bVai a est, raggiungila vicino alle brecce delle mura e aiutala ad abbattere la cavalleria e le bestie d\'assedio che ci stanno minacciando.$b$bSono nascosti, quindi cerca le scintille luminose generate dai loro incantesimi di invisibilità.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'koKR', '야수 조련소', '야수 조련소에 있는 정찰대장 엘시아를 찾아야 합니다.', '잔달라 놈들이 우리 야영장을 공격할 때 부리는 야수들에게 반격을 가해야 하오. 정찰대장 엘시아와 그녀를 엄호해줄 전쟁마법사들을 야수 조련소로 파견했소.$b$b이곳에서 동쪽으로 가면 성벽이 무너진 곳 근처에서 엘시아를 찾을 수 있을 거요. 그녀를 도와 적군 기병단의 수를 좀 줄여주고, 우리를 공격한 야수들을 저지해주시오.$b$b아군은 모습을 감춘 상태이니 은신 마법 주문으로 희미하게 빛나는 모습을 잘 찾아보시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'ptBR', 'O cercado de feras', 'Encontre a Capitã dos Batedores Elsia no Cercado das Feras.', 'Precisamos dar um jeito nas feras que os Zandalari estão usando para atacar o acampamento. Mandei a Capitã dos Batedores Elsia até o Cercado das Feras com alguns magos de guerra, para darem cobertura.$b$bEncontre a capitã a leste daqui, perto das fendas na muralha, e ajude a dar cabo da cavalaria e das bestas de cerco mobilizadas contras nós.$b$bEles estão usando máscaras, então é melhor procurar pelo brilho dos feitiços de camuflagem mágica.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'ruRU', 'Загоны для зверей', 'Разыщите капитана разведчиков Эльсию в загонах для зверей.', 'Нельзя сбрасывать со счетов зверей, которых зандалары используют, чтобы атаковать наш лагерь. Я отправил в загоны для зверей капитана разведчиков Эльсию и нескольких боевых магов для защиты.$b$bДвигайся на восток и разыщи ее у проломов в стене. Помоги ей справиться с кавалерией и осадными животными, которых заставили сражаться против нас.$b$bЭльсия и маги замаскированы, ты узнаешь их по мерцанию аур укрывающих заклинаний.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'zhCN', '兽栏', '在兽栏找到斥候队长艾尔希娅。', '我们必须击退那些被赞达拉巨魔派来攻打我们营地的野兽。我已经派出斥候队长艾尔希娅带领数名战斗法师前往兽栏提供掩护。$b$b去东边的城墙缺口附近找到她，帮她消灭那些攻打我们的骑兵和攻城巨兽。$b$b他们都是隐身的，注意寻找他们魔法隐形法术散发的微光。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32523, 'zhTW', '獸園', '尋找獸園裡的斥候隊長艾爾西雅。', '我們得對抗贊達拉用來襲擊陣營的野獸。我已經派遣斥候隊長艾爾西雅與幾名戰爭法師到獸園作為掩護。$b$b到東邊的牆縫附近找她，幫助她除掉騎兵並圍捕攻擊我們的野獸。$b$b他們身上都有偽裝，尋找魔法披風法術的亮光就能找到他們。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'deDE', 'Im Takt der Trolltrommeln', 'Sucht Späherhauptmann Elsia auf der Terrasse des Eroberers.', 'Hört Ihr das, $C? Den Klang von Kriegstrommeln in der Ferne. Die Zandalari bereiten sich auf uns vor.$b$bWir werden ihnen nicht den Luxus gewähren, ihre Verteidigungslinie zu organisieren. Unser Angriff muss jetzt stattfinden!$b$bMeldet Euch bei Späherhauptmann Elsia auf der Terrasse des Eroberers. Sie hält weitere Befehle für Euch bereit.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'esES', 'Redobles trols', 'Localiza a la capitana de exploradores Elsia en la Platea del Conquistador.', '¿Oyes eso, $c? Es el redoble de tambores de guerra en la distancia. Los Zandalari se están preparando para nuestra llegada.$b$bNo hay que darles ni una pizca de tiempo para que organicen sus defensas. ¡Hay que atacar ya!$b$bInforma a la capitana de exploradores Elsia en la Platea del Conquistador. Ella te proporcionará más detalles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'esMX', 'Redobles trols', 'Localiza a la capitana de exploradores Elsia en la Platea del Conquistador.', '¿Oyes eso, $c? Es el redoble de tambores de guerra en la distancia. Los Zandalari se están preparando para nuestra llegada.$b$bNo hay que darles ni una pizca de tiempo para que organicen sus defensas. ¡Hay que atacar ya!$b$bInforma a la capitana de exploradores Elsia en la Platea del Conquistador. Ella te proporcionará más detalles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'frFR', 'Les tambours des Trolls', 'Localisez le Capitaine des éclaireurs Elsia sur la Terrasse des Conquérants.', 'Vous entendez ça, $c ? C’est le son des tambours de guerre, au loin. Les Zandalari se préparent à notre venue.$b$bNe leur laissons pas le temps d’organiser leurs défenses. Nous devons déclencher l’attaque de suite !$b$bPrésentez-vous au capitaine des éclaireurs Elsia, à la terrasse des Conquérants. Elle aura de nouveaux ordres pour vous.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'itIT', 'A suon di tamburi dei Troll', 'Trova il Capitano degli Esploratori Elsia alla Terrazza del Conquistatore.', 'Hai sentito, $C? I tamburi di guerra in lontananza... Gli Zandalari si stanno preparando.$b$bNon lasciamo loro il tempo di organizzare le difese: dobbiamo iniziare subito l\'attacco!$b$bFai rapporto al Capitano degli Esploratori Elsia nella Terrazza del Conquistatore. Avrà i tuoi ordini.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'koKR', '트롤의 북소리', '정복자의 정원에 있는 정찰대장 엘시아에게 가야 합니다.', '이 소리가 들리시오, $c? 먼 곳에서 전쟁 북소리가 들려오고 있소. 잔달라 부족이 우리와의 싸움에 대비하고 있는 거지.$b$b놈들이 여유롭게 방어선을 구축할 시간을 줄 순 없소. 지금 당장 공격해야 하오!$b$b정복자의 정원에 있는 정찰대장 엘시아에게 가시오. 추후 임무는 그녀가 줄 것이오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'ptBR', 'O rufar dos tambores trólicos', 'Encontre a Capitã dos Batedores Elsia no Terraço do Conquistador.', 'Está ouvindo, $c? O rufar dos tambores de guerra ao longe. Os Zandalari estão se preparando.$b$bNão vamos dar a eles tempo para organizar as defesas. Temos que atacar agora!$b$bFale com a Capitã dos Batedores Elsia no Terraço do Conquistador. Ela terá mais ordens para você.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'ruRU', 'Бой барабанов троллей', 'Найдите капитана разведчиков Эльсию на Террасе Завоевателя.', 'Ты слышишь это, |3-6($c)? Грохот военных барабанов вдали. Зандалары готовятся к бою с нами.$b$bДавай же лишим их этой возможности – успеть подготовить свою защиту. Нужно атаковать прямо сейчас!$b$bОбратись к капитану разведчиков Эльсии на Террасе Завоевателя. Она скажет, что тебе делать дальше.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'zhCN', '巨魔鼓声', '在征服者平台找到斥候队长艾尔希娅。', '你听见了吗，$c？在远处擂响的战鼓声。那是赞达拉巨魔们在准备迎战呢。$b$b我们可不能让他们安逸地部署防御。现在就得发起攻击！$b$b去征服者平台向斥候队长艾尔希娅回报。她会向你传达更多指示。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32524, 'zhTW', '贊達拉戰鼓響起', '找到征戍台的斥候隊長艾爾西雅。', '你聽到了嗎，$c？ 遠方響起隆隆的戰鼓聲，贊達拉準備朝我們進攻了。$b$b別讓他們有時間部署防禦，我們現在就得發動攻擊！$b$b向征戍台的斥候隊長艾爾西雅回報。她會給你下一步命令。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'deDE', 'Asche des Feindes', 'Verbrennt 8 mumifizierte Überreste.', 'Die Mogu im Hof der Knochen stocken ihre Armee auf, indem sie die Geister ihrer Gefallenen erwecken.$B$BWir dürfen nicht zulassen, dass sie ihre gefallenen Krieger wiederbeleben.$B$BNehmt diese Fackel und verbrennt alle mumifizierten Leichen, die Ihr finden könnt.$B$BWir werden unsere Feinde vernichten, bevor sie die Gelegenheit haben, aufzuerstehen. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'esES', 'Las cenizas del enemigo', 'Quema 8 restos momificados.', 'Los mogu de El Patio de los Huesos están reforzando sus filas resucitando los espíritus de sus difuntos.$B$BNo podemos permitirles que revivan a sus guerreros caídos.$B$BLlévate esta antorcha y quema todos los cadáveres momificados que veas.$B$BDestruiremos a nuestros enemigos antes de que tengan oportunidad de alzarse contra nosotros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'esMX', 'Las cenizas del enemigo', 'Quema 8 restos momificados.', 'Los mogu de El Patio de los Huesos están reforzando sus filas resucitando los espíritus de sus difuntos.$B$BNo podemos permitirles que revivan a sus guerreros caídos.$B$BLlévate esta antorcha y quema todos los cadáveres momificados que veas.$B$BDestruiremos a nuestros enemigos antes de que tengan oportunidad de alzarse contra nosotros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'frFR', 'Les cendres de l’ennemi', 'Brûlez 8 Restes momifiés.', 'Les mogu de la cour des Ossements sont en train de renforcer leur armée en ressuscitant les esprits des morts.$b$bNous ne pouvons pas les laisser ranimer leurs guerriers perdus !$b$bPrenez cette torche et brûlez tous les cadavres momifiés que vous pourrez trouver.$b$bNous détruirons nos ennemis avant qu’ils n’aient une chance de se relever.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'itIT', 'Ceneri del nemico', 'Brucia 8 Resti Mummificati.', 'I Mogu alla Corte delle Ossa stanno rinforzando la loro armata resuscitando gli spiriti dei loro morti.$B$BNon possiamo permettere che riescano nel loro intento.$B$BPrendi questa torcia e brucia tutti i corpi mummificati che riesci a trovare.$B$BDistruggeremo i nostri nemici prima che abbiano la possibilità di tornare in vita.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'koKR', '적의 유골', '미라가 된 유해를 8구 불태워야 합니다.', '망자의 궁정에 있는 모구들이 망자의 영혼을 부활시켜 군세를 강화하고 있어요.$B$B놈들이 쓰러진 전사를 살려내는 걸 보고만 있을 순 없어요.$B$B이 횃불을 가져가서 미라가 된 유해를 보이는 대로 태워 버리세요. 적이 흥성할 기회를 주지 않고 없애 버려야 해요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'ptBR', 'Cinzas do inimigo', 'Queime 8 Restos Mumificados.', 'Os mogus no Paço dos Ossos estão reforçando seu exército com espíritos ressuscitados.$B$BNão podemos permitir que eles revivam seus guerreiros caídos.$B$BPegue esta tocha e queime todos os cadáveres mumificados que encontrar.$B$BNós destruiremos nossos inimigos antes que consigam se erguer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'ruRU', 'Прах врагов', 'Сожгите 8 мумифицированных останков.', 'Могу во Дворе Костей увеличивают свою армию, воскрешая своих погибших воинов.$b$bМы не можем позволить этому продолжаться.$b$bВозьми этот факел и сожги все мумифицированные останки, какие найдешь.$b$bМы уничтожим наших врагов до того, как они будут иметь возможность подняться.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'zhCN', '敌人的骨灰', '超度8具干尸残骸。', '白骨庭院中的魔古族正在施法复活亡者之魂来扩充兵力。$B$B我们决不能任由他们让亡者士兵起死回生。$B$B带上这个火把，去超度你所找到的干尸遗骸。$B$B我们要在这些敌人复活之前先干掉他们。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32525, 'zhTW', '焚屍揚灰', '焚燒8個魔古乾屍。', '白骨之廷的魔古正在藉由復活他們的死者以壯大兵力。$B$B我們不能讓他們復活那些死去的戰士。$B$B用這把火炬燒掉你能找到的魔古乾屍。我們要在敵人有機會爬起來之前消滅他們。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'deDE', 'Seelenrettung', 'Rettet 6 Späher des Silberbunds oder der Sonnenhäscher.', 'Wir bekämpfen die Mogu im Hof der Knochen, aber ich fürchte, dass wir ihnen nur Material für ihre abscheulichen Rituale liefern.$B$BDie Mogu töten ihre Gefangenen nicht einfach – sie stehlen ihre Seelen und hauchen sie Gerätschaften aus Stein ein oder formen die Gefallenen zu geisterhaften Kriegsmaschinen um.$B$BHorde oder Allianz, es macht keinen Unterschied. Wir können den Mogu nicht erlauben ihre Reihen wieder aufzustocken.$B$BGeht zur Grabstätte. Befreit die Gefangenen, die noch leben.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'esES', 'Entrégame tu alma', 'Salva a 6 exploradores de El Pacto de Plata o Atracasol.', 'Atacamos a los mogu en El Patio de los Huesos, pero temo que solo les estemos enviando carne fresca para sus abominables rituales.$B$BLos mogu no se limitan a matar a sus prisioneros; les roban el alma y la implantan en máquinas de piedra, o reforjan a los caídos hasta convertirlos en fantasmales armas de guerra.$B$BDa igual si se trata de la Alianza o de la Horda; no podemos permitir que los mogu sigan creciendo.$B$BVe a las criptas y libera a los prisioneros que sigan vivos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'esMX', 'Entrégame tu alma', 'Salva a 6 exploradores de El Pacto de Plata o Atracasol.', 'Atacamos a los mogu en El Patio de los Huesos, pero temo que solo les estemos enviando carne fresca para sus abominables rituales.$B$BLos mogu no se limitan a matar a sus prisioneros; les roban el alma y la implantan en máquinas de piedra, o reforjan a los caídos hasta convertirlos en fantasmales armas de guerra.$B$BDa igual si se trata de la Alianza o de la Horda; no podemos permitir que los mogu sigan creciendo.$B$BVe a las criptas y libera a los prisioneros que sigan vivos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'frFR', 'Abandon d\'âme', 'Sauvez 6 Éclaireurs du Concordat argenté ou saccage-soleil.', 'Nous attaquons les mogu à la cour des Ossements, mais je crains que nous ne fassions que leur envoyer de la matière pour leurs rituels abominables.$B$BLes mogu ne se contentent pas de tuer leurs prisonniers ; ils volent leurs âmes et les emprisonnent dans des mécanismes de pierre, ou bien ils transforment les défunts en instruments de guerre spectraux.$B$BQu’ils fassent partie de l’Alliance ou de la Horde n’a pas d’importance. Nous ne pouvons pas laisser les mogu renforcer leur armée.$B$BEntrez dans les cryptes. Libérez tous les prisonniers encore vivants.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'itIT', 'Resa dell\'anima', 'Salva 6 Esploratori dei Predatori del Sole o Esploratori del Patto d\'Argento.', 'Stiamo assalendo i Mogu alla Corte delle Ossa, ma temo che i nostri uomini stiano soltanto cadendo vittime dei loro abominevoli rituali.$B$BI Mogu non uccidono semplicemente i loro prigionieri, ma rubano le loro anime e le infondono in congegni di pietra, o fanno rinascere i loro caduti come spettrali strumenti di guerra.$B$BChe si tratti di membri dell\'Orda o dell\'Alleanza, non possiamo permettere che i Mogu incrementino il loro esercito.$B$BDirigiti nelle cripte e libera ogni prigioniero che si trovi ancora in vita.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'koKR', '영혼의 굴복', '은빛 서약단 정찰병 또는 선리버 정찰병을 6명 구출해야 합니다.', '우리는 망자의 궁정을 공격하고 있지만, 우리 병사들을 적의 끔찍한 의식의 희생양으로 만들고 있는 건 아닌지 걱정이에요.$B$B모구들은 포로를 그냥 죽이지 않아요. 그 영혼을 빼앗아서 석상에 불어넣거나, 망자들을 유령 같은 전쟁 병기로 바꾸어 버리죠.$B$B얼라이언스든 호드든 상관없어요. 중요한 건 모구가 머릿수를 늘려서는 안 된다는 거죠.$B$B납골당에 들어가서 아직 살아 있는 포로가 있으면 풀어 주세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'ptBR', 'Rendição da alma', 'Resgate 6 Batedores do Pacto de Prata ou Batedores Fendessol.', 'Nós estamos atacando os mogus no Paço dos Ossos, mas temo que só estejamos fornecendo vítimas para seus rituais abomináveis.$B$BOs mogus não se contentam em matar os prisioneiros: eles roubam suas almas e as aprisionam em máquinas de pedra ou transformam os mortos em armas fantasmagóricas.$B$BAliança ou Horda, não importa. Nós não podemos permitir que os mogus aumentem suas fileiras$B$BVá até as criptas. Liberte todos os prisioneiros que ainda estiverem vivos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'ruRU', 'Освобождение душ', 'Спасите 6 разведчиков Серебряного Союза или разведчиков Похитителей Солнца.', 'Мы все время осаждаем могу во Дворе Костей, но боюсь, мы лишь поставляем им жертв для их ужасных ритуалов.$b$bМогу не просто убивают своих пленников. Они похищают их души и используют для оживления камня или превращают погибших в призрачные орудия войны.$b$bЯ не могу допустить, чтобы могу пополняли свои армии засчет разведчиков, и неважно, за кого они сражаются – за Орду или Альянс.$b$bОтправляйся к криптам и спустись в них. Освободи тех, кто еще остался в живых.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'zhCN', '失魂者', '解救6名银色盟约斥候或夺日者斥候。', '我们攻击了白骨庭院的魔古族，可恐怕只是在给他们那邪恶的仪式输送牺牲品而已。$B$B魔古族不仅会消灭他们的俘虏，还会偷走他们的灵魂，将其注入石头里，图谋不轨，或者将牺牲者铸成幽魂战争武器。$B$B联盟也好，部落也罢，身份并不重要。我们决不能让魔古族扩充军力。$B$B快到墓穴去，救出所有一息尚存的俘虏。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32526, 'zhTW', '靈魂也屈服', '拯救6名白銀誓盟斥候或奪日者斥候。', '我們朝白骨之廷的魔古發動攻擊，但我害怕我們只是讓部隊白白犧牲，成為他們可怕儀式的祭品。$B$B魔古不只殺掉囚犯，還會奪取他們的靈魂灌注到石像機關裡，或是將死者變成幽靈般的戰爭工具。$B$B囚犯是部落或聯盟並不重要，重要的是我們不能讓魔古繼續壯大下去。$B$B進入墓穴，救出還活著的囚犯。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'deDE', 'Gravierende Umstände', 'Tötet 10 Grabhüter der Shan\'ze, Seelenreißer der Shan\'ze oder Donnerrufer der Shan\'ze.', 'Der Hof der Knochen wimmelt von Truppen der Mogu. Ohne Unterlass führen sie ihre Rituale von Blitz und Donner aus.$B$BWenn wir unseren Auftrag, diese Insel zu erobern, erfüllen sollen, dürfen wir nicht zulassen, dass sie eine so wichtige strategische Position halten.$B$BSie müssen vernichtet werden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'esES', 'Una situación delicada', 'Mata a 10 guardianes de las tumbas Shan\'ze, desgarradores de almas Shan\'ze o clamatruenos Shan\'ze.', 'El Patio de los Huesos está plagado de guerreros mogu. Celebran sus rituales de relámpagos y truenos sin cesar.$B$BSi lo que queremos es tomar la isla, no podemos dejar que mantengan una posición tan estratégica.$B$BHay que eliminarlos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'esMX', 'Una situación delicada', 'Mata a 10 guardianes de las tumbas Shan\'ze, desgarradores de almas Shan\'ze o clamatruenos Shan\'ze.', 'El Patio de los Huesos está plagado de guerreros mogu. Celebran sus rituales de relámpagos y truenos sin cesar.$B$BSi lo que queremos es tomar la isla, no podemos dejar que mantengan una posición tan estratégica.$B$BHay que eliminarlos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'frFR', 'À tombeau ouvert', 'Tuez 10 Garde-tombes de Shan’ze, Faucheurs d’âme de Shan’ze ou Mande-tonnerres de Shan’ze.', 'La cour des Ossements grouille de mogu. Inlassablement, ils accomplissent leurs rituels de foudre et de tonnerre.$b$bSi nous voulons que notre mission de prise de contrôle de cette île soit couronnée de succès, nous ne pouvons pas les laisser tenir une telle position stratégique.$b$bIl faut les éliminer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'itIT', 'Circostanze funeree', 'Uccidi 10 Custodi delle Lapidi Shan\'ze, Squartatori d\'Anime Shan\'ze o Evocatuoni Shan\'ze.', 'La Corte delle Ossa pullula di forze dei Mogu, perennemente occupate a eseguire i loro rituali di tuoni e fulmini.$B$BPer assicurarci la conquista dell\'isola, dobbiamo impedire che mantengano una posizione così strategicamente importante.$B$BDevono essere fermati!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'koKR', '심각한 상황', '샨쩌 무덤지기, 샨쩌 영혼해체자, 또는 샨쩌 천둥술사 10명을 처치해야 합니다.', '망자의 궁정에는 모구 병사들이 득시글거려요. 그곳에서 끊임없이 번개와 천둥의 의식을 거행하고 있죠.$B$B이 섬을 점령하려는 우리 임무를 성공시키려면, 놈들이 전략적 우위를 점하도록 둘 순 없어요.$B$B막아야만 해요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'ptBR', 'Circunstâncias mortais', 'Mate 10 Guarda-tumbas Shan\'ze, Rasgalmas Shan\'ze ou Arautos do Trovão Shan\'ze.', 'O Paço dos Ossos está cheio de forças mogus. Eles realizam rituais de raio e trovão sem parar.$B$BSe quisermos cumprir nossa missão de tomar esta ilha, não podemos permitir que eles mantenham uma posição tão estratégica assim.$B$BEles precisam ser eliminados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'ruRU', 'Мертвое дело', 'Убейте 10 хранителей могил Шань\'цзэ, кромсателей душ Шань\'цзэ или призывателей грома Шань\'цзэ.', 'Двор Костей, что к югу отсюда, просто кишит могу. Они ни на минуту не прекращают проведение своих ритуалов с громом и молниями.$b$bЕсли мы хотим, чтобы наши планы завладеть этим островом успешно воплотились в жизнь, нам нельзя позволять могу удерживать эту стратегическую позицию.$b$bИх нужно ликвидировать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'zhCN', '严峻局势', '击败10名山泽守陵者、山泽夺魂者或山泽唤雷者。', '白骨庭院里挤满了魔古族大军。他们正在那里肆无忌惮地施展召唤雷电的仪式。$B$B如果我们想要成功占领这座岛屿，就不能放任他们占据如此重要的战略位置。$B$B必须消灭他们。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32527, 'zhTW', '墓況嚴峻', '殺死10個衫織守墓者、衫織撕魂者或衫織喚雷者。', '魔古部隊集結在白骨之廷，日夜不休地進行雷霆儀式。$B$B若我們想成功奪下這座島嶼，便不能讓他們佔據這個戰略要點。$B$B必須消滅他們。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'deDE', 'In die Krypta', 'Benutzt die Zauberformel von Gura in der Halle der Ehrwürdigen und besiegt dann Gura den Zurückgeforderten.', 'Jeden Tag finden wir weitere Relikte im Hof der Knochen.$B$BVor Kurzem haben wir zwischen den Grabsteinen in einer der tiefsten Krypten diese Schriftrolle gefunden.$B$BIch glaube, dass mit dieser Zauberformel bis in die Totenwelt gerufen werden kann. Und wenn meine Mogukenntnisse mich nicht täuschen, würde ich sagen, dass man sie im letzten Raum der Halle der Ehrwürdigen benutzen soll.$B$BWärt Ihr bereit, meine Theorie zu testen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'esES', 'Hacia las criptas', 'Usa el conjuro de Gura en la Cámara de los Eminentes y derrota a Gura el Reclamado.', 'Cada día descubrimos más reliquias en El Patio de los Huesos.$B$BHace poco, encontramos este pergamino en las lápidas de una de las criptas más profundas.$B$BCreo que este conjuro puede utilizarse para comunicarse con el más allá. Y si mi mogu no está demasiado oxidado, diría que deberías utilizarlo en la última sala de la Cámara de los Eminentes.$B$B¿Estás $gdispuesto:dispuesta; a demostrar mi teoría?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'esMX', 'Hacia las criptas', 'Usa el conjuro de Gura en la Cámara de los Eminentes y derrota a Gura el Reclamado.', 'Cada día descubrimos más reliquias en El Patio de los Huesos.$B$BHace poco, encontramos este pergamino en las lápidas de una de las criptas más profundas.$B$BCreo que este conjuro puede utilizarse para comunicarse con el más allá. Y si mi mogu no está demasiado oxidado, diría que deberías utilizarlo en la última sala de la Cámara de los Eminentes.$B$B¿Estás $gdispuesto:dispuesta; a demostrar mi teoría?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'frFR', 'Dans les cryptes', 'Utilisez l’Incantation de Gura dans la Salles des Exaltés, puis tuez Gura le Repris.', 'Chaque jour, nous découvrons de nouvelles reliques de la cour des Ossements.$B$BRécemment, nous avons trouvé ce parchemin parmi les pierres tombales d’une des cryptes les plus profondes.$B$BJe crois que cette incantation peut ramener une créature d’entre les morts. Et si mon mogu n’est pas trop rouillé, je dirais que vous devez l’utiliser dans le dernier segment de la Salle des Exaltés.$B$BAccepteriez-vous de vérifier ma théorie ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'itIT', 'Nelle cripte', 'Usa l\'Incanto di Gura nella Sala degli Osannati e uccidi Gura il Redento.', 'Ogni giorno, rinveniamo nuove reliquie alla Corte delle Ossa.$B$BRecentemente, abbiamo scoperto questa pergamena nascosta tra le lapidi di una delle cripte più profonde.$B$BDovrebbe avere a che fare con la resurrezione dei morti e, se la mia conoscenza della lingua Mogu non mi tradisce, credo che potresti utilizzarla nell\'ultima stanza della Sala degli Osannati.$B$BTi va di verificare la mia teoria?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'koKR', '납골당 안으로', '고귀한 자의 전당에서 구라의 주문을 사용한 후, 되찾은 자 구라를 꺾어야 합니다.', '망자의 궁정에서 매일같이 유물이 발굴되고 있어요.$B$B최근에 우리는 가장 깊은 납골당 중 하나의 묘비 사이에서 이 두루마리를 찾았어요.$B$B이 주문은 무덤 너머 있는 자를 부를 때 사용하는 것이라 짐작돼요. 제가 모구어를 제대로 알고 있다면, 이 두루마리를 고귀한 자의 전당 내 마지막 방에서 사용해야 할 거예요.$B$B제 가설을 시험해 보시겠어요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'ptBR', 'Cripta adentro', 'Use o Encantamento de Gura no Salão dos Exaltados. Depois, derrote Gura, o Recuperado.', 'Todos os dias descobrimos mais relíquias no Paço dos Ossos.$B$BRecentemente, nós encontramos um pergaminho entre as lápides em uma das criptas mais profundas.$B$BAcredito que este encantamento possa ser usado para chamar espíritos do além-túmulo. E se o meu moguzês estiver afiado, acho que deve ser usado na última sala do Salão dos Exaltados.$B$BVocê estaria dispost$go:a; a testar minha teoria?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'ruRU', 'В склепы', 'Воспользуйтесь чарами Гуры в Залах Превознесения, а затем победите Гуру Освобожденного.', 'Каждый день мы находим во Дворе Костей все новые реликвии.$b$bНедавно нашли среди могильных камней в самом глубоком склепе к западу отсюда этот свиток.$b$bЯ думаю, эта магическая формула вполне может использоваться для общения с загробным миром. И если я правильно понимаю язык могу, то ты можешь попытаться воспользоваться этой магической формулой в последней комнате Залов Превознесения.$b$bПроверишь мою теорию?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'zhCN', '进入墓穴', '在至尊殿使用古拉的咒语，然后击败重生的古拉。', '每一天，我们都会从白骨庭院发现更多的遗物。$B$B最近，他们在其中一处最深处墓穴的石碑上找到了这个卷轴。$B$B我相信这个咒语能够唤起坟墓中的死者。如果我的魔古知识没错，那你应该到至尊殿的最后一个房间里使用它。$B$B你愿意去验证一下我的理论吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32528, 'zhTW', '進入墓地', '在崇者大廳使用古拉的咒卷，然後擊敗『回煞魔』古拉。', '我們每天都會從白骨之廷挖掘出新的文物。$B$B最近我們在某個極深的墓穴中，在碑林裡發現了這個卷軸。$B$B我相信這個咒卷能用來召喚墓地的死者。假如我的魔古語沒有生疏的話，我想你應該在崇者大廳的最後一間房間使用這個卷軸。$B$B你願意去測試我的理論嗎?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'deDE', 'Der Ruf des Donners', 'Unterbrecht das Ritual der Donnerrufer der Shan\'ze.', 'Auf dem Friedhof, in den tiefsten Ruinen der westlichen Krypta, laden die mächtigsten Zauberer des Donnerkönigs einen Blitzableiter mit knisternder Elektrizität auf.$B$BWir vermuten, dass mit dieser Waffe unsere Festung auf der Violetten Erhebung zerstört werden soll.$B$BWir dürfen keine Zeit verlieren. Ihr müsst das Ritual sofort stoppen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'esES', 'El clamor del trueno', 'Detén el ritual de los clamatruenos Shan\'ze.', 'En el cementerio, en las ruinas más profundas de la cripta que hay al oeste, los hechiceros más poderosos del Rey del Trueno están cargando un pararrayos con electricidad crepitante.$B$BSospechamos que esta arma está pensada para destruir el bastión que hemos levantado en El Alto Violeta.$B$BNo hay un segundo que perder. Debes detener el ritual ahora.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'esMX', 'El clamor del trueno', 'Detén el ritual de los clamatruenos Shan\'ze.', 'En el cementerio, en las ruinas más profundas de la cripta que hay al oeste, los hechiceros más poderosos del Rey del Trueno están cargando una vara de relámpagos con electricidad crepitante.$B$BSospechamos que esta arma está pensada para destruir el bastión que hemos levantado en El Alto Violeta.$B$BNo hay un segundo que perder. Debes detener el ritual ahora.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'frFR', 'L’appel du tonnerre', 'Mettez un terme au Rituel des Mande-tonnerre de Shan’ze.', 'Dans l’enceinte du cimetière, dans la partie la plus profonde des ruines de la crypte ouest, les plus puissants ensorceleurs du roi-tonnerre sont en train de charger un bâton de foudre d’électricité crépitante.$B$BNous pensons que cette arme ferait pleuvoir la destruction sur notre bastion à la cime Pourpre.$B$BIl n’y a pas de temps à perdre. Vous devez immédiatement mettre un terme à leur rituel.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'itIT', 'Rombo di tuono', 'Ferma il rituale degli Evocatuoni Shan\'ze.', 'Al cimitero, nelle rovine più profonde della cripta occidentale, i seguaci del Re del Tuono stanno caricando di elettricità un pilastro fulminante.$B$BSospettiamo che quest\'arma sia stata ideata per distruggere la nostra guarnigione alla Terrazza Violacea.$B$BNon c\'è tempo da perdere. Devi fermare immediatamente il loro rituale.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'koKR', '천둥의 부름', '샨쩌 천둥술사의 의식을 저지해야 합니다.', '무덤 안, 서쪽 납골당 가장 깊은 곳에 있는 폐허에서 천둥왕의 강력한 마술사들이 피뢰침에 불꽃 튀는 전기를 충전하고 있어요.$B$B보랏빛 마루의 우리 요새를 공격하기 위한 무기일 거예요.$B$B지체할 시간이 없어요. 당장 의식을 멈춰야 해요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'ptBR', 'O chamado do trovão', 'Interrompa o ritual dos Arautos do Trovão Shan\'ze.', 'No cemitério, nas ruínas mais profundas da cripta oeste, os mais poderosos feiticeiros do Rei Trovão estão energizando um para-raios com eletricidade.$B$BSuspeitamos que essa arma vá ser usada para atacar nossa posição no Pico Violeta.$B$BNão há tempo a perder. Você precisa impedir o ritual imediatamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'ruRU', 'Ритуал вызова грома', 'Прервите ритуал призывателей грома Шань\'цзэ.', 'Во Дворе Костей, в глубине руин западной крипты, самые могущественные колдуны Властелина Грома заряжают грозовой разрядник электричеством.$b$bПо-видимому, они планируют использовать это грозное оружие, чтобы разрушить нашу крепость на Аметистовом утесе.$B$BНужно немедленно остановить их ритуал.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'zhCN', '雷电的召唤', '阻止山泽唤雷者的仪式。', '在白骨庭院里，西边墓穴最深处的废墟间，雷电之王麾下最强大的巫师们正在用劈啪作响的电流为一柄闪电权杖充能。$B$B我们怀疑，这把武器将会给我们位于紫罗兰高地的据点带来毁灭性的灾难。$B$B没时间耽搁了。你必须立即阻止他们的仪式。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32529, 'zhTW', '喚雷', '阻止衫織喚雷者的儀式。', '在墓地裡頭，西邊墓穴最深處的廢墟裡，雷王最強大的巫士正以猛爆的電力為閃電之柱充能。$B$B我們懷疑這個武器是要用來摧毀紫羅蘭高崗的要塞。$B$B事不宜遲，你得立即阻止他們的儀式。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'deDE', 'Der Blutvergießer', 'Tötet Kaida den Blutvergießer.', 'Mir ist ein verstörender Bericht zu Ohren gekommen, $C.$B$BGanz in der Nähe, im Hof der Knochen, bringen die Mogu ihre Gefangenen zu einem General namens \"Kaida der Blutvergießer\".$B$BIch erspare Euch die Details, aber er lebt dafür, seine Opfer zu foltern.$B$BDieses Monster muss aufgehalten werden. Solltet Ihr seiner Klinge zum Opfer fallen, wünsche ich Euch einen raschen Tod.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'esES', 'El Sádico', 'Mata a Kaida el Sádico.', 'Ha llegado a mí un informe inquietante, $c.$B$BMás allá de nuestra base, en El Patio de los Huesos, los mogu traen cautivos a un general llamado Kaida el Sádico.$B$BSe regocija torturando a sus víctimas.$B$BHay que detener a ese monstruo. Si te vence, espero que tengas una muerte rápida.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'esMX', 'El Sádico', 'Mata a Kaida el Sádico.', 'Ha llegado a mí un informe inquietante, $c.$B$BMás allá de nuestra base, en El Patio de los Huesos, los mogu traen cautivos a un general llamado Kaida el Sádico.$B$BSe regocija torturando a sus víctimas.$B$BHay que detener a ese monstruo. Si te vence, espero que tengas una muerte rápida.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'frFR', 'Le Saigneur', 'Tuez Keida le Saigneur.', 'J’ai reçu un rapport assez troublant, $c.$b$bJuste au-delà de notre position, dans la cour des Ossements, les mogu amènent leurs prisonniers devant un général nommé Keida le Saigneur.$b$bIl se délecte des tortures qu’il inflige à ses victimes.$b$bCe monstre doit être arrêté. Si vous échouez face à sa lame, je ne peux que vous souhaiter une mort rapide…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'itIT', 'Lo Spargisangue', 'Uccidi Kaida lo Spargisangue.', 'Ho sentito alcuni rapporti inquietanti, $C.$B$BProprio dietro di noi, nella Corte delle Ossa, i Mogu portano i loro prigionieri al cospetto di un generale di nome Kaida lo Spargisangue.$B$BEgli adora torturare le sue vittime.$B$BQuesto mostro deve essere fermato. Se dovessi perire per mano sua, ti auguro solo che la tua morte sia rapida.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'koKR', '피를 부르는 자', '피를 부르는 카이다를 처치해야 합니다.', '심란한 소식을 들었어요, $c님.$B$B우리 진지 바로 너머 망자의 궁정에 있는 모구들이 포로들을 피를 부르는 카이다라는 장군에게 데려가고 있다고 해요.$B$B자세한 내용은 모르시는 편이 나을 듯해 말하지 않겠지만, 놈은 포로를 고문하는 낙으로 산다고 해요.$B$B이 괴물을 막아야 해요. 혹시나 놈의 검 앞에 쓰러지신다면, 빠른 죽음을 맞기를 빌게요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'ptBR', 'O Dessangrador', 'Mate Kaida, o Dessangrador.', 'Eu ouvi um relato preocupante, $c.$B$BUm pouco além de nossa posição, no Paço dos Ossos, os mogus levam os prisioneiros para um general chamado Kaida, o Dessangrador.$B$BEle gosta muito de torturar as vítimas.$B$BEsse monstro precisa ser detido. Se você for derrubad$go:a; pela lâmina dele, espero que tenha uma morte rápida.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'ruRU', 'Кровопускатель', 'Убейте Кайду Кровопускателя.', 'До меня дошла тревожная информация, |3-6($c).$b$bВо Дворе Костей могу отправляют своих пленников к генералу по имени Кайда Кровопускатель.$b$bОн получает удовольствие от того, что пытает своих жертв.$b$bЭто чудовище нужно остановить. А если тебе суждено погибнуть от его клинка, то я желаю тебе быстрой смерти.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'zhCN', '放血者', '击败放血者喀岱。', '我收到了一份令人不安的报告，$c。$B$B在距离我们不远的白骨庭院中，魔古族会将俘虏都交给一个名叫放血者喀岱的将军。$B$B他惯于拷打受害者。$B$B我们必须阻止这个怪物。如果你倒在他的剑下，那我只能祈祷你能速死。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32530, 'zhTW', '『放血者』凱達 ', '殺死『放血者』凱達。', '我聽到一件令人煩惱的報告，$c。$B$B在我們據地的更遠處，位於白骨之廷裡，魔古將俘虜獻給名為『放血者』凱達的將軍。$B$B這位將軍酷愛凌虐囚犯。$B$B我們得阻止這個怪物。若你不幸倒在他的刀下，願你能死個痛快。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'deDE', 'Die steinerne Eroberung', 'Unterbrecht das Ritual des uralten Steineroberers im Haupttempel im Hof der Knochen.', 'Im Hof der Knochen führen die Zauberer des Donnerkönigs finstere Rituale durch.$B$BUm ihre Truppen zu verstärken, hauchen die Seelenreißer der Shan\'ze uralten Steinstatuen die Geister der Lebenden ein.$B$BSind diese Steineroberer einmal zum Leben erweckt, werden sie furchterregende Gegner auf dem Schlachtfeld sein.$B$BWir müssen ihr Ritual unterbrechen, bevor diese Statuen erweckt werden können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'esES', 'A la conquista de las piedras', 'Interrumpe el ritual de los conquistadores de piedra antiguos en el templo principal de El Patio de los Huesos.', 'En El Patio de los Huesos, los hechiceros del Rey del Trueno se dedican a realizar rituales oscuros.$B$BCon tal de reforzar sus filas, los desgarradores de almas Shan\'ze están imbuyendo antiguas estatuas de piedra de espíritus arrebatados a los vivos.$B$BDespués de que resuciten a estos conquistadores, serán unos adversarios temibles en el campo de batalla.$B$BHay que detener el ritual antes de que puedan revivir a las estatuas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'esMX', 'A la conquista de las piedras', 'Interrumpe el ritual de los conquistadores de piedra antiguos en el templo principal de El Patio de los Huesos.', 'En El Patio de los Huesos, los hechiceros del Rey del Trueno se dedican a realizar rituales oscuros.$B$BCon tal de reforzar sus filas, los desgarradores de almas Shan\'ze están imbuyendo antiguas estatuas de piedra de espíritus arrebatados a los vivos.$B$BDespués de que resuciten a estos conquistadores, serán unos adversarios temibles en el campo de batalla.$B$BHay que detener el ritual antes de que puedan revivir a las estatuas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'frFR', 'La conquête de pierre', 'Perturbez le rituel de l’Ancien conquérant de pierre dans le temple principal de la Cour des Ossements.', 'Dans la cour des Ossements, les sorciers du roi-tonnerre accomplissent de sombres rituels.$b$bAfin de renforcer leur nombre, des faucheurs d’âme de Shan’ze imprègnent d’anciennes statues avec des esprits volés aux vivants.$b$bUne fois ces conquérants de pierre animés, ils deviennent de redoutables adversaires sur le champ de bataille.$b$bNous devons faire cesser leurs rituels avant que ces statues ne prennent vie.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'itIT', 'La conquista della pietra', 'Interrompi il rituale del Conquistatore della Pietra Antico al tempio principale nella Corte delle Ossa.', 'Nella Corte delle Ossa, gli stregoni del Re del Tuono eseguono oscuri rituali.$B$BPer rinforzare le proprie file, gli Squartatori d\'Anime Shan\'ze stanno infondendo alcune antiche statue di pietra con gli spiriti rubati dai viventi.$B$BSe queste creature di pietra prendessero vita, causerebbero un\'inimmaginabile distruzione sul campo di battaglia.$B$BDobbiamo fermare il rituale prima che ciò accada.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'koKR', '바위 정복', '망자의 궁정에 있는 주 사원에서 고대 바위 정복자 의식을 중단시켜야 합니다.', '망자의 궁정에서 천둥왕의 마법사들이 사악한 의식을 거행하고 있어요.$B$B샨쩌 영혼해체자들이 고대의 석상에 살아 있는 자로부터 훔친 영혼을 불어넣어 군세를 늘리고 있는 것이죠.$B$B바위 정복자들이 살아나면 전장에서 무서운 적이 될 거예요.$B$B놈들이 석상을 살려 내기 전에 의식을 중단시켜야 해요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'ptBR', 'A conquista de pedra', 'Interrompa o ritual do Antigo Conquistador de Pedra no templo principal do Paço dos Ossos.', 'No Paço dos Ossos, feiticeiros do Rei Trovão realizam rituais sombrios.$B$BPara reforçar seu contingente, os Rasgalmas Shan\'ze estão imbuindo antigas estátuas de pedra em espíritos roubados dos vivos.$B$BQuando esses conquistadores de pedra forem animados, eles se tornarão oponentes formidáveis no campo de batalha.$B$BPrecisamos deter esse ritual antes que as estátuas sejam ressuscitadas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'ruRU', 'Одолеть камень', 'Остановите ритуал по оживлению древнего каменного завоевателя в главном храме Двора Костей.', 'Во Дворе Костей проводят свои темные ритуалы чародеи Властелина Грома.$B$BЧтобы увеличить численность своей армии, кромсатели душ Шань\'цзэ запечатывают украденные у живых души в каменные статуи.$B$BПосле этого каменных завоевателей оживляют и они превращаются в опасных противников на поле боя.$B$BНужно остановить ритуал до того, как статуи оживут.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'zhCN', '征服岩石', '打断在白骨庭院主殿中举行的古代岩石征服者仪式。', '在白骨庭院中，雷电之王麾下的巫师们正在举行黑暗的仪式。$B$B为扩充军力，山泽夺魂者们将从生者身上偷取的灵魂，注入古老的石头雕像里。$B$B当这些岩石征服者被赋予生命后，他们就会成为战场上可怕的敌手。$B$B我们必须在这些雕像被复活前，阻止他们的仪式。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32531, 'zhTW', '石征者', '中斷白骨之廷主廟裡的上古石征者儀式。', '雷王的巫士在白骨之廷裡進行黑暗儀式。$B$B衫織撕魂者將奪來的生者靈魂灌注到上古石像中，以壯大他們的陣容。$B$B這些石征者要是活了過來，將會成為戰場上的強敵。$B$B我們必須在石像甦醒之前阻止他們的儀式。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'deDE', 'Erhebt Euch nie wieder!', 'Tötet 15 auferstandene Urahnen.', 'Habt Ihr die Geister gesehen, die im Friedhof umherwandern?$B$BDas sind auferstandene Urahnen, die in diese Welt zurückgerufen wurden.$B$BWerden sie verärgert, greifen sie schnell und lautlos an, und es werden von Tag zu Tag mehr.$B$BSie gehören nicht hierher. Vernichtet sie und schickt sie zurück in das Totenreich.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'esES', 'Almas en pena', 'Mata a 15 ancestros resucitados.', '¿Has visto a los fantasmas pululando por el cementerio?$B$BSon ancestros resucitados, espíritus de mogu asesinados traídos de vuelta a este mundo.$B$BCuando se les enfurece, atacan con presteza y sigilo, y cada día son más.$B$BNo pertenecen a este mundo. Destrúyelos y haz que vuelvan a ultratumba.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'esMX', 'Almas en pena', 'Mata a 15 ancestros resucitados.', '¿Has visto a los fantasmas vagando por el cementerio?$B$BSon ancestros resucitados, espíritus de mogu asesinados traídos de vuelta a este mundo.$B$BCuando se enfurecen, atacan con presteza y sigilo, y cada día son más.$B$BNo pertenecen a este mundo. Destrúyelos y haz que vuelvan a ultratumba.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'frFR', 'Morts pour de bon !', 'Tuez 15 Ancêtres ressuscités.', 'Avez-vous vu les fantômes qui errent dans le cimetière ?$B$BCe sont des ancêtres ressuscités, les esprits de mogu tués qui ont été rappelés dans ce monde.$B$BQuand ils sont en colère, ils attaquent rapidement et silencieusement, et leur nombre croît de jour en jour.$B$BLeur place n’est pas ici. Anéantissez-les et renvoyez-les dans l’au-delà.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'itIT', 'Fermare la resurrezione!', 'Uccidi 15 Antenati Risorti.', 'Hai visto tutti quei fantasmi che vagano nel cimitero?$B$BSono gli Antenati Risorti, gli spiriti dei Mogu uccisi che sono stati riportati in questo mondo.$B$BSe provocati, attaccano rapidamente e silenziosamente, e il loro numero è in continua crescita.$B$BLoro non appartengono a questo mondo: devi distruggerli e rispedirli nelle loro tombe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'koKR', '이제 일어나지 말지어다!', '되살아난 선조 15명을 처치해야 합니다.', '묘지를 배회하는 유령들을 보셨나요?$B$B그들은 되살아난 선조예요. 이 세상으로 다시 불려온 죽은 모구의 영혼이죠.$B$B그들은 화가 나면 소리 없이 빠르게 공격하고, 그 수도 매일같이 불어나요.$B$B그들은 이곳에 있어서는 안 될 존재예요. 그들을 처치해서 무덤 저편의 세상으로 돌려 보내세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'ptBR', 'Não se levantem mais!', 'Mate 15 Ancestrais Reanimados.', 'Você viu os fantasmas vagando pelo cemitério?$B$BEles são Ancestrais Reanimados, espíritos de mogus mortos que foram chamados de volta para este mundo.$B$BQuando ficam irritados, atacam de forma rápida e silenciosa, e seus números crescem a cada dia.$B$BEles não podem ficar aqui. Destrua-os e mande-os de volta para o além.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'ruRU', 'Покончить с восставшими', 'Убейте 15 восставших предков.', 'Ты $gвидел:видела; призраков на кладбище?$b$bЭто восставшие предки, духи погибших могу, которых призвали в этот мир.$B$BЕсли их разозлить, они атакуют быстро и бесшумно, и число их растет с каждым днем.$B$BОни не принадлежат этому миру. Уничтожь их и заставь вернуться обратно в могилы.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'zhCN', '别再回来了！', '击败15个复生的先祖。', '你看见那些在坟场中徘徊的鬼魂了吗？$B$B他们是复生的先祖，是被重新召唤到这个世界上来的，魔古族死者的灵魂。$B$B当被激怒时，他们会迅捷无声地发起攻击，他们的数量也在日增夜长。$B$B他们不属于这里。去消灭他们，把他们送回坟墓里去。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32532, 'zhTW', '打倒那些亡靈!', '殺死15名甦醒的古代亡靈。', '你是否見到墓地裡那些遊蕩的鬼魂?$B$B它們是甦醒的古代亡靈，被喚回世上的魔古亡魂。$B$B它們憤怒時攻擊快狠無聲，而且它們的數量日漸增加。$B$B這些亡靈不屬於這裡。消滅他們，把他們送回墳裡。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'deDE', 'Marmor, Stein und Qilen bricht', 'Zerstört 8 Grabwächter.', 'Unser Angriff auf den Hof der Knochen schreitet voran. Unsere Fortschritte sind weiterhin langsam, aber messbar.$B$BLeider haben wir mehrere Berichte über steinerne Qilenstatuen erhalten, die ohne Vorwarnung zum Leben erwachen. Diese Qilen sind zu gefährlich, um sie zu ignorieren.$B$BBegebt Euch in den Hof der Knochen und zerstört alle Qilenstatuen, die Ihr finden könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'esES', 'Petrificado de miedo', 'Destruye a 8 guardalápidas.', 'Nuestro asalto a El Patio de los Huesos continúa. Sin prisa pero sin pausa.$B$BDesgraciadamente, hemos recibido varios informes de estatuas quilen de piedra que resucitan sin previo aviso. Estos quilen son demasiado peligrosos para ignorarlos.$B$BAdéntrate en El Patio de los Huesos y destruye todas las estatuas quilen que encuentres.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'esMX', 'Petrificado de miedo', 'Destruye a 8 guardalápidas.', 'Nuestro asalto a El Patio de los Huesos continúa. Sin prisa pero sin pausa.$B$BDesgraciadamente, hemos recibido varios informes de estatuas quilen de piedra que resucitan sin previo aviso. Estos quilen son demasiado peligrosos para ignorarlos.$B$BAdéntrate en El Patio de los Huesos y destruye todas las estatuas quilen que encuentres.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'frFR', 'Froid comme la pierre', 'Détruisez 8 Garde-tombes.', 'Notre assaut sur la cour des Ossements continue. Nos progrès sont lents, mais perceptibles.$B$BMalheureusement, on nous a signalé que plusieurs quilen de pierre se sont animés sans prévenir. Ces statues sont trop dangereuses pour qu’on les ignore.$B$BAventurez-vous dans la cour des Ossements et détruisez toutes les statues quilen que vous trouverez.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'itIT', 'Freddo come la pietra', 'Distruggi 8 Guardiani della Tomba.', 'Il nostro assalto alla Corte delle Ossa continua. I nostri progressi sono lenti, ma apprezzabili.$B$BSfortunatamente, abbiamo ricevuto molti rapporti riguardo a statue di Quilen di pietra che prendono vita senza preavviso. La minaccia rappresentata da questi Quilen è troppo pericolosa perché venga ignorata.$B$BAvventurati nella Corte delle Ossa e distruggi tutte le statue di Quilen che riesci a trovare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'koKR', '돌처럼 차가운', '무덤 수호자를 8마리 파괴해야 합니다.', '우리는 망자의 궁정에 대한 공세를 늦추지 않고 있어요. 느리지만 착실히 앞으로 나아가고 있죠.$B$B그런데 불행하게도, 기렌 석상이 갑작스레 살아나는 경우가 있다는군요. 이 기렌들을 그냥 두면 큰일이 날지도 몰라요.$B$B망자의 궁정으로 가서 기렌 석상을 눈에 띄는 대로 파괴해 주세요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'ptBR', 'Coração de pedra (e rins, e fígado, e...)', 'Destrua 8 Guardiões das Tumbas.', 'Nosso ataque ao Paço dos Ossos continua. Nosso progresso continua lento, mas mensurável.$B$BInfelizmente, recebemos vários relatórios de estátuas de pedra quílen voltando à vida sem aviso. Esses quílens são perigosos demais para os ignorarmos.$B$BVá até o Paço dos Ossos e destrua quaisquer estátuas quílen que você encontrar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'ruRU', 'Каменный холод', 'Уничтожьте 8 могильных стражей.', 'Наше наступление на Двор Костей продолжается. Мы продвигаемся вперед не слишком быстро, но все же прогресс очевиден.$b$bК несчастью, мы получили несколько сообщений о том, что каменные статуи цийлиней неожиданно начали оживать. Эти цийлини слишком опасны, чтобы не обращать на них внимания.$b$bОтправляйся во Двор Костей и уничтожь все статуи цийлиней, какие найдешь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'zhCN', '冷血石像', '摧毁8个陵寝守卫者。', '我们对白骨庭院的攻击仍在继续。尽管进展依旧缓慢，但却相当可观。$B$B不幸的是，我们收到了几份报告，接连有石魁麟雕像在毫无征兆的情况下就活了过来。这些魁麟太过危险，决不能放任不管。$B$B前往白骨庭院，消灭你所能找到的所有魁麟雕像。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32533, 'zhTW', '冷血石獸', '摧毀8個墓穴守護者。', '我們對白骨之廷的攻擊持續進行中，進展雖然緩慢，但是成效顯著。$B$B在這個節骨眼上，我們卻收到好幾名斥候回報說，麒麟獸石像無預警地復活了。這些麒麟獸是不容小看的強敵。$B$B潛入白骨之廷，毀掉所有你能找到的麒麟獸石像!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'deDE', 'Die Skumklingen-Bedrohung', 'Tötet 10 Saurok der Skumklingen.', 'Die Ihgalukklamm wimmelt von Saurok. Diese Bestien greifen meine Männer an, breiten ihre Krankheit auf Wild und Wasser in der Nähe aus und stören unsere Arbeit.$b$bDünnt ihre Reihen aus, $n. Wenn die Allianz diese Insel erst unter Kontrolle hat, können wir uns über permanentere Lösungen Gedanken machen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'esES', 'La amenaza Hojaescoria', 'Mata a 10 saurok Hojaescoria.', 'Los saurok se reproducen como esporas en el Risco Ihgaluk. Esas bestias atacan a mis hombres, propagan su enfermedad por el agua y por toda la fauna de la zona y acaban interfiriendo en nuestras operaciones.$b$bDiézmalos, $n. Ya pensaremos en soluciones más permanentes cuando la Alianza se apodere completamente de la isla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'esMX', 'La amenaza Hojaescoria', 'Mata a 10 saurok Hojaescoria.', 'Los saurok se reproducen como esporas en el Risco Ihgaluk. Esas bestias atacan a mis hombres, propagan su enfermedad por el agua y por toda la fauna de la zona y acaban interfiriendo en nuestras operaciones.$b$bDiézmalos, $n. Ya pensaremos en soluciones más permanentes cuando la Alianza se apodere completamente de la isla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'frFR', 'La menace crasselame', 'Tuez 10 Sauroks crasselames.', 'La combe d’Ihgaluk est un nid à sauroks intarissable. Ces bêtes attaquent mes hommes et répandent leur infection dans l’eau et la nature proches en interférant avec nos opérations.$b$bÉclaircissez leurs rangs, $n. Cette île totalement sous le contrôle de l’Alliance, nous pourrons étudier des solutions à plus long terme.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'itIT', 'La minaccia dei Lamalercia', 'Uccidi 10 Saurok Lamalercia.', 'Sembra che a Ihgaluk i Saurok non finiscano mai. Quelle bestie attaccano i miei uomini e diffondono la loro feccia tra la fauna e le acque circostanti, interferendo con le nostre operazioni.$b$bAssottiglia i loro ranghi, $n. Una volta che l\'Alleanza assumerà il controllo totale dell\'isola, potremo pensare a una soluzione più definitiva al problema.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'koKR', '부랑칼날의 위협', '부랑칼날 사우록을 10마리 처치해야 합니다.', '이갈룩 바위산은 사우록으로 들끓고 있어요. 그 괴물들은 제 부하들을 공격하고 근처의 물과 야생동물에 질병을 퍼뜨려서 우리 작전을 방해하고 있지요.$b$b놈들의 수를 줄여 주세요, $n 님. 얼라이언스가 이 섬을 장악하고 나서 보다 근본적인 해결책을 찾으면 돼요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'ptBR', 'A ameaça Gumeskuma', 'Mate 10 sauroks Gumeskuma.', 'O Penhasco Ihgaluk tem sauroks que não acabam mais! Essas feras atacam os meus homens, contaminam a água e infectam a vida selvagem, interferindo nas nossas operações.$b$bReduza a população deles, $n. Quando a Aliança dominar completamente esta ilha, poderemos procurar soluções mais definitivas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'ruRU', 'Угроза Гнилого Клинка', 'Убейте 10 сауроков Гнилого Клинка.', 'Саурокам на утесах Ихгалук просто нет числа. Эти твари нападают на моих воинов и заражают воду и живую природу вокруг себя, мешая проведению наших операций.$b$bСократи их число, $n. А когда этот остров окажется в руках Альянса, мы сможем подумать о чем-то более основательном.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'zhCN', '邪刃的威胁', '击败10个邪刃蜥蜴人。', '蜥蜴人正源源不断地从伊瓜鲁克峭壁上涌出来。那些畜生不但攻击我的士兵，还在附近的水源和野生动物中间传播病菌，妨碍我们的行动。$b$b快去削减他们的数量，$n。一旦联盟完全控制了这片岛屿，我们就可以转而寻求更加彻底的解决办法了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32535, 'zhTW', '污刃威脅', '殺死10名污刃薩烏洛克。', '伊加路克峭壁上到處都是薩烏洛克。這些野獸攻擊我的手下，並將疾病傳染到鄰近的水域與野生動物身上，干擾我們的行動。$b$b削減他們的數量，$n。一旦聯盟完全掌控這座島，我們便能找出永久的解決辦法。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'deDE', 'Die Saurok manipulieren', 'Meldet Euch bei Magister Yalis, Magister Lyanis und Magistrix Sanal.', 'Ich habe drei Magister in die Ihgalukklamm geschickt, um die Saurok zu überwachen und sie – wenn möglich – gegen die Mogu aufzuwiegeln. Ihr müsst sie für mich finden und herausfinden, ob sie Versorgung oder Hilfe benötigen.$b$bSie sind wahrscheinlich versteckt. Haltet nach schimmernden Lichtsäulen im Dorf der Saurok Ausschau.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'esES', 'Cómo manipular a un saurok', 'Preséntate ante los magistri Yalis, Lyanis y Sanal.', 'Tengo a tres magistri atrincherados en el Risco Ihgaluk, vigilando de cerca a los saurok y volviéndolos en contra de los mogu como buenamente pueden. Quiero que los encuentres y que veas si necesitan ayuda o suministros de algún tipo.$b$bNo podrás verlos. Busca las columnas relucientes de luz en el poblado saurok.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'esMX', 'Cómo manipular a un saurok', 'Preséntate ante los magistri Yalis, Lyanis y Sanal.', 'Tengo a tres magistri atrincherados en el Risco Ihgaluk, vigilando de cerca a los saurok y, cuando se puede, volviéndolos en contra de los mogu. Quiero que los encuentres y que veas si necesitan ayuda o suministros de algún tipo.$b$bNo podrás verlos. Busca las columnas relucientes de luz en el poblado saurok.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'frFR', 'La manipulation des sauroks', 'Présentez-vous au Magistère Yalis, au Magistère Lyanis, et à la Magistrice Sanal.', 'J’ai trois magistères retranchés à la combe d’Ihgaluk, où ils surveillent les sauroks et les montent contre les mogu, lorsque c’est possible. Je veux que vous alliez les trouver pour voir s’ils ont besoin de matériel ou d’un coup de main.$b$bIls seront dissimulés, alors cherchez des colonnes de lumière scintillante dans le village saurok.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'itIT', 'Manipolare i Saurok', 'Parla con il Magistro Yalis, il Magistro Lyanis e la Magistra Sanal.', 'Ho inviato tre magistri a Ihgaluk per tenere sott\'occhio i Saurok e, quando possibile, aizzarli contro i Mogu. Ho bisogno che tu li trovi e che scopra se necessitano di provviste o assistenza.$b$bI tre sono sotto copertura: per trovarli dovrai cercare le colonne luminose all\'interno del villaggio dei Saurok.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'koKR', '사우록 조종하기', '마법학자 야리스, 마법학자 리아니스, 마법학자 사날을 찾아가야 합니다.', '저는 이갈룩 바위산에 마법학자 셋을 잠복시켰어요. 그들은 사우록을 감시하면서 사우록과 모구 사이를 이간질하고 있죠. 그들을 찾아가서 보급품이나 지원이 필요한지 확인해 주세요.$b$b맨눈으로는 그들이 보이지 않을 거예요. 사우록 마을에서 희미한 빛 기둥을 찾아 보세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'ptBR', 'Manipulando os sauroks', 'Fale com Magíster Yalis, Magíster Lyanis e Magistra Sanal.', 'Três dos meus magísteres estão entrincheirados no Penhasco Ihgaluk, monitorando os sauroks e, sempre que possível, voltando os lagartos contra os mogus. Preciso que você os encontre e veja se eles precisam de suprimentos ou ajuda.$B$BEles não estarão à vista. Procure pelas colunas tremeluzentes na aldeia saurok.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'ruRU', 'Манипулирование сауроками', 'Поговорите с магистрами Ялисом, Лианисом и Саналией.', 'На утесах Ихгалук работают три магистра. Они следят за сауроками и всеми силами стараются настроить их против могу. Я хочу, чтобы ты $gнашел:нашла; их и $gузнал:узнала;, не нужны ли им припасы или помощь.$b$bОни находятся в укрытиях. Ищи мерцающие столпы света в деревне сауроков.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'zhCN', '掌控蜥蜴人', '向魔导师亚利斯、魔导师莱尼斯和魔导师莎纳尔报到。', '我向伊瓜鲁克峭壁派出了三名魔导师，以便监视这些不友好的蜥蜴人邻居，顺便看看能不能策反他们对抗魔古族。我要你去找到他们，看看他们是否需要补给或帮助。$b$b他们都是隐身的。你只要找蜥蜴人村子里那些闪烁的光柱就行了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32536, 'zhTW', '操弄薩烏洛克', '找到博學者雅里斯、博學者萊安納斯與博學者薩納爾。', '我讓三位博學者躲在伊加路克峭壁監視薩烏洛克，有機會的時候誘導他們對抗魔古。我需要你去找他們，看他們是否需要補給品或協助。$b$b他們躲起來不讓人發現，注意薩烏洛克村莊中閃閃發亮的光柱。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'deDE', 'Dekonstruktion', 'Beschafft die vier gestohlenen Konstrukte des Silberbunds wieder.', 'Die Saurok testen unaufhörlich unsere Grenzen aus, und normalerweise können unsere Konstrukte sie zurückschlagen. Einige Konstrukte wurden jedoch besiegt und zurück in die Ihgalukklamm geschleppt.$b$bIch will diese Konstrukte zurück. Keine schleimige Echse wird das Eigentum des Silberbunds stehlen, während ich hier das Sagen habe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'esES', 'Nuestra maquinaria', 'Recupera los cuatro ensamblajes de El Pacto de Plata robados.', 'Los saurok ponen a prueba nuestras fronteras día sí, día también, y nuestros ensamblajes suelen mantenerlos a raya. Sin embargo, algunos ensamblajes han sido derrotados y se los han llevado de nuevo al Risco Ihgaluk.$b$bQuiero esos ensamblajes de vuelta. Ningún lagarto asqueroso robará propiedades de El Pacto de Plata bajo mi mando.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'esMX', 'Nuestra maquinaria', 'Recupera los cuatro ensamblajes de El Pacto de Plata robados.', 'Los saurok ponen a prueba nuestras fronteras y nuestros ensamblajes suelen mantenerlos a raya. Sin embargo, algunos ensamblajes han sido derrotados y se los han llevado de nuevo al Risco Ihgaluk.$b$bQuiero esos ensamblajes de vuelta. Ningún lagarto asqueroso robará propiedades de El Pacto de Plata bajo mi mando.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'frFR', 'Dé-construction', 'Récupérez les quatre Assemblages du Concordat argenté volés.', 'Les sauroks mettent constamment nos frontières à l’épreuve, mais nos assemblages parviennent en général à les repousser. Certains assemblages, cependant, ont été vaincus et traînés jusqu’à la combe d’Ihgaluk.$b$bJe veux récupérer ces assemblages. Il est hors de question qu’un lézard ondulant vole la propriété du Concordat argenté pendant mon tour de garde !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'itIT', 'Smantellati', 'Recupera i quattro Costrutti del Patto d\'Argento rubati.', 'I Saurok mettono continuamente alla prova i nostri confini, ma i nostri costrutti riescono quasi sempre a tenerli a bada. Alcuni di loro, però, sono stati distrutti e portati a Ihgaluk.$b$bVoglio che quei costrutti tornino in mano nostra; nessuna viscida lucertola ruberà le proprietà del Patto d\'Argento mentre io sono al comando.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'koKR', '사라진 피조물', '도둑맞은 은빛 서약단 피조물을 4개 되찾아야 합니다.', '사우록이 끊임없이 변경을 침범하고 있고, 그때마다 우리 피조물들이 막아내고 있어요. 하지만 패배해서 이갈룩 바위산으로 끌려간 피조물도 있지요.$b$b피조물들을 되찾아 주세요. 제가 눈을 뜨고 있는 한 도마뱀 따위가 은빛 서약단의 자산을 훔치지는 못할 거예요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'ptBR', 'Esses constructos têm dono', 'Recupere os quatro Constructos do Pacto de Prata roubados.', 'Os sauroks constantemente testam nossas fronteiras e nossos constructos costumam espantá-los. Porém, alguns construtos foram derrotados e arrastados para o Penhasco Ihgaluk.$b$bEu quero os constructos de volta. Nenhum lagarto escamoso vai roubar propriedade do Pacto de Prata no meu turno!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'ruRU', 'Возвращение големов', 'Верните 4 украденных големов Серебряного Союза.', 'Сауроки постоянно пытаются вторгнуться на наши территории, но обычно нашим големам удается дать им отпор. Однако некоторые из них были повержены, и сауроки утащили их на утесы Ихгалук.$b$bЯ хочу вернуть их назад. Ни одной скользкой ящерице не видать имущества Серебряного Союза, пока я здесь командую.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'zhCN', '解体', '夺回4个被偷走的银色盟约构造体。', '蜥蜴人一直在袭扰我们的边界，而我们的构造体通常都能击退他们。可是，有一些构造体却被他们击败并拖回了伊瓜鲁克峭壁。$b$b我想把这些构造体弄回来。那些滑溜溜的蜥蜴人休想从我眼皮底下偷走银色盟约的任何财产。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32537, 'zhTW', '回收傀儡', '取回四個被偷走的白銀誓盟傀儡。', '薩烏洛克不停騷擾邊界，通常我們的傀儡能阻擋他們。不過，有些傀儡卻被擊敗，拖回伊加路克峭壁。$b$b我希望取回那些傀儡。有我在，那些滑溜的蜥蜴休想偷走白銀誓盟的財產。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'deDE', 'Abscheuliche Opfer', 'Zerstört die Opferaltäre der Zandalari.', 'Einige der stärksten Geisterbinder führen schändliche Riten für ihre abscheulichen Götter durch.\n\nWir haben eine Methode entwickelt, um ihre Altäre zu zerstören. Der schwierige Teil der Aufgabe wird also sein, lebend zu jedem der Altäre zu gelangen.\n\nWagt Euch ins Innerste von Za\'Tual und zerstört ihre Altäre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'esES', 'Sacrificios bárbaros', 'Destruye los altares de sacrificios Zandalari.', 'Algunos de los vinculadores de espíritus más poderosos llevan a cabo rituales atroces en honor a sus horripilantes dioses.\n\nHemos dado con un modo de destruir sus altares, así que la parte difícil será la de llegar a cada altar con vida.\n\nEntra en Za\'Tual y destruye sus altares.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'esMX', 'Sacrificios bárbaros', 'Destruye los altares de sacrificios Zandalari.', 'Algunos de los vinculadores de espíritus más poderosos llevan a cabo rituales atroces en honor a sus horripilantes dioses.\n\nHemos dado con un modo de destruir sus altares, así que la parte difícil será la de llegar a cada altar con vida.\n\nEntra en Za\'Tual y destruye sus altares.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'frFR', 'Odieux sacrifices', 'Détruisez les Autels sacrificiels zandalari.', 'Certains des plus puissants lieurs d’esprits accomplissent des rituels haineux en l’honneur de leurs maudits dieux.\n\nNous avons élaboré une méthode pour détruire leurs autels, alors le plus difficile sera d’atteindre chaque autel en vie.\n\nAventurez-vous dans les profondeurs de Za’tual et détruisez leurs autels.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'itIT', 'Un atroce sacrificio', 'Distruggi gli altari sacrificali degli Zandalari.', 'Alcuni dei Vincolaspiriti più potenti eseguono atroci rituali per conto delle loro infide divinità.\n\nAbbiamo ideato un modo per distruggere i loro altari, ma sarà difficile avvicinarsi.\n\nDestreggiati nelle profondità di Za\'tual e distruggili.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'koKR', '극악무도한 희생', '잔달라 희생의 제단을 파괴해야 합니다.', '영혼결속자들 중 특히나 강력한 몇 명은 놈들의 추잡한 신들에게 극악무도한 의식을 치른다고 합니다.\n\n저들의 제단을 파괴할 방법은 마련해놓았는데 문제는 제단까지 살아서 가야 한다는 것이죠.\n\n자투알 깊숙한 곳까지 들어가 놈들의 제단을 파괴해주시겠습니까?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'ptBR', 'Sacrifício hediondo', 'Destrua os altares sacrificiais dos Zandalari.', 'Alguns dos amarra-espíritos mais poderosos realizam ritos abomináveis para seus deuses malditos.\n\nNós descobrimos um método para destruir os altares deles. A dificuldade vai ser chegar até eles com vida.\n\nAventure-se pelas profundezas de Za\'Tual e destrua os altares.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'ruRU', 'Отвратительные жертвоприношения', 'Уничтожьте жертвенные алтари зандаларов.', 'Могущественные душеловы проводят ужасные обряды поклонения своим злобным богам.\n\nМы придумали способ, как можно уничтожить алтари, но трудность заключается в том, чтобы уцелеть, подбираясь к алтарю.\n\nОтправляйся в глубины За\'Туала и уничтожь алтари.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'zhCN', '可憎的献祭', '摧毁赞达拉祭坛。', '有些最强大的缚灵者通过邪恶的仪式取悦自己扭曲的神灵。\n\n我们已经想出了摧毁他们祭坛的办法，最艰巨的任务就是活着接近每个祭坛。\n\n深入赞图尔，摧毁那些祭坛。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32538, 'zhTW', '兇憎祭儀', '摧毀贊達拉獻祭壇。', '有批縛魂者當中的精銳用可憎的儀式祭拜他們的邪神。\n\n我們想出了摧毀祭壇的方法，如今的難題是怎麼活著抵達各個祭壇。\n\n深入札突爾，摧毀他們的祭壇。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'deDE', 'Finstere Opfergaben', 'Zerstört 6 Zandalariopfergaben.', 'Die Geisterbinder der Zandalari bringen finstere Opfer, um ihre Götter zu besänftigen, und dabei töten sie unsere Leute.\n\nZerstört alle Opfergaben, die Ihr in Za\'Tual finden könnt, um die Rate ihrer finsteren Opfer zu verlangsamen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'esES', 'Ofrendas oscuras', 'Destruye 6 ofrendas Zandalari.', 'Los vinculadores de espíritus Zandalari llevan a cabo sacrificios oscuros para satisfacer a sus dioses y, en el proceso, matan a los nuestros.\n\nDestruye todas las ofrendas Zandalari que veas en Za\'Tual para ralentizarlos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'esMX', 'Ofrendas oscuras', 'Destruye 6 ofrendas Zandalari.', 'Los vinculadores de espíritus Zandalari llevan a cabo sacrificios oscuros para satisfacer a sus dioses y, en el proceso, matan a los nuestros.\n\nDestruye todas las ofrendas Zandalari que veas en Za\'Tual para ralentizarlos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'frFR', 'Sombres offrandes', 'Détruisez 6 Offrandes zandalari.', 'Les lieurs d’esprit zandalari multiplient les sombres sacrifices afin d’apaiser leurs dieux, et tuent pour ce faire ceux de notre peuple.$b$bDétruisez toutes les offrandes zandalari que vous pourrez trouver à Za’Tual, afin de nous aider à réduire le rythme de ces sacrifices.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'itIT', 'Rituali oscuri', 'Distruggi 6 Offerte degli Zandalari.', 'I Vincolaspiriti Zandalari eseguono oscuri riti sacrificali per appagare i loro dei, uccidendo i nostri uomini allo stesso tempo.\n\nDistruggi tutte le offerte che riesci a trovare a Za\'tual e forse saremo in grado di mettere un freno alle loro pratiche sconsiderate.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'koKR', '어둠의 공물', '잔달라 공물 6개를 파괴해야 합니다.', '잔달라 영혼결속자들은 자신들이 섬기는 신을 위해 희생 의식을 치른다고 합니다. 그 의식 때문에 우리 백성들이 희생되고 있지요.\n\n자투알에서 보이는 공물들을 모조리 파괴하여 놈들의 의식을 늦춰야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'ptBR', 'Oferendas Negras', 'Destrua 6 Oferendas Zandalari.', 'Os amarra-espíritos Zandalari realizam rituais sombrios para apaziguar seus deuses e, para isso, matam nossa gente.\n\nDestrua todas as oferendas Zandalari que encontrar em Za\'Tual para ajudar a diminuir o ritmo dos sacrifícios.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'ruRU', 'Темные подношения', 'Уничтожьте 6 зандаларских подношений.', 'Душеловы зандаларов проводят жертвенные ритуалы, чтобы умилостивить лоа и своих мерзких богов. В этих темных ритуалах они убивают наших сородичей.\n\nУничтожь все зандаларские подношения, которые сможешь найти в За\'Туале, чтобы уменьшить число этих жертвоприношений.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'zhCN', '邪恶供品', '摧毁6份赞达拉供品。', '赞达拉缚灵者通过举行黑暗献祭来取悦神灵，残害我们的族人。\n\n把你在赞图尔所能找到的所有赞达拉供品全都摧毁，拖延他们黑暗献祭的时间。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32539, 'zhTW', '黑暗獻祭', '摧毀6個贊達拉祭品。', '贊達拉縛靈者在黑暗獻祭中宰掉我們的人民，獻給神明。\n\n毀掉你能在札突爾找到的所有贊達拉祭品，來拖緩黑暗獻祭的進行速度。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'deDE', 'Vorboten der Loa', 'Tötet 2 Vorboten der Loa.', 'Wenn Ihr etwas Zeit in Za\'Tual verbringt, werdet Ihr wahrscheinlich sehen, dass aus einem ihrer Zelte eine Form des reinen Bösen austritt.\n\nBitte, reist zum Trolllager und setzt diesen unaussprechlichen Ritualen ein Ende.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'esES', 'Presagistas de los loa', 'Mata a 2 presagistas de los loa.', 'Si te das una vuelta por Za\'Tual, es posible que veas cómo una de sus tiendas grandes emana el mal en esencia pura.\n\nAdéntrate en el campamento trol y pon fin a estos incalificables rituales.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'esMX', 'Presagistas de los loa', 'Mata a 2 presagistas de los loa.', 'Si te das una vuelta por Za\'Tual, es posible que veas cómo una de sus tiendas grandes emana el mal en esencia pura.\n\nAdéntrate en el campamento trol y pon fin a estos incalificables rituales.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'frFR', 'Messagers des Loas', 'Tuez 2 Messagers des Loas.', 'Si vous passez un peu de temps à Za’Tual, vous verrez sans doute une de leurs grandes tentes émettre une forme de mal pur.\n\nS’il vous plaît, rendez-vous dans le camp troll et mettez un terme à ces rituels innommables.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'itIT', 'Gli Araldi dei Loa', 'Uccidi 2 Araldi dei Loa.', 'Passando del tempo a Za\'tual, ti accorgerai della presenza di una grande tenda che emana una presenza maligna.\n\nTi prego, vai all\'accampamento dei Troll e poni fine a questi terribili rituali.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'koKR', '로아의 선구자들', '로아의 선구자 2명을 처치해야 합니다.', '자투알에서 시간을 좀 보냈다면 순수한 악의 기운을 내뿜는 큰 천막들을 아마 보셨을겁니다.\n\n제발, 트롤들의 야영지로 가서 이 끔찍한 의식을 저지해주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'ptBR', 'Arautos dos Loa', 'Mate 2 Arautos dos Loa.', 'Se passar algum tempo em Za\'Tual, você verá uma das maiores tendas exalando o mal em sua mais pura forma.\n\nPor favor, vá até o acampamento troll e dê fim a esses rituais hediondos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'ruRU', 'Вестники лоа', 'Победите 2 вестников лоа.', 'Если тебе доведется провести некоторое время в За\'Туале, ты скорее всего увидишь большие шатры, воплощающие истинное зло.\n\nПожалуйста, отправляйся в лагерь троллей и положи конец их ужасным ритуалам.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'zhCN', '神灵先驱', '消灭2个神灵先驱。', '如果你在赞图尔待上一会儿，就会看到他们其中一个最大的帐篷散发着纯粹的邪恶气息。\n\n拜托了，请你前往巨魔营地，别让这些邪恶无比的仪式再继续下去。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32540, 'zhTW', '羅亞之靈的使者', '殺死2名羅亞之靈的使者。', '你如果在札突爾待一陣子，很可能會看到某個大帳篷發散出邪惡的純粹形體。\n\n拜託你，深入食人妖營地阻止這些惡毒至極的儀式。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'deDE', 'Einer zukünftigen Bedrohung vorbeugen', 'Erschreckt $1oa Himmelskreischerjungtiere, indem Ihr in ihre Nähe lauft.', 'Es schmerzt mich, an die Qualen zu denken, die die armen Himmelskreischer ertragen müssen, wenn die Zandalari sie zu Kriegsmaschinen erziehen.\n\nDie Jungtiere sind recht schreckhaft und fliegen fort, wenn man sich ihnen auch nur nähert. Bitte verscheucht einige von ihnen, bevor die Zandalari sie verderben können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'esES', 'Más vale prevenir que curar', 'Asusta a $1oa proles de vociferador de cielos corriendo cerca de ellas.', 'Me duele pensar en la tortura a la que se ven sometidos los pobres vociferadores de cielos mientras los Zandalari los entrenan para convertirlos en herramientas de guerra.\n\nLas proles son bastante asustadizas y siempre salen volando si te acercas un poco a ellas. Asústalas antes de que los Zandalari las corrompan.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'esMX', 'Más vale prevenir que curar', 'Asusta a $1oa proles de vociferador de cielos corriendo cerca de ellas.', 'Me duele pensar en la tortura a la que se ven sometidos los pobres vociferadores de cielos mientras los Zandalari los entrenan para convertirlos en herramientas de guerra.\n\nLas proles son bastante asustadizas y siempre salen volando si te acercas un poco a ellas. Asústalas antes de que los Zandalari las corrompan.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'frFR', 'Éviter une future menace', 'Effrayez $1oa Bébés hurleciel en courant près d’eux.', 'Cela me fait de la peine d’imaginer les tortures que ces pauvres hurleciel doivent subir lorsque les Trolls les forment pour devenir des instruments de guerre.\n\nLes bébés sont facilement apeurés et s’envoleront probablement si vous vous approchez trop d’eux. S\'il vous plaît, effrayez-les pour qu\'ils s\'enfuient avant que les zandalari ne les corrompent.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'itIT', 'Prevenire è meglio che curare', 'Spaventa $1oa Cuccioli di Urlacieli correndo vicino a loro.', 'Mi addolora pensare alle torture che quei poveri Urlacieli sono costretti a subire per diventare strumenti di guerra dei Troll.\n\nI cuccioli si spaventano facilmente e non esiteranno a fuggire via se dovessi avvicinarti troppo. Ti prego, spaventane alcuni prima che gli Zandalari riescano a corromperli.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'koKR', '미래의 위험은 미리 위협해야', '아기 비명익룡 $1oa마리 주위를 뛰어다녀 겁을 줘야 합니다.', '비명익룡들이 전쟁의 도구로 사용되기 위해 고문을 당하다니. 도저히 참을 수 없습니다.\n\n그래도 아기 비명익룡들은 아직 겁이 많아 당신이 주위에 가면 날아서 도망칠 것입니다. 잔달라 부족이 비명익룡들을 타락시키기 전에 겁을 줘 쫓아내주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'ptBR', 'Evitando uma ameaça futura', 'Assuste $1oa Filhotes de Gritacéu correndo para perto deles.', 'Parte o coração pensar na tortura que os pobres gritacéus passam nas mãos dos trolls para se tornarem instrumentos de guerra.\n\nOs filhotes são muito assustadiços e voarão assim que você se aproximar. Por favor, afugente alguns deles antes que sejam corrompidos pelos Zandalari.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'ruRU', 'Предотвращение будущей угрозы', 'Напугайте $1oa детенышей небесного крикуна, бегая мимо них.', 'Мне больно думать о том, что приходится терпеть бедным небесным крикунам, пока тролли дрессируют их, превращая в орудия войны.\n\nПтенцы птеродактилей очень пугливы и улетают, если подойти к ним слишком близко. Пожалуйста, напугай нескольких птенцов, пока зандалары не завершили свое грязное дело.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'zhCN', '防患未然', '在$1oa只啸天雏龙身边奔跑以惊吓它们。', '一想到这些可怜的啸天龙在被巨魔训练成战争工具的过程中需要遭受何种折磨，我就感到无比难过。\n\n雏龙相当胆小，只要你一靠近就会吓得飞走。拜托了，在赞达拉巨魔腐化它们之前，把它们吓走吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32541, 'zhTW', '防範未然', '跑到$1oa隻嘯天龍寶寶身邊驚嚇牠們。', '想到食人妖在訓練那些可憐的嘯天龍成為戰爭道具時牠們所遭遇的痛苦，就讓我心痛。\n\n那些龍寶寶膽子很小，你一靠近牠們就會飛走。拜託你，在贊達拉腐化牠們之前嚇跑一些龍寶寶。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'deDE', 'Angriff mit chirurgischer Präzision', 'Tötet 3 Geisterbinder der Zandalari, 3 Bestienrufer der Zandalari und 3 Jaguarkrieger der Zandalari.', 'Die Geisterbinder der Zandalari opfern ihren Göttern, die Bestienrufer unterwerfen unschuldige Himmelskreischer ihrem Willen und die Jaguarkrieger wagen sich von Tag zu Tag näher an uns heran.\n\nMacht diese Bedrohungen in Za\'Tual unschädlich, $R, und Ihr werdet sehen, wie die Zandalari geschwächt werden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'esES', 'Ataque letal', 'Mata a 3 vinculadores de espíritus Zandalari, a 3 clamabestias Zandalari y a 3 guerreros jaguar Zandalari.', 'Los vinculadores de espíritus Zandalari llevan a cabo sacrificios en honor a sus dioses, los clamabestias doblegan la voluntad de inocentes vociferadores de cielos a su merced y los guerreros jaguar cada vez se aproximan más.\n\nNeutraliza estas amenazas en Za\'Tual, $r, y observa cómo el poder de los trols mengua.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'esMX', 'Ataque letal', 'Mata a 3 vinculadores de espíritus Zandalari, a 3 clamabestias Zandalari y a 3 guerreros jaguar Zandalari.', 'Los vinculadores de espíritus Zandalari llevan a cabo sacrificios en honor a sus dioses, los clamabestias doblegan la voluntad de inocentes vociferadores de cielos a su merced y los guerreros jaguar cada vez se aproximan más.\n\nNeutraliza estas amenazas en Za\'Tual, $r, y observa cómo el poder de los trols mengua.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'frFR', 'Frappe chirurgicale', 'Tuez 3 Lieurs d’esprit zandalari, 3 Mande-bêtes zandalari et 3 Guerriers jaguar zandalari.', 'Les lieurs d’esprit zandalari font des sacrifices pour leurs dieux, les mande-bêtes soumettent d’innocents hurleciel à leur volonté, et les guerriers jaguars s’aventurent toujours plus près chaque jour…$b$bDébarrassez-nous de ces menaces dans Za’Tual, $R, et assistez au déclin de la puissance des Trolls.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'itIT', 'Attacco mirato', 'Uccidi 3 Vincolaspiriti Zandalari, 3 Invocatori delle Bestie Zandalari e 3 Guerrieri Giaguaro Zandalari.', 'I Vincolaspiriti Zandalari eseguono sacrifici ai loro dei, gli Invocatori delle Bestie piegano innocenti Urlacieli alla loro volontà e i Guerrieri Giaguaro si avvicinano alla nostra posizione giorno dopo giorno.\n\nOccupati di queste minacce a Za\'tual, $R, e goditi la lenta caduta dei Troll.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'koKR', '해결사가 왔노라', '잔달라 영혼결속자 3명, 잔달라 야수소환사 3명, 그리고 잔달라 재규어 전사 3명을 처치해야 합니다.', '잔달라 영혼결속자들은 자신이 섬기는 신들에게 제물을 바치고 야수소환사들은 죄 없는 비명익룡들을 부려 악행을 일삼습니다. 그리고 재규어 전사들도 점차 우리 기지로 향하고 있죠.\n\n자투알에서 이 트롤들을 제거하십시오, $r님. 그러면 저들의 위세도 한풀 꺾일 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'ptBR', 'Ataque preciso', 'Mate 3 Amarra-espíritos Zandalari, 3 Chama-feras Zandalari e 3 Guerreiros Jaguar Zandalari.', 'Os amarra-espíritos Zandalari executam sacrifícios rituais para os deuses, os chama-feras forçam gritacéus inocentes a agir de acordo com sua vontade e os guerreiros jaguares fecham cada vez mais o cerco.\n\nAcabe com essas ameaças em Za\'Tual, $r, e veja como os Zandalari perdem força.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'ruRU', 'Оперативный удар', 'Убейте 3 зандаларских душеловов, 3 зандаларских заклинателей зверей и 3 зандаларских воинов-ягуаров.', 'Душеловы зандаларов приносят жертвы своим богам. Их заклинатели зверей подчиняют невинных небесных крикунов своей воле. А воины-ягуары с каждым днем подбираются к нам все ближе.\n\nОтправляйся в За\'Туал, |3-6($r), и разберись со всеми этими проблемами. Нанеси зандаларам урон.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'zhCN', '手术式打击', '击败3名赞达拉缚灵者、3名赞达拉驭兽师和3名赞达拉丛林豹战士。', '赞达拉缚灵者举行献祭来取悦神灵，驭兽师迫使无辜的啸天龙听命于自己的意志，而丛林豹战士则向我们日渐逼近。\n\n去赞图尔消灭这些威胁，$r，削弱巨魔的力量。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32542, 'zhTW', '精準襲擊', '殺死3名贊達拉縛靈者、3名贊達拉喚獸者與3名贊達拉狂豹戰士。', '贊達拉縛靈者拿活人獻祭給神明，喚獸者操縱無辜的嘯天龍作為幫手，狂豹戰士越來越深入我們營地的中央。\n\n剷除札突爾裡的這些禍害，$r，削弱食人妖的勢力。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'deDE', 'Gefahren von Za\'Tual', 'Tötet 12 Trolle in Za\'Tual.', 'Die Zandalari sind ernstzunehmende Gegner – gefährlicher als viele, die wir in der Vergangenheit bekämpft haben. Aber auch Ihr seid $gein ernstzunehmender Gegner:eine ernstzunehmende Gegnerin;, $n.\n\nBetretet Za\'Tual und zeigt ihnen, wie gefährlich Ihr wirklich seid.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'esES', 'Los peligros de Za\'Tual', 'Mata a 12 trols en Za\'Tual.', 'Los Zandalari son unos enemigos temibles, más temibles que los adversarios con los que hemos luchado hasta ahora. No obstante, tú también eres temible, $n.\n\nEntra en Za\'Tual, al oeste, para mostrarles el riesgo que comporta enfrentarse a alguien como tú.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'esMX', 'Los peligros de Za\'Tual', 'Mata a 12 trols en Za\'Tual.', 'Los Zandalari son unos enemigos temibles, más temibles que los adversarios con los que hemos luchado hasta ahora. No obstante, tú también eres temible, $n.\n\nEntra en Za\'Tual, al oeste, para mostrarles el riesgo que comporta enfrentarse a alguien como tú.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'frFR', 'Les dangers de Za’Tual', 'Tuez 12 Trolls dans Za’Tual.', 'Les Zandalari sont des ennemis puissants. Bien plus puissants que les ennemis que nous avions affrontés auparavant. Mais vous êtes $gun ennemi puissant:une ennemie puissante; également, $n.\n\nAllez à Za’tual et montrez-leur ce qu’il en coûte de s’opposer à des gens comme vous.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'itIT', 'I pericoli di Za\'tual', 'Uccidi 12 Troll a Za\'tual.', 'Gli Zandalari sono tra i nemici più formidabili che abbiamo mai dovuto affrontare. Ma anche tu sai essere $gpericoloso:pericolosa;, $n.\n\nVai a Za\'tual e mostra loro la tua vera natura.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'koKR', '자투알에 도사리는 위험', '자투알에서 트롤 12명을 처치해야 합니다.', '잔달라 부족은 강력한 적입니다. 과거에 우리가 대적한 그 어떤 자들보다 말이죠. 하지만 당신도 강력하긴 마찬가지입니다, $n.\n\n자투알에 침투하여 당신에게, 그리고 얼라이언스에게 대적하는 것이 얼마나 어리석은 일인지 보여주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'ptBR', 'Perigos de Za\'Tual', 'Mate 12 Trolls em Za\'Tual.', 'Os Zandalari são um inimigo formidável, mais até que a maioria dos que encontramos no passado. Mas você não fica atrás, $n.\n\nEntre em Za\'Tual e mostre a eles o risco que correm quando se metem com alguém como você.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'ruRU', 'Опасности За\'Туала', 'Убейте 12 троллей в За\'Туале.', 'Зандалары – опасные противники. Возможно, куда более опасные, чем все, встречавшиеся нам раньше. Но и ты не лыком $gшит:шита;, $n.\n\nОтправляйся в За\'Туал, это на западе, и покажи им, на что ты $gспособен:способна;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'zhCN', '赞图尔的危机', '在赞图尔消灭12名巨魔。', '赞达拉巨魔是强大的敌人——比我们之前遇到的许多敌人都要可怕。但你也是个令人敬畏的对手，$n。\n\n进入赞图尔，让他们瞧瞧，与像你这样的人作对会有什么下场。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32543, 'zhTW', '札突爾的危機', '殺死札突爾裡的12名食人妖。', '贊達拉是可怕的強敵，比我們過去遭遇過的對手都還要危險。不過你也是個狠角色，$n。\n\n進入札突爾，讓他們瞧瞧惹毛像你這樣的人需要付出什麼代價。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'deDE', 'Kreischen vor Qualen', 'Befreit den gepeinigten Himmelskreischer.', 'Einer der Himmelskreischer in Za\'Tual wird einem Folterritual unterzogen. Sein Wille wird für immer gebrochen, damit er seinen Meistern dient.\n\nTötet diejenigen, die die Kreatur solchen Qualen unterziehen, und wir können nur hoffen, dass sie sich wieder in den Himmel schwingen wird.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'esES', 'Un ptormento pterrible', 'Libera al vociferador de cielos atormentado.', 'Los trols han sometido a uno de sus vociferadores de cielos a un ritual de tortura en Za\'Tual, doblegando su voluntad para siempre a merced de sus amos.\n\nMata a los responsables de causarle tal tormento a la criatura. Esperemos que, así, pueda volver a volar libre...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'esMX', 'Un ptormento pterrible', 'Libera al vociferador de cielos atormentado.', 'Los trols han sometido a uno de sus vociferadores de cielos a un ritual de tortura en Za\'Tual, doblegando su voluntad para siempre a merced de sus amos.\n\nMata a los responsables de causarle tal tormento a la criatura. Esperemos que, así, pueda volver a volar libre...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'frFR', 'Pterrible ptourment', 'Libérez le Hurleciel tourmenté.', 'Un des hurleciel des Trolls de Za’Tual est en train de subir un rituel de torture, et sa volonté pervertie à jamais pour servir ses maîtres.\n\nTuez ceux qui infligent un tel tourment à cette créature et avec un peu de chance, elle retournera dans les cieux.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'itIT', 'Pterribile ptormento', 'Libera l\'Urlacieli Tormentato.', 'Uno degli Urlacieli dei Troll a Za\'tual sta subendo un rituale di tortura che presto lo obbligherà a piegarsi alla volontà dei suoi aguzzini.\n\nUccidi coloro che stanno infliggendo un tale tormento sulla creatura e, se saremo fortunati, potrà tornare in libertà.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'koKR', '끔찍한 고문', '고문당한 비명익룡을 풀어줘야 합니다.', '자투알에 있는 트롤들의 비명익룡 중 하나가 고문의 의식을 받고 있는 모양입니다. 강제로 야수의 의지를 꺾어 제마음대로 부리려고 하는군요.\n\n그 생물을 괴롭히고 있는 자들을 처치하십시오. 그리고 녀석이 하늘로 돌아갈 여력이 있기를 바랍시다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'ptBR', 'Pterrível Ptormento', 'Liberte o Gritacéu Atormentado.', 'Um dos gritacéus dos trolls está sendo submetido a um ritual de tortura em Za\'Tual. Seu espírito está sendo forçado a servir eternamente a seus mestres.\n\nMate todos que estiverem infligindo esse sofrimento à criatura. Depois, só resta torcer para que ela volte aos céus.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'ruRU', 'Измученный птеродактиль', 'Освободите измученного небесного крикуна.', 'Тролли пытают одного из небесных крикунов в За\'Туале, чтобы навеки подчинить своей воле.\n\nУничтожь всех мучителей этого невинного создания. Я надеюсь, что оно еще сможет подняться в небо. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'zhCN', '苦痛折磨', '解救痛苦的啸天龙。', '在赞图尔，其中一只巨魔的啸天龙正在经受某种仪式的摧残，它的意志会被彻底扭曲，效忠自己的主人。\n\n消灭那些折磨它的恶徒，我们只能希望，它能重新在空中展翼翱翔。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32544, 'zhTW', '痛苦的翼手龍', '釋放痛苦的嘯天龍。', '有隻食人妖的嘯天龍正在札突爾遭受折磨儀式，讓牠的意志永遠屈服於牠的主人。\n\n殺死那些折磨這隻生物的人，希望牠能重回天空的懷抱。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'deDE', 'Die Bewohner von Ihgaluk', 'Tötet einen Schläger der Skumklingen, einen Saurierpriester der Skumklingen und einen Meerdrachen der Skumklingen.', 'Die Skumklingen bedrohen unsere Leute, aber mit unverhohlenem Gemetzel zu reagieren, wäre eine Überreaktion.$b$bEine vernünftigere Reaktion auf diese Bedrohung wäre es, einige ihrer Stammesanführer zu töten. So erleiden sie weniger Blutvergießen und werden dennoch unsere Grenzen respektieren.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'esES', 'Los habitantes de Ihgaluk', 'Mata a un tosco Hojaescoria, a un sacerdote saurio Hojaescoria y a un dragón marino Hojaescoria.', 'Los Hojaescoria amenazan a nuestra gente, pero empezar a cortar cabezas a diestro y siniestro sería demasiado.$b$bMatar a los líderes de su tribu sí que sería más inteligente. No correrá tanta sangre y respetarán nuestro perímetro. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'esMX', 'Los habitantes de Ihgaluk', 'Mata a un tosco Hojaescoria, a un sacerdote saurio Hojaescoria y a un dragón marino Hojaescoria.', 'Los Hojaescoria amenazan a nuestra gente, pero empezar a cortar cabezas a diestro y siniestro sería demasiado.$b$bMatar a los líderes de su tribu sí que sería más inteligente. No correrá tanta sangre y respetarán nuestro perímetro. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'frFR', 'Les habitants d’Ihgaluk', 'Tuez une Brute crasselame, un Prêtre-saure crasselame et un Dragon de mer crasselame.', 'Les Crasselames menacent notre peuple, mais leur répondre par un carnage serait un peu exagéré.$b$bUne réponse plus raisonnable à la menace qu’ils représentent serait d’aller tuer quelques-uns de leurs chefs tribaux. Il y aura moins de sang versé et ils apprendront tout autant à respecter notre périmètre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'itIT', 'Gli abitanti di Ihgaluk', 'Uccidi un Bruto Lamalercia, un Sacerdote dei Saurok Lamalercia e un Saurok Marino Lamalercia.', 'I Lamalercia minacciano la nostra gente, ma rispondere con un massacro alla cieca sarebbe una brutale esagerazione.$b$bUna risposta più ragionevole alla loro minaccia sarebbe eliminare alcuni dei loro capi tribù. Anche se verseremo meno sangue, forse basterà a insegnare loro un po\' di rispetto per il nostro territorio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'koKR', '이갈룩의 주민', '부랑칼날 투사, 부랑칼날 공룡사제, 부랑칼날 바다도마뱀을 처치해야 합니다.', '부랑칼날 사우록은 우리를 위협하지만, 대대적인 학살로 맞대응하는 건 잔인한 과민 반응이에요.$b$b보다 합리적인 대응은, 부족의 우두머리를 몇 해치우는 거예요. 그러면 사우록이 피를 덜 흘리고도 우리 변경을 존중하게 되겠죠.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'ptBR', 'Os moradores de Ihgaluk', 'Mate um Bruto Gumeskuma, um Sacerdote-sáurio Gumeskuma e um Dragão-marinho Gumeskuma.', 'Os Gumeskuma ameaçam o nosso povo, mas responder com um massacre descontrolado seria um exagero brutal.$b$bUma resposta mais razoável seria matar alguns líderes tribais deles. Menos sangue será derramado, e eles passarão a respeitar nosso perímetro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'ruRU', 'Обитатели утесов Ихгалук', 'Убейте громилу Гнилого Клинка, сауро-жреца Гнилого Клинка и рассекателя волн Гнилого Клинка.', 'Клан Гнилого Клинка угрожает нашим собратьям, но ответить на это кровавой резней, убивая всех подряд, было бы чересчур. И пусть тебя не удивляют их действия – они просто защищают тот маленький клочок земли, который считают своим домом.$b$bРазумнее всего будет убить вождей их племени. Кровопролития при этом будет меньше, но они поймут, что нужно уважать установленные нами границы. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'zhCN', '伊瓜鲁克的住民', '击败1个邪刃蛮兵、1个邪刃蜥首牧师和1个邪刃海龙战士。', '邪刃部族威胁到了我们的人，可报之以毫不留情的消灭未免也太反应过度了。$b$b去干掉他们的部族首领或许是更加理性的回应方式。这样不仅能少一些流血冲突，也可以让他们尊重我们划下的边界。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32545, 'zhTW', '伊加路克的居民', '殺死一名污刃蠻卒、污刃魔龍祭司與一名污刃海龍。', '污刃薩烏洛克威脅我們人民的安危，但是全面屠殺他們卻又反應過度了。$b$b比較合理的應對方式是除掉他們幾個部落首領。這樣就不用流那麼多血，他們也會因此而尊重我們的領地邊界。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'deDE', 'Nur leichte Aufräumarbeiten', 'Zerstreut 75 Dreckstücke, indem Ihr den wabernden Dreck in der Ihgalukklamm tötet.', 'Das ist keine glamouröse Aufgabe. Lasst mich ganz direkt sein.$b$bDie Saurok der Skumklingen häufen all ihren Dreck in einem Tümpel mitten in ihrem Dorf auf. Dieser Dreck ist so konzentriert und ranzig, dass er sich zu einer bewegten, belebten Masse entwickelt hat.$b$bDie Saurok können ihren Drecktümpel ja gern behalten, aber da er sich außerhalb des Dorfes ausbreitet würde ich sagen, dass es an der Zeit ist einzuschreiten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'esES', 'Hay que ensuciarse las manos de vez en cuando', 'Dispersa 75 montones de basura matando a inmundicias temblorosas en el Risco Ihgaluk.', 'Te lo voy a decir sin tapujos: este trabajo no es sofisticado.$b$bLos saurok Hojaescoria apilan toda su basura en una poza que hay en medio de su poblado. Esta basura es tan concentrada y rancia que se ha solidificado hasta convertirse en una materia viva.$b$bLos saurok se pueden quedar su poza llena de basura, pero una vez que empieza a extenderse más allá del poblado creo que es el momento de intervenir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'esMX', 'Hay que ensuciarse las manos de vez en cuando', 'Dispersa 75 montones de basura matando a inmundicias temblorosas en el Risco Ihgaluk.', 'Te lo voy a decir tal como es: este trabajo no es sofisticado.$b$bLos saurok Hojaescoria apilan toda su basura en una poza que hay en medio de su poblado. Esta basura es tan concentrada y rancia que se ha solidificado hasta convertirse en una materia viva.$b$bLos saurok se pueden quedar su poza llena de basura, pero una vez que empieza a extenderse más allá del poblado creo que es el momento de intervenir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'frFR', 'Un peu de nettoyage', 'Dispersez 75 tas d’ordures en tuant des Crasses tremblantes dans la Combe d’Ihgaluk.', 'Ce n’est pas une mission très glamour, je vous préviens. Laissez-moi vous l’expliquer en deux mots.$b$bLes sauroks crasselames entassent leurs détritus dans un trou au milieu de leur village. Les ordures sont si concentrées et si rances qu’elles ont commencé à se transformer en matière vivante.$b$bLes sauroks peuvent bien garder leur décharge, mais à partir du moment où les ordures commencent à s’étendre en dehors du village, je pense qu’il est temps d’intervenir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'itIT', 'Solo un piccolo lavoretto di pulizia', 'Sbarazzati di 75 pezzi di feccia uccidendo le Fecce Tremolanti a Ihgaluk.', 'Questo non è certamente il lavoro più bello del mondo, lasciatelo dire chiaramente.$b$bI Saurok Lamalercia accumulano tutti i loro scarti in una pozza al centro del loro villaggio. Quello schifo è ormai così rancido e concentrato che ha preso vita e ha dato origine a grossi agglomerati semoventi.$b$bI Saurok possono pure tenersi la loro feccia putrescente, ma quando inizia a vagabondare oltre i confini del villaggio, è il momento di intervenire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'koKR', '단순 청소 작업', '이갈룩 바위산에서 벌벌떠는 구린액을 처치해 구린액 방울을 75개 흩뜨려야 합니다.', '이 일은 화려한 작업은 아닙니다. 그냥 대놓고 이야기하지요.$b$b부랑칼날 사우록은 오물을 마을 중앙의 웅덩이에 쌓아 둡니다. 그 오물이 썩어서 집약된 나머지, 엉기어서 살아 움직이는 덩어리가 되었지요.$b$b사우록 마을에 오물 웅덩이가 있건 말건 상관없지만, 그게 마을 밖으로 퍼지기 시작하면 손을 써야겠지요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'ptBR', 'Só um trabalho leve de limpeza', 'Espalhe 75 pedaços de sujeira matando a Imundície Trêmula no Penhasco Ihgaluk.', 'Não é um trabalho glamoroso. Vou falar com franqueza.$b$bOs sauroks Gumeskuma acumulam toda a sujeira que geram em um poço no meio da aldeia. A sujeira é tão concentrada e apodrecida que se coagulou em matéria viva, animada.$b$b.Se os sauroks querem manter uma pilha de sujeira, é problema deles. Mas quando a coisa começa a se espalhar para fora da aldeia, é hora de intervir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'ruRU', 'Небольшая чистка', 'Уничтожьте 75 частиц дрожащей гнили на утесах Ихгалук.', 'Скажу прямо, у меня для тебя довольно грязная работенка.$b$bСауроки Гнилого Клинка сносят всю свою гниль в водоем в самом центре деревни. Она настолько отвратительна, что стала собираться в плотную, подвижную и живую массу.$b$bСауроки, конечно, могут устраивать у себя болото, но их гниль стала вытекать за пределы деревни. Думаю, настала пора вмешаться в ситуацию.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'zhCN', '清洁工作', '击败伊瓜鲁克峭壁的颤栗污泥怪以分解75块污物。', '我就坦白说吧，这可不是什么好活儿。$b$b邪刃蜥蜴人将他们产生的污物都堆在村子中央的一个坑里。那里的污物太过集中，恶臭熏天，以至于都浓缩成了一种能够移动的活物。$b$b那些蜥蜴人可以留着他们的垃圾坑，可当它扩散到村子外面的时候，那我们就该干涉了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32546, 'zhTW', '小小的清潔工作', '殺死伊加路克峭壁的顫動穢怪來驅散75塊髒東西。', '我坦白告訴你，這不是件光榮的任務。$b$b污刃薩烏洛克將所有的髒東西都排到村莊中央的池子裡。這些髒東西濃稠腐臭，凍凝成會移動的生命體。$b$b薩烏洛克怎麼處理污水池是他們家的事，可是一旦它擴散到村莊以外，我們便不能袖手不管。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'deDE', 'Die krabbelnde Decke auf Ihgaluk ', 'Zerquetscht 75 Ihgaluk-Schaben.', 'Ach ja, Schaben! Ich habe in der Beutebucht mal eine Schabe als Haustier gekauft.$b$bSchaben saugen Krankheiten auf wie kleine Schwämme, und dann verbreiten sie sie, so schnell sie nur laufen können. Und sie können ziemlich weit laufen.$b$bDie Ihgalukklamm wimmelt von den Dingern, und sie verteilen den Dreck der Skumklingen über die ganze Insel. Zerquetscht so viele von ihnen wie Ihr nur könnt, während Ihr dort seid.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'esES', 'La cucaracha de Ihgaluk ya no puede caminar', 'Aplasta a 75 cucarachas Ihgaluk.', '¡Ah, cucarachas! Una vez traje una cucaracha como mascota a Entrañas.$b$bLas cucarachas absorben enfermedades como esponjas y luego las propagan hasta donde pueden correr. Y créeme, corren mucho.$b$bEl Risco Ihgaluk está infestado y vienen a esta isla impregnadas de inmundicia Hojaescoria. Deshazte de tantas como puedas cuando estés allí.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'esMX', 'La cucaracha de Ihgaluk ya no puede caminar', 'Aplasta a 75 cucarachas Ihgaluk.', '¡Ah, cucarachas! Una vez traje una cucaracha como mascota a Entrañas.$b$bLas cucarachas absorben enfermedades como esponjas y luego las propagan hasta donde pueden correr. Y créeme, corren mucho.$b$bEl Risco Ihgaluk está infestado y vienen a esta isla impregnadas de inmundicia Hojaescoria. Deshazte de tantas como puedas cuando estés allí.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'frFR', 'Le tapis rampant d\'Ihgaluk', 'Écrasez 75 Cafards d’Ihgaluk.', 'Ah, les cafards ! J’en ai acheté un, une fois, à Baie-du-Butin.$b$bLes cafards absorbent les maladies comme des éponges, pour les répandre ensuite aussi loin qu’ils peuvent se déplacer. Et croyez-moi, ça fait une sacrée distance.$b$bLa combe d’Ihgaluk grouille de ces bestioles, qui répandent la saleté des Crasselames sur toute l’île. Écrasez-en autant que vous pourrez tant que vous êtes là.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'itIT', 'Il tappeto brulicante di Ihgaluk', 'Schiaccia 75 Scarafaggi di Ihgaluk.', 'Ah, scarafaggi! Una volta ho comprato uno scarafaggio alla Baia del Bottino.$b$bGli scarafaggi assorbono malattie come spugne e le diffondono ovunque riescano a strisciare.$b$bIhgaluk ne è infestata, e sono la principale causa della diffusione della feccia dei Lamalercia su tutta l\'isola. Schiacciane più che puoi mentre ti trovi lì.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'koKR', '이갈룩의 스멀거리는 양탄자', '이갈룩 바퀴를 75마리 뭉개야 합니다.', '으, 바퀴벌레! 무법항에서 애완 바퀴벌레를 산 적이 있습니다.$b$b바퀴벌레는 스펀지처럼 병을 빨아들이고는 기어갈 수 있는 데까지 병을 퍼뜨리지요. 그 범위가 생각보다 넓답니다.$b$b이갈룩 바위산에는 바퀴벌레가 득시글거리는데, 녀석들이 부랑칼날의 오물을 이 섬 전역에 퍼뜨리고 있습니다. 가는 김에 바퀴벌레를 되도록 많이 밟아 버리십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'ptBR', 'O carpete rastejante de Ihgaluk', 'Esmague 75 Baratas Ihgaluk.', 'Ah, baratas! Uma vez, eu comprei uma barata de estimação em Angra do Butim.$b$bBaratas absorvem doenças feito esponjas e depois espalham tudo aonde quer que vão. E vão bem longe.$b$bO Penhasco Ihgaluk está infestado com essas coisas, e elas levam a imundície dos Gumeskuma para toda a ilha. Esmague o máximo delas que conseguir enquanto estiver por lá.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'ruRU', 'Кишащий насекомыми Ихгалук', 'Раздавите 75 тараканов Ихгалук.', 'А, тараканы! Я как-то купил себе таракана в Пиратской Бухте.$b$bТараканы впитывают в себя всякую заразу, как губка, а затем разносят ее на такое расстояние, на какое только способны убежать. То есть очень, очень далеко.$b$bУтесы Ихгалук просто кишат этими тварями, и оттуда они разносят скверну Гнилого Клинка по всему острову. Когда будешь там, уничтожь столько тараканов, сколько сможешь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'zhCN', '伊瓜鲁克的恐怖地毯', '踩死75只伊瓜鲁克蟑螂。', '啊，蟑螂！我曾在藏宝海湾买过一只宠物蟑螂。$b$b蟑螂会像海绵一样吸收病菌，然后将它们散播到自己所经之处。而它们的活动范围偏偏又很广。$b$b伊瓜鲁克峭壁上爬满了这种东西，将邪刃部族的秽物传播到岛上的每一个角落。当你去那里时，记得尽可能多踩死几只蟑螂。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32547, 'zhTW', '遍地蔓延的峭壁蟑螂', '踩扁75隻伊加路克蟑螂。', '喔，蟑螂!我曾經在藏寶海灣買過一隻寵物蟑螂。$b$b蟑螂會像海綿一樣吸收疾病，然後散佈到各處，無邊無際。$b$b伊加路克峭壁上頭爬滿這種東西，牠們將污刃的穢物散佈到整座島上。在你還在峭壁那邊時，盡可能的踩扁那些蟑螂。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'deDE', 'Verrotten lassen', 'Zerstört 3 faulige Fleischhaufen.', 'Die Saurok der Skumklingen verzehren nicht alles Fleisch, das sie erjagen. Sie behalten große Teile davon zurück, die in Stapeln neben ihren Behausungen verrotten und so Ungeziefer anziehen und Krankheiten ausbreiten.$b$bWir können nicht zulassen, dass die Saurok ihre Krankheiten außerhalb der Ihgalukklamm verbreiten. Beseitigt alles überschüssige Fleisch, das Ihr finden könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'esES', 'No hay que desperdiciar la comida', 'Destruye 3 montones de carne fétida.', 'Los saurok Hojaescoria no se comen toda la carne que cazan. Guardan grandes cantidades cerca de sus hogares y dejan que se pudra. Como consecuencia, provocan plagas y propagan enfermedades.$b$bNo podemos dejar que los saurok propaguen su enfermedad más allá del Risco Ihgaluk. Deshazte de todo exceso de carne que veas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'esMX', 'No hay que desperdiciar la comida', 'Destruye 3 montones de carne fétida.', 'Los saurok Hojaescoria no se comen toda la carne que cazan. Guardan grandes cantidades cerca de sus hogares y dejan que se pudra. Como consecuencia, provocan plagas y propagan enfermedades.$b$bNo podemos dejar que los saurok propaguen su enfermedad más allá del Risco Ihgaluk. Deshazte de todo exceso de carne que veas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'frFR', 'Laissé à pourrir', 'Détruisez 3 Piles de viande fétide.', 'Les sauroks crasselames ne mangent pas toute la viande qu’ils chassent. Ils en réservent une grande partie qu’ils laissent pourrir en tas près de leurs maisons, ce qui attire des nuisibles et propage des maladies.$b$bNous ne pouvons pas laisser les sauroks répandre ces maladies par-delà les frontières de la combe d’Ihgaluk. Débarrassez-vous de toute la viande que vous trouverez.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'itIT', 'Lasciato a marcire', 'Distruggi 3 Mucchi di Carne Fetida.', 'I Saurok Lamalercia non mangiano tutta la carne che riescono a cacciare. Ne lasciano grandi quantità a marcire al di fuori delle loro dimore, attirando così insetti e diffondendo malattie.$b$bNon possiamo permettere che i Saurok diffondano il loro morbo oltre i confini di Ihgaluk. Sbarazzati di tutta la carne in eccesso che riesci a trovare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'koKR', '썩도록 방치된', '악취나는 고기 더미를 3개 파괴해야 합니다.', '부랑칼날 사우록은 사냥하는 고기를 다 먹지는 않습니다. 상당 부분을 보금자리 근처에 무더기로 쌓아 두는데, 그게 썩어서 벌레가 꼬이고 질병이 퍼집니다.$b$b사우록이 자기들 질병을 이갈룩 바위산 밖으로 퍼뜨리는 걸 두고 볼 순 없지요. 남는 고기가 눈에 띄는 대로 없애 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'ptBR', 'Apodrecendo', 'Destrua 3 Amontoados de Carne Fétida', 'Os sauroks Gumeskuma não comem toda a carne que caçam. Eles guardam alguns pedaços em pilhas putrefatas perto de suas casas, que atraem doenças de todo tipo.$b$bNós não podemos deixar que os sauroks espalhem essas doenças para além dos limites do Penhasco Ihgaluk. Destrua qualquer excesso de carne que você encontrar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'ruRU', 'Оставленное гнить', 'Уничтожьте 3 зловонные кучи мяса.', 'Сауроки из клана Гнилого Клинка не едят все мясо той дичи, на которую охотятся. Они складывают его в большие кучи и оставляют гнить возле домов, привлекая этим паразитов, которые потом разносят заразу.$b$bМы не можем допустить того, чтобы болезни распространялись за пределы утесов Ихгалук. Найди мясные кучи и избавься от них.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'zhCN', '任其腐烂', '摧毁3堆恶臭肉堆。', '邪刃蜥蜴人并不会将他们打到的肉全吃光。他们会将大部分的肉堆在房子附近，任由其腐烂，以吸引害虫并散播病毒。$b$b我们不能让蜥蜴人将病毒散播到伊瓜鲁克峭壁以外的地方。快去处理掉那些剩余的肉类吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32548, 'zhTW', '腐肉傳疫', '摧毀3個惡臭肉塊。', '污刃薩烏洛克不會吃掉所有獵捕來的肉，他們把大部分的肉堆在家的附近放著腐爛，吸引害蟲並散佈疾病。$b$b我們不能讓薩烏洛克將疾病散佈到伊加路克峭壁以外。摧毀所有找到的腐肉。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'deDE', 'In der Haut eines Saurok', 'Begebt Euch zur Ihgalukklamm und tötet einen Skumklingensaurok.', 'Die Ihgalukklamm ist ein gefährlicher Ort. Glücklicherweise stärkt Euch die Magie der Kirin Tor den Rücken.$b$bIch kann Euch in einen Saurok verwandeln.$b$bIch werde jetzt die erste Hälfte der Illusion auf Euch wirken. Danach solltet Ihr sofort zur Ihgalukklamm gehen und einen Skumklingensaurok töten. Sobald das erledigt ist, werde ich Euch kontaktieren und die zweite Hälfte vollenden.', '', '', '', 'Ich werde Euch so verwandeln, dass Ihr wie ein Saurok ausseht.', 'Abbild von Erzmagier Vargoth', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'esES', 'En la piel de un saurok', 'Ve al Risco Ihgaluk y mata a un saurok Hojaescoria.', 'El Risco Ihgaluk es un lugar peligroso. Por suerte, tienes la magia del Kirin Tor como apoyo.$b$bPuedo transformarte en un saurok.$b$bVoy a proyectar la primera mitad de la ilusión en ti ahora mismo. Luego, ve directamente al Risco Ihgaluk. Cuando llegues, me pondré en contacto contigo y completaré la segunda mitad de la ilusión.', '', '', '', 'Voy a transformarte para que parezcas un saurok.', 'Imagen del archimago Vargoth', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'esMX', 'En la piel de un saurok', 'Ve al Risco Ihgaluk y mata a un saurok Hojaescoria.', 'El Risco Ihgaluk es un lugar peligroso. Por suerte, tienes la magia del Kirin Tor como apoyo.$b$bPuedo transformarte en un saurok.$b$bVoy a proyectar la primera mitad de la ilusión en ti ahora mismo. Luego, ve directamente al Risco Ihgaluk. Cuando llegues, me pondré en contacto contigo y completaré la segunda mitad de la ilusión.', '', '', '', 'Voy a transformarte para que parezcas un saurok.', 'Imagen del archimago Vargoth', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'frFR', 'La peau des sauroks', 'Allez à la Combe d’Ihgaluk et tuez un Saurok crasselame.', 'La combe d’Ihgaluk est un endroit dangereux. Heureusement, vous pouvez compter sur la magie du Kirin Tor pour vous seconder.$b$bJe peux vous transformer en saurok.$b$bJe vais exécuter la première partie de l’illusion sur vous maintenant. Ensuite, rendez-vous directement à la combe d’Ihgaluk et tuez un des sauroks crasselames. Quand ce sera fait, je vous contacterai pour accomplir la seconde partie de l’illusion.', '', '', '', 'Je vais vous transformer pour que vous ressembliez à un saurok.', 'Image de l’archimage Vargoth', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'itIT', 'La pelle dei Saurok', 'Vai a Ihgaluk e uccidi un Saurok Lamalercia.', 'Ihgaluk è un luogo pericoloso, ma fortunatamente hai la magia del Kirin Tor al tuo fianco.$b$bPosso donarti le sembianze di un Saurok.$b$bEseguirò la prima parte del rituale qui con te ora, quindi ti dirigerai a Ihgaluk. Dopo che avrai ucciso uno dei Saurok Lamalercia, ti contatterò per completare la seconda parte dell\'incantesimo.', '', '', '', 'Ti donerò le sembianze di un Saurok.', 'Immagine dell\'Arcimago Vargoth', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'koKR', '사우록의 거죽', '이갈룩 바위산으로 가서 부랑칼날 사우록을 한 마리 처치해야 합니다.', '이갈룩 바위산은 위험한 곳이오. 다행히도 당신 뒤에는 키린 토의 마법이 버티고 있지.$b$b당신을 사우록으로 변신시켜 주겠소.$b$b지금 환영 마법의 앞부분을 준비하도록 하지. 그 다음 바로 이갈룩 바위산로 가서 부랑칼날 사우록을 한 마리 처치하시오. 그러면 내가 연락을 취해 환영 마법을 마무리할 테니.', '', '', '', '사우록의 모습으로 변신시켜 주겠소.', '대마법사 바르고스의 환영', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'ptBR', 'Pele dos sauroks', 'Vá até o Penhasco Ihgaluk e mate um saurok Gumeskuma.', 'O Penhasco Ihgaluk é um lugar perigoso. Por sorte, você tem a proteção da magia do Kirin Tor.$b$bEu posso transformá-l$go:a; em um saurok.$b$bVou realizar a primeira metade da ilusão em você agora. Depois, vá diretamente ao Penhasco Ihgaluk e mate um dos sauroks Gumeskuma. Quando tiver feito isso, eu entrarei em contato para completar a segunda parte da ilusão.', '', '', '', 'Vou transformar você para se parecer com um saurok.', 'Imagem do Arquimago Vargoth', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'ruRU', 'В шкуре саурока', 'Отправляйтесь на утесы Ихгалук и убейте саурока Гнилого Клинка.', 'Утесы Ихгалук – опасное место. К счастью, тебе помогает магия Кирин-Тора.$b$bЯ могу превратить тебя в саурока.$b$bСейчас я сделаю первую половину иллюзии. После этого отправляйся прямо на утесы Ихгалук и убей там одного саурока Гнилого Клинка. Там я свяжусь с тобой и завершу создание иллюзии.', '', '', '', 'Я сделаю так, что ты будешь выглядеть, как саурок.', 'Проекция верховного мага Варгота', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'zhCN', '蜥蜴人的皮', '前往伊瓜鲁克峭壁并消灭一名邪刃蜥蜴人。', '伊瓜鲁克峭壁是一个危险的地方。幸运的是，你还有肯瑞托的魔法支援。$b$b我可以把你变成蜥蜴人。$b$b我会先对你施放一半的幻术。然后，你要直接前往伊瓜鲁克峭壁，消灭其中一名邪刃蜥蜴人。等你到了之后，我会再联系你，并完成后一半的幻术。', '', '', '', '我会把你变成蜥蜴人。', '大法师瓦格斯的影像', '', 0);
INSERT INTO `quest_template_locale` VALUES (32549, 'zhTW', '幻化成薩烏洛克', '前往伊加路克峭壁並殺死一名污刃薩烏洛克。', '伊加路克峭壁這個地方太險惡了。幸運的是，你有祈倫托的魔法保護你。$b$b我可以將你變形成一隻薩烏洛克。$b$b我先施行前半段的幻術。之後，你直接到伊加路克峭壁並殺死一隻污刃薩烏洛克。事成之後，我會跟你聯絡，並完成後半段的幻術。', '', '', '', '我會把你變成薩烏洛克的模樣。', '大法師瓦戈斯的影像', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'deDE', 'Verechst und zugenäht', 'Bezwingt 10 Trolle der Zandalari in den Bestienpferchen.', 'In den Bestienpferchen werdet Ihr einige der besten Bestienzähmer des Zandalarimilitärs finden. Sie sind darauf trainiert, mit Axt, Tier und Klauen zu töten. Dort bilden sie ihre Reiterschaft aus.$b$bIhr müsst für uns alle Zandalari töten, die Ihr in den Bestienpferchen findet, bevor sie ihre berittenen Monstrositäten hinaus aufs Schlachtfeld schicken können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'esES', 'Un mal \"necesaurio\"', 'Derrota a 10 trols Zandalari en El Cerco de las Bestias.', 'En El Cerco de las Bestias encontrarás a algunos de los mejores domadores de bestias del ejército Zandalari. Han sido entrenados para matar con hachas, bestias y garras. Aquí es donde forman su caballería.$b$bNecesitamos que mates a cualquier Zandalari que veas allí antes de que suelten a esas monstruosidades montadas en el campo de batalla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'esMX', 'Un mal \"necesaurio\"', 'Derrota a 10 trols Zandalari en El Cerco de las Bestias.', 'En El Cerco de las Bestias encontrarás a algunos de los mejores domadores de bestias del ejército Zandalari. Han sido entrenados para matar con hachas, bestias y garras. Aquí es donde forman su caballería.$b$bNecesitamos que mates a cualquier Zandalari que veas allí antes de que suelten a esas monstruosidades montadas en el campo de batalla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'frFR', 'Vilaines bestioles', 'Tuez 10 Trolls zandalari dans les Enclos à bêtes.', 'Dans les enclos à bêtes, vous rencontrerez certains des dompteurs de bêtes les plus redoutables de l’armée des Zandalari. Ils sont entraînés à tuer à la hache, mais aussi à l’aide de leurs bêtes et de leurs griffes. C’est là que les Zandalari forment leur cavalerie.$b$bIl faut que vous tuiez tous les Zandalari que vous trouverez là avant qu’ils ne lâchent leurs montures monstrueuses sur le champ de bataille.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'itIT', 'Sauro perdente', 'Uccidi 10 Troll Zandalari nei Recinti delle Bestie.', 'All\'interno dei Recinti delle Bestie incontrerai alcuni tra i più abili addestratori di bestie dell\'esercito Zandalari. Sono addestrati per uccidere con le loro asce e con gli artigli delle bestie al loro comando. Questo è il luogo dove la loro cavalleria viene selezionata e perfezionata.$b$bAbbiamo bisogno che tu uccida tutti i Troll Zandalari che riesci a trovare nei recinti prima che riescano a inviare le loro mostruose cavalcature in battaglia.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'koKR', '공룡이 너무해', '야수 조련소에서 잔달라 트롤 10명을 처치해야 합니다.', '그대는 야수 조련소 안에서 잔달라 병사 중 가장 노련한 야수 조련사와 상대하게 될 것입니다. 그들은 도끼와 야수, 그리고 발톱으로 적을 처치하도록 훈련받았습니다. 야수 조련소는 조련사들이 기병대를 조직한 곳이기도 합니다.$b$b야수 조련사들이 그 흉물스러운 야수에 올라타고 전장 한가운데로 뛰쳐나가기 전에 그곳에 있는 잔달라 트롤들을 전부 처치해버리십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'ptBR', 'Sáurio perdedor', 'Derrote 10 trolls Zandalari no Cercado das Feras.', 'Dentro do Cercado das Feras você encontrará alguns dos mais habilidosos adestradores do exército Zandalari. Eles são treinados para matar com machados, feras e garras. É assim que a cavalaria é disciplinada.$b$bPor favor, mate todos os Zandalari que encontrar no Cercado antes que eles comecem a usar as monstruosidades de montaria no campo de batalha.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'ruRU', 'Расправа', 'Убейте 10 зандаларских троллей в загонах для зверей.', 'В загонах для зверей ты встретишь самых опытных укротителей зверей в зандаларской армии. Они натренированы убивать – топорами, когтями или при помощи зверей. В этом бестиарии зандалары и готовят своих воинов-наездников.$b$bНам нужно, чтобы ты $gубил:убила; всех зандаларов, каких только встретишь в загонах для зверей, пока они не выехали на поле боя верхом на своих чудовищных подопечных.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'zhCN', '纵龙者', '在兽栏中击败的10名赞达拉巨魔。', '在兽栏中，你可能会遇到赞达拉部队中最老练的驯兽师。他们懂得用斧子、野兽和爪子进行攻击。他们就是在这里建立起自己的骑兵部队的。$b$b在他们骑着那些怪物出现在战场上之前，我们要你先去兽栏击败那里的赞达拉巨魔。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32550, 'zhTW', '除掉馴獸師', '擊敗獸園裡的10名贊達拉食人妖。', '在獸園裡，你會遇到幾個贊達拉軍隊裡頭最有才能的馴獸師。他們擅長以斧頭、野獸與利爪殺戮敵人。這裡也是他們訓練騎兵的地方。$b$b我們需要你在食人妖騎乘魔龍加入戰場前，殺掉每個你在獸園裡找到的食人妖。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'deDE', 'Compyknüppler', 'Tötet 5 Raptoren und 10 abgerichtete Sauriden in den Bestienpferchen.', 'Die Raptoren und Sauriden der Zandalari stellen eine erhebliche Bedrohung für unsere Truppen dar. Sie sind hervorragende Jäger und können ihre Beute über große Entfernungen verfolgen. Zudem enden ihre Angriffe oft tödlich, wenn sie ihre Opfer überraschen können.$b$bIch möchte, dass Ihr Euch in die Bestienpferche begebt und ihre Zahl verringert. Wir müssen sicherstellen, dass sie sterben, bevor ihre Ausbildung abgeschlossen wurde.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'esES', 'Matanza jurásica', 'Mata a 5 raptores y a 10 sáuridos adiestrados en El Cerco de las Bestias.', 'Los raptores y los sáuridos de los Zandalari representan una gran amenaza para nuestras fuerzas. Son bestias cazadoras extraordinarias, capaces de rastrear presas a grandes distancias y suelen ser letales si te pillan por sorpresa.$b$bNecesito que vayas a El Cerco de las Bestias y que elimines a algunos. Debemos cerciorarnos de matarlas antes de que estén completamente adiestradas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'esMX', 'Matanza jurásica', 'Mata 5 raptores y 10 sáuridos entrenados en las jaulas de las bestias.', 'Los raptores y los sáuridos de los zandalari representan una amenaza considerable para nuestras fuerzas. Son bestias cazadoras excepcionales que pueden rastrear a sus presas a lo largo de grandes distancias y con frecuencia son letales si te las encuentras de manera inesperada.$b$bNecesito que visites las jaulas de las bestias y reduzcas sus números. Tenemos que acabar con ellos antes de que estén listos para salir a matar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'frFR', 'Compicide', 'Tuez 5 Raptors et 10 Saurides apprivoisés dans les Enclos à bêtes.', 'Les raptors et les saurides des Zandalari représentent une menace non négligeable pour nos troupes. Ce sont de superbes chasseurs, capables de traquer leurs proies sur de grandes distances. Nombreux sont ceux qui, par manque de préparation, ont perdu la vie dans une rencontre avec ces créatures.$b$bJe veux que vous vous rendiez dans les Enclos à bêtes afin de réduire leur nombre. Nous devons faire en sorte de les tuer avant qu’ils ne soient complètement dressés.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'itIT', 'Parco giurassico', 'Elimina 5 Raptor e 10 Sauridi Addestrati nei Recinti delle Bestie.', 'I raptor e i sauridi degli Zandalari rappresentano una grave minaccia per le nostre truppe. Sono dei superbi predatori, in grado di fiutare la loro preda anche a grandi distanze e quindi estremamente pericolosi per chiunque venga colto di sorpresa.$b$bHo bisogno che tu vada ai Recinti delle Bestie e che sfoltisca il loro numero. Dobbiamo ucciderli prima che raggiungano la loro piena maturità.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'koKR', '야수 처치', '야수 조련소에 있는 랩터 5마리와 훈련된 사우리드 10마리를 처치해야 합니다.', '잔달라의 랩터와 사우리드들은 이곳에 주둔한 아군 병력에 잠재적인 위협이 됩니다. 놈들은 머나먼 거리까지 먹이를 추적해 사냥할 수 있으며, 눈치채지 못하게 기습해 치명적인 피해를 입히지요.$b$b그대가 야수 조련소로 가서 놈들의 수를 좀 줄여주십시오. 완벽하게 조련되기 전에 처치해버려야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'ptBR', 'Pisada de dino', 'Mate 5 Raptores e 10 Saurídeos Treinados no Cercado das Feras.', 'Os raptores e saurídeos dos Zandalari representam uma ameaça substancial às nossas forças aqui. São feras caçadoras incríveis que podem rastrear a presa a distâncias enormes e costumam ser letais para os incautos.$b$bPor favor, vá até o Cercado das Feras e reduza o contingente desses bichos. Precisamos acabar com eles antes que cheguem ao fim do treinamento.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'ruRU', 'Компот из компсогната', 'Убейте 5 ящеров и 10 дрессированных сауридов в загонах для ящеров.', 'Ящеры и сауриды зандаларов представляют для наших войск серьезную опасность. Они обладают непревзойденными охотничьими навыками и могут почуять добычу на огромном расстоянии. Если они застанут свою жертву врасплох, этот бой может быть для нее последним.$b$bЯ хочу, чтобы ты $gпробрался:пробралась; в загоны для ящеров и $gсократил:сократила; их численность. Мы должны убить их до того, как они пройдут полный курс дрессировки.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'zhCN', '践踏掠食龙', '在兽栏消灭5只迅猛龙和10只训练有素的掠食龙。', '赞达拉的迅猛龙和细颚龙是对我们部队的重大威胁。它们是出色的狩猎者，能够长途追踪猎物，对毫无准备的目标尤为致命。$b$b我需要你前往兽栏减少那里的恐龙数量。我们要在训练完成前先干掉它们。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32551, 'zhTW', '除掉那些龍', '殺死獸園裡的5隻迅猛龍和10隻訓練有素的羽冠龍。', '贊達拉的迅猛龍和羽冠龍對我們的部隊造成嚴重威脅。牠們是優秀的狩獵者，能長途追逐獵物。出其不意的襲擊往往致獵物於死命。$b$b我要你進入獸園削減牠們的數量，務必在牠們成為訓練有素的猛獸前下手為強。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'deDE', 'Loasaurus', 'Bezwingt einen Loasprecher.', 'Die Zandalari haben einen Weg gefunden, um Loa an ihre Bestien zu binden, was den Tieren beeindruckende und tödliche Kräfte verleiht.$b$bWir müssen das beenden.$b$bSucht in den Bestienpferchen nach einem Loasprecher und tötet ihn. Es sollte Euch möglich sein, einen zu finden, wenn Ihr Ausschau nach ihren leuchtenden Reittieren haltet.$b$bDiese Untaten dürfen einfach nicht weitergehen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'esES', 'Hechizos muy poco loa-bles', 'Derrota a un portavoz loa.', 'Los Zandalari han hallado un modo de vincular los loa a sus bestias, lo cual les otorga un poder asombroso a la par que letal.$b$bNecesitamos que pongas fin a esto.$b$bBusca a un portavoz loa en El Cerco de las Bestias y mátalo. Deberías poder encontrarlo si tienes cuidado con sus bestias resplandecientes.$b$bNo podemos permitir que sigan con esta atrocidad.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'esMX', 'Hechizos muy poco loa-bles', 'Derrota a un portavoz loa.', 'Los Zandalari han hallado un modo de vincular los loa a sus bestias, lo cual les otorga un poder asombroso a la par que letal.$b$bNecesitamos que pongas fin a esto.$b$bBusca a un portavoz loa en El Cerco de las Bestias y mátalo. Deberías poder encontrarlo si tienes cuidado con sus bestias resplandecientes.$b$bNo podemos permitir que sigan con esta atrocidad.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'frFR', 'Loa-saure', 'Battez un Parle-loa.', 'Les Zandalari ont trouvé comment lier un Loa à leurs bêtes pour leur donner des pouvoirs impressionnants et mortels.$b$bNous devons y mettre un terme.$b$bCherchez un parle-loa dans les enclos à bêtes et tuez-le. Guettez leurs bêtes luisantes pour les repérer.$b$bNous ne pouvons pas les laisser poursuivre ces atrocités.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'itIT', 'Loa-sauri', 'Uccidi un Oratore dei Loa.', 'Gli Zandalari hanno trovato un modo per infondere l\'essenza dei Loa nelle loro bestie, donando loro un potere tanto impressionante quanto letale.$b$bDobbiamo porre fine a tutto questo.$b$bCerca un Oratore dei Loa nei Recinti delle Bestie e uccidilo. Li potrai trovare mentre sono intenti a supervisionare le loro preziose bestie.$b$bNon possiamo permettere che queste atrocità continuino.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'koKR', '로아가 깃든 야수', '로아 전령을 처치해야 합니다.', '잔달라가 로아와 야수를 결속시켜 믿을 수 없을 정도로 강력한 힘을 부여하는 방법을 찾아냈어요.$b$b어서 이것을 저지해야만 해요.$b$b야수 조련소를 수색해 로아 전령을 찾아서 처치해버리세요. 붉은빛이 감도는 야수를 계속 찾다 보면 놈들을 발견할 수 있을 거예요.$b$b이 잔학한 일이 계속 일어나도록 해선 안 돼요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'ptBR', 'Loassauro', 'Derrote um Voz-dos-loas.', 'Os Zandalari encontraram uma maneira de vincular loa às suas feras e dar-lhes poderes incríveis e letais.$b$bPrecisamos acabar com isso.$b$bEncontre um voz-dos-loas no Cercado das Feras e dê cabo dele. Você vai conseguir achá-lo se ficar atent$go:a; às feras brilhantes.$b$bNão podemos deixar essas atrocidades continuarem.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'ruRU', 'Лоа-завр', 'Победите говорящего с лоа.', 'Зандалары научились насыщать своих зверей силой лоа, после чего те становятся невероятно сильны и смертельно опасны.$b$bМы должны прекратить это.$b$bУбей говорящего с лоа в загонах для зверей. Его можно найти возле светящихся животных.$b$bНельзя допустить, чтобы эти зверства продолжались.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'zhCN', '神龙', '击败一名神谕者。', '赞达拉巨魔发现了一种将神灵束缚在野兽体内的方法，这可以让它们获得惊人而致命的力量。$b$b我们必须阻止他们。$b$b去兽栏寻找一个神谕者并击败他。你可以通过那些发光的野兽来辨认找到他们。$b$b我们不能让这样的暴行继续下去。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32552, 'zhTW', '羅亞恐龍', '擊敗一名羅亞語者。', '贊達拉找到方法將羅亞灌注到野獸體內，賦予其驚人且致命的力量。$b$b我們得阻止這一切發生。$b$b到獸園尋找一名羅亞語者，殺了他。留意發光的野獸便能找到他們。$b$b這種惡行不能再繼續下去。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'deDE', 'Terrorhorn vs. Teufelssaurier', 'Unterbrecht ein Bindungsritual, indem Ihr alle Trolle tötet, die ein starrsinniges Terrorhorn oder einen widerspenstigen Teufelssaurier umringen.', 'Wir haben eine Gelegenheit, die Kriegsmaschinerie der Zandalari ins Stocken zu bringen und gleichzeitig etwas Spaß zu haben.$b$bIn diesen Bestienpferchen werden ein Teufelssaurier und ein Terrorhorn mit Rüstung ausgestattet, während ihr Geist den Zaubern der Zandalari unterworfen ist.$b$bGeht in das Lager und unterbrecht eines dieser Bindungsrituale. Es ist egal welches, tötet einfach nur diejenigen, die sich bemühen die Kreatur zu binden. Das sollte genügen, um das voll gerüstete Tier auf Verwüstungstour durch ihr Lager zu schicken. Dann lehnt Euch einfach zurück und genießt das Spektakel.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'esES', 'Cuernoatroz contra demosaurio', 'Interrumpe un ritual de vinculación matando a todos los PNJ que estén alrededor de un cuernoatroz desafiante o de un demosaurio desafiante.', 'Hemos vislumbrado una oportunidad para detener la máquina de guerra Zandalari y divertirnos en el proceso.$b$bEn este cerco lleno de bestias, están equipando a un demosaurio y a un cuernoatroz. Su mente es esclava de los hechizos Zandalari.$b$bDirígete al campamento e interrumpe uno de esos rituales de vinculación. No importa cuál. Tú mata a los que estén intentando vincular a la criatura. Esto debería liberar a la bestia completamente equipada para que siembre el caos por todo el campamento Zandalari. Ese será el momento de relajarse y disfrutar del espectáculo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'esMX', 'Cuernoatroz contra demosaurio', 'Interrumpe un ritual de vinculación matando a todos los PNJ que estén alrededor de un cuernoatroz desafiante o de un demosaurio desafiante.', 'Hemos vislumbrado una oportunidad para detener la máquina de guerra Zandalari y divertirnos en el proceso.$b$bEn este cerco lleno de bestias, están equipando a un demosaurio y a un cuernoatroz. Su mente es esclava de los hechizos Zandalari.$b$bDirígete al campamento e interrumpe uno de esos rituales de vinculación. No importa cuál. Tú mata a los que estén intentando vincular a la criatura. Esto debería liberar a la bestia completamente equipada para que siembre el caos por todo el campamento Zandalari. Ese será el momento de relajarse y disfrutar del espectáculo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'frFR', 'Navrecorne contre diablosaure', 'Interrompez un Rituel de lien en tuant tous les participants au rituel autour d’un Navrecorne défiant ou d’un Diablosaure défiant.', 'Nous avons l’occasion de perturber la machine de guerre zandalari et de nous amuser par la même occasion.$b$bDans ces enclos à bêtes, les Zandalari sont en train d’installer des armures sur un diablosaure et un navrecorne, bêtes dont les esprits sont réduits en esclavage par des charmes.$b$bRendez-vous dans le camp et interrompez un des rituels de lien, peu importe lequel. Tuez juste ceux qui essaient de lier une de ces créatures. Cela devrait rendre folle la bête cuirassée qui se ruera dans le camp zandalari. Il ne vous restera plus qu’à vous installer pour profiter du spectacle.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'itIT', 'Cornofurente contro gigantosauro', 'Interrompi un rituale di vincolamento uccidendo tutti i ritualisti attorno a un Cornofurente Ribelle o un Gigantosauro Ribelle.', 'Abbiamo l\'opportunità di smantellare la macchina da guerra degli Zandalari e di divertirci al tempo stesso.$b$bAll\'interno dei recinti delle bestie, un gigantosauro e un cornofurente sono bardati per la guerra, le loro menti plagiate e vincolate dagli amuleti degli Zandalari.$b$bAddentrati nell\'accampamento e interrompi uno dei rituali; non ha importanza quale, limitati a uccidere coloro che stanno cercando di plagiare la mente di quelle povere bestie. Questo dovrebbe bastare a scatenarle contro i loro stessi padroni e a causare un po\' di panico nel loro accampamento. Poi siediti e goditi lo spettacolo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'koKR', '공포뿔이냐 데빌사우루스냐', '반항적인 공포뿔이나 반항적인 데빌사우루스 주변에 있는 잔달라 트롤들을 모두 처치해 결속 의식을 방해해야 합니다.', '잔달라의 전쟁 무기에 훼방을 놓으면서 약간 재미있는 일도 해볼 기회예요.$b$b이 야수 조련소 안에는 갑옷으로 무장한 공포뿔과 데빌사우루스가 있는데, 이 둘의 영혼이 잔달라의 부적에 결속되고 있어요.$b$b이 결속 의식이 진행되는 곳으로 가서 훼방을 좀 놓아주세요. 어떤 놈이든 상관없어요. 의식을 진행 중인 놈들은 모조리 처치해버리세요. 그러면 완전무장한 이 야수들이 잔달라 야영지에서 미쳐 날뛸 거예요. 그다음에 일어나는 우스꽝스러운 일은 그냥 느긋하게 구경하시면 된답니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'ptBR', 'Demossauro x Escornante', 'Interrompa um ritual de vinculação matando todos os PNJs ao redor de um Escornante Teimoso ou de um Demossauro Teimoso.', 'Temos uma chance de acabar com a máquina de guerra Zandalari e, ainda por cima, nos divertir no processo.$b$bDentro dessas jaulas, um demossauro e um escornante, com suas mentes enfeitiçadas pelos Zandalari, estão sendo equipados com armaduras.$b$bVá até o acampamento e interrompa um desses rituais de vinculação. Não importa qual. Apenas mate quem está tentando vincular a criatura. Isso vai fazer com que a fera de armadura corra sem direção pelo acampamento Zandalari. Depois, é só relaxar e ver a magia acontecer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'ruRU', 'Дикорог или дьявозавр', 'Прервите подчиняющий ритуал, убив всех неигровых персонажей вокруг непокорного дикорога или непокорного дьявозавра.', 'У нас есть отличная возможность нарушить планы зандаларов и заодно как следует посмеяться.$b$bВ этих загонах для зверей держат дьявозавра и дикорога. На них надета броня, а их разум при помощи заклинаний контролируют зандалары.$b$bОтправляйся в лагерь и прерви один из этих подчиняющих ритуалов. Какой именно – не имеет значения. Просто убей тех, кто пытается подчинить себе существо. Разьяренный зверь начнет носиться по всему лагерю зандаларов. А ты расслабься и наслаждайся веселым зрелищем.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'zhCN', '恐角龙大战魔暴龙', '击败傲慢的恐角龙或傲慢的魔暴龙身边的所有NPC以扰乱束缚仪式。', '眼下，我们有了一个干扰赞达拉战争机器的机会，还能顺便给自己找点乐子。$b$b在这片兽栏中，有一头魔暴龙和一头恐角龙，它们都是全副武装，并且心智被赞达拉符咒所奴役。$b$b我要你潜入营地并干扰那里的任意一个束缚仪式。没有特定目标。你只要解决掉那些想要束缚这些野兽的家伙就行了。这能够让这些武装到牙齿的野兽发狂并肆虐赞达拉的营地。然后，我们只要坐着看热闹就行了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32553, 'zhTW', '恐角龍對戰魔暴龍', '殺死不肯馴服的恐角龍或魔暴龍身邊進行禁錮儀式的NPC，來中斷儀式。', '我們有機會阻止贊達拉的戰爭機器，並且從這個過程中獲得一點樂趣。$b$b獸園裡有一頭身穿護甲的魔暴龍和恐角龍，牠們的心智受贊達拉的符咒所操縱。$b$b進入營帳阻止其中一個禁錮儀式，哪一個都可以，只要殺掉那些企圖禁錮生物的施法者。如此一來，便能讓那隻裝甲生物在贊達拉營帳中瘋狂亂跑。接下來你只要等著看好戲就行了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'deDE', 'Saurierstark', 'Findet Saurierfetische und wendet sie auf 5 Raptoren, Compys, Himmelskreischer oder Terrorhörner in den Bestienpferchen an.', 'Die Zandalari verwenden eine ganze Batterie von Fetischen und Amuletten, um ihre Wildtiermenagerie im Zaum zu halten.$b$bBei diesem Spielchen können wir mitspielen.$b$bIch möchte, dass Ihr einige dieser Fetische auftreibt und ihre Wirkung auf die verschiedenen Tiere in den Bestienpferchen untersucht.$b$bNotiert Euch aufmerksam, wie welches Tier auf welchen Fetisch reagiert. Gebt Eure Untersuchungen dann an mich weiter.$b$bVielleicht kann uns das genau den Vorteil verschaffen, den wir gegen die Zandalari benötigen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'esES', 'Fuerza jurásica', 'Encuentra y utiliza fetiches saurios en 5 raptores, minirraptores, vociferadores de cielos o cuernoatroces en El Cerco de las Bestias.', 'Los Zandalari utilizan una serie de fetiches y talismanes para mantener el control sobre su manada de bestias.$b$bEsto puede ser un arma de doble filo.$b$bQuiero que te hagas con unos cuantos fetiches y que los pruebes en las bestias de El Cerco de las Bestias.$b$bObserva bien cómo responden a los fetiches e infórmame de los resultados.$b$bQuizás esto nos dé la ventaja que necesitamos sobre los Zandalari.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'esMX', 'Fuerza jurásica', 'Encuentra y utiliza fetiches saurios en 5 raptores, minirraptores, vociferadores de cielos o cuernoatroces en El Cerco de las Bestias.', 'Los Zandalari utilizan una serie de fetiches y talismanes para mantener el control sobre su manada de bestias.$b$bEsto puede ser un arma de doble filo.$b$bQuiero que te hagas con unos cuantos fetiches y que los pruebes en las bestias de El Cerco de las Bestias.$b$bObserva bien cómo responden a los fetiches e infórmame de los resultados.$b$bQuizás esto nos dé la ventaja que necesitamos sobre los Zandalari.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'frFR', 'Dino mythe', 'Trouvez et utilisez des Fétiches de saurien sur 5 Raptors, Compies, Hurleciel ou Navrecornes dans les Enclos à bêtes.', 'Les Zandalari utilisent une série de fétiches et de charmes pour conserver le contrôle de leur ménagerie.$b$bÀ bon chat, bon rat !$b$bJe veux que vous trouviez quelques-uns de ces fétiches et que vous les essayiez sur les différents animaux dans les enclos à bêtes.$b$bObservez bien comment chaque bête réagit à chaque fétiche. Ensuite, venez me faire votre rapport.$b$bCela nous donnera peut-être l’avantage dont nous avons besoin contre les Zandalari.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'itIT', 'Forza preistorica', 'Trova e usa i Feticci dei Sauri su 5 raptor, compy, urlacieli o cornofurente nei Recinti delle Bestie.', 'Gli Zandalari usano una serie di feticci e amuleti per mantenere il controllo sulle loro bestie.$b$bVoglio che tu vada a recuperare quei feticci e che li provi sugli animali che si trovano nei Recinti delle Bestie.$b$bOsserva attentamente le loro reazioni all\'uso di ogni feticcio e poi portami i risultati.$b$bForse questo ci darà un vantaggio contro gli Zandalari.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'koKR', '강력한 공룡', '사우루스 우상을 찾아서 야수 조련소에 있는 랩터나 콤피, 비명익룡 또는 공포뿔 5마리에게 사용해야 합니다.', '잔달라는 우상과 부적으로 야수 조련소에 있는 야수들을 제어하고 있습니다.$b$b우리도 뭔가 수를 써야겠군요.$b$b그대가 이 우상을 찾아서 야수 조련소에 있는 다양한 야수들에게 시험해보면 좋겠습니다.$b$b여러 야수들이 우상에 각각 어떻게 반응하는지 상세히 알아보십시오. 그리고 결과를 제게 알려주십시오.$b$b그러면 잔달라를 상대할 때 우위에 설 기회가 생길 것입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'ptBR', 'Poder dino', 'Encontre e use Fetiches Sáurios em 5 raptores, dinos, gritacéus ou escornantes no Cercado das Feras.', 'Os Zandalari usam uma série de fetiches e feitiços para controlar sua coleção de feras.$b$bNós podemos pagar na mesma moeda.$b$bEu quero que você consiga alguns desses fetiches e os use nas criaturas do Cercado das Feras.$b$bPreste bastante atenção na maneira como cada fera reage a cada fetiche. Depois, relate os resultados a mim.$b$bTalvez isso nos dê a vantagem de que precisamos contra os Zandalari.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'ruRU', 'Мощь динозавра', 'Найдите и используйте заврофетиши на 5 ящерах, компсогнатах, небесных крикунах или дикорогах в загонах для зверей.', 'У зандаларов есть множество фетишей и оберегов, при помощи которых они управляют своим зверинцем.$b$bЧто ж, мы можем сделать то же самое.$b$bНайди какие-нибудь из этих фетишей и опробуй их действие на разных животных в загонах для зверей.$b$bОбрати внимание, как действует каждый из них на разных животных. А затем сообщи результаты мне.$b$bМожет быть, это даст нам необходимое преимущество в борьбе с зандаларами.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'zhCN', '史前巨兽', '找到并对兽栏中的5只迅猛龙、掠食龙、啸天龙或恐角龙使用恐龙神像。', '赞达拉巨魔使用了一系列的神像和符咒来维持他们对兽栏的控制。$b$b不妨以其人之道还治其人之身。$b$b我想让你找些这种神像来，然后在兽栏中的各种野兽身上试试效果。$b$b把每只野兽对每个神像的反应都认真记录下来。然后向我汇报。$b$b也许这能使我们取得对抗赞达拉的优势。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32554, 'zhTW', '恐龍的力量', '在獸園找到恐龍神像並對5隻迅猛龍、細顎龍、嘯天龍或恐角龍使用它。', '贊達拉使用一系列的神像與符咒來操縱野獸。$b$b那一套你會我也會。$b$b我要你去找一些神像過來，對獸園裡的各種野獸進行測試。$b$b仔細記錄野獸們對每個雕像的反應，然後向我回報。$b$b也許我們能找到對付贊達拉的利器。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'deDE', 'Jahrhundertwachen', 'Zerstört 5 geist-gebundene Wachposten und nutzt den Resonanzkristall, um die in ihnen gefangenen Zandalarigeister einzusammeln.', 'Innerhalb der Gesellschaft der Zandalari sind Opferungen gang und gäbe. Jedoch gibt es eine Kuriosität im Kreis dieser barbarischen Rituale: Freiwillige. Sie betrachten es als große Ehre!$b$bSelbst die Schwachen wollen noch ihren Teil beitragen, selbst wenn dies bedeutet, dass sie ihr Leben geben müssen. Ihre Seelen werden an die Wachen gebunden, die Ihr hier herumlaufen seht. Eingesperrt, um ihrem Volk zu Diensten zu sein.$b$bNehmt diesen Resonanzkristall. Entzieht den Wachen damit die ihnen innewohnenden Zandalarigeister, sobald sie zerstört wurden. Bringt den Kristall zu mir zurück, wenn Eure Aufgabe abgeschlossen ist.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'esES', '¡Que no escapen!', 'Mata a 5 avizores vinculados a espíritus y utiliza el cristal de resonancia para capturar a los espíritus Zandalari que hay atrapados en ellos.', 'Los sacrificios son algo común en la sociedad Zandalari. No obstante, hay algo muy raro en estos rituales barbáricos: voluntarios. ¡Es un gran honor para ellos!$b$bLos débiles seguirán la costumbre a pesar de que tengan que vivir atados a los centinelas que ves por ahí, atrapados al servicio de su tribu.$b$bToma este cristal resonador. Cuando destruyas a un centinela, utiliza el cristal para absorber el Zandalari que hay dentro. Devuélveme el cristal cuando termines.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'esMX', '¡Que no escapen!', 'Mata a 5 avizores vinculados a espíritus y utiliza el cristal de resonancia para capturar a los espíritus Zandalari que hay atrapados en ellos.', 'Los sacrificios son algo común en la sociedad Zandalari. No obstante, hay algo muy raro en estos rituales barbáricos: voluntarios. ¡Es un gran honor para ellos!$b$bLos débiles seguirán la costumbre a pesar de que tengan que vivir atados a los centinelas que ves por ahí, atrapados al servicio de su tribu.$b$bToma este cristal resonador. Cuando destruyas a un centinela, utiliza el cristal para absorber el Zandalari que hay dentro. Devuélveme el cristal cuando termines.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'frFR', 'Sentinelles des siècles', 'Tuez 5 Factionnaires liés par les esprits, puis utilisez le Cristal de résonance pour capturer les Esprits zandalari piégés à l’intérieur.', 'Les sacrifices sont monnaie courante dans la société des Zandalari. Il y a néanmoins une curiosité dans le cadre de ces rituels barbares : les volontaires. Ils considèrent cela comme un grand honneur !$b$bLes faibles cherchent à contribuer ainsi, même si cela implique de perdre la vie afin d’être lié à l’une des sentinelles que vous avez pu croiser dans les parages. À jamais au service de leur peuple…$b$bPrenez ce cristal de résonance. Une fois détruite, drainez la sentinelle de son occupant zandalari. Ramenez-moi le cristal lorsque votre tâche sera accomplie.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'itIT', 'Sentinelle secolari', 'Uccidi 5 Avvistatori Vincolati e poi usa il Cristallo Risonante per catturare gli Spiriti Zandalari intrappolati al loro interno.', 'I sacrifici sono normale amministrazione nella società Zandalari. La cosa strana di questi rituali barbarici, però, è la presenza di molti volontari: lo considerano un grande onore!$b$bI deboli cercano di contribuire, anche se questo significa dare la propria vita per animare le sentinelle che vedi scorrazzare in giro, condannate a servire in eterno la loro gente.$b$bPrendi questo Cristallo Echeggiante e, dopo aver distrutto una di quelle sentinelle, risucchia l\'anima dello Zandalari al suo interno. Riportami il cristallo quando avrai finito.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'koKR', '보초병 안에서 수백 년을', '영혼결속 보초병 5명을 처치하고 그 안에 갇힌 잔달라 영혼을 공명의 수정으로 포획해야 합니다.', '잔달라 부족의 사회에서 제물을 바치는 행위는 놀라운 일이 아니에요. 이 야만적인 행위 중에서도 가장 의외인 점은, 바로 이들이 제물이 되기 위해 자발적으로 지원한다는 점이죠. 이들은 그게 큰 영광이라 생각합니다!$b$b약자들은 어떻게라도 기여할 방법을 찾습니다. 자신의 영혼이 이 근처를 배회하는 보초병에게 결속되어야 한다고 해도 말이죠. 부족을 위해 보초병 속에 갇히는 것입니다.$b$b이 공명의 수정을 가져가세요. 잔달라 보초병을 쓰러뜨리고 그 안의 영혼을 수정으로 흡수하고 임무가 끝나면 제게 가져오세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'ptBR', 'Séculos de sentinela', 'Mate 5 Sentinelas Almatadas e use o Cristal de Ressonância para capturar os espíritos Zandalari presos nelas.', 'Sacrifícios são comuns na sociedade Zandalari, mas esses rituais bárbaros têm uma peculiaridade: voluntários. Isso é considerado uma grande honra!$b$bOs fracos continuam querendo contribuir, mesmo que isso signifique ter a vida vinculada a uma das sentinelas que você viu perambulando por aí. Acabar como prisioneiro dos serviços prestados ao próprio povo.$b$bLeve este cristal ressoante. Assim que destruir a sentinela, sugue o habitante Zandalari dela. Não se esqueça de me devolver o cristal quando tiver concluído a tarefa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'ruRU', 'Вековые стражи', 'Уничтожьте 5 питаемых духом стражей и воспользуйтесь резонирующим кристаллом, чтобы вытянуть из них духов зандаларов.', 'В зандаларском обществе распространены жертвоприношения. Порой жертвы даже сами вызываются участвовать в их варварских ритуалах. Это считается у них великой честью!$b$bСлабые желают проявить себя даже ценой собственной жизни – их души заключают в тела стражей, которые охраняют эту территорию. Они отдают себя в услужение своему народу.$b$bВозьми этот резонирующий кристалл. Уничтожь несколько стражей и извлеки духов из их тел, а затем принеси мне кристалл.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'zhCN', '世代戍守', '消灭5名缚灵哨兵，并使用共振水晶捕捉被禁锢在哨兵中的赞达拉巨魔灵魂。', '献祭在赞达拉社会中不足为奇。但在这些野蛮的仪式中却存在一种诡异的现象，那就是志愿者。他们竟把它视作莫大的荣耀！$b$b弱者仍然渴望作出贡献，哪怕那意味着要将自己的生命束缚在那些四处游荡的哨兵身上，从而以这种方式为人民效忠。$b$b拿上这块共振水晶。消灭那些赞达拉哨兵，然后把深藏其中的灵魂汲取出来。完成任务后再把水晶交还给我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32555, 'zhTW', '哨衛體內的百年魂靈', '殺死5個縛靈哨衛，然後使用共鳴水晶捕捉困在體內的贊達拉魂靈。', '在贊達拉的社會中，獻祭是一件很普通的事情。在這種野蠻儀式中有一點比較奇怪的是，有人會自願當活祭品。他們認為這是無上的榮耀!$b$b即使那代表得犧牲性命，魂靈禁錮在你所見的這些移動哨衛體內，病弱的贊達拉仍然樂於奉獻。$b$b帶著這塊共鳴水晶。打倒哨衛之後便用水晶吸取裡頭的贊達拉魂靈。完成任務之後將水晶帶回來給我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'deDE', 'Wissenssammlung', 'Sammelt in der Gegend um die Terrasse des Eroberers 6 Ritualartefakte ein.', 'Überall in diesem Gebiet liegen Artefakte der Zandalari herum. Ich würde lügen, würde ich behaupten, sie wären für mich von keinem Interesse. Ich bin von der Signifikanz dieser Objekte für die Rituale dieser Kultur fasziniert.$b$bJe besser wir unsere Feinde verstehen, desto besser sind wir darauf vorbereitet, ihnen gegenüberzutreten.$b$bWenn ich sie untersuchen soll, muss ich sie von Nahem begutachten. Bringt mir so viele Artefakte, wie Ihr finden könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'esES', 'El saber no ocupa lugar', 'Recupera 6 artefactos de ritual en la Platea del Conquistador.', 'Toda esta zona está llena de artefactos Zandalari. Te mentiría si dijera que no me interesa estudiarlos. Me fascina el significado de estos objetos en sus rituales.$b$bCuanto más entendamos a nuestros enemigos, mejor preparados estaremos para derrotarlos.$b$bSi quiero estudiarlos, tendré que observarlos de cerca. Tráeme todos los artefactos que puedas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'esMX', 'El saber no ocupa lugar', 'Recupera 6 artefactos de ritual en la Platea del Conquistador.', 'Toda esta zona está llena de artefactos Zandalari. Te mentiría si dijera que no me interesa estudiarlos. Me fascina el significado de estos objetos en sus rituales.$b$bCuanto más entendamos a nuestros enemigos, mejor preparados estaremos para derrotarlos.$b$bSi debo estudiarlos, tendré que observarlos de cerca. Tráeme todos los artefactos que puedas. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'frFR', 'Plus vous en savez', 'Récupérez 6 Artéfacts de rituel aux alentours de la Terrasse des Conquérants.', 'Toute cette zone déborde d’artéfacts zandalari. Je mentirais si je disais que les étudier ne m’intéresserait pas… Je trouve fascinante la signification de ces objets dans le cadre de leurs rituels.$b$bPlus nous comprendrons nos ennemis, mieux nous serons préparés pour les vaincre.$b$bSi je dois les étudier, je vais devoir les examiner de près. Rapportez-moi autant d’artéfacts que vous pourrez en trouver.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'itIT', 'Sapere è potere', 'Recupera 6 Manufatti Rituali nella Terrazza del Conquistatore.', 'Quest\'intera area pullula di manufatti degli Zandalari. Mentirei se ti dicessi che analizzarli non stimola la mia curiosità. Sono affascinata dall\'importanza che questi manufatti hanno nei loro rituali.$b$bPiù riusciamo a scoprire dei nostri nemici, più possibilità avremo di sconfiggerli.$b$bMa per poterli studiare ho bisogno di analizzarli da vicino. Portami tutti i manufatti che riesci a trovare. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'koKR', '적을 알면 이긴다', '정복자의 정원 근처에서 의식용 유물 6개를 획득해야 합니다.', '이 지역 전체가 잔달라 부족의 유물로 가득해요. 제가 이것들을 연구하고 싶지 않다면 거짓말이겠죠. 전 그들의 의식에 있어 이 유물들이 갖는 중요성에 매료되었어요.$b$b우리가 적들에 대해 알면 알수록, 그들을 쓰러뜨리는 데에도 도움이 될 겁니다.$b$b연구를 시작하려면 유물을 가까이에서 봐야 해요. 유물을 발견하시면 모두 제게 가져오세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'ptBR', 'Quanto mais você sabe', 'Colete 6 Artefatos Ritualísticos do Terraço do Conquistador.', 'Esta área está atulhada de artefatos Zandalari. Eu estaria mentindo se dissesse que não tenho interesse em estudá-los. O significado desses objetos nos rituais me fascina.$b$bQuanto mais aprendemos sobre nossos inimigos, mais nos aproximamos da vitória.$b$bSe quiser estudá-los, vou precisar dar uma olhada de perto. Traga todos os artefatos que encontrar. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'ruRU', 'Чем больше ты знаешь…', 'Принесите 6 ритуальных артефактов с Террасы Завоевателя.', 'Все округа завалена зандаларскими артефактами. Я бы слукавила, если бы сказала, что они меня совсем не интересуют. Мне хочется понять, какую роль эти предметы играют в зандаларских ритуалах.$b$bЧем лучше понимаешь врага, тем проще с ним справиться.$b$bДля того чтобы изучить артефакты, мне нужно посмотреть на них поближе. Принеси мне все артефакты, что сможешь найти.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'zhCN', '知己知彼', '在征服者平台附近找回6个仪式神器。', '这里到处都是赞达拉神器。要是我跟你说，我没兴趣研究它们，那我一定是在说谎。这些器物对赞达拉仪式的重要性令我着迷。$b$b知己知彼，才能百战百胜。$b$b要想研究它们，我就必须得近距离观察清楚才行。把你能找到的所有神器都带回来交给我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32556, 'zhTW', '知己知彼', '從征戍台附近取回6個儀式文物。', '這裡到處都是贊達拉文物。我非常想研究這些東西，它們對贊達拉祭儀的重要性讓我著迷。$b$b再說，知己知彼，百戰不殆嘛!$b$b我若想認真研究，就得近距離地觀察它們。帶越多的文物回來給我越好。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'deDE', 'Feindeshänden entrissen', 'Stehlt 7 loabeseelte Klingen von der Terrasse des Eroberers.', 'Magie ist eine erschreckende Waffe, wenn sie von einem erfahrenen Nutzer angewandt wird. Eine Klinge jedoch ist selbst in unbedarften Händen eine große Bedrohung.$b$bDie Zandalari sind im Besitz von beeindruckenden Waffen, welche durch die Magie, mit der sie erfüllt sind, umso tödlicher werden. Wir sollten sie um diesen Vorteil erleichtern.$b$bStehlt so viele dieser Waffen, wie Ihr finden könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'esES', 'Se va a armar una buena', 'Roba 7 hojas imbuidas de los loa en la Platea del Conquistador.', 'La magia es un rival temible si quien la posee tiene experiencia con ella. Una hoja, sin embargo, es un arma aterradora hasta en manos del más novato.$b$bLos Zandalari tienen algunas armas impresionantes que llegan a ser todavía más letales por la magia con que las imbuyen. Deberíamos disipar esa ventaja que tienen.$b$bRoba todas las armas que puedas encontrar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'esMX', 'Se va a armar una buena', 'Roba 7 hojas imbuidas de los loa en la Platea del Conquistador.', 'La magia es un rival temible si quien la posee tiene experiencia con ella. Una hoja, sin embargo, es un arma aterradora hasta en manos del más novato.$b$bLos Zandalari tienen algunas armas impresionantes que llegan a ser todavía más letales por la magia con que las imbuyen. Deberíamos disipar esa ventaja que tienen.$b$bRoba todas las armas que puedas encontrar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'frFR', 'Hors des griffes de l’ennemi', 'Volez 7 Lames imprégnées par les Loas sur la Terrasse des Conquérants.', 'La magie est un adversaire effrayant lorsqu’elle est manipulée par un lanceur de sorts expérimenté. Une lame, néanmoins, demeure terrifiante, même entre les mains les plus inexpérimentées.$b$bLes Zandalari disposent d’armes impressionnantes, rendues encore plus mortelles par la magie qui les améliore. Il faudrait les priver de cet avantage.$b$bVolez autant de ces armes que vous le pourrez.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'itIT', 'Lontano da mani nemiche', 'Ruba 7 Lame Infuse dai Loa nella Terrazza del Conquistatore.', 'La magia è un avversario temibile, se maneggiata da un incantatore esperto. Una lama, invece, può essere letale anche nelle mani di un novellino.$b$bGli Zandalari possiedono alcune armi impressionanti, rese ancora più letali dalla magia con cui sono state infuse. Dovremmo privarli di questo vantaggio.$b$bRuba tutte le armi che riesci a trovare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'koKR', '적의 손 밖으로', '정복자의 정원에서 로아가 주입된 검 7개를 훔쳐야 합니다.', '마법은 숙련자가 썼을 때 무시무시한 적이 됩니다. 반면 검은 풋내기의 손에 쥐어져도 무시무시한 적이 되지요.$b$b잔달라 부족은 훌륭한 무기들을 갖고 있는데, 마법으로 이 무기들을 강화하여 더욱 위협적으로 만들었습니다. 놈들이 조금이라도 우위를 점하게 둬선 안 됩니다.$b$b무기를 발견하는 대로 훔쳐오십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'ptBR', 'Longe das mãos inimigas', 'Roube 7 Lâminas Imbuídas em Loa do Terraço do Conquistador.', 'Magia é um oponente formidável nas mãos de um lançador experiente. Uma lâmina, contudo, é aterrorizante mesmo nas mãos mais inexperientes.$b$bOs Zandalari têm armas impressionantes, que, com magia, são ainda mais letais. Devemos tirar esta vantagem deles.$b$bRoube todas as armas que encontrar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'ruRU', 'Обезоружить врага', 'Выкрадите 7 усиленных лоа клинков с Террасы Завоевателя.', 'Магия – это ужасающая сила в руках опытного заклинателя. Клинок же – мощное оружие даже в самых неумелых руках.$b$bЗандалары владеют мощным оружием, которое усилено с помощью магических чар. Нужно лишить их этого преимущества.$b$bУкради у них столько единиц оружия, сколько сможешь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'zhCN', '夺自敌手', '从征服者平台偷取7把灌注神力的利刃。', '魔法只有被老练的施法者驾驭，才会变成可怕的敌人。但利刃则不然——即使是被毫无战斗经验的人挥持，也足以令人胆寒。$b$b赞达拉巨魔掌管着一些颇具杀伤力的武器，灌注其上的魔法又更加令其如虎添翼。我们一定要让他们丧失这种优势。$b$b把你所能找到的武器全都偷回来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32557, 'zhTW', '奪敵之刃', '從征戍台偷走7把羅亞附靈之刃。', '魔法由高手來施展，是可怕的強敵。可是利刃交由莽夫來揮舞，才真正嚇人。$b$b贊達拉擁有一些強大的武器，附魔之後更加致命。我們得斬除他們的這項優勢。$b$b盡可能多偷一些武器回來。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'deDE', 'Familienangelegenheit', 'Entwendet Geisterbinder Tu\'chek und Arkanital Ra\'kul auf der Terrasse des Eroberers die Maske des dunklen Mystikers und die Maske des Geisterrufers.', 'Die Aufseher der Terrasse des Eroberers sind zwei Brüder, Geisterbinder Tu\'chek und Arkanital Ra\'kul. Sie sind mächtige Magiewirker, die man nicht unterschätzen sollte.$b$bIhre Macht scheint den verzierten Masken zu entspringen, die sie tragen; Relikte, die von einer Generation an die nächste weitergegeben wurden. Jeder Besitzer erfüllte sie mit der ihm eigenen Magie, bevor er sie weitergab.$b$bWir müssen uns um die Brüder kümmern und ihre Masken zerstören, bevor sie in die falschen Hände geraten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'esES', 'La familia', 'Recupera la máscara del místico oscuro y la máscara del clamaespíritus del vinculador de espíritus Tu\'chek y del arcanital Ra\'kul en la Platea del Conquistador.', 'La Platea del Conquistador está vigilada por dos hermanos: el vinculador de espíritus Tu\'chek y el arcanital Ra\'kul. Son hechiceros muy poderosos que no hay que subestimar.$b$bParece que su poder viene de las máscaras ornamentadas que llevan. Son reliquias que se han ido pasando de generación en generación, y cada portador la ha imbuido de su propia magia antes de cederla.$b$bHay que ocuparse de ellos y destruir esas máscaras antes de que caigan en las manos equivocadas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'esMX', 'La familia', 'Recupera la máscara del místico oscuro y la máscara del clamaespíritus del vinculador de espíritus Tu\'chek y del arcanital Ra\'kul en la Platea del Conquistador.', 'La Platea del Conquistador está vigilada por dos hermanos: el vinculador de espíritus Tu\'chek y el arcanital Ra\'kul. Son hechiceros muy poderosos que no hay que subestimar.$b$bParece que su poder viene de las máscaras ornamentadas que llevan. Son reliquias que se han ido pasando de generación en generación, y cada portador la ha imbuido de su propia magia antes de cederla.$b$bHay que ocuparse de ellos y destruir esas máscaras antes de que caigan en las manos equivocadas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'frFR', 'Une famille formidable', 'Récupérez le Masque du sombre mystique et le Masque du mande-esprits sur le Lieur d’esprit Tu’chek et sur le Rituarcaniste Ra’kul, sur la Terrasse des Conquérants.', 'La terrasse des Conquérants est surveillée par deux frères : le lieur d’esprit Tu’chek et le rituarcaniste Ra’kul. Ce sont de puissants utilisateurs de la magie et ils ne doivent pas être sous-estimés.$b$bLeur pouvoir semble émaner des masques ornementaux qu’ils portent, des reliques transmises de génération en génération. Chaque propriétaire les imprègne de sa propre magie avant de le transmettre.$b$bNous devons nous débarrasser des frères et détruire leurs masques avant qu’ils ne tombent entre de mauvaises mains.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'itIT', 'Tutto in famiglia', 'Recupera la Maschera del Mistico Oscuro e la Maschera dell\'Evocatore di Spiriti dal Vincolaspiriti Tu\'chek e dall\'Arcanital Ra\'kul nella Terrazza del Conquistatore.\n', 'La Terrazza del Conquistatore è guardata a vista da due fratelli: il Vincolaspiriti Tu\'chek e l\'Arcanital Rak\'ul. Sono potenti esperti di magia che non dovrebbero essere sottovalutati.$b$bIl loro potere sembra provenire dalle maschere che indossano, reliquie tramandate di generazione in generazione e potenziate dalle conoscenze arcane di ogni nuovo possessore.$b$bDobbiamo occuparci dei fratelli e distruggere le loro maschere prima che cadano nelle mani sbagliate.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'koKR', '가문의 가면', '정복자의 정원에 있는 영혼결속자 투첵과 비전주술사 라쿨에게서 암흑 비술사의 가면과 영혼소환사의 가면을 획득해야 합니다.\n', '정복자의 정원은 두 형제가 지키고 있어요. 바로 영혼결속자 투첵과 비전주술사 라쿨이죠. 그들은 강력한 마법을 쓰기 때문에 결코 얕잡아봐선 안 되는 자들이에요.$b$b그들의 힘은 그들이 쓰는 화려한 가면에서 오는 것 같아요. 그 가면은 몇 세대에 걸쳐 내려받은 유물인데, 전 소유자가 후세에 넘기기 전에 본인의 마법으로 강화한다고 해요.$b$b형제들을 처리하고 놈들의 가면이 다른 자의 손에 넘어가기 전에 반드시 파괴해야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'ptBR', 'A família inteira', 'Recupere a Máscara do Místico Sombrio e a Máscara do Chama-espíritos do Amarra-espírito Tu\'chek e do Arcanital Ra\'kul no Terraço do Conquistador.\n', 'O Terraço do Conquistador é vigiado por dois irmãos, o Amarra-espírito Tu\'chek e o Arcanital Ra\'kul. Ambos são poderosos feiticeiros e não devem ser subestimados.$b$bO poder dos dois parece emanar das máscaras ornadas que usam, relíquias passadas de geração em geração que contêm a magia acumulada de cada um dos donos.$b$bPrecisamos dar cabo desses dois e destruir as máscaras antes que elas caiam em mãos erradas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'ruRU', 'Все в семью', 'Отнимите маску темного мистика и маску призывателя духов у душелова Ту\'чека и ритуалиста Рак\'ула на Терассе Завоевателя.', 'За Террасой Завоевателя троллей следят два брата, душелов Ту\'чек и ритуалист Ра\'кул. Это могущественные заклинатели, которых не следует недооценивать.$b$bИсточник их силы, по-видимому, находится в узорных масках, которые они носят. Эти маски передаются по наследству из поколения в поколение. Перед тем, как передать маску новому владельцу, прежний обладатель наделяет ее частицей своей силы.$b$bНужно расправиться с братьями и уничтожить их маски до того, как они попадут в плохие руки.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'zhCN', '不得外传', '从征服者平台的缚灵者图切克和魔术士拉古尔处夺取黑暗秘法面具和唤魂者面具。\n', '缚灵者图切克和魔术士拉古尔这两兄弟看守着征服者平台。他们会使用强大的魔法，绝对不容小觑。$b$b他们的力量似乎是来源于他们的面具，那是代代相传的圣物。每位拥有者在将面具传世之前，都会将自己的法力灌注在面具上。$b$b我们必须解决掉这两兄弟，然后摧毁面具，以免让其落入恶人手中。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32558, 'zhTW', '家傳古物', '前往征戍台從縛靈者圖柴克與秘宗拉庫爾身上取回黑暗秘儀面具與喚靈者面具。\n', '征戍台由一對兄弟看守;縛靈者圖柴克與秘宗拉庫爾。他們是強大的施法者，千萬別對他們掉以輕心。$b$b他們的魔力似乎源自於臉上所穿戴的華麗面具。這些面具是代代相傳的古物，而且每一位擁有者在傳給下一代之前，還會再將自身的魔法灌注在上頭。$b$b我們得解決掉這對兄弟，並且在面具落入他人手中前摧毀面具。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'deDE', 'Gigantische Zerstörung', 'Zerstört Wächter Tak\'u auf der Terrasse des Eroberers.', 'Die Zandalari verehren die Loatiergeister, die sie oft zu ihrer Unterstützung anrufen. Möglicherweise versuchen sie, diese Geister an einen mächtigen Golem zu binden. Das Endresultat wäre katastrophal.$b$bIch habe einen Späher vorgeschickt, um mehr Informationen zu sammeln. Jedoch glaube ich, dass bei dieser Aufgabe Eure Expertise vonnöten ist.$b$bDieses Ritual muss unterbrochen werden, um jeden Preis!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'esES', 'Lucha de gigantes', 'Destruye al guardián Tak\'u en la Platea del Conquistador.', 'Los Zandalari adoran a los loa; son espíritus animales que invocan a menudo en busca de ayuda. Es posible que estén intentando vincular estos espíritus a un gólem enorme. El resultado puede ser catastrófico.$b$bYa he enviado a un explorador para que recopile más información, pero creo que esta misión requiere experiencia como la que tú tienes.$b$b¡Hay que detener el ritual a toda costa!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'esMX', 'Lucha de gigantes', 'Destruye al guardián Tak\'u en la Platea del Conquistador.', 'Los Zandalari adoran a los loa; son espíritus animales que invocan a menudo en busca de ayuda. Es posible que estén intentando vincular estos espíritus a un gólem enorme. El resultado puede ser catastrófico.$b$bYa he enviado a un explorador para que recopile más información, pero creo que esta misión requiere experiencia como la que tú tienes.$b$b¡Hay que detener el ritual a toda costa!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'frFR', 'Même les géants tombent', 'Détruisez le Gardien Tak’u sur la Terrasse des Conquérants.', 'Les Zandalari vénèrent les Loas, des esprits animaux qu’ils appellent fréquemment à leur aide. Il est possible qu’ils soient en train d’essayer de lier ces esprits à un gigantesque golem. Les conséquences pourraient en être désastreuses…$b$bJ’ai envoyé un éclaireur afin de récolter des informations, mais je pense que cette tache pourrait bénéficier de votre expertise.$b$bArrêtez ce rituel à tout prix !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'itIT', 'Anche i giganti cadono', 'Distruggi il Guardiano Tak\'u nella Terrazza del Conquistatore.', 'Gli Zandalari venerano i Loa, gli spiriti degli animali che spesso invocano in loro aiuto. Sembra che stiano cercando di vincolare questi spiriti in un enorme golem. Le conseguenze sarebbero a dir poco disastrose.$b$bHo inviato un esploratore per raccogliere informazioni, ma credo che abbia bisogno della tua esperienza.$b$bFerma quel rituale a tutti i costi!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'koKR', '거인도 쓰러진다', '정복자의 정원에 있는 수호자 탁쿠를 쓰러뜨려야 합니다.', '잔달라 부족은 로아라는 동물의 영혼들을 섬깁니다. 그들에게 종종 도움을 요청하곤 하죠. 그런데 이들이 이 영혼들을 거대한 골렘에 결속시키려는 것 같습니다. 이대로 둔다면 끔찍한 결과를 불러올 것입니다.$b$b정보를 캐내기 위해 정찰병을 보냈지만, 아마 당신의 전문성이 필요할 것 같군요.$b$b무슨 수를 써서라도 이 의식을 막아주십시오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'ptBR', 'Maior o gigante, maior a queda', 'Destrua o Guardião Tak\'u no Terraço do Conquistador.', 'Os Zandalari cultuam os Loa, espíritos animais que eles costumam invocar para auxiliá-los. Talvez estejam tentando vincular esses espíritos a um enorme golem, o que seria desastroso.$b$bEnviei um batedor para coletar mais informações, mas acho que sua ajuda será necessária.$b$bInterrompa o ritual a qualquer custo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'ruRU', 'Даже великанам приходит конец', 'Уничтожьте стража Та\'ку на Террасе Завоевателя.', 'Зандалары поклоняются лоа, духам животных, которых они часто призывают на помощь. Они даже могут попытаться привязать призванных духов к одному гигантскому голему. Последствия этого будут просто ужасны.$b$bЯ выслал вперед разведчика для сбора дополнительной информации, но нам потребуется и твоя помощь.$b$bНужно остановить зандаларский ритуал любой ценой.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'zhCN', '巨人的陨殁', '在征服者平台消灭守护者塔克乌。', '赞达拉巨魔崇拜神灵，通常会召唤这些动物之魂与自己并肩作战。他们会尝试将这些魂灵束缚在一个巨像身上。这会招致毁灭性的后果。$b$b我已经派斥候前去搜集信息了，但这项任务也许需要由你发挥专长。$b$b不惜一切代价阻止那个仪式！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32559, 'zhTW', '打倒魔像', '摧毀征戍台的守護者大庫。', '羅亞是贊達拉崇拜的動物之靈，祂們被贊達拉召喚並祈求給予協助。現在贊達拉正試圖將這些魂靈綁縛到巨大的魔像身上，成功的話將釀成極大的災難。$b$b我已派遣一名密探去收集更多資訊，但是若要完成這項任務，得借重你的才幹才行。$b$b不惜任何代價都要阻止那場祭儀!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'deDE', 'Wahrt das Geheimnis', 'Tötet 8 Trolle der Zandalari jeglicher Art auf der Terrasse des Eroberers.', 'Die Zandalari hier stellen eine Bedrohung für unseren Feldzug dar, $C.$b$bIhre Art der Magie ist eine Abscheulichkeit. Sie vermengen die Mächte des Arkanen mit dem Dunkel der Schatten, beinahe ungeachtet der Konsequenzen.$b$bWir können dies nicht gestatten - weder, dass sie ihre volle Stärke erlangen, noch dass die Horde die Geheimnisse ihrer dunklen Magie erlent.$b$bIhre Ränge müssen ausgedünnt werden. Ich überlasse diese Aufgabe Euch.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'esES', 'No se lo digas a nadie', 'Mata a 8 trols Zandalari cualesquiera en la Platea del Conquistador.', 'Los Zandalari que hay aquí suponen una amenaza para nuestra campaña, $c.$b$bEl uso que hacen de la magia es abominable. Mezclan los poderes arcanos con la oscuridad de las sombras sin pensar en las consecuencias.$b$bNo podemos permitirnos que alcancen su máximo poder aquí o que la Horda aprenda esa retorcida magia.$b$bHay que diezmarlos. Lo dejo en tus manos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'esMX', 'No se lo digas a nadie', 'Mata a 8 trols Zandalari cualesquiera en la Platea del Conquistador.', 'Los Zandalari que hay aquí suponen una amenaza para nuestra campaña, $c.$b$bEl uso que hacen de la magia es abominable. Mezclan los poderes arcanos con la oscuridad de las sombras sin pensar en las consecuencias.$b$bNo podemos permitirnos que alcancen su máximo poder aquí o que la Horda aprenda esa retorcida magia.$b$bHay que diezmarlos. Lo dejo en tus manos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'frFR', 'Garder le secret', 'Tuez 8 Trolls zandalari sur la Terrasse des Conquérants.', 'Les Zandalari représentent une menace pour notre campagne, $c.$b$bLeur utilisation de la magie est une abomination. Ils mêlent le pouvoir des arcanes aux ténèbres de l’ombre, sans se soucier des conséquences.$b$bNous ne pouvons pas les laisser découvrir leur puissance réelle, ni laisser la Horde en apprendre plus sur leur magie corrompue.$b$bIl est impératif de réduire leur nombre. Je vous confie cette tâche.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'itIT', 'Mantenere il segreto', 'Uccidi 8 Troll Zandalari nella Terrazza del Conquistatore.', 'Gli Zandalari sono una minaccia per la nostra campagna, $C.$b$bIl loro uso della magia è un abominio; mischiano il potere arcano con l\'oscurità dell\'ombra senza preoccuparsi delle conseguenze.$b$bNon possiamo permettere che riacquistino la loro piena forza, o che l\'Orda impari a sfruttare la loro distorta magia.$b$bI loro ranghi devono essere sfoltiti. Lascerò questo compito a te.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'koKR', '아무도 모르게', '정복자의 정원에 있는 잔달라 트롤 8명을 처치해야 합니다.', '이곳의 잔달라 부족은 우리 임무에 큰 위협이 됩니다, $c님.$b$b놈들은 마법으로 끔찍한 일을 꾸미고 있어요. 결과 따윈 개의치 않고 비전의 힘을 불길한 어둠과 합치고 있습니다.$b$b잔달라 부족이 이 의식으로 자신들의 완전한 힘을 찾거나 호드가 이런 뒤틀린 마법을 배우게 할 순 없어요.$b$b놈들의 머릿수를 줄여야 합니다. 이 임무는 당신께 맡기지요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'ptBR', 'Só entre nós', 'Mate 8 trolls Zandalari de qualquer tipo no Terraço do Conquistador.', 'Esses Zandalari são uma ameaça para nossa campanha, $c.$b$bO uso que eles fazem de magia é abominável. Misturam os poderes do arcano e a escuridão das sombras sem se preocupar com as consequências.$b$bNão podemos permitir que eles se fortaleçam, nem que a Horda aprenda essa magia depravada.$b$bOs números deles precisam ser reduzidos. Essa será sua tarefa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'ruRU', 'Хранить в тайне', 'Убейте 8 зандаларских троллей на Террасе Завоевателя.', 'Местные зандалары представляют угрозу для нашей кампании, |3-6($c).$b$bОни используют магию самым отвратительным образом – смешивают силу тайной магии с силой тьмы, ни капли не задумываясь о последствиях.$b$bНельзя, чтобы они и дальше оттачивали здесь свои гнусные навыки. Что еше хуже, об этой ужасной магии может прознать Орда.$b$bМы должны уменьшить их численность. Думаю, ты знаешь, что делать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'zhCN', '保守秘密', '在征服者平台消灭任意8名赞达拉巨魔。', '这里的赞达拉巨魔对我们的行动构成了威胁，$c。$b$b他们对魔法的使用更是让人难以容忍。巨魔们将奥术之力与无边幽暗混为一体，丝毫不考虑后果。$b$b我们决不能让他们在这里变得羽翼渐丰，或是让部落领悟到他们扭曲的魔法。$b$b必须削减他们的数量。我把这项任务交给你了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32560, 'zhTW', '務必保密', '殺死征戍台的8名贊達拉食人妖。', '贊達拉是這場戰役的威脅，$c。$b$b他們將暗影能量融入秘法系的魔法之中，絲毫不顧會造成什麼結果，這種使用魔法的手段真是討厭。$b$b我們絕不能讓他們在這場戰爭中施展全力，或讓部落學習到他們扭曲的魔法。$b$b我們得削減他們的數量，這個任務就交給你了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32561, 'deDE', 'Konkurrierende Magie', 'Tötet Arkanwirker Jor\'guva.', 'Einer meiner Seher hat einen der Anführer der Zandalari in der Nähe des zentralen Turmes ausgemacht. Er muss beseitigt werden.$B$BWärt Ihr gewillt, das für mich zu tun, $C?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32561, 'esES', 'Competencia mágica', 'Mata al tejearcos Jor\'guva.', 'Uno de mis arúspices ha identificado a un líder Zandalari cerca de la torre central. Hay que eliminarlo.$B$B¿Estás $gdispuesto:dispuesta; a hacerme este favor, $c?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32561, 'esMX', 'Competencia mágica', 'Mata al tejearcos Jor\'guva.', 'Uno de mis arúspices ha identificado a un líder Zandalari cerca de la torre central. Hay que eliminarlo.$B$B¿Estás $gdispuesto:dispuesta; a hacerme este favor, $c?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32561, 'frFR', 'Compétition de magie', 'Tuez le Tisse-arc Jor’guva.', 'Un de mes clairvoyants a repéré un chef zandalari au pied de la tour centrale. Il doit être supprimé.$B$BVous voulez bien vous charger de ça pour moi, $c ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32561, 'ruRU', 'Магия против магии', 'Убейте чародея Джор\'гуву.', 'Один из моих ясновидцев обнаружил командира зандаларов рядом с центральной башней. Его надо уничтожить.$B$BТы сможешь это сделать, |3-6($c)?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32562, 'esES', 'Bajo amenaza', 'Mata a Horgak el Esclavista.', 'Uno de mis exploradores ha vuelto con información acerca de un líder mogu que está debajo de la torre central. Hay que eliminarlo.$B$B¿Estás $gdispuesto:dispuesta; a hacerme este favor, $c?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32562, 'esMX', 'Bajo amenaza', 'Mata a Horgak el Esclavista.', 'Uno de mis exploradores ha vuelto con información acerca de un líder mogu que está debajo de la torre central. Hay que eliminarlo.$B$B¿Estás $gdispuesto:dispuesta; a hacerme este favor, $c?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32562, 'frFR', 'Une menace impressionnante', 'Tuez Horgak l’Esclavagiste.', 'Un de mes éclaireurs m’a rapporté qu’un chef mogu se trouvait au pied de la tour centrale. Il doit être supprimé.$B$BVous voulez bien vous charger de ça pour moi, $c ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32562, 'ruRU', 'Серьезная угроза', 'Убейте Хограка Поработителя.', 'Один из моих разведчиков вернулся и доложил о командире могу у подножия центральной башни. Его надо уничтожить.$B$BТы сможешь это сделать, |3-6($c)?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32564, 'deDE', 'Der Aufstieg der Zandalari', 'Tötet Arkanwirker Jor\'guva.', 'Wir haben einen Anführer der Zandalari auf der mittleren Treppe entdeckt. Wir müssen ihn vernichten, bevor wir ins Lager zurückkehren.$B$BKönnt Ihr das übernehmen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32564, 'esES', 'Los Zandalari renacen', 'Mata al tejearcos Jor\'guva.', 'Hemos localizado a un líder Zandalari arriba, en las escaleras centrales. Necesitamos eliminarlo antes de que vuelva al campamento.$B$B¿Puedes ocuparte de ello?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32564, 'esMX', 'Los Zandalari renacen', 'Mata al tejearcos Jor\'guva.', 'Hemos localizado a un líder Zandalari arriba, en las escaleras centrales. Necesitamos eliminarlo antes de que vuelva al campamento.$B$B¿Puedes ocuparte de ello?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32564, 'frFR', 'Un Zandalari au sommet', 'Tuez le Tisse-arc Jor’guva.', 'Nous avons repéré un chef zandalari en haut des escaliers principaux. Nous devons l’éliminer avant de retourner au campement.$B$BVous pouvez vous en occuper ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32564, 'itIT', 'Zandalari sulla vetta', 'Uccidi il Mistico Arcano Jor\'guva.', 'Abbiamo scoperto la presenza di un capo degli Zandalari in cima alla scalinata centrale. Dobbiamo eliminarlo prima di tornare all\'accampamento.$B$BPuoi occupartene tu?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32564, 'ptBR', 'Zandalari em ascensão', 'Mate o Tece-arcos Jor\'guva.', 'Localizamos um líder Zandalari no topo da escadaria central. Precisamos eliminá-lo antes de voltar ao acampamento.$b$bVocê pode cuidar dele?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32564, 'ruRU', 'Зандалары на подъеме', 'Убейте чародея Джор\'гуву.', 'В верхней части центральной лестницы мы заметили одного из вождей зандаларов. Нужно избавиться от него до того, как мы вернемся в лагерь.$b$bМожешь позаботиться об этом?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32565, 'esMX', 'Fuerzas invasoras', 'Mata a Nurkala, líder de guerra Zandalari.', 'Hemos localizado a un líder Zandalari en la playa. Necesitamos eliminarlo antes de que vuelva al campamento.$B$B¿Quieres ocuparte de ello?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32565, 'itIT', 'Forza opprimente', 'Uccidi Nurkala, il Signore della Guerra Zandalari.', 'Abbiamo trovato un gruppo armato degli Zandalari lungo la spiaggia. Dobbiamo eliminarlo prima di tornare all\'accampamento.$B$BTi senti all\'altezza del compito?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32565, 'ptBR', 'Força de invasão', 'Mate Nurkala, o Líder Guerreiro dos Zandalari.', 'Localizamos um grupo de guerreiros Zandalari na praia. Precisamos eliminá-lo antes de voltar ao acampamento.$b$bVocê estaria $gdisposto:disposta; a cuidar dele?', '', '', '', '', '', '', 0);
