INSERT INTO `quest_template_locale` VALUES (32575, 'ruRU', 'Зандаларский колосс', 'Уничтожьте зандаларского колосса.', 'Тролли выпустили одну из своих громадин на южной дороге и блокировали наш проход по острову. Проследи, чтобы дорога была очищена, |3-6($c).', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32575, 'zhCN', '赞达拉巨神像', '消灭赞达拉巨神像。', '巨魔已将一个巨大的构造体部署在南边的路上，阻碍了我们岛屿四周的通行。去帮我们除掉障碍，$c。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32575, 'zhTW', '贊達拉巨像', '摧毀贊達拉巨像。', '食人妖在南方道路上部署了一隻巨大的傀儡，擋住了我們的道路。去將它料理乾淨，$c。', '', '', '', '', '', '', 0);
