INSERT INTO `quest_template_locale` VALUES (32580, 'ruRU', 'Вторжение', 'Убейте Нуркала, военного вождя зандаларов.', 'Нам доложили, что прямо за городской стеной, к северу от крепости, на пляже замечен зандаларский военный отряд. Противника нужно найти и уничтожить.$B$BВызовешься добровольцем, |3-6($c)?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32580, 'zhCN', '渗透力量', '消灭赞达拉战争首领奴卡拉。', '我们收到消息，海滩上出现了一支赞达拉作战小队，就在我们据点的北边。我需要有人前去追击并消灭他们。$B$B你愿意帮我们完成这项任务吗，$c？', '', '', '', '', '', '', 0);
