INSERT INTO `quest_template_locale` VALUES (32581, 'koKR', '눈앞에 닥친 위기', '노예사냥꾼 호르각을 처치해야 합니다.', '점술가들이 중앙탑에서 모구 지휘관을 발견했어요. 그자가 우리 위치에 공세를 가하기 전에 처치해야만 해요.$B$B당신이 이 임무를 맡아주시겠어요?', '', '', '', '', '', '', 0);
