INSERT INTO `quest_template_locale` VALUES (32581, 'ruRU', 'Серьезная угроза', 'Убейте Хограка Поработителя.', 'Ясновидцы обнаружили командира могу под центральной башней. Нужно его уничтожить до того, как он сможет организовать атаку на наши позиции.$B$BТы возьмешься провести эту операцию?', '', '', '', '', '', '', 0);
