INSERT INTO `quest_template_locale` VALUES (32583, 'itIT', 'Zandalari sulla vetta', 'Uccidi il Mistico Arcano Jor\'guva.', 'Abbiamo scoperto la presenza di un capo degli Zandalari in cima alla scalinata centrale. Dobbiamo eliminarlo prima di tornare all\'accampamento.$B$BPuoi occupartene tu?', '', '', '', '', '', 'Torna dal Capitano degli Esploratori Daelin a nord della Landa Putrida.', 0);
INSERT INTO `quest_template_locale` VALUES (32583, 'ptBR', 'Zandalari em ascensão', 'Mate o Tece-arcos Jor\'guva.', 'Localizamos um líder Zandalari no topo da escadaria central. Precisamos eliminá-lo antes de voltar ao acampamento.$b$bVocê pode cuidar dele?', '', '', '', '', '', 'Fale novamente com o Capitão dos Batedores Daelin a norte da Charneca Turva.', 0);
INSERT INTO `quest_template_locale` VALUES (32584, 'deDE', 'Feindlicher Übergriff', 'Tötet Nurkala, den Kriegsanführer der Zandalari.', 'Wir haben unten am Strand einen Kriegstrupp der Zandalari entdeckt, knapp hinter diesen Mauern. Wir müssen ihn vernichten, bevor wir ins Lager zurückkehren.$B$BSeid Ihr dieser Aufgabe gewachsen? ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32584, 'esES', 'Fuerzas invasoras', 'Mata a Nurkala, líder de guerra Zandalari.', 'Hemos localizado a un destacamento de guerra Zandalari en la playa que hay al otro lado de los muros. Necesitamos eliminarlos antes de que vuelvan al campamento.$B$B¿Quieres ocuparte de ello?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32584, 'esMX', 'Fuerzas invasoras', 'Mata a Nurkala, líder de guerra Zandalari.', 'Hemos localizado a un destacamento de guerra Zandalari en la playa que hay al otro lado de los muros. Necesitamos eliminarlos antes de que vuelvan al campamento.$B$B¿Quieres ocuparte de ello?', '', '', '', '', '', 'Vuelve con: Capitán de exploradores Daelin. Zona: Norte de Malandanza.', 0);
INSERT INTO `quest_template_locale` VALUES (32584, 'frFR', 'Une troupe envahissante', 'Tuez Nurkala, le chef de guerre zandalari.', 'Nous avons repéré une troupe de guerriers zandalari sur la plage juste à l’extérieur de nos murs. Nous devons les éliminer tous avant de revenir au campement.$b$bVous vous sentez à la hauteur ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32584, 'itIT', 'Forza opprimente', 'Uccidi Nurkala, il Signore della Guerra Zandalari.', 'Abbiamo scoperto un gruppo armato di Zandalari sulla spiaggia poco fuori dalle mura. Dobbiamo eliminarli prima di tornare all\'accampamento.$B$BSei all\'altezza del compito?', '', '', '', '', '', 'Torna dal Capitano degli Esploratori Daelin a nord della Landa Putrida.', 0);
INSERT INTO `quest_template_locale` VALUES (32584, 'ptBR', 'Força de invasão', 'Mate Nurkala, o Líder Guerreiro dos Zandalari.', 'Localizamos um grupo de guerreiros Zandalari na praia, do outro lado dessa muralha. Precisamos eliminá-los antes de voltar ao acampamento.$b$bVocê pode fazer isso?', '', '', '', '', '', 'Fale novamente com o Capitão dos Batedores Daelin a norte da Charneca Turva.', 0);
INSERT INTO `quest_template_locale` VALUES (32584, 'ruRU', 'Вторжение', 'Убейте Нуркала, военного вождя зандаларов.', 'На побережье внизу сразу за стенами мы заметили военный отряд зандаларов. Нужно избавиться от них до того, как мы вернемся в лагерь.$b$bВозьмешься за это?', '', '', '', '', '', 'Вернитесь к капитану разведчиков Дэлину. Его можно найти к северу от Мрачных топей.', 0);
INSERT INTO `quest_template_locale` VALUES (32585, 'deDE', 'Feindlicher Übergriff', 'Tötet Nurkala, den Kriegsanführer der Zandalari.', 'Wir haben einen Kriegstrupp der Zandalari am Strand entdeckt. Wir müssen ihn vernichten, bevor wir ins Lager zurückkehren.$B$BWärt Ihr bereit, das für mich zu erledigen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32585, 'esES', 'Fuerzas invasoras', 'Mata a Nurkala, líder de guerra Zandalari.', 'Hemos localizado a un líder Zandalari en la playa. Necesitamos eliminarlo antes de que vuelva al campamento.$B$B¿Quieres ocuparte de ello?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32585, 'esMX', 'Fuerzas invasoras', 'Mata a Nurkala, líder de guerra Zandalari.', 'Hemos localizado a un líder Zandalari en la playa. Necesitamos eliminarlo antes de que vuelva al campamento.$B$B¿Quieres ocuparte de ello?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32585, 'frFR', 'Une troupe envahissante', 'Tuez Nurkala, le chef de guerre zandalari.', 'Nous avons repéré une troupe de guerriers zandalari sur la plage. Nous devons les éliminer tous avant de revenir au campement.$b$bAccepteriez-vous de vous en charger ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32585, 'itIT', 'Forza opprimente', 'Uccidi Nurkala, il Signore della Guerra Zandalari.', 'Abbiamo trovato un gruppo armato degli Zandalari lungo la spiaggia. Dobbiamo eliminarlo prima di tornare all\'accampamento.$B$BTi senti all\'altezza del compito?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32585, 'ptBR', 'Força de invasão', 'Mate Nurkala, o Líder Guerreiro dos Zandalari.', 'Localizamos um grupo de guerreiros Zandalari na praia. Precisamos eliminá-lo antes de voltar ao acampamento.$b$bVocê estaria $gdisposto:disposta; a cuidar dele?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32585, 'ruRU', 'Вторжение', 'Убейте Нуркала, военного вождя зандаларов.', 'Там, на пляже, мы заметили зандаларский отряд. Нужно избавиться от него до того, как мы вернемся в лагерь.$b$bВозьмешься за это?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'deDE', 'Der Maximalkondensator', 'Zerstört 3 Blitzbohrer in den Blitzaderminen und tötet Metallfürst Mono-Han.', 'Die Minen nördlich von hier sind ein empfindlicher Teil der Versorgungslinie der Mogu. Sie benutzen riesige, mit Blitzen verzauberte Bohrer, um das Erz zu fördern.\n\nWenn Ihr die Bohrer manipulieren könntet, wären sie so gut wie zerstört und die Waffenfabrikation der Mogu würde wesentlich verlangsamt werden.\n\nOh, noch etwas – tötet auch denjenigen, der dort unten das Sagen hat, alles klar?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'esES', 'Condensando energías', 'Destruye las 3 taladradoras de relámpagos en las minas de hierro fantasma y mata al señor del metal Mono-Han.', 'Las minas que hay al norte de aquí son fundamentales para los suministros de los mogu. Utilizan unos taladros gigantes imbuidos de electricidad para extraer minerales.\n\nSi puedes detenerlos, los destruirás, y eso ralentizará mucho la fabricación de sus armas.\n\nAh, y ya que estás allí, acaba con quien esté dirigiendo la operación. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'esMX', 'Condensando energías', 'Destruye las 3 taladradoras de relámpagos en las minas de hierro fantasma y mata al señor del metal Mono-Han.', 'Las minas que hay al norte de aquí son fundamentales para los suministros de los mogu. Utilizan unos taladros gigantes imbuidos de electricidad para extraer minerales.\n\nSi puedes detenerlos, los destruirás, y eso ralentizará mucho la fabricación de sus armas.\n\nAh, y ya que estás allí, acaba con quien esté dirigiendo la operación. ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'frFR', 'Condensateur au max', 'Détruisez 3 Foreuses de foudre à la Veine de Foudremine et tuez le Seigneur du métal Mono Han.', 'Les mines au nord d’ici sont essentielles pour les voies de ravitaillement des mogu. Ils utilisent des foreuses de foudre géantes et enchantées pour extraire le minerai.\n\nSi vous pouviez interrompre les foreuses, cela les détruirait et ralentirait fortement leur capacité à construire des armes.\n\nOh, et éliminez la personne responsable de cette opération, d\'accord ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'itIT', 'Il condensatore sovraccarico', 'Distruggi le 3 Trivelle Elettrificate nelle Miniere della Folgore e uccidi il Signore del Metallo Mono-Han.', 'Le miniere a nord sono di fondamentale importanza per le linee di rifornimento dei Mogu. Stanno utilizzando delle grosse Trivelle Elettrificate incantate per estrarre il minerale.\n\nSe riuscissi a manomettere le trivelle e a distruggerle, colpiresti duramente la loro capacità di forgiare armi.\n\nOh, e già che ci sei, ti dispiacerebbe uccidere chiunque sia a capo dell\'operazione?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'koKR', '천공기 과부하', '유령무쇠 광산에 있는 번개 천공기 3개를 파괴하고 무쇠군주 모노한을 처치해야 합니다.', '이곳 북쪽에 있는 광산은 모구 보급망의 요충지입니다. 그들은 번개의 힘이 주입된 천공기를 이용해 광물을 캐내고 있습니다.\n\n당신이 천공기 작동을 방해해 부서뜨리면, 모구의 무기 생산 능력이 현저히 감소할 것입니다.\n\n아, 그리고 거기 책임자도 함께 처치해 주시겠습니까?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'ptBR', 'Capacitor máximo', 'Destrua as 3 Brocas de Raios nas Minas de Ferro Fantasma e mate o Senhor do Metal Mono-Han.', 'As minas a norte daqui são fundamentais para suprir os mogus. Eles estão usando imensas brocas elétricas encantadas para extrair minérios.\n\nSe você conseguir danificar as brocas, a produção de armas deles será prejudicada.\n\nAh, aproveite pra matar quem estiver no comando, pode ser? ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'ruRU', 'Максимальная нагрузка', 'Уничтожьте 3 молниевых бура в Шахте молний и убейте повелителя металла Моно-Ханя.', 'Шахта, которая располагается к северу отсюда, имеет огромное значение для могу. Для добычи руды они пользуются гигантскими молниевыми бурами.\n\nЕсли тебе удастся разрушить эти буры, это будет огромным ударом для могу и сильно замедлит их производство оружия.\n\nДа, и заодно убей того, кто руководит операцией, хорошо?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'zhCN', '电容上限', '在闪电矿脉中摧毁3个闪电钻，并击败金铁领主莫诺汗。', '位于此地北方的矿脉对魔古的补给线至关重要。他们正在用经由闪电灌能的巨型钻机开采矿石。\n\n要是你能破坏这些钻机，它们就会瘫痪，这将大大折损他们制造武器的能力。\n\n噢，还有，不管那里是谁在负责，都别让他活命，没问题吧？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32586, 'zhTW', '電容上限', '摧毀鬼鐵礦坑裡的3台閃電鑽孔機，並殺死冶煉統領魔諾汗。', '北方的礦坑是魔古重要的補給線。他們使用巨大的閃電附魔鑽孔機來採礦。\n\n假如你能破壞鑽孔機，中斷採礦行動，便能拖延魔古製造武器的速度。\n\n喔，對了。順便殺掉採礦的負責人，可以嗎?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'deDE', 'Schmiede voraus!', 'Tötet den Meister der Schmiede und sammelt ein Kodexfragment von Fleischschmied Hoku auf der Insel des Donners ein.', 'Die Schmiede liegt vor Euch und mit ihr das Herz der Moguschmiedekunst.\n\nEin wenig weiter befindet sich der Mogutorwächter, der den Schlüssel zum nächsten Teil der Stadt in seinen Händen hält.\n\nZeit für den Vorstoß!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'esES', '¡Hacia la forja!', 'Mata al maestro de la forja y recoge un fragmento de códice del modelador de carne Hoku en la Isla del Trueno.', 'La forja se halla ante ti, y en ella la esencia de la forja de armas mogu.\n\nA esto hay que añadir al guardián mogu, que posee la llave de la puerta que conduce a la siguiente parte de la ciudad.\n\n¡Es hora de partir!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'esMX', '¡Hacia la forja!', 'Mata al maestro de la forja y recoge un fragmento de códice del modelador de carne Hoku en la Isla del Trueno.', 'La forja se halla ante ti, y en ella la esencia de la forja de armas mogu.\n\nA esto hay que añadir al guardián mogu, que posee la llave de la puerta que conduce a la siguiente parte de la ciudad.\n\n¡Es hora de partir!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'frFR', 'Forge droit devant !', 'Tuez le Maître de la forge et récupérez un Fragment de codex sur le Façonneur de chair Hoku, sur l’Île du Tonnerre.', 'La forge se trouve droit devant vous, et en son sein se trouve le cœur de la capacité de production d’armement des mogu.\n\nEt par-delà, se trouve le gardien de la porte mogu, qui détient la clé permettant l’accès à la section suivante de la cité.\n\nIl est temps d’avancer !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'itIT', 'Verso la Forgia!', 'Uccidi il Maestro della Forgia e recupera un Frammento di Codice dei Mogu da Hoku il Carnefice sull\'Isola del Tuono.', 'La forgia giace davanti a te, e con essa la fonte della maestria dei Mogu nella forgiatura delle armi.\n\nPoco dietro si trova il Guardiano delle Porte Mogu, colui che detiene la chiave per aprire i cancelli verso la zona più interna della città.\n\nÈ ora di muoversi!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'koKR', '제련소로!', '천둥의 섬에 있는 제련소 대장을 처치하고 살덩이창조자 호쿠에게서 모구 전서 조각을 수집해야 합니다.', '제련소가 눈앞에 있습니다. 그리고 제련소 안에는 모구의 무기제련 능력의 핵심이 들어 있습니다.\n\n또 제련소 안에는 그것 말고도 도시의 다음 지역으로 통하는 문의 열쇠를 가진 모구 문지기가 있습니다.\n\n이제 움직일 때입니다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'ptBR', 'Forja adiante!', 'Mate o Mestre da Forja e pegue um Fragmento de Códice do Moldacarne Hoku na Ilha do Trovão.', 'A forja está adiante e, dentro dela, está o coração das habilidades de armaria dos mogus.\n\nAlém dela, está o guarda-pórtico mogu, que guarda a chave para destrancar o portão para a próxima parte da cidade.\n\nÉ hora de prosseguir!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'ruRU', 'Впереди кузня!', 'Убейте главного кузнеца и получите фрагмент кодекса от стража врат могу на острове Грома.', 'Перед тобой – кузня, центр производства оружия могу.\n\nТам же находится страж ворот, который хранит ключи, открывающие врата от следующей части города.\n\nПора двигаться вперед!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'zhCN', '近在眼前！', '击败锻造大师，并从雷神岛的塑肌者霍古身上取得一块法典碎片。', '熔炉就在你面前了，还有魔古族锻造武器的核心机密。\n\n在它的后面，就是魔古族的守门者，他掌握着开启下一个城区的钥匙。\n\n该我们行动了！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32587, 'zhTW', '朝熔爐前進!', '在雷王島上殺死熔爐統領，並從血肉工匠郝古身上取得聖典殘片。', '你眼前有一座熔爐，它是魔古鍛造武器的核心。\n\n更遠處的魔古守門人手中握有打開大門的鑰匙，從這扇門可以進入城市的另一區。\n\n該是時候出發了!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'deDE', 'Im Geheimdienst Ihrer Magietät', 'Rettet die drei gefangenen Agenten der Kirin Tor aus der Blitzadermine und tötet Metallfürst Mono-Han.', 'Auf Befehl von Jaina selbst versuchten einige unserer Agenten, sich hinter die Linien der Mogu zu schleichen – um herauszufinden, was in der Mine vor sich geht.\n\nSie wurden gefangen und werden wer weiß wie schrecklichen Foltern ausgesetzt.\n\nIhr müsst sie befreien und denjenigen töten, der sie gefangen hält. Wir lassen niemanden zurück!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'esES', 'Rescata y deja morir', 'Rescata a los tres agentes del Kirin Tor secuestrados en las minas de hierro fantasma y mata al señor del metal Mono-Han.', 'Bajo las órdenes de la propia Jaina, algunos agentes nuestros intentaron penetrar las líneas enemigas mogu e infiltrarse en las Minas para ver qué ocurre ahí dentro.\n\nLos capturaron y los están sometiendo a vete-a-saber qué clase de horribles torturas.\n\nDebes liberarlos y matar a sus captores. ¡Nosotros nunca dejamos atrás a nadie!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'esMX', 'Rescata y deja morir', 'Rescata a los tres agentes del Kirin Tor secuestrados en las minas de hierro fantasma y mata al señor del metal Mono-Han.', 'Bajo las órdenes de la propia Jaina, algunos agentes nuestros intentaron penetrar las líneas enemigas mogu e infiltrarse en las Minas para ver qué ocurre ahí dentro.\n\nLos capturaron y los están sometiendo a vete-a-saber qué clase de horribles torturas.\n\nDebes liberarlos y matar a sus captores. ¡Nosotros nunca dejamos atrás a nadie!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'frFR', 'Au service secret de sa magie-sté', 'Sauvez les 3 Agents du Kirin Tor capturés à la Veine de Foudremine, et tuez le Seigneur du métal Mono Han.', 'Suivant les ordres de Jaina en personne, plusieurs de nos agents ont essayé de se glisser derrière les lignes mogu afin de découvrir ce qui se passe dans les mines.\n\nIls ont été capturés et on leur inflige je ne sais quel genre de tortures infâmes.\n\nVous devez les libérer et tuer ceux qui les ont capturés. Nous ne laisserons personne derrière nous !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'itIT', 'Al servizio segreto di sua magicità', 'Salva i tre Agenti del Kirin Tor catturati nelle viscere delle Miniere della Folgore e uccidi il Signore del Metallo Mono-Han.', 'Per ordine di Jaina in persona, alcuni dei nostri agenti hanno tentato di infiltrarsi tra le linee dei Mogu per scoprire cosa stava accadendo nelle miniere.\n\nSono stati catturati e in questo momento staranno subendo chissà quali torture.\n\nDevi liberarli e uccidere i responsabili della loro cattura. Non lasciamo mai nessuno al proprio destino!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'koKR', '제이나의 비밀 요원들', '유령무쇠 광산 안에서 생포된 키린 토 요원 세 명을 구출한 뒤 무쇠군주 모노한을 처치해야 합니다.', '우리 쪽 요원 몇 명이 제이나 님께 직접 명령을 하달받아 유령무쇠 광산 상황을 조사하려고 모구 공격선을 넘었어요.\n\n그들은 지금 사로잡혀서 말하기조차 끔찍한 고문을 수도 없이 당하고 있답니다.\n\n그대가 우리 요원을 구출해주셔야만 해요. 그리고 그들을 생포한 자가 누구든지 당장 처치해버리세요. 얼라이언스는 그 어떤 병사라도 적진에 남겨놓지 않아요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'ptBR', 'A serviço secreto da autoridade arcana', 'Resgate os três agentes do Kirin Tor capturados dentro das Minas de Ferro Fantasma e mate o Senhor do Metal Mono-Han.', 'Sob ordens expressas da própria Jaina, alguns dos nossos agentes tentaram cruzar as linhas mogus para descobrir o que estava acontecendo nas minas.\n\nEles foram capturados e estão sendo sujeitados a sabe-se lá que tipo de torturas terríveis.\n\nVocê precisa libertá-los e matar quem quer que os tenha capturado. Não deixamos ninguém para trás!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'ruRU', 'Секретные агенты Ее Mагичества', 'Спасите 3 захваченных представителей Кирин-Тора в Шахте молний и убейте повелителя металла Моно-Ханя.', 'По личному приказанию Джайны некоторые из наших разведчиков пытались проникнуть на территорию могу, чтобы выяснить, что происходит в шахте.\n\nИх схватили и никто не знает, каким ужасным пыткам подвергают.\n\nТы $gдолжен:должна; освободить их и убить того, кто за всем этим стоит!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'zhCN', '女士密令', '在闪电矿脉中解救3名被捕的肯瑞托密探，并击败金铁领主莫诺汗。', '奉吉安娜本人之命，我们的部分间谍试图溜过魔古族防线，探明在矿脉里究竟发生着什么事。\n\n结果他们被抓住了，正在遭受难以言喻的可怕酷刑。\n\n你必须把他们解救出来，并消灭那些囚禁他们的人。我们的格言是，不抛弃，不放弃！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32588, 'zhTW', '為魔法女士閣下的秘密效命', '拯救被囚禁在鬼鐵礦坑裡的3名祈倫托密探，並殺死冶煉統領魔諾汗。', '珍娜下令派遣幾名密探試圖溜過魔古的防線，去探查礦坑裡的活動。\n\n他們卻被捉住了，受到鬼曉得是什麼樣的嚴刑拷打。\n\n請你務必救出他們，並殺死捉住他們的魔古。我們絕不拋下夥伴!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'deDE', 'Lebensblut', 'Benutzt den abgestimmten Kristall, um dem dunklen Animus auf dem Thron des Donners die Reste des Animus zu entziehen.', 'Späher haben dieses seltsame Konstrukt ausgegraben, das in den Blitzaderminen verborgen lag. Eine Geheimwaffe des Donnerkönigs, kein Zweifel.$b$bVielleicht besteht eine Chance, diese Entdeckung zu unserem Vorteil zu nutzen. Wenn wir das Konstrukt reanimieren können, können wir es studieren. Und noch wichtiger: Wir können seine Schwächen aufdecken.$b$bGeht zum Thron des Donners und nutzt diesen Kristall, um etwas von der Essenz einzufangen, die diese Konstrukte animiert.$b$bKehrt zu mir zurück, wenn die Aufgabe abgeschlossen ist.', '', '', 'Der dunkle Animus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'esES', 'Sangre de vida', 'Usa el cristal armonizado para recuperar los remanentes de Animus del Animus oscuro en el Solio del Trueno.', 'Unos exploradores han desenterrado este extraño ensamblaje oculto en la Mina del Relámpago. Se trata de un arma secreta del Rey del Trueno, no cabe duda.$b$bQuizás podamos aprovecharnos de este hallazgo. Si podemos revivirlo, podremos estudiarlo. Es más, podremos descubrir su debilidad.$b$bVe al Solio del Trueno y utiliza este cristal para capturar la esencia que vivifica estos ensamblajes.$b$bVuelve cuando hayas terminado.', '', '', 'El Animus oscuro', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'esMX', 'Sangre de vida', 'Usa el cristal armonizado para recuperar los remanentes de Animus del Animus oscuro en el Solio del Trueno.', 'Unos exploradores han desenterrado este extraño ensamblaje oculto en la Mina del Relámpago. Se trata de un arma secreta del Rey del Trueno, no cabe duda.$b$bQuizás podamos aprovecharnos de este hallazgo. Si podemos revivirlo, podremos estudiarlo. Es más, podremos descubrir su debilidad.$b$bVe al Solio del Trueno y utiliza este cristal para capturar la esencia que vivifica estos ensamblajes.$b$bVuelve cuando hayas terminado.', '', '', 'El Animus oscuro', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'frFR', 'Le sang de vie', 'Utilisez le Cristal harmonisé pour récupérer les Restes de l’animus sur le Sombre animus au sein du Trône du tonnerre.', 'Des éclaireurs ont déterré cet étrange assemblage dissimulé au cœur de la Veine de Foudremine. Il s’agit sans doute d’une arme secrète du roi-tonnerre.$b$bIl y a peut-être une chance pour que nous puissions utiliser cette découverte à notre profit. Si nous parvenons à le réanimer, nous pourrons l’étudier. Mais, plus important, nous pourrons découvrir son point faible.$b$bRendez-vous au Trône du tonnerre et utilisez ce cristal pour capturer l’essence qui anime ces assemblages.$b$bRevenez me voir une fois votre tâche accomplie.', '', '', 'Le Sombre animus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'itIT', 'Sangue vitale', 'Usa il Cristallo Armonizzato per recuperare i Resti dell\'Animus dall\'Animus Oscuro nel Regno del Tuono.', 'Gli esploratori hanno portato alla luce questo strano costrutto nascosto nelle Miniere della Folgore. Senza dubbio è un\'arma segreta del Re del Tuono.$b$bForse possiamo usare questa scoperta a nostro vantaggio. Possiamo rianimarlo, studiarlo e, soprattutto, scoprirne i punti deboli.$b$bVai al Regno del Tuono e usa questo cristallo per catturare qualsiasi essenza animi quei costrutti.$b$bTorna da me quando avrai finito.', '', '', 'L\'Animus Oscuro', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'koKR', '활력의 근원', '천둥의 왕좌에 있는 암흑 원령으로부터 원령의 잔재를 구해 조율된 수정에 담아야 합니다.', '정찰병들이 번개 광맥 광산에 숨겨진 기묘한 피조물을 발굴했소. 의심할 것도 없이 천둥왕의 비밀 병기일 거요.$b$b하지만 이것을 우리에게 유리하게 이용할 방법이 있을지도 모르오. 그 피조물을 다시 살릴 수만 있다면 연구도 할 수 있소. 무엇보다도 그 약점을 파악할 수 있겠지.$b$b이 피조물에게 동력을 공급하는 것이 뭔지는 모르겠지만, 천둥의 왕좌로 가서 이 수정에다가 담아 오시오.$b$b임무를 완수하면 내게 돌아오시오.', '', '', '암흑 원령', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'ptBR', 'Sangue Vital', 'Use este Cristal Sintonizado para recuperar os Restos de Animus do Animus Sombrio no Trono do Trovão.', 'Alguns batedores desenterraram este estranho constructo escondido na Mina do Veio Relampejante. Sem dúvida uma arma secreta do Rei Trovão.$b$bTalvez haja uma chance de usarmos esta descoberta a nosso favor. Se pudermos reanimá-lo, poderemos estudá-lo e, ainda mais importante, descobrir suas fraquezas.$b$bVá até o Trono do Trovão e use este cristal para capturar a essência que anima esses constructos, seja qual for.$b$bFale comigo novamente quando concluir a tarefa.', '', '', 'O Animus Sombrio', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'ruRU', 'Живая кровь', 'Используйте настроенный кристалл, чтобы извлечь останки Анимуса из Темного Анимуса на Престоле Гроз.', 'Разведчики обнаружили странного голема в Шахте Молний. Несомненно, это секретное оружие Властелина Грома.$b$bВозможно, мы могли бы его использовать в своих целях. Если мы сможем запустить устройство, то у нас получится изучить его подробнее. И самое главное, мы сумеем выявить его уязвимые части.$b$bОтправляйся в Престол Гроз и используй этот кристалл, чтобы поймать сущность, вдыхающую жизнь в подобных големов.$b$bВозвращайся ко мне по завершении задания.', '', '', 'Темный Анимус', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'zhCN', '生命力', '使用调和水晶从雷电王座的黑暗意志身上取得意志残渣。', '斥候们在闪电矿脉中发现了这个诡异的构造体。这无疑是雷电之王的秘密武器。$b$b也许，我们有机会从这一发现中获益。只要我们能重新将它激活，就可以对其加以研究。更为重要的是，还可以探明它的弱点。$b$b前往雷电王座，并用这枚水晶捕获任何能激活这些构造体的精华。$b$b等任务完成后再回来找我。', '', '', '黑暗意志', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32589, 'zhTW', '生命之血', '使用調諧水晶從雷霆王座裡的黑暗憎惡魔像吸取憎惡魔像的殘魂。', '斥候從電光礦脈中挖掘出這個奇怪的傀儡，這肯定是雷王的秘密武器。$b$b也許，我們可以好好利用這個機會。如果能重新賦予傀儡生命，我們便能研究它。更重要的是，還能發現它的弱點。$b$b前往雷霆王座，使用這塊水晶捕捉任何驅動這些傀儡的精魄。$b$b完成任務後向我回報。', '', '', '黑暗憎惡魔像', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'deDE', 'Trefft mich oben', 'Trefft Furorion im zweiten Stock der Taverne im Nebel an der Verhüllten Treppe.', 'Da ist jemand, den Ihr treffen solltet. Der Kronprinz von Sturmwind höchstselbst hat diesem bescheidenen Gasthof vor Kurzem einen Besuch abgestattet. Ich habe meinen Wachen befohlen, ihn gegen jegliche Angriffe der Horde zu verteidigen – Ich möchte keine unnötige Aufmerksamkeit auf mich ziehen.$b$bEr und ich haben uns über die Lage der Welt unterhalten, zum Beispiel über die Schlacht an der Südküste, sein Treffen mit Garrosh und den kommenden Angriff auf den Donnerkönig.$b$bKommt! Lasst uns oben weiterreden...', '', '', '', '', '', 'Trefft Furorion im zweiten Stock der Taverne im Nebel an der Verhüllten Treppe.', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'esES', 'Nos vemos arriba', 'Reúnete con Wrathion arriba, en la segunda planta de la Taberna en la Niebla, en La Escalera Velada.', 'Quiero que conozcas a alguien. Hace poco, el príncipe heredero de Ventormenta vino a esta humilde posada. He ordenado a mis guardias que lo protejan de cualquier agresión de la Horda; no quiero llamar la atención más de la cuenta.$b$bÉl y yo hemos estado hablando de sucesos del mundo, como la batalla en las costas del sur, su encuentro con Garrosh y el inminente asalto al Rey del Trueno.$b$b¡Ven! Vamos a hablar arriba...', '', '', '', '', '', 'Reúnete con: Wrathion. Zona: segunda planta, Taberna en la Niebla, La Escalera Velada.', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'esMX', 'Nos vemos arriba', 'Reúnete con Wrathion arriba, en la segunda planta de la Taberna en la Niebla, en La Escalera Velada.', 'Quiero que conozcas a alguien. Hace poco, el príncipe heredero de Ventormenta vino a esta humilde posada. He ordenado a mis guardias que lo protejan de cualquier agresión de la Horda; no quiero llamar la atención más de la cuenta.$b$bÉl y yo hemos estado hablando de sucesos del mundo, como la batalla en las costas del sur, su encuentro con Garrosh y el inminente asalto al Rey del Trueno.$b$b¡Ven! Vamos a hablar arriba...', '', '', '', '', '', 'Reúnete con: Wrathion. Zona: segunda planta, Taberna en la Niebla, La Escalera Velada.', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'frFR', 'Retrouvez-moi là-haut', 'Retrouvez Irion au deuxième étage de la Taverne dans les Brumes, dans l’Escalier Dérobé.', 'Il y a quelqu’un que j’aimerais vous présenter. Le prince de la couronne de Hurlevent a récemment honoré cette humble auberge de sa présence. J’ai ordonné à mes gardes de le protéger de toute agression de la Horde, car je n’ai pas envie d’attirer l’attention sur moi.$b$bLui et moi avons discuté des évènements mondiaux, comme la bataille le long du rivage sud, de sa rencontre avec Garrosh et de l’assaut imminent contre le roi-tonnerre.$b$bVenez ! Allons discuter là-haut…', '', '', '', '', '', 'Retrouvez Irion à l’étage de la Taverne dans les brumes, à l’Escalier Dérobé.', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'itIT', 'Vediamoci di sopra', 'Incontra Irathion al piano superiore della Taverna nelle Nebbie al Passo Velato.', 'C\'è qualcuno che vorrei farti incontrare. Il principe di Roccavento in carne e ossa ha fatto recentemente visita a questa umile taverna. Ho ordinato alle mie guardie di difenderlo contro ogni aggressione dell\'Orda, senza attirare troppo l\'attenzione.$b$bAbbiamo discusso insieme degli eventi del mondo, come la battaglia sulle coste meridionali, il suo incontro con Garrosh e l\'imminente assalto al Re del Tuono.$b$bVieni! Parliamo di sopra...', '', '', '', '', '', 'Incontra Irathion al piano superiore della Taverna nelle Nebbie al Passo Velato.', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'koKR', '위층으로', '장막의 계단, 안개 속의 주점 2층에서 래시온을 만나야 합니다.', '소개하고 싶은 사람이 있다. 스톰윈드의 왕세자가 최근 이 초라한 여관을 찾았지. 내 경비병에게 호드로부터 그의 신변을 보호하라고 말해 두었다. 난 불필요하게 눈에 띄고 싶진 않으니까.$b$b나는 그와 함께 세상사를 논했다. 남쪽 해변의 교전 이야기라든지, 그가 가로쉬를 만난 이야기라든지, 천둥왕을 공격할 예정이라는 이야기 말이지.$b$b오거라! 위층에서 이야기하자...', '', '', '', '', '', '장막의 계단, 안개 속의 주점 2층에서 래시온을 만나야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'ptBR', 'Encontre-me no segundo andar', 'Fale com Wrathion no segundo andar da Taverna na Névoa, que fica na Escadaria Oculta.', 'Quero que conheça alguém. O herdeiro da coroa de Ventobravo em pessoa fez uma visita a esta humilde estalagem recentemente. Instruí meus guardas a defendê-lo de qualquer agressão da Horda - não quero nenhuma atenção indesejada voltada para mim.$b$bEle e eu discutimos acontecimentos mundiais, como a batalha nas praias do sul, o encontro dele com Garrosh e o ataque vindouro ao Rei Trovão.$b$bVenha! Vamos conversar lá em cima...', '', '', '', '', '', 'Fale com Wrathion no segundo andar da Taverna na Névoa, que fica na Escadaria Oculta.', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'ruRU', 'Встретимся наверху', 'Встретьтесь с Гневионом в таверне \"В туманах\", что на Сокрытой лестнице.', 'Я хочу, чтобы ты кое с кем $gвстретился:встретилась;. Эту скромную таверну недавно посетил наследный принц Штормграда. Я приказал своим стражам защищать его от любых проявлений агрессии со стороны Орды. Мне ни к чему излишнее внимание.$b$bМы с ним обсуждали последние произошедшие в мире события – сражение у южных берегов, его стычку с Гаррошем и предстоящее нападение на Властелина Грома.$b$bПойдем! Поговорим наверху...', '', '', '', '', '', 'Встретьтесь с Гневионом на втором этаже таверны \"В туманах\", что на Сокрытой лестнице.', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'zhCN', '楼上的约见', '在雾纱栈道的迷雾酒肆二楼与拉希奥会面。', '我想带你见个人。暴风城的王子殿下不久前造访了这间简陋的旅舍。我命令守卫保护他，使他免受任何来自部落的威胁——当然，我可不想为自己招来任何不必要的关注。$b$b他与我纵谈世界大事，比如发生在南部海岸上的战争，他与加尔鲁什的会面，以及雷电之王即将发起的攻击。$b$b来吧！让我们到楼上聊聊……', '', '', '', '', '', '在雾纱栈道的迷雾酒肆二楼与拉希奥会面。', 0);
INSERT INTO `quest_template_locale` VALUES (32590, 'zhTW', '上樓碰面', '到朦朧天梯的霧隱客棧二樓見怒西昂。', '我希望你去見一個人。暴風城的王儲近日下榻敝店，我已經吩咐守衛保護王儲的安危，因為我可不想惹麻煩上身。$b$b安杜因與我商談國家大事，諸如南方海岸的戰爭和他與卡爾洛斯的那場戰鬥，以及即將對雷王發動的攻擊。$b$b來吧!到樓上來談談...', '', '', '', '', '', '到朦朧天梯的霧隱客棧二樓見怒西昂。', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'deDE', 'Geheimnisse des ersten Kaiserreiches', 'Sammelt 20 Geheimnisse des Kaiserreichs und 40 Trilliumbarren.', 'Zweitausend Jahre lang haben die Mogu dieses ganze Land beherrscht. Ihr Regime war so brutal, dass sein Name nie laut ausgesprochen wird: Die Pandaren nennen es entweder das \"alte Königreich\" oder \"Erste Dynastie\".$b$bDer Donnerkönig war sein Begründer und erster Kaiser. Er sammelte große Macht an dem Ort, der nun die Insel des Donners ist.$b$bSeine Zeit ist vorbei, $n. Wir sollten etwas von seiner Macht für uns selbst beanspruchen. Tragt für mich Mogurelikte vom Thron des Donners zusammen und sammelt etwas Trillium – ich habe einen Plan!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'esES', 'Los secretos del Primer Imperio', 'Recoge 20 secretos del Imperio y 40 barras de trillium.', 'Durante dos mil años los mogu dominaron estas tierras. Su régimen era tan cruel que nadie osa pronunciar su nombre en voz alta; los pandaren lo llaman el \"Antiguo Reino\" o la \"Primera Dinastía\".$b$bEl Rey del Trueno fue el fundador y su primer Emperador. Acumuló un poder enorme en lo que ahora se conoce como la Isla del Trueno.$b$bSu época ha terminado, $n. Debemos reclamar nuestra parte de su poder. Reúne reliquias mogu del Solio del Trueno y recoge el trillium que veas por ahí. ¡Tengo un plan!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'esMX', 'Los secretos del Primer Imperio', 'Recoge 20 secretos del imperio y 40 barras de trillium.', 'Durante dos mil años los mogu dominaron estas tierras. Su régimen era tan cruel que nadie osa pronunciar su nombre en voz alta; los pandaren lo llaman el \"Antiguo Reino\" o la \"Primera Dinastía\".$b$bEl Rey del Trueno fue el fundador y su primer Emperador. Acumuló un poder enorme en lo que ahora se conoce como la Isla del Trueno.$b$bSu época ha terminado, $n. Debemos reclamar nuestra parte de su poder. Reúne reliquias mogu del Solio del Trueno y recoge el trillium que veas por ahí. ¡Tengo un plan!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'frFR', 'Les secrets du premier empire', 'Récupérez 20 Secrets de l’empire et 40 Barres de trillium.', 'Pendant deux mille ans, les mogu ont régné sur ces terres. Leur régime était si brutal que son nom n’est jamais mentionné à voix haute… Les Pandarens utilisent généralement les expressions « l’ancien royaume » ou « la première dynastie ».$b$bLe roi-tonnerre fut son fondateur et son premier empereur. Il accumula un pouvoir faramineux sur ce qui est désormais l’île du Tonnerre.$b$bMais son temps est révolu, $n. Nous devons revendiquer une partie de son pouvoir. Rassemblez des reliques mogu pour moi au Trône du tonnerre et récoltez du trillium… J’ai un plan !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'itIT', 'I segreti del Primo Impero', 'Recupera 20 Segreti dell\'Impero e 40 Lingotti di Trillio.', 'Per duemila anni, i Mogu hanno dominato questa terra. Il loro regime era così brutale che solo il suo nome incuteva terrore: i Pandaren lo chiamano \"Vecchio Regno\" o \"Prima Dinastia\".$b$bIl Re del Tuono ne fu il fondatore e il primo imperatore. Egli concentrò un enorme potere su quella che è ora l\'Isola del Tuono.$b$bIl suo regno è finito, $n, e ora dovremmo reclamare parte di quel potere per noi stessi. Recupera le reliquie dei Mogu dal Regno del Tuono, insieme a un po\' di trillio; ho un piano!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'koKR', '첫 번째 제국의 비밀', '제국의 비밀 20개와 트릴리움 주괴 40개를 모아야 합니다.', '모구는 이천 년 동안 이 땅 전역을 지배했다. 그들의 체제는 너무나 잔혹해서 누구도 그 이름을 감히 입 밖에 내지 않았지. 판다렌은 그것을 \"구 왕국\" 또는 \"첫 번째 왕조\"라 불렀다.$b$b천둥왕이 그 시조이자 첫 황제였다. 그는 지금 천둥의 섬이라 불리는 곳에서 엄청난 권력을 축적했다.$b$b하지만 그의 시대는 갔다, $n. 우리는 그의 힘을 손에 넣어야 한다. 천둥의 왕좌에서 모구 유물을 수집하고, 트릴리움을 모아 오거라. 내게 생각이 있다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'ptBR', 'Segredos do primeiro império', 'Colete 20 Segredos do Império e 40 Barras de Trílio.', 'Por dois mil anos, os mogus governaram toda esta terra. O regime deles foi tão brutal que seu nome nunca é proferido — os pandarens o chamam apenas de \"Velho Reino\" ou \"Primeira Dinastia\".$b$bSeu fundador e primeiro imperador foi o Rei Trovão, que reuniu um imenso poder no que hoje é a Ilha do Trovão.$b$bO tempo dele passou, $n. Devíamos reivindicar parte do poder para nós. Reúna relíquias mogus do Trono do Trovão e colete algum trílio — eu tenho um plano!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'ruRU', 'Секреты Первой империи', 'Соберите 20 тайн империи и 40 слитков триллия.', 'Две тысячи лет могу правили всей этой землей. Их правление было столь жестоким, что имя их империи никогда не произносилось вслух. Пандарены называют ее Древним королевством или Первой династией.$b$bЕе основателем и первым императором был Властелин Грома. Он собрал невероятную силу в том месте, которое мы сейчас знаем, как остров Грома.$b$bЕго время прошло, $n. И мы должны завладеть частью этой силы. Собери для меня реликвии могу и немного триллия в Престоле Гроз. У меня есть план!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'zhCN', '第一帝国的秘密', '收集20份帝国秘史和40块延极锭。', '两千年来，这片土地始终都在魔古族的统治之下。他们施行的暴政如此残酷，以至于无人敢大声说出它的名字——熊猫人将它称作“古代王朝”或“第一王朝”。$b$b雷电之王是这一王朝的创建者，也是它的第一任君主。他在如今被称作雷神岛的地方积聚着巨大的力量。$b$b他的时代已经过去了，$n。我们应该将他的部分力量化为己用。替我从雷电王座收集圣物，再找些延极锭来——我有个好办法！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32591, 'zhTW', '初代帝國秘史', '收集20本帝國秘史與40塊延齡錠。', '「魔古統治這塊土地長達兩千年的時間，殘暴的統治手法讓人們對這段歷史噤若寒蟬。熊貓人含蓄地稱其為『古王國』或『第一王朝』。$b$b雷王是這個王朝的創始者，也是始皇帝，他在現今的雷王島上蓄積了龐大的勢力。$b$b他的時代已經結束了，$n。我們應該將他的遺寶據為己有。從雷霆王座取一些魔古聖物回來給我，並收集一些延齡錠 - 我有個計畫!」', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'deDE', 'Ich brauche einen Champion', 'Erlangt einen ehrfürchtigen Ruf beim Schwarzen Prinzen, indem Ihr feindliche Mogu, Zandalari und Saurok auf der Insel des Donners besiegt.', 'Ihr seid ein großartiges Beispiel für Euer Volk, $R. Aber ich brauche jemand besonderen. Ich habe große Pläne. Und um sie durchzuführen, brauche ich einen Champion, der mein Feuer an alle Enden der Welt trägt.$b$bSeid Ihr dieser Champion?$b$bBeweist es mir auf der Insel des Donners! Vernichtet alle Mogu, Zandalari oder Saurok, die Ihr finden könnt. Beeindruckt mich!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'esES', 'En busca de un campeón', 'Alcanza la reputación Exaltado con el Príncipe Negro derrotando a enemigos mogu, Zandalari y saurok en la Isla del Trueno.', 'Eres $gun:una; excelente representante de tu raza, $r, pero necesito a alguien especial. Tengo en mente grandes planes y, para llevarlos a cabo, necesito a $gun campeón:una campeona; que porte mi llama a los confines del mundo.$b$b¿Eres tú $gel campeón:la campeona; que busco?$b$b¡Demuéstramelo en la Isla del Trueno! Acaba con todos los mogu, Zandalari o saurok que veas. ¡Sorpréndeme!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'esMX', 'En busca de un campeón', 'Alcanza la reputación Exaltado con el Príncipe Negro derrotando a enemigos mogu, Zandalari y saurok en la Isla del Trueno.', 'Eres $gun:una; excelente representante de tu raza, $r, pero necesito a alguien especial. Tengo en mente grandes planes y, para llevarlos a cabo, necesito a $gun campeón:una campeona; que porte mi llama a los confines del mundo.$b$b¿Eres tú $gel campeón:la campeona; que busco?$b$b¡Demuéstramelo en la Isla del Trueno! Acaba con todos los mogu, Zandalari o saurok que veas. ¡Sorpréndeme!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'frFR', 'Il me faut un champion', 'Devenez $gExalté:Exaltée; auprès du Prince noir en éliminant des ennemis mogu, zandalari et sauroks sur l’Île du Tonnerre.', 'Vous êtes $gun:une; magnifique $greprésentant:représentante; de votre peuple, $R. Toutefois, j’ai besoin de quelqu’un de spécial. J’ai de grands projets. Et pour les réaliser, il me faudra un champion capable de porter mon flambeau aux quatre coins du monde.$b$bSerez-vous cette personne ?$b$bFaites vos preuves sur l’île du Tonnerre ! Massacrez tous les mogu, les Zandalari, ou les sauroks que vous croiserez. Impressionnez-moi !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'itIT', 'Mi serve un campione', 'Diventa Osannato dal Principe Nero eliminando Mogu, Zandalari e Saurok sull\'Isola del Tuono.', 'La tua reputazione ti precede, $R, ma ho bisogno di qualcuno davvero speciale. Ho dei grandi piani in serbo e per metterli in pratica ho bisogno di qualcuno che porti la mia fiamma ai quattro angoli del mondo.$b$bSei forse tu?$b$bDimostramelo sull\'Isola del Tuono! Massacra ogni Mogu, Zandalari o Saurok che riesci a trovare. Stupiscimi!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'koKR', '용사가 필요하다', '검은 왕자에게 확고한 동맹 평판을 얻어야 합니다. 천둥의 섬에 있는 모구, 잔달라 트롤, 사우록을 처치하면 평판을 얻을 수 있습니다.', '너는 너희 진영의 대표로 적합한 인물이다, $r. 그러나 내게는 특별한 인물이 필요하다. 내겐 큰 계획이 있고, 그것을 실행하기 위해서는 나의 봉화를 세상의 네 귀퉁이에 가져갈 용사가 필요하다.$b$b과연 네가 그 용사일까?$b$b천둥의 섬에서 자격을 증명해라! 모구, 잔달라 트롤, 사우록을 눈에 띄는 대로 척살해라! 날 감탄하게 만들어 보아라!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'ptBR', 'Preciso de um campeão', 'Ganhe a reputação de Exaltado pelo Príncipe Negro derrotando inimigos mogus, Zandalari e sauroks na Ilha do Trovão.', 'Você é $gum soberbo:uma soberba; representante do seu povo, $r. Mas preciso de alguém especial. Tenho grandes planos. E para realizá-los, vou precisar de um campeão para carregar minha chama até os quatro cantos do mundo.$b$bSerá você?$b$bProve na Ilha do Trovão! Acabe com todos os mogus, Zandalari e sauroks que encontrar. Você precisa me impressionar!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'ruRU', 'Мне нужен герой', 'Побеждая могу, зандаларов или сауроков на острове Грома, добейтесь того, чтобы вас превозносил Черный принц.', 'Ты – $gвыдающийся представитель:выдающаяся представительница; своего народа, |3-6($r). Но я ищу кого-то особенного. У меня большие планы. И для воплощения их в жизнь мне нужен настоящий герой, который станет исполнителем моей воли во всех концах мира.$b$bСчитаешь себя $gдостойным:достойной;?$b$bДокажи мне это своими подвигами на острове Грома. Истреби всех могу, зандаларов или сауроков, каких найдешь. Порази меня!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'zhCN', '我需要勇士', '在雷神岛上击败魔古、赞达拉巨魔和蜥蜴人，在黑王子阵营中达到崇拜声望。', '你是你族人当中的盖世精锐，$r。可我需要的是一个特别的帮手。我有一揽子的大计划。若要将这些计划变为现实，就得有一位勇士，将我的光辉散播到世界各处。$b$b你是合适的人选吗？$b$b去雷神岛向我证明你的实力吧！消灭你所遇到的所有魔古、赞达拉巨魔或蜥蜴人。让我对你刮目相看！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32592, 'zhTW', '我需要一位勇士', '擊敗雷王島上的魔古、贊達拉與薩烏洛克，在黑龍王子面前贏得崇拜聲望。', '你是個出類拔萃的人物，$r。但我需要的是一位特殊的人才。我有個偉大的計畫，為了執行這個計畫，我需要一位勇士將我的改變之火傳向全世界。$b$b你是這個計畫的不二人選嗎?$b$b到雷王島上證明你的實力!殺掉你遇到的任何一個摩古、贊達拉與薩烏洛克。讓我見識你的厲害!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'deDE', 'Die Donnerschmiede', 'Kämpft Euch durch die Insel des Donners, bis die Donnerschmiede zugänglich wird. Trefft Euch vor der Donnerschmiede mit Furorion und schließt seine Aufgabe ab.', 'Alles ist vorbereitet, $n, und ich habe Euch als meinen Champion gewählt.$b$bAls nächstes müssen wir die legendäre Donnerschmiede des Donnerkönigs selbst infiltrieren! Ihr und Eure Fraktion müsst vordringen, bis die Donnerschmiede geöffnet ist – wenn sie das noch nicht ist.$b$bHabt Ihr die Schmiede einmal infiltriert, trefft mich dort. Macht Euch auf einen Kampf gefasst!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'esES', 'La Forja del Trueno', 'Sigue avanzando en la Isla del Trueno hasta que desbloquees la Forja del Trueno. Reúnete con Wrathion fuera de la Forja del Trueno y sigue sus órdenes.', 'Todo está listo, $n, y te he seleccionado a ti como mi $gcampeón:campeona;.$b$b¡Ahora debemos infiltrarnos en la legendaria Forja del Trueno del mismísimo Lei Shen! Tu facción y tú tendréis que avanzar hasta que la Forja del Trueno esté expuesta, si es que no lo está ya.$b$bCuando hayáis entrado en la forja, reúnete conmigo. ¡Prepárate para la batalla!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'esMX', 'La Forja del Trueno', 'Sigue avanzando en la Isla del Trueno hasta que desbloquees la Forja del Trueno. Reúnete con Wrathion fuera de la Forja del Trueno y sigue sus órdenes.', 'Todo está listo, $n, y te he seleccionado a ti como mi $gcampeón:campeona;.$b$b¡Ahora debemos infiltrarnos en la legendaria Forja del Trueno del mismísimo Lei Shen! Tu facción y tú tendrán que avanzar hasta que la Forja del Trueno esté expuesta, si es que no lo está ya.$b$bCuando hayan entrado en la forja, reúnanse conmigo. ¡Prepárate para la batalla!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'frFR', 'La forge du Tonnerre', 'Progressez dans l’île du Tonnerre jusqu’à débloquer la Forge du Tonnerre. Retrouvez Irion à l’extérieur de la Forge du Tonnerre et faites ce qu’il vous demande.', 'Tout est prêt, $n, et je vous ai $gchoisi:choisie; pour être $gmon champion:ma championne;.$b$bMaintenant, nous devons nous infiltrer dans la légendaire forge du Tonnerre de Lei Shen lui-même ! Votre faction et vous devrez avancer jusqu’à ce que la forge soit exposée, si ce n’est pas déjà le cas.$b$bUne fois que vous vous serez infiltrés dans la forge, rejoignez-moi. Et préparez-vous au combat !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'itIT', 'La Forgia dei Fulmini', 'Avanza sull\'Isola del Tuono per ottenere l\'accesso alla Forgia dei Fulmini. Incontra Irathion fuori dalla Forgia dei Fulmini e completa il suo incarico.', 'Tutto è pronto, $n, e d\'ora in poi tu sarai $gil mio campione:la mia campionessa;.$b$bOra dobbiamo infiltrarci nella leggendaria Forgia dei Fulmini di Lei Shen in persona! Tu e la tua fazione dovrete avanzare fino a ottenere l\'accesso alla Forgia dei Fulmini, se non è già successo.$b$bUna volta nella forgia, ci incontreremo lì. Sii $gpronto:pronta; alla battaglia!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'koKR', '천둥 제련소', '천둥의 섬에서 천둥 제련소가 개방될 때까지 진행해야 합니다. 그런 다음 천둥 제련소 밖에서 래시온을 만나 임무를 완수해야 합니다.', '모두 준비되었다, $n. 그리고 나는 너를 내 용사로 택했지.$b$b이번에는 다름 아닌 레이 션의 천둥 제련소에 침투해야 한다! 너희 진영은 천둥 제련소가 노출될 때까지 전진해야 한다.$b$b제련소에 침투한 후 그곳에서 나를 찾아라. 싸울 각오를 하고 와야 할 것이다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'ptBR', 'A Forja do Trovão', 'Avance pela Ilha do Trovão até desbloquear a Forja do Trovão. Encontre Wrathion fora da Forja e cumpra a tarefa dele.', 'Está tudo preparado, $n, e eu escolhi você para ser $gmeu campeão:minha campeã;.$b$bAgora, precisamos nos infiltrar na lendária Forja do Trovão, que pertenceu ao próprio Lei Shen! Você e sua facção precisam avançar até que a Forja do Trovão fique exposta, se já não está.$b$bDepois de infiltrar-se na forja, encontre-me lá. Prepare-se para uma boa luta!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'ruRU', 'Кузня Грома', 'Исследуйте остров Грома до тех пор, пока вам не откроется доступ в Кузню Грома. Найдите Гневиона у кузни и завершите данное им задание.', 'Все готово, $n, и именно ты $gстал:стала; избранным мною героем.$b$bТеперь мы должны пробраться в легендарную Кузню Грома, принадлежащую самому Лэй Шэню! Двигайся вперед вместе со своей фракцией, пока тебе не откроется доступ к Кузне Грома, если этого еще не произошло ранее.$b$bЯ буду ждать тебя там. Будь $gготов:готова; к бою!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'zhCN', '雷霆熔炉', '在雷神岛上继续探索，直到你找到雷霆熔炉。与拉希奥在雷霆熔炉外面碰头，并完成他的任务。', '一切都准备好了，$n，我也已经将你选为我的勇士。$b$b接下来，我们必须接近传说中雷神本尊的雷霆熔炉！你和你的阵营需要继续前进，直到雷霆熔炉暴露出来——如果它还没暴露的话。$b$b等你接近熔炉后，与我在那里会面。做好战斗的准备吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32593, 'zhTW', '雷霆熔爐', '參加雷王島的戰役直到進度已經解鎖雷霆熔爐後，在雷霆熔爐外與怒西昂碰面來完成任務。', '一切都準備就緒，$n。我已選定你為我的勇士。$b$b下一步，我們得潛入傳說中雷王所擁有的雷霆熔爐!你和你的部隊得作先鋒，攻打雷王的軍隊直到雷霆熔爐外的守軍都已被消滅。$b$b一旦你能夠潛入熔爐時，就在那兒與我會面。準備大戰一場吧!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'deDE', 'Geist des Sturmfürsten', 'Kämpft Euch durch die Insel des Donners, bis der Innenhof zugänglich wird. Beschafft Euch eine gehärtete Blitzlanze, indem Ihr Nalak dem Sturmfürsten die Lanze in den Leib stoßt und lang genug überlebt, um ihm seine Macht zu entziehen.', 'Unser nächstes Ziel ist es, diese Waffe mit dem Blut eines Halbgottes zu härten, der mit den Mächten des Donnerkönigs beseelt ist.$b$bMeine Agenten berichten mir, dass der Mogu namens Shan\'Bu eine solche Kreatur erschafft, mit dem Ziel, sie die Zitadelle des Donnerkönigs bewachen zu lassen.$b$bGreift Nalak den Sturmfürsten an, sobald sich Eure Fraktion zu den Haupttoren durchgekämpft hat, und stoßt diese Lanze in seine Seite. Die Kreatur wird alles in ihrer Macht stehende tun, um Euch aufzuhalten – handelt schnell und tötet Nalak, während die Lanze noch in seinem Fleisch steckt!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'esES', 'El espíritu del Señor de la Tormenta', 'Avanza a través de la Isla del Trueno hasta que desbloquees el patio final. Clávale la lanza al Señor de la Tormenta Nalak y sobrevive el tiempo suficiente para succionar su poder para obtener una lanza del relámpago templada.', 'Nuestro próximo objetivo es templar esta arma con la sangre de un semidiós imbuido de los poderes del Rey del Trueno.$b$bMis agentes me dicen que, en estos momentos, el mogu Shan Bu está creando una criatura así para que vigile la ciudadela del Rey del Trueno.$b$bCuando tu facción se haya apostado en las puertas principales, ataca al señor de la tormenta Nalak y clávale esta lanza en un costado. La criatura hará todo lo que pueda para detenerte, ¡así que permanece con vida el tiempo necesario para absorber todo el poder del Señor de la Tormenta!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'esMX', 'El espíritu del Señor de la Tormenta', 'Avanza a través de la Isla del Trueno hasta que desbloquees el patio final. Clávale la lanza al Señor de la Tormenta Nalak y sobrevive el tiempo suficiente para succionar su poder para obtener una lanza del relámpago templada.', 'Nuestro próximo objetivo es templar esta arma con la sangre de un semidiós imbuido de los poderes del Rey del Trueno.$b$bMis agentes me dicen que, en estos momentos, el mogu Shan Bu está creando una criatura así para que vigile la ciudadela del Rey del Trueno.$b$bCuando tu facción se haya apostado en las puertas principales, ataca al señor de la tormenta Nalak y clávale esta lanza en un costado. La criatura hará todo lo que pueda para detenerte, ¡así que permanece con vida el tiempo necesario para absorber todo el poder del Señor de la Tormenta!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'frFR', 'L’esprit du seigneur des tempêtes', 'Progressez sur l’Île du Tonnerre jusqu’à ce que vous ayez débloqué la dernière cour. Obtenez un Javelot de foudre trempé en transperçant le Seigneur des tempêtes Nalak avec le javelot assez longtemps pour siphonner sa puissance.', 'Notre objectif suivant est de tremper cette arme dans le sang d’un demi-dieu, imprégné des pouvoirs du roi-tonnerre.$b$bD’après mes agents, le mogu appelé Shan Bu est justement en train de créer une telle créature pour garder la citadelle du Roi-tonnerre.$b$bQuand votre faction se sera frayé un chemin jusqu’aux portes principales, attaquez le seigneur des tempêtes Nalak et transpercez son flanc avec ce javelot. La créature fera tout son possible pour vous arrêter, mais restez en vie assez longtemps pour siphonner le pouvoir du seigneur des tempêtes !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'itIT', 'Lo spirito del Signore della Tempesta', 'Continua l\'esplorazione dell\'Isola del Tuono e libera l\'accesso al cortile finale. Ottieni la Lancia della Folgore Temprata conficcando la lancia nel corpo di Nalak il Signore della Tempesta e sopravvivendo abbastanza a lungo per riuscire ad assorbire il suo potere.', 'Il nostro prossimo obiettivo è temprare quest\'arma con il sangue di un semidio, uno di coloro in possesso dei poteri del Re del Tuono.$b$bI miei agenti mi riferiscono che un Mogu di nome Shan Bu sta creando proprio una di queste creature, con lo scopo di metterla a guardia della Cittadella del Re del Tuono.$b$bQuando la tua fazione avrà ottenuto il controllo dei cancelli frontali, affronta Nalak il Signore della Tempesta e conficca questa lancia nel suo fianco. La creatura farà di tutto per fermarti, quindi cerca di rimanere in vita abbastanza a lungo da riuscire ad assorbire il potere dal Signore della Tempesta!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'koKR', '폭풍 군주의 영혼', '천둥의 섬에서 최후의 안마당이 개방될 때까지 진행해야 합니다. 폭풍 군주 나락크의 옆구리에 창을 꽂은 후 그 힘을 흡수할 때까지 살아남아 담금질된 번개 창을 획득해야 합니다.', '우리의 다음 목표는 이 무기를 반신의 피로 담금질하는 것이다. 천둥왕의 힘을 부여받은 녀석으로 말이지.$b$b내 요원들에게 듣기로는, 샨 부라는 모구가 천둥왕의 성채를 지키기 위해 그런 생물을 창조하고 있다고 한다.$b$b너희 진영이 적진을 돌파해 정문에 다다르면, 폭풍 군주 나락크를 공격하여 이 창을 그 옆구리에 꽂아라. 녀석은 무슨 짓을 해서라도 널 막으려 하겠지. 폭풍 군주의 힘을 모두 흡수할 때까지 죽어서는 안 된다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'ptBR', 'Espírito do Senhor da Tempestade', 'Avance pela Ilha do Trovão até desbloquear o último pátio. Obtenha um Lança de Raio Temperada cravando a lança no Senhor da Tempestade Nalak e sobrevivendo por tempo suficiente para drenar seu poder.', 'Nosso próximo objetivo é temperar esta arma com o sangue de um semideus, um imbuído de poderes do Rei Trovão.$B$BMeus agentes dizem que o mogu chamado Shan Bu está criando justamente essa criatura, para vigiar e proteger a cidadela do Rei Trovão.$B$BQuando sua facção tiver aberto caminho até os portões frontais, ataque o Senhor da Tempestade Nalak e enfie esta lança nele. A criatura fará tudo o que puder para impedir você. Sobreviva por tempo suficiente para drenar a energia do Senhor da Tempestade!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'ruRU', 'Дух Повелителя Гроз', 'Продвигайтесь по острову Грома, пока не откроете последний внутренний двор. Получите закаленное Копье молний, вонзив копье в Повелителя Гроз Налака и убив его.', 'Наша следующая цель – закалить это оружие в крови полубога, получившего силы Властелина Грома.$b$bМои агенты говорят, что могу по имени Шань-Бу сейчас создает такое существо, чтобы оно охраняло цитадель Властелина Грома.$b$bКак только твой отряд пробьет дорогу к главным воротам, атакуй Повелителя Гроз Налака и вонзи это копье ему в бок. Существо сделает все, что в его силах, чтобы остановить тебя – постарайся прожить достаточно долго, чтобы заполучить силу Повелителя Гроз!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'zhCN', '暴风领主之魂', '在雷神岛上继续探索，直到你找到最后一个庭院。将长枪插进暴风领主纳拉克的身体里，坚持到将他的力量虹吸出来，以获得淬火闪电长枪。', '我们的下一个目标，就是要用充满雷电之王力量的半神之血来调节这把武器。$b$b据密探回报，有位名叫山怖的魔古族正在创造这样一种怪物，想让其戍守雷电堡。$b$b当你的阵营攻到前门时，攻击暴风领主纳拉克，并将这根长枪插进它的身侧。这只怪物会使出浑身解数来阻止你——在成功将暴风领主的力量吸出来以前，千万要挺住！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32594, 'zhTW', '風暴龍王之靈', '參加雷王島的戰役直到進度已經解鎖最後的宮殿中庭，再將長矛刺進『風暴龍王』納拉卡的體內，支撐到長矛吸足它的能量以獲得一把淬鍛的閃電長槍。', '我們的下一個目標是用灌注雷王能量的半神之血來淬鍛這把武器。$b$b我的密探告訴我，一個名叫衫布的魔古剛創造了一個這樣的生物，企圖用來守衛雷王城塞。$b$b一旦你的陣營攻到城門底下便攻擊『風暴龍王』納拉卡，並將這把長矛刺進牠的體側。這隻生物施展所有的能量來阻攔你，因此你要盡量拖延時間，以吸取『風暴龍王』的所有能量!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'deDE', 'Die Krone des Himmels', 'Trefft Euch auf des Steinmetz\' Torheit an der Verhüllten Treppe mit Furorion, um Eure Belohnung zu erhalten.', 'Erinnert Ihr Euch an des Steinmetz\' Torheit, die Hügelkuppe östlich von hier, wo wir uns schon einmal getroffen haben?$b$bIch werde die Blitzlanze benutzen, um etwas besonderes zu erschaffen, und hier drin kann ich das ganz bestimmt nicht tun.$b$bTrefft mich oben auf des Steinmetz\' Torheit, damit wir unsere Siege gemeinsam feiern können!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'esES', 'La corona del cielo', 'Reúnete con Wrathion en la cima de la Locura del Albañil en La Escalera Velada y recoge tu recompensa.', '¿Recuerdas la Locura del Albañil, esa colina que hay al este donde tú y yo estuvimos?$b$bVoy a utilizar la lanza del relámpago para crear algo especial, y tengo muy claro que no pienso hacerlo aquí.$b$b¡Reúnete conmigo en la cima de la Locura del Albañil para que celebremos nuestra victoria juntos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'esMX', 'La corona del cielo', 'Reúnete con Wrathion en la cima de la Locura del Albañil en La Escalera Velada y recoge tu recompensa.', '¿Recuerdas la Locura del Albañil, esa colina que hay al este donde tú y yo estuvimos?$b$bVoy a utilizar la lanza del relámpago para crear algo especial, y tengo muy claro que no pienso hacerlo aquí.$b$b¡Reúnete conmigo en la cima de la Locura del Albañil para que celebremos nuestra victoria juntos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'frFR', 'La couronne des cieux', 'Retrouvez Irion en haut de la Folie du maçon, à l’Escalier Dérobé, pour récupérer votre récompense.', 'Vous vous souvenez de la Folie du maçon, la colline à l’est d’ici où nous nous sommes rencontrés la fois d’avant ?$b$bJe vais utiliser le javelot de foudre pour créer quelque chose de spécial et je ne peux vraiment pas le faire ici.$b$bRejoignez-moi au sommet de la Folie du maçon afin que nous célébrions nos victoires ensemble !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'itIT', 'La corona del paradiso', 'Incontra Irathion in cima alla Scalinata della Follia al Passo Velato e reclama la tua ricompensa.', 'Ricordi la Scalinata della Follia, in cima al colle a est di qui, dove ci siamo già incontrati?$b$bHo intenzione di usare la Lancia della Folgore per creare qualcosa di speciale, ma non posso certo farlo qui.$b$bTroviamoci in cima alla Scalinata della Follia, così che potremo finalmente celebrare le tue vittorie insieme!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'koKR', '천공의 정점', '장막의 계단에 있는 석공의 우둔함 꼭대기에서 래시온을 만나 보상을 받아야 합니다.', '우리가 전에 만났던 동쪽의 언덕, 석공의 우둔함을 기억하느냐?$b$b이제 번개 창을 이용해 특별한 것을 만들 참인데, 여기선 할 수가 없구나.$b$b석공의 우둔함 꼭대기에서 만나 우리의 승리를 자축하자꾸나!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'ptBR', 'A Coroa do Paraíso', 'Encontre-se com Wrathion no topo da Loucura do Alvanel, que fica na Escadaria Oculta, e receba sua recompensa.', 'Você se lembra da Loucura do Alvanel, o morro a leste daqui onde nos encontramos antes?$B$BVou usar a Lança de Raio para criar algo especial, mas não dá pra fazer isso aqui.$B$BEncontre-me no topo da Loucura do Alvanel para que possamos celebrar nossas vitórias juntos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'ruRU', 'Корона Небес', 'Встретьтесь с Гневионом на вершине Причуды Камнетеса на Сокрытой лестнице и получите свою награду.', 'Помнишь Причуду Камнетеса, холм к востоку отсюда, где мы с тобой уже встречались?$b$bЯ собираюсь использовать Копье молний и создать нечто особое, но здесь я это, конечно, сделать не смогу.$b$bВстретимся на вершине Причуды Камнетеса и мы вместе отпразднуем наши победы!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'zhCN', '苍穹之冠', '与拉希奥在雾纱栈道的石匠之愚碰面，并领取奖赏。', '你还记得石匠之愚吗？就在东边的山顶上，我们曾在那里见过面。$b$b我要用这把闪电长枪创造一样特别的东西，当然不能在这进行了。$b$b去石匠之愚与我会面，让我们共同欢庆胜利！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32595, 'zhTW', '天堂之冠', '到朦朧天梯的巧匠之愚頂端與怒西昂碰面，並領取獎賞。', '還記得你我之前碰面的巧匠之愚嗎?就是東方的那個山丘頂上。$b$b我要使用閃電長槍來製造一個特別的東西，肯定沒辦法在這裡完成。$b$b來巧匠之愚的頂端和我碰面，我們也許能一同慶祝勝利!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'deDE', 'Echos der Titanen', 'Sammelt 12 Runensteine der Titanen von Schlachtzugsbossen in den Hallen der Fleischformer und der Spitze der Stürme.', 'Langsam sehe ich das große Ganze und was die Mogu mit den Titanen zu tun haben. Aber jede Antwort wirft nur weitere Fragen auf!$b$bDie einzige Möglichkeit, dieses Rätsel zu lösen, ist es, im Palast des Donnerkönigs so viele Informationsschnipsel wie möglich über die Titanen zu sammeln. Vernichtet den Donnerkönig und seine Diener und sammelt so viele Runensteine der Titanen, wie Ihr nur könnt!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'esES', 'El eco de los titanes', 'Reúne 12 piedras rúnicas de los titanes de jefes de bandas en las Cámaras de Modelado de Carne y en el Pináculo de las Tormentas.', 'Ahora empiezo a enterarme de qué va todo y qué tienen que ver los mogu con los titanes, ¡pero cada respuesta lleva a más preguntas!$b$bEl único modo de resolver este rompecabezas es encontrar cada retazo de información de los titanes que se halle en el palacio del Rey del Trueno. ¡Destruye al Rey del Trueno y a sus esbirros y hazte con todas las piedras rúnicas de los titanes que puedas! ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'esMX', 'El eco de los titanes', 'Reúne 12 piedras rúnicas de los titanes de jefes de bandas en las Cámaras de Modelado de Carne y en el Pináculo de las Tormentas.', 'Ahora empiezo a enterarme de qué va todo y qué tienen que ver los mogu con los titanes, ¡pero cada respuesta lleva a más preguntas!$b$bEl único modo de resolver este rompecabezas es encontrar cada retazo de información de los titanes que se halle en el palacio del Rey del Trueno. ¡Destruye al Rey del Trueno y a sus esbirros y hazte con todas las piedras rúnicas de los titanes que puedas! ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'frFR', 'Les échos des titans', 'Récupérez 12 Pierres runiques des titans sur les Boss de raid dans les Salles des Sculpte-chairs et à la Cime des Tempêtes.', 'Je commence à avoir une meilleure vue d’ensemble, maintenant, et à comprendre en quoi les mogu sont liés aux titans. Mais chaque nouvelle réponse apporte son lot de questions !$b$bLa seule façon de résoudre ce rébus est de récupérer dans le palais du roi-tonnerre toutes les informations que nous pourrons trouver sur les titans. Détruisez le roi-tonnerre et ses serviteurs, et récoltez autant de pierres runiques des titans que possible !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'itIT', 'La forza dei Titani', 'Recupera 12 Pietre Runiche Titaniche dai boss da incursione nelle Sale dei Plasmatori di Carne e nel Pinnacolo delle Tempeste.', 'Sto iniziando a comprendere il quadro generale della situazione e il legame tra i Mogu e i Titani. Ma ogni nuova risposta fa nascere altre domande!$b$bL\'unico modo per risolvere questo enigma è trovare degli indizi sui Titani nel palazzo del Re del Tuono. Distruggi il Re del Tuono e i suoi servitori, quindi recupera tutte le Pietre Runiche Titaniche che riesci a trovare!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'koKR', '티탄의 메아리', '살점구체자의 전당과 폭풍의 첨탑에 있는 공격대 우두머리를 쓰러뜨리고 티탄 마법석을 12개 모아야 합니다.', '이제 큰 그림이 보이기 시작하는구나. 모구와 티탄의 관계도 알 것 같고 말이지. 하지만 답을 찾을수록 더 많은 의문이 생기는구나!$b$b이 퍼즐을 맞추려면, 천둥왕의 궁전을 샅샅이 뒤져 티탄에 대한 정보를 긁어모으는 수밖에 없다. 천둥왕과 그 수하들을 처치하고 티탄 마법석을 보이는 대로 가져와라!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'ptBR', 'Ecos dos Titãs', 'Colete 12 Pedras Rúnicas Titânicas de chefes de raides nos Salões da Carne Moldada e no Pináculo das Tempestades.', 'Estou começando a entender a grande questão por trás de tudo, e como os mogus se relacionam aos Titãs. O problema é que cada resposta traz ainda mais perguntas!$B$BA única maneira de montar esse quebra-cabeça é encontrar cada pedacinho de informação dos Titãs que pudermos no palácio do Rei Trovão. Destrua o Rei Trovão e seus lacaios e colete o máximo de pedras rúnicas titânicas que conseguir!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'ruRU', 'Эхо титанов', 'Соберите 12 рунических камней титанов. Их можно добыть с рейдовых боссов в залах Искажения Плоти и на Вершине Бурь.', 'Я теперь начинаю лучше понимать ситуацию – и то, как могу связаны с титанами. Но каждый ответ вызывает еще больше вопросов!$b$bЕдинственный способ разгадать эту тайну – найти всю информацию о титанах, которая только есть во дворце Властелина Грома. Уничтожь Властелина Грома и его приспешников и собери столько рунических камней титанов, сколько сумеешь!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'zhCN', '泰坦回音', '从修身殿和风暴之巅的团队首领身上取得12块泰坦符文石。', '我开始放眼大局了，并开始了解魔古族与泰坦的关联。但每个答案却衍生出更多的问题！$b$b要想把谜团解开，唯一的办法就是把从雷电之王宫殿里找到的所有关于泰坦的信息碎片都搜集起来。消灭雷电之王和他的爪牙，把能找到的泰坦符文石都拿回来！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32596, 'zhTW', '泰坦的回聲', '從血肉塑形大廳與風暴之巔的團隊首領身上收集12個泰坦符石。', '現在我漸漸看出事情的全貌，以及魔古與泰坦的關係。然而每個答案都衍生出更多的問題!$b$b解開謎題的唯一辦法就是從雷王的宮殿找出每一條關於泰坦的線索。殺掉雷王和他的爪牙，並盡可能收集泰坦符石!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'deDE', 'Herz des Donnerkönigs', 'Tötet den Donnerkönig in den Hallen des Donners und nehmt sein Herz an Euch.', 'Der Donnerkönig. Seit zwölftausend Jahren tot, nur damit die Stimme der Zandalari ihn wieder zum Leben erwecken kann. Ohne Zweifel hat die Macht der Titanen seinen abstoßenden Leichnam konserviert.$b$bEine Macht, die von Rechts wegen mir gebührt.$b$b$n! Bringt mir das Herz des Donnerkönigs. Es wird mir gehören, und sobald ich es habe, wird ihn niemand je wieder zum Leben erwecken können.$b$bBringt es mir, $C. Bringt mir sein kaltes, totes Herz!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'esES', 'El corazón del Rey del Trueno', 'Mata al Rey del Trueno en las Cámaras del Trueno y recoge el corazón del Rey del Trueno.', 'El Rey del Trueno. Muerto durante doce mil años. ¿Y para qué? Para que los Zandalari lo resuciten. Sin duda, su infame cadáver se ha conservado gracias al poder de los titanes.$b$bUn poder que me pertenece por derecho.$b$b¡$n! Tráeme el corazón del Rey del Trueno. Yo me lo quedaré, y así nadie podrá volver a resucitarlo.$b$bTráemelo, $c.$b$b¡Tráeme su corazón frío y muerto!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'esMX', 'El corazón del Rey del Trueno', 'Mata al Rey del Trueno en las Cámaras del Trueno y recoge el corazón del Rey del Trueno.', 'El Rey del Trueno. Muerto durante doce mil años y ahora la voz Zandalari lo resucitó. Sin duda, su infame cadáver se conservó gracias al poder de los titanes.$b$bUn poder que me pertenece por derecho.$b$b¡$n, tráeme el corazón del Rey del Trueno! Yo me lo quedaré y así nadie podrá volver a resucitarlo.$b$bTráemelo, $c.¡Tráeme su corazón frío y muerto! ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'frFR', 'Le cœur du roi-tonnerre', 'Tuez le Roi-tonnerre dans les Salles du Tonnerre et récupérez le Cœur du roi-tonnerre.', 'Le roi-tonnerre. Mort depuis douze mille ans, ramené à la vie par les voix zandalari. C’est sans nul doute la puissance des titans qui a préservé son corps maléfique.$b$bUn pouvoir qui me revient de droit.$b$b$n ! Rapportez-moi le cœur du roi-tonnerre. Une fois qu’il sera en ma possession, personne ne pourra plus jamais le ressusciter.$b$bApportez-le-moi, $c. Apportez-moi son cœur froid !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'itIT', 'Il cuore del Re del Tuono', 'Uccidi il Re del Tuono nella Sala del Tuono e ottieni il Cuore del Re del Tuono.', 'Il Re del Tuono. Per dodicimila anni è rimasto fuori dai giochi, per poi essere riportato in vita dalla voce degli Zandalari. Senza ombra di dubbio è stato il potere dei Titani a preservare il suo vile corpo.$b$bUn potere che è mio di diritto.$b$b$n! Portami il cuore del Re del Tuono e, una volta che sarà nelle mie mani, nessuno sarà mai più in grado di resuscitare il tiranno.$b$bPortamelo, $C. Portami il suo freddo cuore privo di vita! ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'koKR', '천둥왕의 심장', '폭풍의 첨탑에 있는 천둥왕을 처치하고 천둥왕의 심장을 손에 넣어야 합니다.', '천둥왕... 만이천 년 전에 죽은 몸이면서 잔달라의 목소리에 깨어났지. 티탄의 힘 덕에 시신이 보존될 수 있었던 것이 분명하다.$b$b내것이어야 마땅한 티탄의 힘 말이지.$b$b$n! 천둥왕의 심장을 가져다 다오. 심장은 내 차지가 될 것이다. 그러고 나면, 누구도 다시는 천둥왕을 되살리지 못할 거야.$b$b심장을 가져와라, $c. 차갑게 굳어진 심장을!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'ptBR', 'Coração do Rei Trovão', 'Mate o Rei Trovão nos Salões Trovejantes e pegue o Coração do Rei Trovão.', 'O Rei Trovão. Morto por doze mil anos e trazido de volta pelas vozes Zandalari. Sem dúvida o poder titânico preservou seu cadáver.$B$BO poder titânico que é meu por direito.$B$B$n! Traga para mim o coração do Rei Trovão. Quando eu puser as mãos nele, ele será meu e ninguém mais poderá ressuscitá-lo.$B$BVá buscá-lo para mim, $c. Traga-me o coração morto e gelado dele! ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'ruRU', 'Сердце Властелина Грома', 'Убейте Властелина Грома и заберите сердце Властелина Грома.', 'Властелин Грома уже двенадцать тысяч лет как мертв, но зов зандаларов вернул его. Без сомнения, сила титанов сохранила его мерзкий труп.$b$bСила титанов, которая принадлежит мне по праву.$b$b$n! Принеси мне сердце Властелина Грома. Когда оно станет моим, никто не сможет возродить его снова.$b$bПринеси его мне, |3-6($c). Принеси мне его холодное, мертвое сердце!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'zhCN', '雷神之核', '在雷电王座内殿里消灭雷电之王，并取得雷神之核。', '已死去一万两千年之久的雷电之王，却被赞达拉的声音唤了回来。无疑是泰坦之力使他邪恶的尸身恒久不腐。$b$b泰坦之力应该归我所有。$b$b$n！把雷神之核给我带回来。一旦我得到了它，谁也别想再让他起死回生。$b$b把它带来交给我，$c。把他冰冷的核心交给我！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32597, 'zhTW', '雷王之心', '殺死雷霆大廳裡的雷王並取出雷王的心臟。', '死了一萬兩千年的雷王，在贊達拉的召喚下回到人世。肯定是泰坦的力量保存了他的邪惡之軀。$b$b照道理來說，泰坦的力量應該是屬於我的。$b$b$n!把雷王之心帶回來給我。它是我的。一旦它屬於我，就再也沒人能使雷王復活。$b$b把它帶回來給我，$c。我要雷王那顆冰涼、不再跳動的心臟!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'deDE', 'Eine Abrechnung', 'Sprecht mit Furorion im oberen Stock der Taverne im Nebel an der Verhüllten Treppe.', 'Der Donnerkönig ist tot, und seine Werkzeuge der Unterdrückung werden von der Allianz und der Horde für deren eigene Zwecke ausgeschlachtet.$b$bWir kommen nun zum Höhepunkt der Pandarenkampagne. Höllschreis Intrigen haben den Zorn der Allianz auf ihn gezogen – aber was noch wichtiger ist: Er hat die Einheit der Horde zerschlagen. Wie die Pandaren sagen: \"Der Wok ist heiß.\" Es braut sich etwas zusammen, und alle unsere Anstrengungen hängen davon ab.$b$bIch brauche Zeit, um Pläne zu schmieden. Sprecht mit mir, sobald Eure Fraktion bereit ist, zu handeln!', '', '', '', '', '', 'Sprecht mit Furorion im oberen Stock der Taverne im Nebel an der Verhüllten Treppe.', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'esES', 'Un reconocimiento', 'Habla con Wrathion en el piso de arriba de la Taberna en la Niebla, en la Escalera Velada.', 'El Rey del Trueno ha muerto, y la Horda y la Alianza están reuniendo sus pertrechos de opresión para sus propios fines.$b$bLlegamos en el apogeo de la campaña pandaren. Las maquinaciones de Grito Infernal han provocado que la Alianza se le abalance encima, pero lo más importante es que ha roto la unidad de la Horda. Como dicen los pandaren: \"Ten cuidado con la sartén, pues quemarte puedes en un santiamén\". Algo está a punto de ocurrir; todo nuestro esfuerzo ha sido para este momento.$b$bNecesito tiempo para prepararme. ¡Ven a verme cuando tu facción esté lista para avanzar!', '', '', '', '', '', 'Habla con Wrathion en el piso de arriba de la Taberna en la Niebla, en la Escalera Velada.', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'esMX', 'Un reconocimiento', 'Habla con Wrathion en el piso de arriba de la Taberna en la Niebla, en la Escalera Velada.', 'El Rey del Trueno ha muerto, y la Horda y la Alianza están reuniendo sus pertrechos de opresión para sus propios fines.$b$bLlegamos en el apogeo de la campaña pandaren. Las maquinaciones de Grito Infernal han provocado que la Alianza se le abalance encima, pero lo más importante es que ha roto la unidad de la Horda. Como dicen los pandaren: \"Ten cuidado con la sartén, pues quemarte puedes en un santiamén\". Algo está a punto de ocurrir; todo nuestro esfuerzo ha sido para este momento.$b$bNecesito tiempo para prepararme. ¡Ven a verme cuando tu facción esté lista para avanzar!', '', '', '', '', '', 'Habla con Wrathion en el piso de arriba de la Taberna en la Niebla, en la Escalera Velada.', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'frFR', 'Un jugement', 'Parlez à Irion à l’étage de la Taverne dans les Brumes, à l’Escalier Dérobé.', 'Le roi-tonnerre est mort et l’Alliance s’empare tout comme la Horde de ses instruments d’oppression pour servir ses propres desseins.$b$bNous en sommes à l’apogée de la campagne de Pandarie. Par ses machinations, Hurlenfer s’est attiré la colère de l’Alliance, mais plus important encore, il a rompu l’unité de la Horde. Comme disent les Pandarens : « Le wok est chaud. » Quelque chose est sur le point de céder, et tout notre travail se résume à ça.$b$bJ’ai besoin de temps pour élaborer un plan. Venez me voir dès que votre faction sera prête à agir !', '', '', '', '', '', 'Parlez à Irion à l’étage de la Taverne dans les Brumes, à l’Escalier Dérobé.', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'itIT', 'Regolamento di conti', 'Parla con Irathion al piano superiore della Taverna nelle Nebbie al Passo Velato.', 'Il Re del Tuono è morto e i suoi strumenti di oppressione stanno cadendo nelle mani dell\'Alleanza e dell\'Orda.$b$bStiamo giungendo al culmine della campagna di Pandaria. Le macchinazioni di Malogrido hanno spinto l\'Alleanza alle sue calcagna, ma soprattutto hanno incrinato l\'unità dell\'Orda. Come i Pandaren sono soliti dire: \"Il Wok è caldo\". Sta per succedere qualcosa, e tutto il nostro lavoro ci ha portati a questo momento.$b$bHo bisogno di tempo per organizzare un piano. Vieni da me quando la tua fazione sarà pronta a fare la sua mossa!', '', '', '', '', '', 'Parla con Irathion al piano superiore della Taverna nelle Nebbie al Passo Velato.', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'koKR', '징벌', '장막의 계단, 안개 속의 주점 2층에 있는 래시온과 대화해야 합니다.', '천둥왕은 죽었고, 그가 압제에 사용하던 수단을 얼라이언스와 호드가 나름의 꿍꿍이로 손에 넣고 있다.$b$b이제 판다리아 점령전이 절정에 이르렀구나. 헬스크림의 책략으로 얼라이언스가 큰 타격을 입었으나, 한편으로는 호드의 단결력에 금이 갔지. 판다렌 속담에 \"무쇠솥은 뜨겁다\"라는 말이 있지. 뭔가가 무너지려 하고 있고, 우리가 애를 쓴 것도 모두 그 때문이다.$b$b나는 계획할 시간이 필요하다. 너희 진영이 움직일 준비가 되면 내게 오거라!', '', '', '', '', '', '장막의 계단, 안개 속의 주점 2층에 있는 래시온과 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'ptBR', 'Um ajuste de contas', 'Fale com Wrathion no andar de cima, na Taberna nas Nuvens, que fica na Escadaria Oculta.', 'O Rei Trovão está morto, e agora seus instrumentos de opressão estão sendo usados pela Aliança e pela Horda.$B$BChegamos ao clímax da campanha pandariana. As maquinações de Grito Infernal provocaram a Aliança a querer sua cabeça — e ainda mais importante, ele partiu a unidade da Horda. Como dizem os pandarens, \"a chapa tá quente\". Algo está prestes a acontecer, e foi para isso que tivemos todo esse trabalho.$B$BPreciso de tempo para planejar. Venha me encontrar quando sua facção estiver pronta pra dar o próximo passo!', '', '', '', '', '', 'Fale com Wrathion no andar de cima, na Taberna nas Nuvens, que fica na Escadaria Oculta.', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'ruRU', 'Расплата', 'Поговорите с Гневионом на верхнем этаже таверны \"В туманах\" на Сокрытой лестнице.', 'Властелин Грома мертв, и теперь Альянс и Орда используют в своих целях инструменты, с помощью которых он подавлял народ.$b$bМы приближаемся к кульминации пандаренской кампании. Интриги Адского Крика заставили Альянс выступить против него – но самое главное, он вызвал раздробленность в рядах Орды. Как говорят пандарены, \"лапша готова\". Все балансирует на грани взрыва, и вся наша работа была проделана ради этого.$b$bМне нужно время, чтобы составить план. Приходи ко мне, как только твоя фракция будет готова выступить!', '', '', '', '', '', 'Поговорите с Гневионом на верхнем этаже таверны \"В туманах\" на Сокрытой лестнице.', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'zhCN', '清算', '在雾纱栈道的迷雾酒肆楼上与拉希奥交谈。', '雷电之王死了，他用于镇压的工具已由联盟和部落夺取，以实现自己的目的。$b$b我们现在已来到熊猫人战役的巅峰。地狱咆哮的诡计已使联盟无力应对——但更为重要的是，他还使部落变得分崩离析。正如熊猫人所说：“打铁趁热。”必须采取行动了，我们所有的努力也都是为了这个。$b$b我需要时间来制定计划。等你的阵营做好准备就来见我！', '', '', '', '', '', '去雾纱栈道迷雾酒肆的楼上找拉希奥谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (32598, 'zhTW', '斟酌下一步', '回到朦朧天梯的霧隱客棧的樓上向怒西昂回報。', '雷王死了，他用來壓迫人民的工具落入了聯盟與部落的手中。$b$b潘達利亞的戰役來到了白熱化的階段。地獄吼的陰謀惹火了聯盟，他們要把帳都算在他頭上。然而更重要的是，他造成了部落的分裂。熊貓人有句成語說:『打鐵趁熱』。我知道有一件大事就要發生了，而我們的所有工作都已為這件事鋪好了路。$b$b我需要時間好好計畫。等你的陣營準備好進行下一步時便來見我!', '', '', '', '', '', '回到朦朧天梯的霧隱客棧的樓上向怒西昂回報。', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'deDE', 'Zukunftssicherung', 'Besiegt den Donnerkönig in der Spitze der Stürme und benutzt Antonidas\' Stab, um seiner Estrade die restliche Macht zu entziehen.', 'Man sagt, dass die Macht des Donnerkönigs direkt aus einer verborgenen Quelle in seinem Palast stammt.$b$bSelbst wenn er besiegt wird, könnte ein neuer Tyrann, vielleicht Garrosh, diese Magie für schreckliche Zwecke nutzen.$b$bAllerdings nur, wenn wir ihm nicht zuvorkommen!$b$bBringt meinen Stab in die Spitze der Stürme. Wenn Lei Shen besiegt ist, stellt meinen Stab in die Mitte seiner Estrade.$b$bWir werden den Rest seiner Macht entziehen und sicherstellen, dass sie in unseren Händen bleibt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'esES', 'Un futuro seguro', 'Derrota al Rey del Trueno en el Pináculo de las Tormentas y usa el bastón de Antonidas para drenar el poder que le queda de su estrado.', 'Corre el rumor de que el poder del Rey del Trueno viene directamente de una fuente oculta en su palacio.$b$bIncluso cuando lo derrotan, un nuevo tirano, Garrosh quizás, podría utilizar su magia con fines horribles...$b$b¡A menos que actuemos nosotros primero!$b$bLleva mi bastón al Pináculo de las Tormentas. Cuando Lei Shen haya muerto, coloca el bastón en el centro de su estrado.$b$bVamos a absorberle el poder que le queda y, así, asegurarnos de que permanece en nuestras manos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'esMX', 'Un futuro seguro', 'Derrota al Rey del Trueno en el Pináculo de las Tormentas y usa el bastón de Antonidas para drenar el poder que le queda de su estrado.', 'Corre el rumor de que el poder del Rey del Trueno viene directamente de una fuente oculta en su palacio.$b$bIncluso cuando lo derrotan, un nuevo tirano, Garrosh quizás, podría utilizar su magia con fines horribles...$b$b¡A menos que actuemos nosotros primero!$b$bLleva mi bastón al Pináculo de las Tormentas. Cuando Lei Shen haya muerto, coloca el bastón en el centro de su estrado.$b$bVamos a absorberle el poder que le queda y, así, asegurarnos de que permanece en nuestras manos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'frFR', 'Assurer l’avenir', 'Terrassez le Roi-tonnerre à la Cime des Tempêtes, et utilisez le Bâton d’Antonidas pour drainer le reste de sa puissance de son estrade.', 'On raconte que le pouvoir du roi-tonnerre provient directement d’une source dissimulée dans son palais.$b$bMême s’il est vaincu, un nouveau tyran, Garrosh par exemple, pourrait user de cette magie à des fins terrifiantes…$b$bÀ moins que nous n’agissions les premiers !$b$bEmportez mon bâton à la cime des Tempêtes. Une fois que Lei Shen aura été vaincu, placez mon bâton au centre de son estrade.$b$bNous drainerons le reste de son pouvoir, afin de nous assurer qu’il demeure entre nos mains.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'itIT', 'Assicurarsi un futuro', 'Sconfiggi il Re del Tuono al Pinnacolo delle Tempeste e usa il Bastone di Antonidas per assorbire il potere rimasto dal suo podio.', 'Si dice che il potere del Re del Tuono derivi direttamente da una sorgente nascosta all\'interno del suo palazzo.$b$bAnche se venisse sconfitto, un nuovo tiranno come Garrosh potrebbe utilizzare questa magia e usarla per i suoi terribili scopi.$b$bA meno che noi non facciamo la prima mossa!$b$bPorta il mio bastone al Pinnacolo delle Tempeste. Una volta che Lei Shen sarà stato sconfitto, posiziona il bastone al centro del suo podio.$b$bAssorbiremo il resto del suo potere e faremo in modo che rimanga nelle nostre mani.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'koKR', '미래 확보하기', '폭풍의 첨탑에서 천둥왕을 쓰러뜨리고 안토니다스의 지팡이를 사용해 그의 연단에서 남은 힘을 흡수해야 합니다.', '천둥왕의 힘은 그 궁전에 숨겨져 있는 원천에서 나온다는 소문이 있습니다.$b$b천둥왕이 쓰러진다 해도, 새로운 폭군이 그 마법을 이용해 끔찍한 짓을 저지를 수도 있어요. 가로쉬가 특히 위험하죠.$b$b하지만 우리가 먼저 움직인다면 걱정 없어요!$b$b내 지팡이를 폭풍의 첨탑으로 가져가세요. 레이 션을 쓰러뜨린 후에 내 지팡이를 그의 연단 한가운데에 놓으세요.$b$b놈의 힘을 마저 흡수해서 우리 손에 넣어야 해요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'ptBR', 'Garantindo um futuro', 'Derrote o Rei Trovão no Pináculo das Tempestades e use o Cajado de Antônidas para drenar o restante do poder da tribuna.', 'Rumores dizem que o poder do Rei Trovão vem diretamente de uma fonte oculta em algum lugar do palácio.$B$BMesmo derrotado, um novo tirano, como Garrosh, por exemplo, ainda poderia usar sua magia para fins terríveis.$B$BLeve meu cajado até o Pináculo das Tempestades. Quando Lei Shen for derrotado, coloque meu cajado no centro da tribuna dele.$B$BDrenando o resto do poder, garantiremos que ele fique em nossas mãos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'ruRU', 'Защита будущего', 'Уничтожьте Властелина Грома на Вершине Бурь и используйте посох Антонидаса, чтобы вытянуть остаточную силу из его помоста.', 'Ходят слухи, что Властелин Грома черпает силу прямо из скрытого источника, находящегося в его дворце.$b$bДаже если он будет побежден, новый тиран, возможно, Гаррош, все равно сможет использовать эту силу для достижения ужасных целей.$b$bНам нужно нанести удар первыми!$b$bВозьми мой посох на Вершину Бурь. После победы над Лэй Шэнем помести мой посох в центр его помоста.$b$bМы вытянем остатки его силы, чтобы она навсегда осталась в наших руках.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'zhCN', '开创未来', '在风暴之巅击败雷电之王，然后用安东尼达斯之杖从他的平台中汲取剩余力量。', '有传闻说，雷电之王的力量是直接来自他宫殿内的某个秘密源头。$b$b即便在他被击败后，新的暴君，比如说加尔鲁什，也能凭借这股魔法酿成悲剧。$b$b除非我们先发制人！$b$b带上我的法杖，进入风暴之巅。击败雷神后，将我的法杖插进他平台的正中央。$b$b我们要将他剩下的力量汲取出来，确保不落在他人手中。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32599, 'zhTW', '拯救未來', '在風暴之巔擊敗雷王，並使用安東尼達斯之杖從高臺上吸取他剩餘的能量。', '謠傳說雷王的力量源自於宮殿底下的一處秘密來源。$b$b就算擊敗了他，下一位暴君，說不定就是卡爾洛斯，也可能會利用這股魔力來進行暴虐統治。$b$b因此我們得搶先一步！$b$b帶著我的法杖進入風暴之巔。一旦擊敗雷神，便將我的法杖插入高臺中心。$b$b我們要吸取他剩餘的能量，以確保為我們所用。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'deDE', 'Die Zandalariprophezeiung', 'Bringt \"Die Zandalariprophezeiung\" zu Lehrensucher Cho.', '$p,$b$bich habe Eure Berichte über die Zandalari gelesen und glaube, dass ich ein paar Hinweise in Bezug auf ihre neuesten Übergriffe habe.$b$bWenn Ihr uns im Heim des Wissens im Tal der Ewigen Blüten besuchen kommt, können wir diese Informationen Eurer Sammlung hinzufügen.$b$bVielen Dank für Eure Mühen!$b$bHerzliche Grüße,$b$bLehrensucher Cho', '', '', '', '', '', 'Sprecht mit Lehrensucher Cho im Heim des Wissens im Tal der Ewigen Blüten.', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'esES', 'La profecía Zandalari', 'Lleva la profecía Zandalari al eremita Cho.', '$GQuerido:Querida; $p:$b$bHe leído tu investigación sobre los Zandalari y creo que tengo algunas pistas sobre su ataque más reciente.$b$bSi vienes a vernos al Trono del Saber, en el Valle de la Flor Eterna, podremos añadir esta contribución a tu colección personal.$b$b¡Gracias por tu diligencia!$b$bAtentamente,$b$bEremita Cho.', '', '', '', '', '', 'Habla con: Eremita Cho. Zona: Trono del Saber, Valle de la Flor Eterna.', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'esMX', 'La profecía Zandalari', 'Lleva la profecía Zandalari al eremita Cho.', '$GQuerido:Querida; $p:$b$bHe leído tu investigación sobre los Zandalari y creo que tengo algunas pistas sobre su ataque más reciente.$b$bSi vienes a vernos al Trono del Saber, en el Valle de la Flor Eterna, podremos añadir esta contribución a tu colección personal.$b$b¡Gracias por tu diligencia!$b$bAtentamente,$b$bEremita Cho.', '', '', '', '', '', 'Habla con: Eremita Cho. Zona: Trono del Saber, Valle de la Flor Eterna.', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'frFR', 'La prophétie zandalari', 'Apportez La prophétie zandalari au Chroniqueur Cho.', '$gCher:Chère; $p,$b$bJ’ai lu vos rapports sur les Zandalari et je crois avoir des informations sur leur plus récente attaque.$b$bVenez donc nous voir au siège de la Connaissance dans le val de l’Éternel printemps ! Nous pourrons ajouter cette contribution à votre collection personnelle.$b$bMerci pour votre diligence !$b$bCordialement,$b$bChroniqueur Cho', '', '', '', '', '', 'Parlez au Chroniqueur Cho au Siège de la Connaissance, dans le Val de l’Éternel printemps.', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'itIT', 'La profezia degli Zandalari', 'Porta la Profezia degli Zandalari al Ramingo della Sapienza Cho.', '$gCaro:Cara; $p,$b$bho letto i tuoi rapporti sugli Zandalari e credo di essermi fatto un\'idea sulle motivazioni dei loro recenti attacchi.$b$bQuando ci farai visita all\'Archivio della Conoscenza nella Vallata dell\'Eterna Primavera, saremo felici di aggiungere questo contributo alla tua collezione personale.$b$bGrazie ancora per la tua abnegazione!$b$bCon affetto,$b$bRamingo della Sapienza Cho', '', '', '', '', '', 'Parla con il Ramingo della Sapienza Cho all\'Archivio della Conoscenza nella Vallata dell\'Eterna Primavera.', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'koKR', '잔달라의 예언', '전승지기 초에게 잔달라의 예언을 가져가야 합니다.', '친애하는 $p|1이여;여;,$b$b자네가 수집한 잔달라 관련 자료를 읽고, 그들의 최근의 공격 행위에 대해 알아낸 것이 좀 있다네.$b$b영원꽃 골짜기의 지식의 연단에 들러준다면 그 자료를 자네의 개인 장서에 추가해 주겠네.$b$b자네의 노고에 감사하네!$b$b자네의 친구,$b$b전승지기 초', '', '', '', '', '', '영원꽃 골짜기의 지식의 연단에 있는 전승지기 초와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'ptBR', 'A profecia zandalari', 'Leve a Profecia Zandalari para o Andarilho das Lendas Cho.', 'Car$Go:a; $p,$B$BLi seu relatório sobre os Zandalari e acredito ter algumas pistas acerca da última agressão deles.$B$BSe você nos visitar no Trono do Conhecimento, no Vale das Flores Eternas, podemos acrescentar mais essa contribuição à sua coleção particular.$B$BFicamos gratos pela sua presteza!$B$BCom estima,$B$BAndarilho das Lendas Cho', '', '', '', '', '', 'Fale com o Andarilho das Lendas Cho no Trono do Conhecimento, no Vale das Flores Eternas.', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'ruRU', 'Пророчество зандаларов', 'Принесите хранителю истории Чо книгу \"Пророчество Зандаларов\".', '$gДорогой:Дорогая; $p!$b$bМы прочитали твои заметки о зандаларах и, кажется, я понимаю, чем вызвана их нынешняя агрессия.$b$bЕсли ты посетишь Обитель Мудрости в Вечноцветущем доле, мы сможем внести эту работу в твою личную коллекцию.$b$bБлагодарим за приложенные усилия!$b$bС уважением,$b$bХранитель истории Чо', '', '', '', '', '', 'Поговорите с хранителем истории Чо в Обители Мудрости, что в Вечноцветущем доле.', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'zhCN', '赞达拉预言书', '将赞达拉预言书带给游学者周卓。', '亲爱的$p，$b$b我已经读过了你关于赞达拉巨魔的报告，并确信我已经拼凑出他们近期进攻的线索。$b$b如果你能到锦绣谷的闻道之座来一趟，我们就可以将这一成果加入你的私人收藏。$b$b感谢你的努力！$b$b此致，$b$b游学者周卓', '', '', '', '', '', '去锦绣谷的闻道之座和游学者周卓谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (32600, 'zhTW', '贊達拉預言', '把贊達拉預言交給博學行者阿洲。', '親愛的$p，$b$b我已讀過你對贊達拉做的相關研究，我相信對於他們最近的大規模攻擊已經找出幾條線索。$b$b如果你到恆春谷的知識之座來找我們，我們會把這項成果加到你的個人收藏中。$b$b感謝你的努力！$b$b誠摯的，$b$b博學行者阿洲', '', '', '', '', '', '到恆春谷的知識之座找博學行者阿洲。', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'deDE', 'Donnergrollen', 'Bringt \"Donnergrollen\" zu Lehrensucher Cho.', '$p,$b$bdas bedrohliche Geräusch des Donners hat mein Volk über Generationen hinweg verfolgt. Und wie Ihr selbst herausgefunden habt, ist Lei Shen der Ursprung dieser Angst.$b$bWenn Ihr uns im Heim des Wissens im Tal der Ewigen Blüten besuchen kommt, können wir diese Informationen Eurer Sammlung hinzufügen.$b$bVielen Dank für Eure Mühen!$b$bHerzliche Grüße,$b$bLehrensucher Cho', '', '', '', '', '', 'Sprecht mit Lehrensucher Cho im Heim des Wissens im Tal der Ewigen Blüten.', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'esES', 'Rumores de tormenta', 'Lleva los rumores de tormenta al eremita Cho.', '$GQuerido:Querida; $p:$b$bEl ominoso sonido del trueno ha atormentado a mi pueblo durante generaciones y, como has descubierto en tus investigaciones, Lei Shen es el responsable de ese miedo.$b$bSi vienes a vernos al Trono del Saber, en el Valle de la Flor Eterna, podremos añadir esta contribución a tu colección personal.$b$b¡Gracias por tu diligencia!$b$bAtentamente,$b$bEremita Cho.', '', '', '', '', '', 'Habla con: Eremita Cho. Zona: Trono del Saber, Valle de la Flor Eterna.', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'esMX', 'Rumores de tormenta', 'Lleva los rumores de tormenta al eremita Cho.', '$GQuerido:Querida; $p:$b$bEl ominoso sonido del trueno ha atormentado a mi pueblo durante generaciones y, como has descubierto en tus investigaciones, Lei Shen es el responsable de ese miedo.$b$bSi vienes a vernos al Trono del Saber, en el Valle de la Flor Eterna, podremos añadir esta contribución a tu colección personal.$b$b¡Gracias por tu diligencia!$b$bAtentamente,$b$bEremita Cho.', '', '', '', '', '', 'Habla con: Eremita Cho. Zona: Trono del Saber, Valle de la Flor Eterna.', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'frFR', 'Le roulement du tonnerre', 'Apportez Le roulement du tonnerre au Chroniqueur Cho.', '$gCher:Chère; $p,$b$bLe son menaçant du tonnerre hante mon peuple depuis des générations. Et comme vous l’avez découvert avec vos recherches, Lei Shen est au cœur de cette peur.$b$bVenez donc nous voir au siège de la Connaissance dans le val de l’Éternel printemps ! Nous pourrons ajouter cette contribution à votre collection personnelle.$b$bMerci pour votre diligence !$b$bCordialement,$b$bChroniqueur Cho', '', '', '', '', '', 'Parlez au Chroniqueur Cho au Siège de la Connaissance, dans le Val de l’Éternel printemps.', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'itIT', 'Rombi di tuono', 'Porta Rombi di Tuono al Ramingo della Sapienza Cho.', '$gCaro:Cara; $p,$b$bil terribile rombo del tuono ha perseguitato la mia gente per generazioni e, come hai potuto scoprire nelle tue ricerche, Lei Shen è la fonte di quella paura.$b$bQuando ci farai visita all\'Archivio della Conoscenza nella Vallata dell\'Eterna Primavera, saremo felici di aggiungere questo contributo alla tua collezione personale.$b$bGrazie ancora per la tua abnegazione!$b$bCon affetto,$b$bRamingo della Sapienza Cho', '', '', '', '', '', 'Parla con il Ramingo della Sapienza Cho all\'Archivio della Conoscenza nella Vallata dell\'Eterna Primavera.', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'koKR', '우르릉거리는 천둥', '전승지기 초에게 우르릉거리는 천둥을 가져가야 합니다.', '친애하는 $p|1이여;여;,$b$b우리 민족은 몇 세대에 걸쳐 불길한 천둥 소리에 벌벌 떨었지. 자네도 알게 되었듯이, 레이 션이 그 두려움의 중심에 있다네.$b$b영원꽃 골짜기의 지식의 연단에 들러준다면 그 자료를 자네의 개인 장서에 추가해 주겠네.$b$b자네의 노고에 감사하네!$b$b자네의 친구,$b$b전승지기 초', '', '', '', '', '', '영원꽃 골짜기의 지식의 연단에 있는 전승지기 초와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'ptBR', 'Ribombar do Trovão', 'Leve o Ribombar do Trovão para o Andarilho das Lendas Cho.', 'Car$Go:a; $p,$B$BO agourento som do trovão assombra meu povo há gerações. Como você descobriu em sua pesquisa, Lei Shen está no âmago deste medo.$B$BSe você nos visitar no Trono do Conhecimento, no Vale das Flores Eternas, podemos acrescentar mais essa contribuição à sua coleção particular.$B$BFicamos gratos pela sua presteza!$B$BCom estima,$B$BAndarilho das Lendas Cho', '', '', '', '', '', 'Fale com o Andarilho das Lendas Cho no Trono do Conhecimento, no Vale das Flores Eternas.', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'ruRU', 'Раскаты грома', 'Принесите хранителю истории Чо книгу \"Раскаты грома\".', '$gДорогой:Дорогая; $p!$b$bЗловещие раскаты грома уже давно вселяют страх в мой народ. Теперь, благодаря тебе, мы знаем, что виной всему Лэй Шэнь.$b$bЕсли ты посетишь Обитель Мудрости в Вечноцветущем доле, мы сможем внести эту работу в твою личную коллекцию.$b$bБлагодарим за приложенные усилия!$b$bС уважением,$b$bХранитель истории Чо', '', '', '', '', '', 'Поговорите с хранителем истории Чо в Обители Мудрости, что в Вечноцветущем доле.', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'zhCN', '雷电轰鸣', '将雷电轰鸣带给游学者周卓。', '亲爱的$p，$b$b滚雷的不祥之音令我一代又一代的族人饱受困扰。正如你在自己研究中发现的那样，雷神正是那恐惧的源头所在。$b$b如果你能到锦绣谷的闻道之座来一趟，我们就可以将这一成果加入你的私人收藏。$b$b感谢你的努力！$b$b此致，$b$b游学者周卓', '', '', '', '', '', '去锦绣谷的闻道之座和游学者周卓谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (32601, 'zhTW', '雷聲迴盪', '把雷聲迴盪交給博學行者阿洲。', '親愛的$p，$b$b不祥的雷霆之聲讓我們世代的子民惶惶不安。就如你在研究中所發現，雷神是這股恐懼的源頭。$b$b如果你到恆春谷的知識之座來找我們，我們會把這項成果加到你的個人收藏中。$b$b感謝你的努力！$b$b誠摯的，$b$b博學行者阿洲', '', '', '', '', '', '到恆春谷的知識之座找博學行者阿洲。', 0);
INSERT INTO `quest_template_locale` VALUES (32602, 'deDE', 'Götter und Monster', 'Bringt \"Götter und Monster\" zu Lehrensucher Cho.', '$p,$b$bist es wirklich wahr, dass die Mogu mit den Titanen in Verbindung stehen? Ich kann das kaum glauben. Aber beim Lesen Eurer Nachforschungen nimmt doch etwas der Wahrheit über die Mogu Gestalt an.$b$bWenn Ihr uns im Heim des Wissens im Tal der Ewigen Blüten besuchen kommt, können wir diese Informationen Eurer Sammlung hinzufügen.$b$bVielen Dank für Eure Mühen!$b$bHerzliche Grüße,$b$bLehrensucher Cho', '', '', '', '', '', 'Sprecht mit Lehrensucher Cho im Heim des Wissens im Tal der Ewigen Blüten.', 0);
INSERT INTO `quest_template_locale` VALUES (32602, 'esES', 'Dioses y monstruos', 'Lleva Dioses y monstruos al eremita Cho.', '$GQuerido:Querida; $p:$b$b¿Es cierto que los mogu están relacionados con los titanes? Me cuesta creerlo, pero al revisar tus investigaciones, algunas verdades sobre los mogu empiezan a tomar forma.$b$bSi vienes a vernos al Trono del Saber, en el Valle de la Flor Eterna, podremos añadir esta contribución a tu colección personal.$b$b¡Gracias por tu diligencia!$b$bAtentamente,$b$bEremita Cho.', '', '', '', '', '', 'Habla con: Eremita Cho. Zona: Trono del Saber, Valle de la Flor Eterna.', 0);
