INSERT INTO `quest_template_locale` VALUES (32618, 'deDE', 'Lernt Reiten', 'Findet Randal Weidmann.', 'Es wird Zeit, dass Ihr das Reiten erlernt! Besucht Randal Weidmann im Holzfällerlager des Osttals im Wald von Elwynn. Vergesst nicht, ein Reittier zu erwerben, wenn Ihr dort seid!', '', 'Findet Randal Weidmann und lernt, wie man reitet!', 'Randal Weidmann', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'esES', 'A montar se ha dicho', 'Encuentra a Randal Predatio.', '¡Es hora de que aprendas a montar! Buscar a Randal Predatio en el Aserradero de la Vega del Este, en el Bosque de Elwynn. ¡No te olvides de comprar tu montura cuando llegues!', '', '¡Encuentra a Randal Predatio y aprende a montar!', 'Randal Predatio', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'esMX', 'A montar se ha dicho', 'Encuentra a Randal Predatio.', '¡Es hora de que aprendas a montar! Buscar a Randal Predatio en el Aserradero de la Vega del Este, en el Bosque de Elwynn. ¡No te olvides de comprar tu montura cuando llegues!', '', '¡Encuentra a Randal Predatio y aprende a montar!', 'Randal Predatio', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'frFR', 'Un cavalier débutant', 'Trouvez Randal Hunter.', 'Il est temps que vous appreniez à chevaucher une monture ! Partez à la recherche de Randal Hunter dans le camp de bûcherons du val d’Est, dans la forêt d’Elwynn. N’oubliez pas d’acheter une monture pendant que vous serez là-bas !', '', 'Trouvez Randal Hunter et apprenez à monter !', 'Randal Hunter', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'itIT', 'Imparare a cavalcare', 'Trova Randal Hunter.', 'È ora di imparare a cavalcare! Cerca Randal Hunter al Campo di Taglio di Vallevante nella Foresta di Elwynn. Non dimenticare di acquistare la tua cavalcatura quando sarai lì!', '', 'Trova Randal Hunter e impara a cavalcare!', 'Randal Hunter', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'koKR', '타는 법 배우기', '랜들 헌터를 찾아야 합니다.', '이제 당신도 타는 법을 배워야겠군요! 엘윈 숲에 있는 동쪽계곡 벌목지에서 랜들 헌터를 찾아보십시오. 그곳에 가면 탈것을 사는 걸 잊지 마십시오!', '', '랜들 헌터를 찾아서 타는 법을 배우십시오!', '랜들 헌터', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'ptBR', 'Aprender a montar', 'Encontre Randal Caçador.', 'É hora de aprender a montar! Procure o caçador Randal na Madeireira Vale do Leste, na Floresta de Elwynn. Não se esqueça de comprar sua montaria quando estiver por lá!', '', 'Encontre Randal Caçador e aprenda a montar!', 'Randal Caçador', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'ruRU', 'Уроки верховой езды', 'Найдите Рандала Охотника.', 'Пора тебе научиться верховой езде! Найди Рандала Охотника на лесопилке Восточной долины в Элвиннском лесу. Не забудь купить себе там ездовое животное!', '', 'Найдите Рандала Охотника и научитесь верховой езде!', 'Рандал Охотник', '', '', ' ', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'zhCN', '学习骑乘', '找到兰达尔·亨特。', '你该学习骑术了！到艾尔文森林的东谷伐木场找兰达尔·亨特。到那别忘了买坐骑！', '', '找到兰达尔·亨特并学习如何骑乘坐骑！', '兰达尔·亨特', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32618, 'zhTW', '騎術訓練', '找到蘭達爾‧亨特。', '你學習騎術的時候到了！到艾爾文森林的東谷伐木場找蘭達爾‧亨特。到了那裡別忘了買個坐騎！', '', '去找蘭達爾‧亨特，學會怎麼騎乘坐騎！', '蘭達爾‧亨特', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'deDE', 'Donnerstahl', 'Erkundet die Donnerschmiede, um die Geheimnisse des Donnerstahls zu erfahren.', 'Mit erfahrenem Blick erkennt Ihr, dass dieser Metallblock ein paar sehr ungewöhnliche Eigenschaften hat. Vielleicht sind irgendwo hier in der Nähe Hinweise auf seine wahre Natur?', '', '', '', '', '', 'Bringt die Geheimnisse des Donnerstahls in Erfahrung.', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'esES', 'Acero del relámpago', 'Investiga la Forja del Trueno para encontrar los secretos del acero del relámpago.', 'Tus avezados ojos pueden ver que este lingote de metal tiene propiedades muy extrañas. ¿Y si por aquí hay pistas de su naturaleza?', '', '', '', '', '', 'Encuentra los secretos del acero del relámpago.', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'esMX', 'Acero del relámpago', 'Investiga la Forja del Trueno para encontrar los secretos del acero del relámpago.', 'Tus avezados ojos pueden ver que este lingote de metal tiene propiedades muy extrañas. ¿Y si por aquí hay pistas de su naturaleza?', '', '', '', '', '', 'Encuentra los secretos del acero del relámpago.', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'frFR', 'Le foudracier', 'Fouillez les Forges du tonnerre afin de trouver les Secrets du foudracier.', 'Vous voyez grâce à votre œil exercé que ce lingot de métal a des propriétés très étranges. Peut-être y a-t-il des indices sur sa nature quelque part dans les environs.', '', '', '', '', '', 'Trouvez Les secrets du foudracier.', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'itIT', 'Acciaio Fulminante', 'Ispeziona la Forgia dei Fulmini per trovare i Segreti dell\'Acciaio Fulminante.', '<I tuoi occhi esperti sono in grado di notare che questi lingotti possiedono alcune strane proprietà. Forse troverai qualche indizio sulla loro natura qui attorno.>', '', '', '', '', '', 'Scopri i segreti dell\'Acciaio Fulminante.', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'koKR', '번개 강철', '천둥 제련소를 조사해 번개 강철의 비밀을 찾아야 합니다.', '당신의 훈련된 눈으로 보니, 이 금속 주괴는 매우 특이한 속성을 지니고 있습니다. 이 근처에 그 정체에 대한 실마리가 있을지도 모르겠군요.', '', '', '', '', '', '번개 강철의 비밀을 찾아야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'ptBR', 'Aço Relâmpago', 'Investigue a Forja do Trovão para encontrar os Segredos do Aço Relâmpago.', 'Seus olhos treinados percebem que este lingote de metal tem propriedades muito estranhas. Talvez algo por aqui dê pistas sobre a natureza dele.', '', '', '', '', '', 'Encontre os Segredos do Aço Relâmpago.', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'ruRU', 'Молниеносная сталь', 'Исследуйте Кузню Грома, чтобы найти \"Секреты молниеносной стали\".', 'Ваш наметанный глаз сразу видит, что эта стальная заготовка обладает какими-то очень странными свойствами. Может быть, что-нибудь неподалеку подскажет, в чем тут дело?', '', '', '', '', '', 'Найдите \"Секреты молниеносной стали\".', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'zhCN', '霹雳钢', '调查雷霆熔炉，找到霹雳钢的奥秘。', '你训练有素的双眼能看出这块金属有着某种极其诡异的属性。也许在这附近能找到与它特质相关的蛛丝马迹？', '', '', '', '', '', '找到霹雳钢的奥秘。', 0);
INSERT INTO `quest_template_locale` VALUES (32621, 'zhTW', '雷鋼', '調查雷霆熔爐，找出雷鋼的秘密。', '以你的專業眼光，可以看出這塊金屬具有非常奇異的特徵。也許在這附近有什麼線索可以透露出它的性質。', '', '', '', '', '', '找出雷鋼的秘密。', 0);
