INSERT INTO `quest_template_locale` VALUES (32630, 'deDE', 'Chefingenieur Jards Tagebuch', 'Erkundet die Donnerschmiede, um die Geheimnisse des stabilisierten Blitzes zu erfahren.', 'Was für eine ungewöhnliche Energiequelle. Wie sie wohl funktioniert? Und was könnte man mit ihr erschaffen?', '', '', '', '', '', 'Bringt die Geheimnisse des stabilisierten Blitzes in Erfahrung.', 0);
