INSERT INTO `quest_template_locale` VALUES (32630, 'frFR', 'Le journal de l’ingénieur en chef Jard', 'Fouillez les Forges du tonnerre afin de trouver les Secrets de la foudre stabilisée.', 'Quelle étrange source d’énergie… Vous vous demandez comment elle fonctionne, et ce qu’elle pourrait servir à créer…', '', '', '', '', '', 'Trouvez Les secrets de la foudre stabilisée.', 0);
INSERT INTO `quest_template_locale` VALUES (32630, 'itIT', 'Il Diario del Capo Ingegnere Jard', 'Ispeziona la Forgia dei Fulmini per trovare i Segreti dell\'Acciaio Fulminante.', '<Che strana fonte di potere... Come funziona? E per cosa potrebbe essere utilizzata?>', '', '', '', '', '', 'Trova i Segreti dell\'Acciaio Fulminante.', 0);
INSERT INTO `quest_template_locale` VALUES (32630, 'koKR', '선임기술자 자르드의 일지', '천둥 제련소를 조사해 안정된 번개의 비밀을 찾아야 합니다.', '매우 특이한 동력원입니다. 어떻게 동작하는지, 이것으로 무엇을 만들 수 있는지 궁금하십니까?', '', '', '', '', '', '안정된 번개의 비밀을 찾아야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (32630, 'ptBR', 'Diário do Engenheiro-chefe Jard', 'Investigue as Forjas do Trovão para encontrar os Segredos do Relâmpago Estabilizado.', 'Que fonte de poder singular... Como será que funciona? E o que ela pode criar?', '', '', '', '', '', 'Descubra os Segredos do Relâmpago Estabilizado.', 0);
INSERT INTO `quest_template_locale` VALUES (32630, 'ruRU', 'Дневник главного инженера Цзарда', 'Исследуйте Кузню Грома, чтобы найти \"Секреты стабилизированных молний\".', 'Какой необычный источник энергии. Интересно, как он работает? И что можно с его помощью создать?', '', '', '', '', '', 'Найдите \"Секреты стабилизированных молний\".', 0);
INSERT INTO `quest_template_locale` VALUES (32630, 'zhCN', '首席工程师贾德的日记', '调查雷霆熔炉，找到固化闪电的秘诀。', '多么另类的能量之源啊！不知它是怎么运作的？它又能创造出什么呢？', '', '', '', '', '', '找到固化闪电的秘诀。', 0);
