INSERT INTO `quest_template_locale` VALUES (32632, 'deDE', 'Für den Krieg geschaffen', 'Tötet den Pyrosternvernichter.', 'Manche Dinge werden gebaut, um mehrere Zwecke zu erfüllen. Manche Dinge werden mit nur einem Zweck gebaut: Zerstörung.$b$bEin gigantisches Konstrukt, das man den Pyrosternvernichter nennt, bewacht das Morgensucherkap. Es dient keinem anderen Zweck als der Zerstörung. Es muss zerlegt werden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32632, 'esES', 'Hecho para la guerra', 'Mata al demoledor Pirastral.', 'Algunas cosas se crean con varios propósitos en mente. Otras se crean con el único fin de destruir.$b$bUn gigantesco ensamblaje llamado demoledor Pirastral vigila el Promontorio Buscalba. Su único fin es destruir. Debe ser desmantelado.', '', '', '', '', '', '', 0);
