INSERT INTO `quest_template_locale` VALUES (32632, 'frFR', 'Conçu pour la guerre', 'Tuez le Démolisseur ignétoile.', 'Certaines choses sont construites dans plusieurs buts. D’autres ne voient le jour que pour détruire.$b$bUn énorme assemblage appelé le démolisseur ignétoile monte la garde au promontoire de Cherchelaube. Son seul but est de détruire. Il faut le démanteler.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32632, 'itIT', 'Creato per la guerra', 'Distruggi il Pirodemolitore.', 'Alcune cose vengono costruite per perseguire più scopi, altre invece con il solo scopo di scatenare distruzione.$b$bUn enorme costrutto denominato Pirodemolitore giace a guardia del Promontorio Albafurente. Il suo scopo è unicamente quello di distruggere. Deve essere smantellato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32632, 'koKR', '전쟁을 위해 만들어진', '장작별 파괴자를 처치해야 합니다.', '여러 가지 목적으로 만들어지는 물건이 있는가 하면, 파괴라는 단 한 가지 목적으로 만들어지는 물건도 있지요.$b$b장작별 파괴자라는 거대한 피조물이 돈시커 곶에 버티고 서서 그곳을 지키고 있습니다. 그 목적은 오직 파괴이지요. 피조물을 해체해야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32632, 'ptBR', 'Feito para a guerra', 'Mate o Demolidor Flamestela.', 'Algumas coisas são construídas com múltiplos fins em mente. Algumas coisas são construídas com o único propósito de causar destruição.$b$bUm constructo enorme chamado Demolidor Flamestela guarda o Promontório Buscalva. Seu propósito: somente destruir. Ele precisa ser desmantelado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32632, 'ruRU', 'Создан для войны', 'Уничтожьте разрушитель \"Погребальный огонь\".', 'Некоторые вещи создаются ради множества целей. Некоторые – только ради разрушения.$b$bОгромное создание под названием разрушитель \"Погребальный огонь\" охраняет мыс Искателя Рассвета. Он был создан исключительно ради того, чтобы уничтожать. Его необходимо обезвредить.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32632, 'zhCN', '为战斗而生', '击败葬火之星歼灭者。', '制作者在造就某些东西时，为其赋予了多种用途。而有些东西的唯一用途就是毁灭。$b$b一种名叫葬火之星歼灭者的巨型构造体戍守着寻晨者海角。它的作用就是摧毁。必须将它卸除。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32632, 'zhTW', '戰爭利器', '殺死焰星毀滅者。', '有些東西存在的理由很複雜，有些東西只是為了破壞而生。$b$b一種名叫焰星毀滅者的巨大傀儡鎮守著曦尋者之岬，它的唯一功用就是造成破壞，務必除掉它。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'deDE', 'Bezaubernd', 'Beschafft Euch Rommaths Buch der Zaubersprüche am Morgensucherkap.', 'Großmagister Rommath besitzt etwas, das ich haben will.$b$bEr hat ein sehr mächtiges Buch mitgebracht. Es wäre unserer Sache dienlich, wenn es sich nicht weiterhin in den Händen der Sonnenhäscher befände.$b$bFindet Rommaths Buch der Zaubersprüche und bringt es mir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'esES', 'Encantado', 'Recupera el libro de conjuros de Rommath del Promontorio Buscalba.', 'El gran magister Rommath tiene algo en su posesión que quiero conseguir.$b$bHa traído consigo un tomo muy poderoso. Sería mejor para nuestra causa si no estuviera en manos Atracasol.$b$bEncuentra el libro de conjuros de Rommath y tráemelo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'esMX', 'Encantado', 'Recupera el libro de conjuros de Rommath del Promontorio Buscalba.', 'El gran magister Rommath tiene algo en su posesión que quiero conseguir.$b$bHa traído consigo un tomo muy poderoso. Sería mejor para nuestra causa si no estuviera en manos Atracasol.$b$bEncuentra el libro de conjuros de Rommath y tráemelo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'frFR', 'Sous le charme', 'Récupérez le Livre d’incantations de Rommath au Promontoire de Cherchelaube.', 'Le grand magistère Rommath a en sa possession quelque chose que je veux.$b$bIl a emporté un grimoire très puissant. Il serait préférable pour notre cause qu’il ne reste pas entre les mains des Saccage-Soleil.$b$bTrouvez le livre d’incantations de Rommath et apportez-le-moi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'itIT', 'Vincolato alla magia', 'Recupera il Libro degli Incantesimi di Rommath dal Promontorio Albafurente.', 'Il Gran Magistro Rommath possiede qualcosa che desidero, un libro molto potente.$b$bSe lo togliessimo dalle mani dei Predatori del Sole, sarebbe un gran bel colpo per la nostra causa.$b$bTrova il Libro degli Incantesimi di Rommath e portamelo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'koKR', '마법으로 장정된 책', '돈시커 곶에서 롬매스의 마법서를 손에 넣어야 합니다.', '대마법학자 롬매스가 제가 탐내는 물건을 가지고 있습니다.$b$b그는 아주 강력한 힘을 지닌 고서를 가지고 왔지요. 그것이 선리버의 손에 없는 편이 우리의 대의를 위해 바람직할 것입니다.$b$b롬매스의 마법서를 찾아 제게 가져오십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'ptBR', 'Enfeitiçado', 'Recupere o Livro de Encantamentos de Rommath no Promontório Buscalva.', 'O grão-magíster Rommath possui algo que eu quero.$b$bEle trouxe um tomo muito poderoso consigo. Seria melhor para nossa causa se ele não ficasse mais nas mãos dos Fendessol.$b$bEncontre o livro de encantamentos de Rommath e traga-o para mim.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'ruRU', 'Зачарованная книга', 'Заберите книгу магических формул Роммата с мыса Искателя Рассвета.', 'У Верховного магистра Роммата есть предмет, который очень мне нужен.$b$bУ него с собой могущественный фолиант. Будет лучше, если мы заберем его у Похитителей Солнца.$b$bНайди книгу магических формул Роммата и принеси ее мне.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'zhCN', '魔法束缚', '从寻晨者海角找回罗曼斯的咒术之书。', '大法师罗曼斯手里有件令我向往的东西。$b$b他随身带着一本非常强大的书卷。要是能把它从夺日者手中抢过来，就会对我们大有益处。$b$b找到罗曼斯的咒术之书，把它交给我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32633, 'zhTW', '夢寐以求的寶物', '從曦尋者之岬取回洛摩斯的咒術之書。', '大博學者洛摩斯身上有一件我非常想要的東西。$b$b他隨身帶著一本強大的魔典，如果他失去了這本書，那對我們的戰況會非常有利。$b$b找出洛摩斯的咒術之書並帶回來給我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'deDE', 'Die Verteidigung zerstören', 'Zerstört 6 Schutzzauber der Sonnenhäscher am Morgensucherkap.', 'Selbst die stärkste Verteidigung kann ihre Schwächen haben, $C. Wir müssen nur die Schwäche in der Verteidigung der Sonnenhäscher finden und sie dann so gut wie möglich ausnutzen.$b$bSpäher berichten von Schutzzaubern um das Morgensucherkap herum. Sie sind mächtig, aber nicht tödlich.$b$bWenn wir sie deaktivieren, könnten wir ihre Aktivitäten genauer beobachten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'esES', 'Rompiendo las defensas', 'Destruye 6 resguardos de perímetro Atracasol cerca del Promontorio Buscalba.', 'Hasta la defensa más fuerte puede tener un punto débil, $c. Solo tenemos que encontrar la debilidad de la defensa Atracasol y aprovecharla al máximo.$b$bLos exploradores nos han informado del uso de resguardos de perímetro alrededor del Promontorio Buscalba. Son potentes, pero no letales.$b$bDesactivarlos nos permitiría vigilar más de cerca sus actividades.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'esMX', 'Rompiendo las defensas', 'Destruye 6 resguardos de perímetro Atracasol cerca del Promontorio Buscalba.', 'Hasta la defensa más fuerte puede tener un punto débil, $c. Solo tenemos que encontrar la debilidad de la defensa Atracasol y aprovecharla al máximo.$b$bLos exploradores nos han informado del uso de resguardos de perímetro alrededor del Promontorio Buscalba. Son potentes, pero no letales.$b$bDesactivarlos nos permitiría vigilar más de cerca sus actividades.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'frFR', 'Briser les défenses', 'Détruisez 6 Garde-périmètre saccage-soleil autour du Promontoire de Cherchelaube.', 'Même la plus solide des défenses peut avoir une faiblesse, $c. Il nous suffit de trouver le point faible de la défense des Saccage-Soleil, et de l’exploiter autant que possible.$b$bDes éclaireurs m’ont rapporté qu’ils ont placé des garde-périmètre autour du promontoire de Cherchelaube. Ils sont puissants, mais pas mortels.$b$bDésactiver ces garde-périmètre nous permettrait de surveiller leurs activités de plus près.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'itIT', 'Abbattere le difese', 'Distruggi 6 Guardie Perimetrali dei Predatori del Sole attorno al Promontorio Albafurente.', 'Anche la migliore strategia difensiva ha delle debolezze, $C. Il nostro obiettivo è trovare il punto debole della difesa dei Predatori del Sole e sfruttarlo il più possibile.$b$bGli esploratori hanno rilevato un utilizzo massiccio di Guardie Perimetrali attorno al Promontorio Albafurente; sono potenti, ma non letali.$b$bDisattivarle ci permetterebbe di tenere più facilmente sotto controllo tutte le loro attività.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'koKR', '방어 무너뜨리기', '돈시커 곶 주변에 있는 선리버 구역 수호물을 6개 파괴해야 합니다.', '아무리 강한 방어에도 약점은 있게 마련이지요. 선리버 방어의 약점을 찾은 후 가능한 한 활용하면 돼요.$b$b정찰대의 보고에 따르면 돈시커 곶 주변에는 구역 수호물이 설치되어 있다고 해요. 강력하긴 하지만 치명적이진 않죠.$b$b수호물을 무력화하면 적의 동향을 보다 빈틈없이 파악할 수 있을 거예요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'ptBR', 'Derrubando as defesas', 'Destrua 6 Proteções do Perímetro Fendessol perto do Promontório Buscalva.', 'Até mesmo a defesa mais forte pode ter fraquezas, $c. Só precisamos encontrar o ponto fraco das defesas dos Fendessol e explorá-lo tanto quanto possível.$b$bOs batedores relataram que há proteções de perímetro perto do Promontório Buscalva. Elas são poderosas, mas não letais.$b$bCom as proteções desativadas, vai ser mais fácil ficar de olho nas atividades deles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'ruRU', 'Вывести защиту из строя', 'Уничтожьте 6 защитных кристаллов Похитителей Солнца на мысе Искателя Рассвета.', 'Даже в самой сильной обороне можно обнаружить брешь, |3-6($c). Нам надо найти слабое место в обороне Похитителей Солнца и использовать его.$b$bРазведчики доносят о том, что их лагерь на мысе Искателя Рассвета охраняется защитными кристаллами. Они мощные, но не смертельно опасные.$b$bЕсли их отключить, мы сможем более эффективно наблюдать за противником.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'zhCN', '粉碎防御', '在寻晨者海角附近摧毁6个夺日者监视结界。', '哪怕是最无懈可击的防御也会有薄弱点，$c。我们只需要找到夺日者防线的薄弱点，然后尽可能地加以利用。$b$b据斥候回报，在寻晨者海角四处布满了监视结界。这些结界十分强大，可并不具有致命的杀伤力。$b$b如果能让它们失效，我们就能近距离监视夺日者的行动了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32634, 'zhTW', '摧毀防禦', '摧毀6個曦尋者之岬周圍的奪日者陣地結界。', '再縝密的防禦也會有漏洞，$c。我們只要找出奪日者防禦中的漏洞，然後盡可能攻破。$b$b斥候回報曦尋者之岬四周設下了陣地結界。這些結界威力強大，卻不會置人於死命。$b$b摧毀這些結界能讓我們更靠近觀察他們的活動。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'deDE', 'Jetzt reichts aber mit den Bomben!', 'Entschärft die taktischen Manabomben bei den Allianzschiffen, dem Konstruktritual und den Vorräten auf der Violetten Erhebung.', 'Die Sonnenhäscher haben in unserem Lager taktische Manabomben platziert. Sie müssen auseinandergenommen werden, bevor sie detonieren.$b$bSucht sie an wichtigen Standorten, zum Beispiel bei unseren Vorräten. Geht sicher, dass Ihr sie alle findet und entschärft. Wir können uns hier keine Rückschläge leisten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'esES', '¡Ya vale con las bombas!', 'Desarma las bombas de maná tácticas en los barcos de la alianza, el ritual de ensamblaje y los suministros en el Alto Violeta.', 'Los Atracasol han colocado bombas de maná tácticas alrededor de nuestro campamento. Hay que desactivarlas antes de que estallen.$b$bBúscalas en áreas de gran importancia, como nuestras provisiones. Asegúrate de encontrarlas y desmantelarlas todas. No podemos permitirnos perder terreno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'esMX', '¡Ya basta con las bombas!', 'Desarma las bombas de maná tácticas en los barcos de la alianza, el ritual de ensamblaje y los suministros en el Alto Violeta.', 'Los Atracasol han colocado bombas de maná tácticas alrededor de nuestro campamento. Hay que desactivarlas antes de que estallen.$b$bBúscalas en áreas de gran importancia, como nuestras provisiones. Asegúrate de encontrarlas y desmantelarlas todas. No podemos permitirnos perder terreno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'frFR', 'Ça suffit avec ces bombes !', 'Désamorcez les Bombes de mana tactiques sur les Vaisseaux de l’Alliance, le Rituel d’assemblage et les Fournitures qui se trouvent sur la Cime Pourpre.', 'Les Saccage-Soleil ont posé des bombes de mana tactiques dans notre camp. Il faut les démanteler avant qu’elles n’explosent.$b$bCherchez-les dans les endroits stratégiques, comme nos fournitures. Veillez à toutes les trouver et neutralisez-les. Nous ne pouvons pas nous permettre de céder du terrain.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'itIT', 'Basta bombe!', 'Disarma le Bombe di Mana Tattiche piazzate sulle navi dell\'Alleanza, vicino al Rituale del Costrutto e vicino ai rifornimenti all\'interno della Terrazza Violacea.', 'I Predatori del Sole hanno piazzato alcune Bombe di Mana Tattiche attorno al nostro accampamento. Devono essere smantellate prima che esplodano.$b$bCercale nelle zone di più grande rilevanza strategica, vicino ai nostri rifornimenti; assicurati di disattivarle tutte, non possiamo permetterci di perdere terreno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'koKR', '폭탄은 지긋지긋해!', '보랏빛 마루 안의 얼라이언스 함선, 피조물 생성소, 보급품 화물에 설치된 전술 마나 폭탄을 해제해야 합니다.', '선리버가 우리 진지 주변에 전술 마나 폭탄을 심었어요. 놈들이 폭파시키기 전에 해제해야만 해요.$b$b보급고라든지, 중요한 곳에서 폭탄을 찾아 보세요. 반드시 모두 찾아서 해제해야만 합니다. 그 어느 곳도 잃어선 안 되니까요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'ptBR', 'Chega de bombas, pombas!', 'Desarme as Bombas Táticas de Mana nos Navios da Aliança, no Ritual do Constructo e nos Suprimentos do Pico Violeta.', 'Os Fendessol plantaram bombas táticas de mana ao redor do nosso acampamento. Elas precisam ser desativadas antes que detonem.$b$bProcure as bombas em áreas importantes, como o estoque de suprimentos. Certifique-se de encontrar e desativar todas. Não podemos nos dar ao luxo de perder mais terreno aqui.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'ruRU', 'Хватит бомб!', 'Обезвредьте тактические мана-бомбы, установленные на кораблях Альянса, а также около припасов на Аметистовом утесе и рядом с местом проведения ритуала возведения.', 'Похитители Солнца установили тактические мана-бомбы возле нашего лагеря. Их надо обезвредить, пока они не успели взорваться.$b$bИх надо искать в районе важных объектов – например, рядом с нашими припасами. Не пропусти ни одной мана-бомбы. Мы не должны уступать Похитителям Солнца ни одного клочка земли.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'zhCN', '终止爆炸！', '在紫罗兰高地卸除联盟舰船、构造体仪式和补给品附近的战术魔法炸弹。', '夺日者在我们的营地四周布满了战术魔法炸弹。必须在这些炸弹爆破前将其卸除。$b$b在重要区域搜寻这些炸弹，比如我们的补给品附近。一定要找到并把它们全都卸除。我们决不能再失去任何阵地了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32635, 'zhTW', '受夠那些炸彈了!', '拆除聯盟船艦、占卜水晶與紫羅蘭高崗的補給品上的戰術法力炸彈。', '奪日者在我們的營地四周放置戰術法力炸彈，我們得在炸彈引爆之前將它們拆除掉。$b$b到重點區域，例如我們的補給品附近，來尋找這些炸彈的蹤跡。務必找出炸彈並且拆掉它們。我們絕不能在這裡敗退。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'deDE', 'Gefesseltes Publikum', 'Befreit 5 gefangene Kirin Tor am Morgensucherkap.', 'Die Sonnenhäscher machen wirklich vor nichts halt, um zu bekommen, was sie wollen, $n.$b$bMir wurde berichtet, dass mehrere Kirin Tor verschwunden sind. Ich vermute, dass die Sonnenhäscher etwas damit zu tun haben.$b$bHoffentlich sind sie nicht verletzt worden. Andernfalls wäre meine Rache schnell und entsetzlich. Ich verlasse mich darauf, das Ihr sie in einem Stück zurückbringt, aber bitte beeilt Euch.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'esES', 'Público entregado', 'Libera a 5 miembros del Kirin Tor cautivos cerca del Promontorio Buscalba.  ', 'Los Atracasol caerán en lo más bajo para conseguir sus propósitos, $n.$b$bHe recibido informes sobre la desaparición de varios Kirin Tor. Sospecho que los Atracasol tienen algo que ver.$b$bEspero que no les hayan hecho daño, o mi furia será rauda y terrible. Confío en que puedas traerlos de una pieza, pero te ruego que te des prisa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'esMX', 'Público entregado', 'Libera a 5 miembros del Kirin Tor cautivos cerca del Promontorio Buscalba.  ', 'Los Atracasol caerán en lo más bajo para conseguir sus propósitos, $n.$b$bHe recibido informes sobre la desaparición de varios Kirin Tor. Sospecho que los Atracasol tienen algo que ver.$b$bEspero que no les hayan hecho daño, o mi furia será rauda y terrible. Confío en que puedas traerlos de una pieza, pero te ruego que te des prisa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'frFR', 'Entretien forcé', 'Libérez 5 Captifs du Kirin Tor près du Promontoire de Cherchelaube.', 'Les Saccage-Soleil sont prêts à toutes les bassesses tant que cela leur permet d’obtenir ce qu’ils veulent, $n.$b$bOn m’a signalé la disparition de plusieurs membres du Kirin Tor. Je suppose que les Saccage-Soleil n’y sont pas étrangers.$b$bJ’espère qu’ils ne leur ont fait aucun mal, dans le cas contraire, mon courroux sera rapide et terrible. Je compte sur vous pour nous les ramener en un seul morceau, mais faites vite.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'itIT', 'Pubblico prigioniero', 'Libera 5 Prigionieri del Kirin Tor vicino al Promontorio Albafurente.', 'I Predatori del Sole ricorreranno a qualsiasi mezzo per ottenere ciò che vogliono, $n.$b$bGli ultimi rapporti parlano di numerosi membri del Kirin Tor scomparsi e sospetto che i Predatori del Sole abbiano qualcosa a che fare con tutto ciò.$b$bSpero che a loro non sia stato fatto alcun male, o i responsabili cadranno vittime della mia incontenibile ira. Sono certa che li riporterai a casa sani e salvi, ma ti prego di sbrigarti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'koKR', '포로 접견', '돈시커 곶 근처에서 키린 토 포로를 5명 구출해야 합니다.', '선리버는 원하는 것을 손에 넣기 위해서라면 아무리 치졸한 짓도 서슴지 않아요.$b$b얼마 전에 키린 토 몇 명이 실종됐다는 보고를 받았어요. 제 생각에는 여기 선리버가 관련돼 있는 것 같아요.$b$b부디 그들이 다치지는 않았으면 좋겠네요. 아니면 놈들에게 끔찍한 분노를 보여줄 거예요. 그대라면 그들을 몸성히 데리고 올 수 있을 거라 믿지만, 서둘러 주세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'ptBR', 'Audiência cativa', 'Liberte 5 Cativos do Kirin Tor perto do Promontório Buscalva.', 'Os Fendessol descerão aos níveis mais baixos para obter o que querem, $n.$b$bHoje mais cedo, ouvi relatos de que vários membros do Kirin Tor estão desaparecidos. Desconfio que os Fendessol possam ter algo a ver com isso.$b$bEspero que não tenham sido feridos, ou minha fúria será célere e terrível. Confio em você para trazê-los de volta inteiros. Por favor, apresse-se.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'ruRU', 'Возвращение пленников', 'Освободите 5 пленников из Кирин-Тора, удерживаемых в окрестностях мыса Искателя Рассвета.', 'Похитители Солнца не гнушаются самых отвратительных методов в достижении своих целей, $n.$b$bНедавно мне доложили, что несколько представителей Кирин-Тора пропали без вести. Подозреваю, что Похитители Солнца приложили к этому руку.$b$bИ лучше бы нашим пропавшим собратьям быть в добром здравии, иначе месть моя будет невероятно страшна. Я прошу тебя вернуть пропавших целыми и невредимыми, но для этого тебе нужно поторопиться.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'zhCN', '不情愿的观众', '在寻晨者海角附近解救5名肯瑞托俘虏。', '夺日者会毫无底线地实现自己的目标，$n。$b$b我之前收到报告，几位肯瑞托成员不知所踪。我怀疑这也许跟夺日者有关。$b$b希望他们还没惨遭毒手，否则我的怒火恐怕要喷薄而出了。相信你能把他们安然无恙地带回来，但一定要抓紧时间。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32636, 'zhTW', '釋放俘虜', '釋放5名曦尋者之岬附近的祈倫托俘虜。', '奪日者願意不擇手段，以獲得想要的東西，$n。$b$b之前我收到回報，得知幾名祈倫托的成員不見了。我懷疑是奪日者動的手腳。$b$b希望他們安然無恙，否則我會採取立即且殘暴的報復手段。我相信你能將他們安全帶回來，不過請快一點。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'deDE', 'Viel zu stark', 'Zerstört 7 Manasammler der Sonnenhäscher am Morgensucherkap.', 'Die Truppen der Sonnenhäscher sind in letzter Zeit mächtiger geworden. Ich vermute, dass hier Magie im Spiel ist, aber ich kann mir nicht sicher sein.$b$bIch glaube, es wäre klug, ihr Lager zu untersuchen und zu versuchen, die Quelle dieser zusätzlichen Macht zu finden.$b$bWenn Ihr sie gefunden habt, benutzt diesen Kristall um sie zu überladen und zu zerstören.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'esES', 'Subyugado', 'Destruye 7 colectores de maná Atracasol cerca del Promontorio Buscalba.', 'Las fuerzas Atracasol han aumentado su poder últimamente. Sospecho que se trata de magia, pero no lo sé con seguridad.$b$bCreo que convendría investigar su campamento para ver si encuentras la fuente de ese poder adicional.$b$bCuando la encuentres, usa este cristal para sobrecargarla y destruirla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'esMX', 'Subyugado', 'Destruye 7 colectores de maná Atracasol cerca del Promontorio Buscalba.', 'Las fuerzas Atracasol han aumentado su poder últimamente. Sospecho que se trata de magia, pero no lo sé con seguridad.$b$bCreo que convendría investigar su campamento para ver si encuentras la fuente de ese poder adicional.$b$bCuando la encuentres, usa este cristal para sobrecargarla y destruirla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'frFR', 'Surpuissance', 'Détruisez 7 Collecteurs de mana saccage-soleil près du Promontoire de Cherchelaube.', 'Les troupes saccage-soleil sont devenues plus puissantes dernièrement. Je suppose que la magie est à l’œuvre, mais je n’ai aucune certitude.$b$bJe pense qu’il serait sage d’inspecter leur camp pour tenter de découvrir la source de ce regain de puissance.$b$bQuand vous l’aurez trouvée, utilisez ce cristal pour la surcharger et la détruire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'itIT', 'Potere ineguagliabile', 'Distruggi 7 Collettori di Mana dei Predatori del Sole al Promontorio Albafurente.', 'Le forze dei Predatori del Sole sono diventate più potenti negli ultimi tempi. Temo che la magia abbia avuto un ruolo fondamentale, ma non è possibile dirlo con certezza.$b$bCredo che sarebbe saggio fare una visita al loro accampamento per trovare la fonte di questo potere.$b$bQuando l\'avrai trovata, usa questo cristallo per sovraccaricarla e distruggerla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'koKR', '과부하', '돈시커 곶 주변에 있는 선리버 마나 수집기를 7개 파괴해야 합니다.', '선리버의 병력이 최근에 더 강해졌어요. 아무래도 마법이 작용하고 있는 것 같은데, 확신할 수는 없네요.$b$b적진을 조사해서 이 힘의 원천을 찾는 게 좋을 것 같군요.$b$b힘의 원천을 찾으면 이 결정으로 과부하를 걸어 파괴하세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'ptBR', 'Subjugados', 'Destrua 7 Coletores de Mana Fendessol perto do Promontório Buscalva.', 'As forças dos Fendessol andaram se fortalecendo recentemente. Desconfio que estejam usando magia, mas não posso ter certeza.$b$bCreio que seria sábio investigar o acampamento deles para tentar encontrar a fonte desse poder adicional.$b$bQuando você encontrá-la, use este cristal para sobrecarregá-la e destruí-la.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'ruRU', 'Перегрузка', 'Уничтожьте 7 сборников маны Похитителей Солнца на мысе Искателя Рассвета.', 'Войска Похитителей Солнца становятся все сильнее. Думаю, в этом замешана магия, но точно сказать не могу.$b$bМудрым шагом будет прочесать их лагерь в поисках дополнительного источника силы.$b$bКак найдешь, используй эти кристаллы, чтобы перегрузить источник и уничтожить его.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'zhCN', '超强能量', '在寻晨者海角附近摧毁7个夺日者法力收集器。', '夺日者的军队近来变得越发强大。我怀疑与魔法有关，但还不能确定。$b$b我认为比较明智的方法是去调查下他们的营地，看看是否能找到这股额外力量的源头。$b$b等你找到之后，用这块水晶使其过载，并摧毁它。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32637, 'zhTW', '壓制', '摧毀7個曦尋者之岬周圍的奪日者法力收集器。', '最近奪日者的軍力變得更加強大了。我懷疑是因為使用魔法的緣故，但是又不敢肯定。$b$b調查他們的營地，尋找這個額外能量的來源，應該是比較聰明的做法。$b$b一旦你找到了，便使用這塊水晶超載能量並摧毀它。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'deDE', 'Manadiebe', 'Tötet 9 Manawyrms der Sonnenhäscher.', 'Manchmal können selbst die kleinsten Kreaturen die größte Bedrohung darstellen.$b$bDie Sonnenhäscher haben Manawyrms mitgebracht. Sie mögen klein sein, aber sie können unsere Magiewirker im Kampf stark beeinträchtigen. Viele von ihnen haben auf die schwere Art gelernt, sie nicht zu unterschätzen.$b$bSie müssen beseitigt werden, und zwar schnell.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'esES', 'Carroñeros de maná', 'Mata a 9 vermis de maná Atracasol.', 'A veces, hasta las criaturas más pequeñas pueden suponer la mayor amenaza.$b$bLos Atracasol han traído consigo vermis de maná. Aunque son de pequeño tamaño, pueden inutilizar a nuestros hechiceros en combate. Muchos han aprendido por las malas a no subestimarlas.$b$bHay que acabar con ellas, y rápido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'esMX', 'Carroñeros de maná', 'Mata a 9 vermis de maná Atracasol.', 'A veces, hasta las criaturas más pequeñas pueden suponer la mayor amenaza.$b$bLos Atracasol han traído consigo vermis de maná. Aunque son de pequeño tamaño, pueden inutilizar a nuestros hechiceros en combate. Muchos han aprendido por las malas a no subestimarlas.$b$bHay que acabar con ellas, y rápido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'frFR', 'Les pilleurs de mana', 'Tuez 9 Wyrms de mana saccage-soleil.', 'Parfois, même les plus petites créatures peuvent représenter la plus grande des menaces.$b$bLes Saccage-Soleil ont apporté avec eux des wyrms de mana. Malgré leur petite taille, ils sont capables de neutraliser nos lanceurs de sorts au combat. Beaucoup ont appris à leurs dépens qu’il ne faut pas les sous-estimer.$b$bIl faut les éliminer, et vite.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'itIT', 'Predoni di Mana', 'Uccidi 9 Dragoni di Mana dei Predatori del Sole.', 'A volte, anche le più piccole creature possono trasformarsi nella più grande delle minacce.$b$bI Predatori del Sole hanno portato dei Dragoni di Mana con loro, piccole creature in grado di sconfiggere anche i nostri migliori incantatori in combattimento. In molti hanno imparato a caro prezzo a non sottovalutarli.$b$bDobbiamo occuparcene, e in fretta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'koKR', '마나의 청소부', '선리버 마나 지룡을 9마리 처치해야 합니다.', '때로는 작디작은 생물이 크나큰 위협이 될 수도 있지요.$b$b선리버가 이곳에 마나 지룡을 데리고 왔어요. 놈들은 크기는 작을지 몰라도, 우리 측의 마법사를 무력화할 수 있는 힘을 지녔어요. 놈들을 얕잡아 봐서는 안 된다는 걸 고생 끝에 깨달은 이들이 많죠.$b$b최대한 빨리 처치해야 해요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'ptBR', 'Catadores de mana', 'Mate 9 Moreias de Mana Fendessol.', 'Às vezes as menores criaturas representam o maior perigo.$b$bOs Fendessol trouxeram moreias de mana com eles. Elas são pequenas, mas podem incapacitar nossos feiticeiros em combate. Muitos aprenderam da maneira mais difícil a não subestimá-las.$b$bÉ preciso dar um jeito delas, e rápido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'ruRU', 'Пожиратели маны', 'Убейте 9 маназмеев Похитителей Солнца.', 'Иногда даже самые крохотные создания могут представлять большую угрозу.$b$bПохитители Солнца привезли с собой маназмеев. Небольшие по размеру, они, тем не менее, способны нейтрализовать наших заклинателей на время боя. Многие на собственной шкуре поняли, что их лучше не недооценивать.$b$bРасправься с ними как можно быстрее.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'zhCN', '魔法清道夫', '消灭9只夺日者法力浮龙。', '有时候，最微不足道的生物却能造成最大的威胁。$b$b夺日者携带法力浮龙作战。虽然体型小巧，但却能让我们的施法者在战斗中阵脚大乱。许多人都为轻敌付出了惨痛代价。$b$b必须尽快消灭它们。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32638, 'zhTW', '法力食腐者', '殺死9隻奪日者法力龍鰻。', '有時候，即使是微小的生物也能造成巨大的威脅。$b$b奪日者隨身帶著法力龍鰻。這些龍鰻體型雖小，卻能在戰鬥中羈絆我們的施法者。許多人都吃過這些龍鰻的苦頭而不敢小看它們。$b$b我們得盡快解決這些龍鰻。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'deDE', 'De-Konstruktion', 'Zerstört 3 Konstrukte der Sonnenhäscher vor dem Morgensucherkap.', 'Späher berichten, dass eine große Anzahl von Konstrukten vor dem Morgensucherkap patrouilliert.$b$bWenn sie in Frieden gelassen werden, werden sie eine beachtliche Bedrohung darstellen, falls die Sonnenhäscher einen Angriff unternehmen.$b$bEin Erstschlag ist die einzige Lösung. Wir werden sie zerstören, bevor sie uns angreifen können!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'esES', 'Deconstrucción', 'Destruye 3 ensamblajes Atracasol fuera del Promontorio Buscalba.', 'Los exploradores informan de un gran número de ensamblajes patrullando los alrededores del Promontorio Buscalba.$b$bSi no los detenemos, serán una amenaza considerable si los Atracasol lanzan un ataque.$b$bLa única respuesta es un ataque preventivo. ¡Los destruiremos antes de que puedan atacarnos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'esMX', 'Deconstrucción', 'Destruye 3 ensamblajes Atracasol fuera del Promontorio Buscalba.', 'Los exploradores informan de un gran número de ensamblajes patrullando los alrededores del Promontorio Buscalba.$b$bSi no los detenemos, serán una amenaza considerable si los Atracasol lanzan un ataque.$b$bLa única respuesta es un ataque preventivo. ¡Los destruiremos antes de que puedan atacarnos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'frFR', 'Déconstruction', 'Détruisez 3 Assemblages saccage-soleil à l’extérieur du Promontoire de Cherchelaube.', 'D’après nos éclaireurs, un grand nombre d’assemblages patrouille à l’extérieur du promontoire de Cherchelaube.$b$bIl faut s’en occuper, sans quoi, ils représenteront une sérieuse menace si les Saccage-Soleil lancent une attaque.$b$bLa seule solution est une frappe préventive. Nous allons les détruire avant qu’ils ne nous attaquent !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'itIT', 'Demolizione', 'Distruggi 3 Costrutti dei Predatori del Sole al Promontorio Albafurente.', 'Gli esploratori hanno rilevato la presenza di un grande numero di costrutti che sorvegliano i confini del Promontorio Albafurente.$b$bSe i Predatori del Sole dovessero tentare un attacco, la loro presenza potrebbe rivelarsi una considerevole minaccia.$b$bL\'unica opzione possibile è un attacco preventivo. Li distruggeremo prima che possano attaccarci!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'koKR', '해체 작업', '돈시커 곶 외곽에 있는 선리버 피조물을 3개 파괴해야 합니다.', '정찰대의 보고에 따르면 돈시커 곶 외곽을 많은 피조물이 순찰하고 있다고 해요.$b$b그대로 둔다면, 선리버가 공격해 올 때 큰 위협이 될 거예요.$b$b선제공격이 유일한 답이에요. 놈들이 우릴 공격하기 전에 파괴해 버리는 거죠!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'ptBR', 'Desconstrução', 'Destrua 3 Constructos Fendessol perto do Promontório Buscalva.', 'Batedores andam reportando um grande número de constructos em patrulha perto do Promontório Buscalva.$b$bSe não forem detidos, eles representarão uma ameaça considerável caso os Fendessol ataquem.$b$bA única resposta é um ataque preventivo. Nós os destruiremos antes que possam nos atacar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'ruRU', 'Уничтожение големов', 'Уничтожьте 3 голема Похитителей Солнца возле мыса Искателя Рассвета. ', 'Разведчики докладывают, что местность, прилегающая к мысу Искателя Рассвета, патрулируется большим количеством големов Похитителей Солнца.$b$bНельзя оставлять этот факт без внимания, поскольку големы могут представлять серьезную угрозу в случае нападения Похитителей Солнца.$b$bНам остается только одно – нанести упреждающий удар. Мы уничтожим их, прежде чем они нападут на нас!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'zhCN', '解体', '在寻晨者海角外摧毁3个夺日者构造体。', '据斥候回报，有大批构造体在寻晨者海角外面巡逻。$b$b如果不加抑制，一旦夺日者发起攻击，这些构造体会对我们形成重大威胁。$b$b唯一的办法就是先发制人。我们要在夺日者发起攻击之前摧毁它们！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32639, 'zhTW', '摧毀傀儡', '摧毀3個曦尋者之岬外圍的奪日者傀儡。', '斥候回報，有大批的傀儡巡邏於曦尋者之岬的外圍。$b$b若放任不管，將來奪日者發動攻擊時，它們將是可怕的威脅。$b$b唯一的辦法就是先發制人。在傀儡攻擊我們之前先摧毀它們!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'deDE', 'Champions des Donnerkönigs', 'Findet Ritualsteine der Shan\'ze, um das Blitzritual, das Geisterritual und das Urzeitliche Ritual an verschiedenen Orten auf der Insel des Donners durchzuführen und besiegt dann die beschworenen mächtigen Gegner.', 'Diese Insel ist mit lauernder Gefahr von drei mächtigen Gruppen übersät, die nur darauf warten, uns zu überrumpeln. Meister der Blitze, uralte und mächtige Geister und urtümliche Monstrositäten der Zandalari.$B$BWenn Ihr einige der Ritualsteine der Shan\'ze finden könnt, die auf der Insel verteilt sind, solltet Ihr in der Lage sein, diese mächtigen Gegner aus ihren Verstecken zu locken und sie zu bekämpfen.$B$BFindet und zerstört Mitglieder jeder Gruppe, damit sie destabilisiert bleiben, und ich werde Euch belohnen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'esES', 'Los campeones del Rey del Trueno', 'Usa las piedras de ritual de Shan\'ze para realizar el ritual del relámpago, el ritual del espíritu y el ritual primigenio en diversos lugares de la Isla del Trueno y, a continuación, derrota a los poderosos adversarios que has invocado.', 'Esta isla está infestada de amenazas acechantes de tres poderosos grupos a la espera de una oportunidad para echársenos al cuello. Maestros de los relámpagos, temibles espíritus ancestrales y primitivas monstruosidades Zandalari.$B$BSi puedes hacerte con unas cuantas piedras rituales de Shan\'ze que hay por la isla, podrás llamar la atención de estos adversarios para, así, atacarlos.$B$BBusca y elimina a unos cuantos miembros de cada grupo para mantenerlos desestabilizados y te recompensaré por ello.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'esMX', 'Los campeones del Rey del Trueno', 'Usa las piedras de ritual de Shan\'ze para realizar el ritual del relámpago, el ritual del espíritu y el ritual primigenio en diversos lugares de la Isla del Trueno y, a continuación, derrota a los poderosos adversarios que has invocado.', 'Esta isla está infestada de amenazas acechantes de tres poderosos grupos a la espera de una oportunidad para echársenos al cuello. Maestros de los relámpagos, temibles espíritus ancestrales y primitivas monstruosidades Zandalari.$B$BSi puedes hacerte con unas cuantas piedras rituales de Shan\'ze que hay por la isla, podrás llamar la atención de estos adversarios para, así, atacarlos.$B$BBusca y elimina a unos cuantos miembros de cada grupo para mantenerlos desestabilizados y te recompensaré por ello.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'frFR', 'Les champions du roi-tonnerre', 'Utilisez les Pierres rituelles shan’ze pour accomplir le Rituel de la foudre, le Rituel de l’esprit et le Rituel primordial en différents endroits de l’Île du Tonnerre, puis terrassez les puissants adversaires invoqués.', 'Cette île est truffée de menaces… Trois puissants groupes sont tapis dans l’ombre, dans l’attente d’une occasion pour nous tomber dessus : les maîtres de la foudre, d’anciens et puissants esprits, et les monstruosités primordiales des Zandalari.$b$bSi vous parveniez à mettre la main sur quelques-unes des pierres rituelles shan’ze éparpillées sur l’île, vous seriez en mesure de prendre l’offensive en faisant sortir de leur cachette ces puissants adversaires.$b$bTrouvez et détruisez des membres de chacun des groupes pour les déstabiliser, et je vous récompenserai pour vos efforts.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'itIT', 'I campioni del Re del Tuono', 'Usa le Pietre Rituali degli Shan\'ze per eseguire il Rituale del Fulmine, il Rituale Spirituale e il Rituale Primordiale in varie zone lungo tutta l\'isola del Re del Tuono, poi sconfiggi i potenti avversari evocati.', 'Quest\'isola pullula di oscure minacce provenienti da tre potenti gruppi, in trepidante attesa di colpirci alle spalle. Maestri del fulmine, antichi e potenti spiriti, e mostruosità primordiali Zandalari.$B$BSe riuscissi a mettere le mani su alcune delle Pietre Rituali degli Shan\'ze sparse per tutta l\'isola, saresti in grado di costringere questi pericolosi avversari a uscire allo scoperto per poi colpirli.$B$BTrova e distruggi i membri di ogni gruppo per evitare che si organizzino e io ti ricompenserò a dovere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'koKR', '천둥왕의 용사들', '샨쩌 의식용 돌을 사용해 천둥의 섬 각지에서 번개 의식, 영혼 의식, 원시 의식을 거행하고, 소환되는 강력한 적을 처치해야 합니다.', '이 섬에서는 강대한 세 세력이 우리를 덮칠 기회만 노리며 웅크리고 있다네. 번개의 주인들, 고대의 강력한 영혼들, 잔달라의 원시 괴물들이지.$b$b이 섬 전역에 흩어져 있는 샨쩌 의식용 돌을 모을 수 있다면, 이 강력한 적들을 꾀어 내어 처치할 수 있네.$B$B각 세력의 적을 찾아 처치해서, 그들을 약화시키게. 사례는 섭섭하지 않게 할 테니.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'ptBR', 'Campeões do Rei Trovão', 'Use as Pedras Rituais de Shan\'ze para realizar o Ritual do Relâmpago, o Ritual dos Espíritos e o Ritual Primevo em vários pontos da Ilha do Trovão, e depois derrote os poderosos oponentes que serão evocados.', 'Esta ilha está cheia de ameaças furtivas de três grupos poderosos, que esperam a chance de nos atacar. Mestres do relâmpago, espíritos antigos e poderosos, e monstruosidades primevas dos Zandalari.$B$BSe você conseguir obter algumas das Pedras Rituais de Shan\'ze que estão espalhadas pela ilha, poderá tirar esses poderosos oponentes de seus esconderijos e atacá-los.$B$BEncontre e destrua membros de cada grupo para mantê-los desestabilizados, e eu farei com que seu esforço valha a pena.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'ruRU', 'Лучшие бойцы Властелина Грома', 'Используйте ритуальные камни Шань\'цзэ, чтобы провести ритуал молний, ритуал духов и первобытный ритуал в разных частях острова Грома. Затем одержите победу над противниками, которые появятся после ритуала призыва. ', 'На острове таится опасность: нам угрожают повелители молний, древние духи и первобытные зандаларские чудовища. Эти три группы бойцов только и ждут, чтобы напасть на нас из засады.$B$BЕсли тебе удастся добыть ритуальные камни Шань\'цзэ, разбросанные по всему острову, ты сможешь выманить этих могущественных существ из засады и вступить с ними в бой.$B$BНайди и уничтожь врагов из каждой группы, чтобы помешать их планам, и я награжу тебя.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'zhCN', '雷电之王的勇士', '在雷神岛的各个位置使用山泽仪式石举行闪电仪式、灵魂仪式和原始仪式，然后击败被召唤出的强大对手。', '在这座岛屿上布满了伺机而动的敌对力量，主要来自三个强大的组织，时刻准备朝我们发起攻击。他们分别是驾驭雷电的大师，古老而强大的幽灵，以及原始赞达拉畸体。$B$B要是你能找到一些散落在这岛屿四处的山泽仪式石，你应该就能让这些强大的敌人现身，一举击败他们。$B$B找到并消灭每个组织的成员，让他们阵脚大乱，我不会让你白忙的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32640, 'zhTW', '雷王的勇士', '在雷王島上的各地使用衫織儀式石以進行閃電儀式、靈魂儀式與原始儀式，然後擊敗召喚出來的強大對手。', '這座島上四伏的危機來自於三大群敵人:控雷者、強大的古靈與原始贊達拉巨獸，他們總是伺機從旁攻擊我們。$B$B如果你能取回一些散落在島上的衫織儀式石，你便能喚出這些強大的對手與他們正面交鋒。$B$B找出並擊敗每一群敵人的成員來動搖他們的根本，你會得到豐厚的獎勵。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'deDE', 'Champions des Donnerkönigs', 'Findet Ritualsteine der Shan\'ze, um das Blitzritual, das Geisterritual und das Urzeitliche Ritual an verschiedenen Orten auf der Insel des Donners durchzuführen und besiegt dann die beschworenen mächtigen Gegner.', 'Diese Insel ist mit lauernder Gefahr von drei mächtigen Gruppen übersät, die nur darauf warten, uns zu überrumpeln. Meister der Blitze, uralte und mächtige Geister und urtümliche Monstrositäten der Zandalari.$B$BWenn Ihr einige der Ritualsteine der Shan\'ze finden könnt, die auf der Insel verteilt sind, solltet Ihr in der Lage sein, diese mächtigen Gegner aus ihren Verstecken zu locken und sie zu bekämpfen.$B$BFindet und zerstört Mitglieder jeder Gruppe, damit sie destabilisiert bleiben, und ich werde Euch belohnen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'esES', 'Los campeones del Rey del Trueno', 'Usa las piedras de ritual de Shan\'ze para realizar el ritual del relámpago, el ritual del espíritu y el ritual primigenio en diversos lugares de la Isla del Trueno y, a continuación, derrota a los poderosos adversarios que has invocado.', 'Esta isla está infestada de amenazas acechantes de tres poderosos grupos a la espera de una oportunidad para echársenos al cuello. Maestros de los relámpagos, temibles espíritus ancestrales y primitivas monstruosidades Zandalari.$B$BSi puedes hacerte con unas cuantas piedras rituales Shan\'ze que hay por la isla, podrás llamar la atención de estos adversarios para, así, atacarlos.$B$BBusca y elimina a unos cuantos miembros de cada grupo para mantenerlos desestabilizados y te recompensaré por ello.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'esMX', 'Los campeones del Rey del Trueno', 'Usa las piedras de ritual de Shan\'ze para realizar el ritual del relámpago, el ritual del espíritu y el ritual primigenio en diversos lugares de la Isla del Trueno y, a continuación, derrota a los poderosos adversarios que has invocado.', 'Esta isla está infestada de amenazas acechantes de tres poderosos grupos a la espera de una oportunidad para echársenos al cuello. Maestros de los relámpagos, temibles espíritus ancestrales y primitivas monstruosidades Zandalari.$B$BSi puedes hacerte con unas cuantas piedras rituales Shan\'ze que hay por la isla, podrás llamar la atención de estos adversarios para, así, atacarlos.$B$BBusca y elimina a unos cuantos miembros de cada grupo para mantenerlos desestabilizados y te recompensaré por ello.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'frFR', 'Les champions du roi-tonnerre', 'Utilisez les Pierres rituelles shan’ze pour accomplir le Rituel de la foudre, le Rituel de l’esprit et le Rituel primordial en différents endroits de l’Île du Tonnerre, puis terrassez les puissants adversaires invoqués.', 'Cette île est truffée de menaces… Trois puissants groupes sont tapis dans l’ombre, dans l’attente d’une occasion pour nous tomber dessus : les maîtres de la foudre, d’anciens et puissants esprits, et les monstruosités primordiales des Zandalari.$b$bSi vous parveniez à mettre la main sur quelques-unes des pierres rituelles shan’ze éparpillées sur l’île, vous seriez en mesure de prendre l’offensive en faisant sortir de leur cachette ces puissants adversaires.$b$bTrouvez et détruisez des membres de chacun des groupes pour les déstabiliser, et je vous récompenserai pour vos efforts.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'itIT', 'I campioni del Re del Tuono', 'Usa le Pietre Rituali degli Shan\'ze per eseguire il Rituale del Fulmine, il Rituale Spirituale e il Rituale Primordiale in varie zone lungo tutta l\'isola del Re del Tuono, poi sconfiggi i potenti avversari evocati.', 'Quest\'isola pullula di oscure minacce provenienti da tre potenti gruppi, in trepidante attesa di colpirci alle spalle. Maestri del fulmine, antichi e potenti spiriti, e mostruosità primordiali Zandalari.$B$BSe riuscissi a mettere le mani su alcune delle Pietre Rituali degli Shan\'ze sparse per tutta l\'isola, saresti in grado di costringere questi pericolosi avversari a uscire allo scoperto per poi colpirli.$B$BTrova e distruggi i membri di ogni gruppo per evitare che si organizzino e io ti ricompenserò a dovere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'koKR', '천둥왕의 용사들', '샨쩌 의식용 돌을 사용해 천둥의 섬 각지에서 번개 의식, 영혼 의식, 원시 의식을 거행하고, 소환되는 강력한 적을 처치해야 합니다.', '이 섬에서는 강대한 세 세력이 우리를 덮칠 기회만 노리며 웅크리고 있다네. 번개의 주인들, 고대의 강력한 영혼들, 잔달라의 원시 괴물들이지.$b$b이 섬 전역에 흩어져 있는 샨쩌 의식용 돌을 모을 수 있다면, 이 강력한 적들을 꾀어 내어 처치할 수 있네.$B$B각 세력의 적을 찾아 처치해서, 그들을 약화시키게. 사례는 섭섭하지 않게 할 테니.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'ptBR', 'Campeões do Rei Trovão', 'Use as Pedras Rituais de Shan\'ze para realizar o Ritual do Relâmpago, o Ritual dos Espíritos e o Ritual Primevo em vários pontos da Ilha do Trovão, e depois derrote os poderosos oponentes que serão evocados.', 'Esta ilha está cheia de ameaças furtivas de três grupos poderosos, que esperam a chance de nos atacar. Mestres do relâmpago, espíritos antigos e poderosos, e monstruosidades primevas dos Zandalari.$B$BSe você conseguir obter algumas das Pedras Rituais de Shan\'ze que estão espalhadas pela ilha, poderá tirar esses poderosos oponentes de seus esconderijos e atacá-los.$B$BEncontre e destrua membros de cada grupo para mantê-los desestabilizados, e eu farei com que seu esforço valha a pena.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'ruRU', 'Лучшие бойцы Властелина Грома', 'Используйте ритуальные камни Шань\'цзэ, чтобы провести ритуал молний, ритуал духов и первобытный ритуал в разных частях острова Грома. Затем одержите победу над противниками, которые появятся после ритуала призыва. ', 'На острове таится опасность: нам угрожают три группы врагов – повелители молний, древние духи и первобытные зандаларские чудовища. Они все только и ждут, чтобы напасть на нас из засады.$B$BЕсли тебе удастся добыть ритуальные камни Шань\'цзэ, разбросанные по всему острову, ты сможешь выманить этих могущественных существ из засады и вступить с ними в бой.$B$BНайди и уничтожь врагов из каждой группы, чтобы помешать их планам, и я награжу тебя.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'zhCN', '雷电之王的勇士', '在雷神岛的各个位置使用山泽仪式石举行闪电仪式、灵魂仪式和原始仪式，然后击败被召唤出的强大对手。', '在这座岛屿上布满了伺机而动的敌对力量，主要来自三个强大的组织，时刻准备朝我们发起攻击。他们分别是驾驭雷电的大师，古老而强大的幽灵，以及原始赞达拉畸体。$B$B要是你能找到一些散落在这岛屿四处的山泽仪式石，你应该就能让这些强大的敌人现身，一举击败他们。$B$B找到并消灭每个组织的成员，让他们阵脚大乱，我不会让你白忙的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32641, 'zhTW', '雷王的勇士', '在雷王島上的各地使用衫織儀式石以進行閃電儀式、靈魂儀式與原始儀式，然後擊敗召喚出來的強大對手。', '這座島上四伏的危機來自於三大群敵人:控雷者、強大的古靈與原始贊達拉巨獸，他們總是伺機從旁攻擊我們。$B$B如果你能取回一些散落在島上的衫織儀式石，你便能喚出這些強大的對手與他們正面交鋒。$B$B找出並擊敗每一群敵人的成員來動搖他們的根本，你會得到豐厚的獎勵。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'deDE', 'Arbeitsauftrag: Herrschaftsoffensive I', 'Pflanzt 8 Rotblütenlauchsamen auf dem Gehöft Sonnensang.', '<Yoons Handschrift überzieht die Seite, doch er scheint mit gemischten Gefühlen geschrieben zu haben.>$b$bNormalerweise versuche ich, mich aus Konflikten herauszuhalten, aber Bestellung ist Bestellung. Da Ihr zur Horde gehört, habe ich beschlossen, diese Bestellung an Euch weiterzuleiten.$b$bDie Herrschaftsoffensive hat eine Fuhre Rotblütenlauch angefordert.$b$bDie Truppen dort können davon anscheinend gar nicht genug bekommen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'esES', 'Pedido de la Ofensiva de Dominancia I', 'Planta 8 semillas de puerro Rojaflor en el Rancho Cantosol.', '<La letra de Yoon decora la página, aunque sus sentimientos parecen contradictorios.>$b$bNormalmente intento mantenerme al margen de los conflictos, pero un pedido es un pedido. Ya que eres de la Horda, he decidido transmitirte este pedido a ti.$b$bLa Ofensiva de Dominancia ha solicitado puerros Rojaflor.$b$b¡Parece que las tropas no se cansan de ellos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'esMX', 'Pedido de la Ofensiva de Dominancia I', 'Planta 8 semillas de puerro Rojaflor en el Rancho Cantosol.', '<La letra de Yoon decora la página, aunque sus sentimientos parecen contradictorios.>$b$bNormalmente intento mantenerme al margen de los conflictos, pero un pedido es un pedido. Ya que eres de la Horda, he decidido transmitirte este pedido a ti.$b$bLa Ofensiva de Dominancia ha solicitado puerros Rojaflor.$b$b¡Parece que las tropas no se cansan de ellos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'frFR', 'Commande : offensive Domination I', 'Plantez 8 Graines de poireau à fleur rouge à la Ferme Chant du Soleil.', '<L’écriture de Yoon s\'étale sur la page avec élégance, mais ses sentiments semblent partagés.>$b$bJe tente normalement de ne pas me mêler des conflits, mais une commande est une commande. Comme vous faites partie de la Horde, j’ai décidé de vous transmettre celle-ci.$b$bUne commande de poireaux à fleur rouge m’a été passée pour l’offensive Domination.$b$bLes troupes sur le terrain semblent en raffoler !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'itIT', 'Ordine: Offensiva del Dominio I', 'Pianta 8 Semi di Porro Rosso alla Tenuta Cantasole.', '<La scrittura di Yoon decora la pagina, anche se le emozioni che traspaiono dalla sua calligrafia sembrano contrastanti.>$b$bNormalmente cerco di rimanere fuori da ogni tipo di conflitto, ma un ordine è un ordine. Dal momento che fai parte dell\'Orda, ho deciso di assegnare quest\'ordine a te.$b$bL\'Offensiva del Dominio ha richiesto dei Porri Rossi Maturi.$b$bSembra che le truppe laggiù non riescano a farne a meno!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'koKR', '주문서: 지배령 선봉대 I', '태양노래 농장에 붉은꽃 부추 씨앗을 8개 심어야 합니다.', '<종이를 장식한 윤의 필체에 그의 갈등하는 심경이 드러나 있습니다.>$b$b저는 보통 마찰을 피하는 편이지만, 주문은 주문이니까요. 당신이 호드라서 이 주문을 당신에게 맡기기로 했습니다.$b$b지배령 선봉대에서 붉은꽃 부추를 주문했습니다.$b$b그곳에 있는 부대에서 부추를 애타게 찾는 모양입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'ptBR', 'Ordem de Serviço: Ofensiva de Dominância I', 'Plante 8 Sementes de Alho-poró de Florada Vermelha na Fazenda Sol Cantante.', '<A escrita de Yoon preenche a página; seus sentimentos parecem conflituosos.>$b$bEu geralmente tento ficar fora de conflitos, mas um pedido é um pedido. Como você é da Horda, eu decidi passar esse pedido para você.$b$bA Ofensiva de Dominância pediu alho-poró de florada vermelha.$b$bParece que as tropas de lá adoram!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'ruRU', 'Заказ: Армия Покорителей I', 'Посадите 8 семян красного лука-порея на ферме Солнечной Песни.', '<Страница исписана почерком Юна. Похоже, его одолевали противоречивые чувства.>$b$bЯ стараюсь держаться в стороне от вооруженных конфликтов, но заказ есть заказ. Поскольку ты из Орды, я решил передать его тебе.$b$bАрмии Покорителей нужна партия красного лука-порея.$b$bПохоже, их бойцы жить без него не могут!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'zhCN', '产品订单：统御先锋军I', '在日歌农场播种8颗红韭花种子。', '<从老愚的字迹中可以看出来，他的内心似乎充满矛盾。>$b$b我总是尽量避免冲突，但订单就是订单。鉴于你是部落的成员，我决定把这份订单交给你处理。$b$b统御先锋军要订购一批红韭花。$b$b下方的军队仿佛有再多红韭花都不够用！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32642, 'zhTW', '工作令：制霸先鋒軍I', '在日歌農莊播下8顆紅花蔥的種子。', '<從字跡似乎可以看出尹矛盾的情緒。>$b$b我向來不喜歡淌渾水，但是工作令就是工作令。既然你是部落的一員，我決定將工作令交給你。$b$b制霸先鋒軍需要紅花蔥。$b$b那裡的部隊似乎總是吃不膩紅花蔥!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'deDE', 'Arbeitsauftrag: Herrschaftsoffensive II', 'Erntet 8 reife Rotblütenlauchpflanzen und liefert 40 Rotblütenlauchstangen an die Herrschaftsoffensive.', 'Sobald Euer Rotblütenlauch erntereif ist, müsst Ihr ihn nur noch für die Abholung einpacken.$b$bDenkt Euch nur, Euer Rotblütenlauch wird ein Teil des Kampfes Eurer Fraktion in Pandaria sein!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'esES', 'Pedido de la Ofensiva de Dominancia II', 'Cosecha 8 plantas de puerro Rojaflor maduras y consigue 40 puerros Rojaflor para la Ofensiva de Dominancia.', 'Cuando tus puerros Rojaflor estén listos para recogerlos, solo tienes que empaquetarlos para que se los lleven.$b$b¡Piensa que tus puerros Rojaflor serán parte de la aportación de tu facción a Pandaria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'esMX', 'Pedido de la Ofensiva de Dominancia II', 'Cosecha 8 plantas de puerro Rojaflor maduras y consigue 40 puerros Rojaflor para la Ofensiva de Dominancia.', 'Cuando tus puerros Rojaflor estén listos para recogerlos, solo tienes que empaquetarlos para que se los lleven.$b$b¡Piensa que tus puerros Rojaflor serán parte de la aportación de tu facción a Pandaria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'frFR', 'Commande : offensive Domination II', 'Récoltez 8 plants de Poireaux à fleur rouge mûrs et fournissez 40 Poireaux à fleur rouge pour l’Offensive Domination.', 'Quand vos poireaux à fleur rouge seront prêts à être cueillis, vous n’aurez qu’à les conditionner pour la livraison.$b$bPensez-y. Vos poireaux à fleur rouge contribueront aux efforts de votre faction en Pandarie !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'itIT', 'Ordine: Offensiva del Dominio II', 'Raccogli 8 Porri Rossi Maturi per consegnare 40 Porri Rossi all\'Offensiva del Dominio.', '<Una volta che i Porri Rossi saranno pronti per essere raccolti, bisognerà solo confezionarli per il trasporto.>$b$b<Non vedi l\'ora che i tuoi Porri Rossi giungano a destinazione!>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'koKR', '주문서: 지배령 선봉대 II', '잘 익은 붉은꽃 부추를 8개 수확한 후 지배령 선봉대에 붉은꽃 부추를 40개 공급해야 합니다.', '붉은꽃 부추를 수확하고 나면, 운송할 수 있도록 포장하기만 하면 됩니다.$b$b생각해 보십시오. 붉은꽃 부추 농사로 판다리아에서 평판을 얻을 수 있다니요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'ptBR', 'Ordem de Serviço: Ofensiva de Dominância II', 'Colha 8 pés de Alho-poró de Florada Vermelha Maduro e forneça 40 Alhos-porós de Florada Vermelha à Ofensiva de Dominância.', 'Quando seus alhos-porós de florada vermelha estiverem prontos para serem colhidos, você só precisará embalá-los para a entrega.$b$bPense só: seus alhos-porós de florada vermelha farão parte dos esforços de sua facção em Pandária!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'ruRU', 'Заказ: Армия Покорителей II', 'Соберите урожай с 8 растений красного лука-порея и упакуйте 40 единиц красного лука порея для Армии Покорителей.', 'Когда красный лук-порей вырастет, останется только сложить его в ящик.$b$bПодумать только, этот красный лук-порей поможет нашей армии в Пандарии!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'zhCN', '产品订单：统御先锋军II', '收获8个成熟的红韭花，并向统御先锋军提供40份红韭花。', '等你的红韭花长到可以采摘时，你只要将它们打包，等待提取就好了。$b$b记住一点，你的红韭花能帮你提升在潘达利亚的阵营声望！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32643, 'zhTW', '工作令：制霸先鋒軍II', '採收8株成熟的紅花蔥，並提供40顆紅花蔥給制霸先鋒軍。', '一旦你的紅花蔥長大成熟了，你只需要將它們裝入木箱等人來取。$b$b只要這樣想，這些紅花蔥也會是你的陣營對潘達利亞所做的貢獻!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'deDE', 'Der Angriff auf Shaol\'mara', 'Schließt den Angriff auf Shaol\'mara ab. Sprecht mit Jaina Prachtmeer, um den Angriff zu beginnen.', 'Unsere Erkundungstätigkeiten an der Küste haben uns wertvolle Informationen über die Insel eingebracht, $C.$B$BWir werden allerdings nichts erreichen, wenn wir nicht weiter auf das Festland vordringen.$B$BIch habe einen Trupp unserer besten Lufteinheiten zusammengestellt, um die Attacke einzuleiten.$B$BIch möchte, dass Ihr Euch diesem Angriff anschließt. Ein Champion wie Ihr könnte die Schlacht im kritischen Moment zu unseren Gunsten wenden.$B$BWenn Ihr bereit seid, mit dem Angriff zu beginnen, sprecht mit mir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'esES', 'El Asalto a Shaol\'mara', 'Completa el Asalto a Shaol\'mara. Habla con Jaina Valiente para iniciar el ataque.', 'Nuestro afán en explorar la costa nos ha proporcionado valiosos datos de inteligencia acerca de la isla, $c.$B$BSin embargo, no conseguiremos nada si no avanzamos hacia el interior.$B$BHe reunido a un equipo con nuestras mejores unidades aéreas para iniciar el ataque.$B$BMe gustaría que te unieras al asalto. $gUn campeón:Una campeona; como tú podría inclinar la balanza de la batalla a nuestro favor en un momento crítico.$B$BVen a verme cuando estés $glisto:lista; para el ataque.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'esMX', 'El Asalto a Shaol\'mara', 'Completa el Asalto a Shaol\'mara. Habla con Jaina Valiente para iniciar el ataque.', 'Nuestro afán en explorar la costa nos ha proporcionado valiosos datos de inteligencia acerca de la isla, $c.$B$BSin embargo, no conseguiremos nada si no avanzamos hacia el interior.$B$BHe reunido a un equipo con nuestras mejores unidades aéreas para iniciar el ataque.$B$BMe gustaría que te unieras al asalto. $gUn campeón:Una campeona; como tú podría inclinar la balanza de la batalla a nuestro favor en un momento crítico.$B$BVen a verme cuando estés $glisto:lista; para el ataque.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'frFR', 'L’assaut sur Shaol’mara', 'Terminez l’Assaut sur Shaol’mara. Parlez à Jaina Portvaillant pour commencer l’attaque.', 'Nos missions de reconnaissance le long de la côte nous ont permis de récolter des informations précieuses à propos de cette île, $c.$b$bCependant, nous n’arriverons à rien si nous n’avançons pas plus en avant dans les terres.$b$bJ’ai rassemblé une équipe composée de l’élite de nos unités aériennes afin de commencer l’assaut.$b$bJe souhaiterais que vous vous joigniez à cette attaque. $gUn champion tel:Une championne telle; que vous pourrait bien faire basculer la bataille en notre faveur au moment critique.$b$bLorsque vous serez $gprêt:prête; à démarrer l’attaque, prévenez-moi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'itIT', 'Assalto a Shaol\'mara', 'Completa l\'Assalto a Shaol\'mara. Parla con Jaina Marefiero per dare il via all\'attacco.', 'Le nostre esplorazioni lungo la costa ci hanno fornito alcune importanti informazioni sull\'isola, $C.$B$BComunque, non riusciremo a concludere nulla se non troveremo il modo di avanzare nell\'entroterra.$B$BHo organizzato una squadra delle nostre unità aeree più letali perché inizi l\'attacco.$B$BVorrei che ti unissi all\'assalto. $gUn campione:Una campionessa; come te potrebbe cambiare le sorti della battaglia a nostro favore nei momenti decisivi.$B$BQuando sarai $gpronto:pronta; a iniziare l\'attacco, parla con me.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'koKR', '샤올마라 강습', '샤올마라 강습 작전을 완수해야 합니다. 제이나 프라우드무어와 대화하여 작전을 시작할 수 있습니다.', '해안을 정찰한 결과 이 섬에 대해 소중한 정보를 얻었어요.$B$B하지만 계속 전진하여 본토를 밟을 수 없다면 이 모든 게 헛일이에요.$B$B공격을 개시하려고 제가 최정예 공군 부대를 소집해 두었어요.$B$B그대도 강습에 참여해 주세요. 그대 같은 용사가 있어 주면 결정적인 시점에 전세를 뒤집을 수도 있을 테니까요.$B$B공격을 시작할 준비가 되면 제게 이야기하세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'ptBR', 'O ataque a Shaol\'mara', 'Complete o Ataque a Shaol\'mara. Fale com Jaina Proudmore para iniciar o ataque.', 'Com seus esforços ao longo da costa, nossos batedores nos deram informações preciosas sobre a ilha.$B$BNo entanto, não vamos conseguir nada sem avançar terra adentro.$B$BReuni uma equipe com a elite de nossas unidades aéreas para iniciar o ataque.$B$BGostaria que você fosse também. $GUm campeão:Uma campeã; como você pode ser $Gdecisivo:decisiva; para nós neste momento crítico.$B$BQuando estiver $Gpronto:pronta; para iniciar o ataque, fale comigo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'ruRU', 'Атака на Шаол\'мару', 'Завершите сценарий \"Атака на Шаол\'мару\". Поговорите с Джайной Праудмур, чтобы начать атаку.', 'Благодаря усилиям разведки на побережье у нас появилась ценная информация об острове, |3-6($c).$B$BОднако мы ничего не достигнем, если не начнем продвигаться вглубь суши.$B$BЯ собрала команду лучших бойцов из наших воздушных отрядов.$B$BЯ бы хотела, чтобы ты $gприсоединился:присоединилась; к нападению. Такой герой, как ты, может переломить ход боя в нашу пользу в решающий момент.$B$BКогда будешь $gготов:готова; к атаке, поговори со мной.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'zhCN', '突袭绍尔马拉', '完成突袭绍尔马拉。与吉安娜·普罗德摩尔交谈从而开始进攻。', '我们在海岸沿线部署的侦察力量为我们提供了关于这座岛屿的宝贵情报，$c。$B$B然而，如果我们不向本岛推进，就必将一事无成。$B$B我已经集结了一支由精英空军单位组成的战队，准备发起攻击。$B$B我想让你参与这次行动。像你这样的英雄一定能帮我们在紧要关头力挽狂澜。$B$B当你做好出击准备时，来与我谈谈。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32644, 'zhTW', '襲擊韶嘛喇', '完成襲擊韶嘛喇。與珍娜‧普勞德摩爾交談以展開攻擊。', '我們的密探在海岸進行的偵察行動，提供我們島上的可貴資訊，$c。$B$B然而，我們若不朝大陸發動攻擊，便無法取得任何成果。$B$B我集結了一支最優秀的空中部隊準備展開進攻。$B$B我要你加入攻擊的行列。像你這樣的勇士能在關鍵時刻扭轉戰局。$B$B當你準備好開戰時告訴我一聲。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'deDE', 'Arbeitsauftrag: Operation Schildwall I', 'Pflanzt 8 Hexenbeerensamen auf dem Gehöft Sonnensang.', '<Yoons Handschrift überzieht die Seite, doch er scheint mit gemischten Gefühlen geschrieben zu haben.>$b$bNormalerweise versuche ich, mich aus Konflikten herauszuhalten, aber Bestellung ist Bestellung. Da Ihr zur Allianz gehört, habe ich beschlossen, diese Bestellung an Euch weiterzuleiten.$b$bOperation Schildwall hat eine große Menge Hexenbeeren angefordert.$b$bIch bin mir nicht sicher, aber ich glaube, sie haben vor damit eine Art Waffe zu bauen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'esES', 'Pedido de la Operación: Muro de escudos I', 'Planta 8 semillas de moruja en el Rancho Cantosol.', '<La letra de Yoon decora la página, aunque sus sentimientos parecen contradictorios.>$b$bNormalmente intento mantenerme al margen de los conflictos, pero un pedido es un pedido. Ya que eres de la Alianza, he decidido transmitirte este pedido a ti.$b$bLa Operación: Muro de escudos ha solicitado un montón de morujas.$b$bNo lo sé con seguridad, pero creo que pretenden convertirlas en algún tipo de arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'esMX', 'Pedido de la Operación: Muro de escudos I', 'Planta 8 semillas de moruja en el Rancho Cantosol.', '<La letra de Yoon decora la página, aunque sus sentimientos parecen contradictorios.>$b$bNormalmente intento mantenerme al margen de los conflictos, pero un pedido es un pedido. Ya que eres de la Alianza, he decidido transmitirte este pedido a ti.$b$bLa Operación: Muro de escudos ha solicitado un montón de morujas.$b$bNo lo sé con seguridad, pero creo que pretenden convertirlas en algún tipo de arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'frFR', 'Commande : opération Bouclier I', 'Plantez 8 Graines de sorcielle à la Ferme Chant du Soleil.', '<L’écriture de Yoon s\'étale sur la page avec élégance, mais ses sentiments semblent partagés.>$b$bJe tente normalement de ne pas me mêler des conflits, mais une commande est une commande. Comme vous faites partie de l’Alliance, j’ai décidé de vous transmettre celle-ci.$b$bL’opération Bouclier demande une grosse quantité de sorcielles.$b$bCe n’est qu’une supposition, mais je crois qu’ils envisagent de les utiliser pour une sorte d’arme.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'itIT', 'Ordine: Operazione Baluardo I', 'Pianta 8 Semi di Stregobacca alla Tenuta Cantasole.', '<La scrittura di Yoon decora la pagina, e le emozioni che traspaiono dalla sua calligrafia sembrano contrastanti.>$b$bNormalmente cerco di rimanere fuori da ogni tipo di conflitto, ma un ordine è un ordine. Dal momento che fai parte dell\'Alleanza, ho deciso di assegnare quest\'ordine a te.$b$bL\'Operazione Baluardo ha richiesto un\'intera cassa di Stregobacche.$b$bNon ne sono sicuro, ma credo che abbiano intenzione di usarle con qualche tipo di arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'koKR', '주문서: 철벽방패 작전대 I', '태양노래 농장에 요술딸기 씨앗을 8개 심어야 합니다.', '<종이를 장식한 윤의 필체에 그의 갈등하는 심경이 드러나 있습니다.>$b$b저는 보통 마찰을 피하는 편이지만, 주문은 주문이니까요. 당신이 얼라이언스라서 이 주문을 당신에게 맡기기로 했습니다.$b$b철벽방패 작전대에서 요술딸기를 잔뜩 주문했습니다.$b$b잘은 모르겠지만, 요술딸기를 모종의 무기에 이용할 생각인 것 같습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'ptBR', 'Ordem de Serviço: Operação: Muralha de Escudos I', 'Plante 8 Sementes de Uva-de-bruxa na Fazenda Sol Cantante.', '<A escrita de Yoon preenche a página; seus sentimentos parecem conflituosos.>$b$bEu geralmente tento ficar fora de conflitos, mas um pedido é um pedido. Já que você é da Aliança, decidi passar esse pedido para você.$b$bA Operação: Muralha de Escudos pediu um montão de uvas-de-bruxa.$b$bNão sei ao certo, mas parece que querem transformá-las em alguma arma.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'ruRU', 'Заказ: операция \"Заслон\" I', 'Посадите 8 семян ведьминых ягод на ферме Солнечной Песни.', '<Страница исписана почерком Юна. Похоже, его одолевали противоречивые чувства.>$b$bЯ стараюсь держаться в стороне от вооруженных конфликтов, но заказ есть заказ. Поскольку ты из Альянса, я решил передать его тебе.$b$bОперации \"Заслон\" нужно много ведьминых ягод.$b$bТочно не знаю, но, кажется, они собираются использовать их в качестве оружия.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'zhCN', '产品订单：神盾守备军I', '在日歌农场播种8颗女巫浆果种子。', '<从老愚的字迹中可以看出来，他的内心似乎充满矛盾。>$b$b我总是尽量避免冲突，但订单就是订单。鉴于你是联盟的成员，我决定把这份订单交给你处理。$b$b神盾守备军要订购一批女巫浆果。$b$b尽管我不能确定，但我想他们有办法把它们用在某种武器上。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32645, 'zhTW', '工作令：鐵壁特遣行動I', '在日歌農莊播下8顆巫莓的種子。', '<從字跡似乎可以看出尹矛盾的情緒。>$b$b我向來不喜歡淌渾水，但是工作令就是工作令。既然你是聯盟的一員，我決定將工作令交給你。$b$b鐵壁特遣行動需要一堆巫莓。$b$b雖然不肯定，但是我認為他們想出了某種把巫莓當成武器的辦法。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'deDE', 'Arbeitsauftrag: Operation Schildwall II', 'Erntet 8 reife Hexenbeerenpflanzen und liefert 40 Hexenbeeren an Operation Schildwall.', 'Sobald Eure Hexenbeeren erntereif sind, müsst Ihr sie nur noch für die Abholung einpacken.$b$bDenkt Euch nur, Eure Hexenbeeren werden ein Teil der Anstrengungen Eurer Fraktion in Pandaria sein!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'esES', 'Pedido de la Operación: Muro de escudos II', 'Cosecha 8 plantas de moruja maduras y consigue 40 morujas para la Operación: Muro de escudos.', 'Cuando tus morujas estén listas para recogerlas, solo tienes que empaquetarlas para que se las lleven.$b$b¡Piensa que tus morujas serán parte de la aportación de tu facción en Pandaria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'esMX', 'Pedido de la Operación: Muro de escudos II', 'Cosecha 8 plantas de moruja maduras y consigue 40 morujas para la Operación: Muro de escudos.', 'Cuando tus morujas estén listas para recogerlas, solo tienes que empaquetarlas para que se las lleven.$b$b¡Piensa que tus morujas serán parte de la aportación de tu facción en Pandaria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'frFR', 'Commande : opération Bouclier II', 'Récoltez 8 plants de Sorcielles mûres et fournissez 40 Sorcielles pour l’Opération Bouclier.', 'Quand vos sorcielles seront prêtes à être cueillies, vous n’aurez qu’à les conditionner pour la livraison.$b$bPensez-y. Vos sorcielles contribueront aux efforts de votre faction en Pandarie !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'itIT', 'Ordine: Operazione Baluardo II', 'Raccogli 8 Stregobacche Mature per consegnare 40 Stregobacche all\'Operazione Baluardo.', '<Una volta che le Stregobacche saranno pronte per essere raccolte, bisognerà solo confezionarle per il trasporto.>$b$b<Non vedi l\'ora che le tue Stregobacche giungano a destinazione!>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'koKR', '주문서: 철벽방패 작전대 II', '잘 익은 요술딸기를 8개 수확한 후 철벽방패 작전대에 요술딸기를 40개 공급해야 합니다.', '요술딸기를 수확하고 나면, 운송할 수 있도록 포장하기만 하면 됩니다.$b$b생각해 보십시오. 요술딸기 농사로 판다리아에서 평판을 얻을 수 있다니요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'ptBR', 'Ordem de Serviço: Operação: Muralha de Escudos II', 'Colha 8 pés de Uvas-de-bruxa Maduras e forneça 40 Uvas-de-bruxa para a Operação: Muralha de Escudos.', 'Quando suas uvas-de-bruxa estiverem prontas para serem colhidas, você só precisará embalá-las para a entrega.$b$bPense só: suas uvas-de-bruxa farão parte dos esforços de sua facção em Pandária!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'ruRU', 'Заказ: операция \"Заслон\" II', 'Соберите урожай с 8 кустов ведьминых ягод и передайте 40 ведьминых ягод операции \"Заслон\".', 'Когда ведьмины ягоды вырастут, останется только сложить их в ящик.$b$bПодумать только, эти ведьмины ягоды помогут нашим силам в Пандарии!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'zhCN', '产品订单：神盾守备军II', '收获8个成熟的女巫浆果，并向神盾守备军提供40份女巫浆果。', '等你的女巫浆果长到可以采摘时，你只要将它们打包，等待提取就好了。$b$b记住一点，你的女巫浆果能帮你提升在潘达利亚的阵营声望！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32646, 'zhTW', '工作令：鐵壁特遣行動II', '採收8株成熟的巫莓，並提供40顆巫莓給鐵壁特遣行動。', '一旦你的巫莓長大成熟了，你只需要將它們裝入木箱等人來取。$b$b你想想，這些巫莓會成為你的陣營對潘達利亞所做的貢獻!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'deDE', 'Arbeitsauftrag: Goldener Lotus I', 'Pflanzt 8 weiße Rübensamen auf dem Gehöft Sonnensang.', '<Yoon hat seine Bestellung kurz gefasst.>$b$bDer Goldene Lotus hat eine große Menge weißer Rüben angefordert.$b$bSie haben etwas von ärgerlichen Geistern erwähnt, die in Nebelhauch ihr Unwesen treiben. Anscheinend mögen die Geister keine Rüben!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'esES', 'Pedido del Loto Dorado I', 'Planta 8 semillas de naba blanca en el Rancho Cantosol.', '<Yoon ha sido breve en su pedido.>$b$bEl Loto Dorado ha solicitado gran cantidad de nabas blancas.$b$bSu pedido mencionaba algo sobre duendes alborotadores que siembran el caos en la Aldea Bruma Otoñal. ¡Parece que a los duendes no les gustan las nabas!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'esMX', 'Pedido del Loto Dorado I', 'Planta 8 semillas de naba blanca en el Rancho Cantosol.', '<Yoon ha sido breve en su pedido.>$b$bEl Loto Dorado ha solicitado gran cantidad de nabas blancas.$b$bSu pedido mencionaba algo sobre duendes alborotadores que siembran el caos en la Aldea Bruma Otoñal. ¡Parece que a los duendes no les gustan las nabas!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'frFR', 'Commande : Lotus doré I', 'Plantez 8 Graines de navet blanc à la Ferme Chant du Soleil.', '<Yoon ne s’est pas étendu sur cette commande.>$b$bLe Lotus doré demande un grand nombre de navets blancs.$b$bLeur commande mentionne des lutins pénibles semant le chaos à Tombe-Brume. Apparemment, les lutins n’aiment pas les navets !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'itIT', 'Ordine: Loto Dorato I', 'Pianta 8 Semi di Rapa Bianca alla Tenuta Cantasole.', '<In questo ordine Yoon ha inserito soltanto i dettagli essenziali.>$b$bIl Loto Dorato ha richiesto un grosso numero di Rape Bianche.$b$bLa loro richiesta faceva riferimento a qualcosa riguardo ad alcuni spiritelli piantagrane che stanno creando problemi a Calanebbia. A quanto pare a questi spiritelli non piacciono le rape!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'koKR', '주문서: 황금 연꽃 I', '태양노래 농장에 하얀색 순무 씨앗을 8개 심어야 합니다.', '<윤이 이번 주문 의뢰서는 짧게 썼습니다.>$b$b황금 연꽃에서 하얀색 순무를 대량 주문했습니다.$b$b골칫덩어리 요마들이 안개내림 마을에서 말썽을 부린다던가 하는 이야기가 쓰여 있더군요. 요마들은 순무를 싫어하니까요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'ptBR', 'Ordem de Serviço: Lótus Dourado I', 'Plante 8 Sementes de Nabo Branco na Fazenda Sol Cantante.', '<Yoon fez uma lista de pedidos curta.>$b$bO Lótus Dourado requisitou um grande número de nabos brancos.$b$bO pedido dizia algo sobre duendes encrenqueiros espalhando o caos na Vila da Bruma Baixa. Parece que duendes não gostam de nabos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'ruRU', 'Заказ: Золотой Лотос I', 'Посадите 8 семян белой репы на ферме Солнечной Песни.', '<Этот заказ Юн изложил кратко.>$b$bЗолотой Лотос попросил огромное количество белой репы.$b$bВ тексте заказа говорилось о злых духах, которые устроили беспорядки в деревне Туманного Водопада. Судя по всему, духи не любят репу!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'zhCN', '产品订单：金莲教I', '在日歌农场播种8颗白色芜菁种子。', '<老愚对这份订单的要求说得简明扼要。>$b$b金莲教要订购一大批白色芜菁。$b$b他们在提出需求时提到了在雾临村里大肆破坏的捣蛋林精。显然，林精不喜欢芜菁！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32647, 'zhTW', '工作令：金蓮會I', '在日歌農莊播下8顆白菜頭的種子。', '<尹的這張工作令相當簡短。>$b$b金蓮會需要大量的白菜頭。$b$b他們提到有愛搗蛋的妖精在霧臨村大鬧。顯然這些妖精討厭菜頭!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'deDE', 'Arbeitsauftrag: Goldener Lotus II', 'Erntet 8 reife Rübenpflanzen und liefert 40 weiße Rüben an den Goldenen Lotus.', 'Hoffen wir, dass dieses Gemüse geeignet ist!$b$bSobald Eure weißen Rüben erntereif sind, müsst Ihr sie nur noch für die Abholung einpacken.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'esES', 'Pedido del Loto Dorado II', 'Cosecha 8 plantas de naba blanca maduras y consigue 40 nabas blancas para el Loto Dorado.', '¡Bueno, esperemos que esta cosecha sea adecuada!$b$bCuando tus nabas blancas estén listas para recogerlas, solo tendrás que empaquetarlas para que se las lleven.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'esMX', 'Pedido del Loto Dorado II', 'Cosecha 8 plantas de naba blanca maduras y consigue 40 nabas blancas para el Loto Dorado.', '¡Bueno, esperemos que esta cosecha sea adecuada!$b$bCuando tus nabas blancas estén listas para recogerlas, solo tendrás que empaquetarlas para que se las lleven.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'frFR', 'Commande : Lotus doré II', 'Récoltez 8 plants de Navets blancs mûrs et fournissez 40 Navets blancs au Lotus doré.', 'Bien, espérons que cette récolte conviendra !$b$bQuand vos navets blancs seront prêts à être cueillis, vous n’aurez qu’à les conditionner pour la livraison.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'itIT', 'Ordine: Loto Dorato II', 'Raccogli 8 Rape Bianche Mature per consegnare 40 Rape Bianche al Loto Dorato.', '<Mentre li prepari, ti chiedi se questi ortaggi siano di buona qualità.>$b$b<Una volta che le tue Rape Bianche saranno pronte per essere raccolte, bisognerà solo confezionarle per il trasporto.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'koKR', '주문서: 황금 연꽃 II', '잘 익은 하얀색 순무를 8개 수확한 후 황금 연꽃에 하얀색 순무를 40개 공급해야 합니다.', '요마 퇴치에 특효겠군요!$b$b하얀색 순무를 수확하고 나면, 운송할 수 있도록 포장하기만 하면 됩니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'ptBR', 'Ordem de Serviço: Lótus Dourado II', 'Colha 8 pés de Nabo Branco Maduro e forneça 40 Nabos Brancos para o Lótus Dourado.', 'Bem, espero que a safra sirva!$b$bQuando seus nabos brancos estiverem prontos para serem colhidos, você só precisará embalá-los para a entrega.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'ruRU', 'Заказ: Золотой Лотос II', 'Соберите 8 спелых белых реп и приготовьте 40 белых реп для Золотого Лотоса.', 'Будем надеяться, урожай им понравится!$b$bКогда эта белая репа созреет, ее надо будет лишь сложить в ящик.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'zhCN', '产品订单：金莲教II', '收获8个成熟的白色芜菁，并向金莲教提供40份白色芜菁。', '嗯，希望这些作物符合他们的要求！$b$b等你的白色芜菁长到可以采摘时，你只要将它们打包，等待提取就好了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32648, 'zhTW', '工作令：金蓮會II', '採收8株成熟的白菜頭，並提供40顆白菜頭給金蓮會。', '希望這些作物符合他們的需要!$b$b一旦你的白菜頭準備好收成，你只要把它們打包好等人來拿就可以了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'deDE', 'Arbeitsauftrag: Shado-Pan I', 'Pflanzt 8 Grünkohlsamen auf dem Gehöft Sonnensang.', '<Yoons Handschrift erscheint fahrig. Er muss aufgeregt gewesen sein.>$b$bDie Shado-Pan wollen Gemüse von Euch! Was für eine Ehre!$b$bHeute Morgen kam eine Bestellung für Grünkohl an. Anscheinend machen sie daraus eine sehr schmackhafte Kohlsuppe.$b$bNur das Beste ist für die Shado-Pan gut genug!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'esES', 'Pedido del Shadopan I', 'Planta 8 semillas de repollo verde en el Rancho Cantosol.', '<La letra de Yoon parece apresurada. Debía de estar nervioso.>$b$b¡El Shadopan te pide cosechas! ¡Qué honor!$b$bEsta mañana ha llegado un pedido de repollos verdes. Parece ser que los usan para hacer una sabrosa sopa de repollo.$b$b¡Hay que darle lo mejor al Shadopan!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'esMX', 'Pedido del Shadopan I', 'Planta 8 semillas de repollo verde en el Rancho Cantosol.', '<La letra de Yoon parece apresurada. Debía de estar nervioso.>$b$b¡El Shadopan te pide cosechas! ¡Qué honor!$b$bEsta mañana ha llegado un pedido de repollos verdes. Parece ser que los usan para hacer una sabrosa sopa de repollo.$b$b¡Hay que darle lo mejor al Shadopan!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'frFR', 'Commande : Pandashan I', 'Plantez 8 Graines de chou vert à la Ferme Chant du Soleil.', '<L’écriture de Yoon trahit sa hâte. Il devait être excité.>$b$bLes Pandashan veulent votre récolte ! Quel honneur !$b$bIls m’ont fait parvenir une commande de choux verts ce matin. Apparemment, ils en font une soupe assez délicieuse.$b$bLes Pandashan ne veulent que du premier choix !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'itIT', 'Ordine: Shandaren I', 'Pianta 8 Semi di Cavolo Verde alla Tenuta Cantasole.', '<La scrittura di Yoon sembra affrettata. Doveva essere molto eccitato quando l\'ha scritto.>$b$bGli Shandaren desiderano i tuoi prodotti! Quale onore!$b$bÈ arrivato un ordine di Cavoli Verdi questa mattina; a quanto pare ci vogliono fare una gustosa zuppa.$b$bGli Shandaren esigono solo il meglio!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'koKR', '주문서: 음영파 I', '태양노래 농장에 녹색 양배추 씨앗을 8개 심어야 합니다.', '<윤의 필체를 보니 급하게 휘갈겨 쓴 듯합니다. 흥분한 모양입니다.>$b$b음영파가 당신에게 농작물을 주문했습니다! 대단한 영예로군요!$b$b주문은 오늘 아침에 들어왔는데, 녹색 양배추가 필요하다고 합니다. 음영파에서는 그걸로 맛있는 양배추 수프를 끓이는 모양이에요.$b$b음영파인 만큼 최고가 아니면 안 됩니다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'ptBR', 'Ordem de Serviço: Os Shado-Pan I', 'Plante 8 Sementes de Repolho Verde na Fazenda Sol Cantante.', '<A escrita de Yoon parece apressada. Ele devia estar empolgado.>$b$bOs Shado-Pan querem sua colheita! Que honra!$b$bChegou um pedido hoje de manhã de repolhos verdes. Parece que fazem uma sopa de repolho bem gostosa com eles.$b$bPara os Shado-Pan, só do bom e do melhor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'ruRU', 'Заказ: Шадо-Пан I', 'Посадите 8 семян белокочанной капусты на ферме Солнечной Песни.', '<Судя по почерку, Юн торопился. Видимо, его переполняли эмоции.>$b$bШадо-Пан попросили тебя вырастить для них овощи! Какая честь!$b$bСегодня утром пришел заказ на белокочанную капусту. Я слышал, они варят из нее вкуснейший суп.$b$bШадо-Пан нужен только продукт высшего качества!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'zhCN', '产品订单：影踪派I', '在日歌农场播种8颗绿色卷心菜种子。', '<老愚的字迹显得仓促潦草。他一定是在异常兴奋的情况下书写的。>$b$b影踪派想让你帮他们种庄稼！这简直是无上的荣耀！$b$b我在今天早上收到了他们订购绿色卷心菜的订单。显然，他们能用这种食材熬制出相当可口的卷心菜汤来。$b$b只有最优质的果蔬才配得上影踪派！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32649, 'zhTW', '工作令：影潘I', '在日歌農莊播下8顆綠色捲心菜的種子。', '<尹的字跡潦草，他當時的心情肯定很興奮。>$b$b影潘需要你的作物!這是多麼榮幸的事情啊!$b$b今早他們提出需要綠色捲心菜的要求。顯然他們之前做的捲心菜湯相當美味。$b$b只有最棒的食材才配得上影潘!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'deDE', 'Arbeitsauftrag: Shado-Pan II', 'Erntet 8 reife Grünkohlpflanzen und liefert 40 Stück Grünkohl an die Shado-Pan.', 'Nun, für heute ist unsere Arbeit getan! Jetzt müsst Ihr nur noch auf die morgige Ernte warten.$b$bSobald Euer Grünkohl erntereif ist, müsst Ihr ihn nur noch für die Abholung einpacken.$b$bFür die Shado-Pan ist nur der beste Kohl gut genug!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'esES', 'Pedido del Shadopan II', 'Cosecha 8 plantas de repollo verde maduras y consigue 40 repollos verdes para el Shadopan.', '¡Bueno, tu trabajo ha terminado por hoy! Ahora hay que esperar a la cosecha de mañana.$b$bCuando tus repollos verdes estén listos para recogerlos, solo tendrás que empaquetarlos para que se los lleven.$b$b¡Hay que darle los mejores repollos al Shadopan!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'esMX', 'Pedido del Shadopan II', 'Cosecha 8 plantas de repollo verde maduras y consigue 40 repollos verdes para el Shadopan.', '¡Bueno, tu trabajo ha terminado por hoy! Ahora hay que esperar a la cosecha de mañana.$b$bCuando tus repollos verdes estén listos para recogerlos, solo tendrás que empaquetarlos para que se los lleven.$b$b¡Hay que darle los mejores repollos al Shadopan!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'frFR', 'Commande : Pandashan II', 'Récoltez 8 plants de Choux verts mûrs et fournissez 40 Choux verts pour les Pandashan.', 'Bien, vous avez assez travaillé pour aujourd’hui ! Il n’y a plus qu’à attendre la récolte demain.$b$bQuand vos choux verts seront prêts à être cueillis, vous n’aurez qu’à les conditionner pour la livraison.$b$bLes Pandashan ne veulent que des choux de premier choix !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'itIT', 'Ordine: Shandaren II', 'Raccogli 8 Cavoli Verdi Maturi per consegnare 40 Cavoli Verdi agli Shandaren.', '<Bene, il lavoro è finito per oggi! Ora non resta che attendere la raccolta di domani.>$b$b<Una volta che i Cavoli Verdi saranno pronti per essere raccolti, bisognerà solo confezionarli per il trasporto.>$b$b<Gli Shandaren esigono solo il meglio!>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'koKR', '주문서: 음영파 II', '잘 익은 녹색 양배추를 8개 수확한 후 음영파에 녹색 양배추를 40개 공급해야 합니다.', '녹색 양배추를 수확하고 나면, 운송할 수 있도록 포장하기만 하면 됩니다.$b$b음영파인 만큼 최고의 양배추가 아니면 안 됩니다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'ptBR', 'Ordem de Serviço: Os Shado-Pan II', 'Colha 8 pés de Repolho Verde Maduro e forneça 40 Repolhos Verdes para os Shado-Pan.', 'Bem, por hoje chega! Agora é só esperar a colheita amanhã.$b$bQuando seus repolhos verdes estiverem prontos para serem colhidos, você só precisará embalá-los para a coleta.$b$bPara os Shado-Pan, só os melhores repolhos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'ruRU', 'Заказ: Шадо-Пан II', 'Соберите урожай с 8 участков белокочанной капусты и приготовьте 40 кочанов белокочанной капусты для Шадо-Пан.', 'На сегодня работа закончена! Завтра можно будет собирать урожай.$b$bКогда белокочанная капуста вырастет, останется только сложить ее в ящик.$b$bШадо-Пан нужна только самая лучшая капуста!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'zhCN', '产品订单：影踪派II', '收获8个成熟的绿色卷心菜，并向影踪派提供40份绿色卷心菜。', '嗯，你今天的工作已经完成了！等待明天的收获吧。$b$b等你的绿色卷心菜长到可以采摘时，你只要将它们打包，等待提取就好了。$b$b只有最棒的卷心菜才配得上影踪派！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32650, 'zhTW', '工作令：影潘II', '採收8株成熟的綠色捲心菜，並提供40顆綠色捲心菜給影潘。', '你今日的工作已經完成了!現在只要等著明天收割就好。$b$b一旦你的綠色捲心菜長大成熟，你只要把它們打包好等人來拿就可以了。$b$b只有最棒的捲心菜才配得上影潘!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'deDE', 'In die Lüfte!', 'Verschafft Euch Zugang zu den Blitzaderminen.', 'Wir haben einen Weg gefunden, die Waffenproduktion der Mogu aufzuhalten, und es hat etwas mit der Kreatur zu tun, die Ihr von den Zandalari befreit habt.\n\nSprecht mit mir, wenn Ihr bereit seid, mehr zu erfahren.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'esES', '¡Hacia los Cielos!', 'Consigue acceder a la Mina del Relámpago.', 'Tenemos un plan para frenar la capacidad armamentística de los mogu que tiene que ver con la criatura que recuperaste de los Zandalari.\n\nHabla conmigo cuando estés $glisto:lista; para saber más.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'esMX', '¡Hacia los Cielos!', 'Consigue acceder a la Mina del Relámpago.', 'Tenemos un plan para frenar la capacidad armamentística de los mogu que tiene que ver con la criatura que recuperaste de los Zandalari.\n\nHabla conmigo cuando estés $glisto:lista; para saber más.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'frFR', 'En route vers les cieux !', 'Frayez-vous un chemin jusqu’à la Veine de Foudremine.', 'Nous avons un plan pour empêcher les mogu de fabriquer d’autres armes, et il implique la créature que vous avez prise aux Zandalari.\n\nParlez-moi quand vous serez $gprêt:prête; à en apprendre plus.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'itIT', 'Verso il Cielo!', 'Ottieni l\'accesso alle Miniere della Folgore.', 'Abbiamo un piano per porre freno alla produzione di armi dei Mogu, un piano che prevede l\'utilizzo della creatura che hai catturato dalle mani degli Zandalari.\n\nParla con me quando sei $gpronto:pronta; a saperne di più.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'koKR', '하늘 높이!', '번개 광맥 광산을 확보해야 합니다.', '모구의 무기 생산 과정에 제동을 걸 계획을 생각해냈습니다. 당신이 잔달라로부터 구해온 생물을 이용해서 말이지요.\n\n자세한 이야기를 알고 싶거든 제게 이야기하세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'ptBR', 'Para os Céus!', 'Obtenha acesso à Mina do Veio Relampejante.', 'Temos um plano para reduzir a capacidade de criar armas dos mogus, e ele envolve a criatura que você recuperou dos Zandalari.\n\nFale comigo quando estiver $Gpronto:pronta; para saber mais.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'ruRU', 'В небо!', 'Проникните в Шахту Молний.', 'С помощью террокрыла, которого ты $gнашел:нашла; у зандаларов, мы сможем нанести могу решающий удар и лишить их возможности продолжать изготавливать оружие.\n\nСкажи мне, когда будешь $gготов:готова;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'zhCN', '直冲云霄！', '进入闪电矿脉。', '我们想出了终止魔古族武器锻造的计划，而且要用上你从赞达拉那里找回的飞翼龙。\n\n等你准备好了解更多细节时，来和我交谈吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32652, 'zhTW', '飛上天吧!', '進入電光礦脈。', '我們計畫終止魔古製造武器的能力，計畫中會用到你從贊達拉那裡取得的生物。\n\n準備好的時候跟我說一聲。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'deDE', 'Arbeitsauftrag: Die Himmlischen Erhabenen I', 'Pflanzt 8 Streifenmelonensamen auf dem Gehöft Sonnensang.', '<Yoons vertraute Handschrift bedeckt das rissige Papier.>$b$bDie Schüler drüben in Chi-Jis Wiege brauchen Streifenmelonen. Anscheinend benutzen sie sie in ihren Übungen.$b$bEin Repräsentant wird bis morgen vorbeikommen und alle Streifenmelonen mitnehmen, die Ihr erübrigen könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'esES', 'Pedido de los Augustos Celestiales I', 'Planta 8 semillas de melón a rayas en el Rancho Cantosol.', '<La familiar letra de Yoon cubre el papel deshilachado.>$b$bLos aprendices de la Cuna de Chi-Ji necesitan melones a rayas. Parece ser que los utilizan en sus ejercicios de entrenamiento.$b$bMañana vendrá un representante para recoger todos los melones a rayas que puedas darle.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'esMX', 'Pedido de los Augustos Celestiales I', 'Planta 8 semillas de melón a rayas en el Rancho Cantosol.', '<La familiar letra de Yoon cubre el papel deshilachado.>$b$bLos aprendices de la Cuna de Chi-Ji necesitan melones a rayas. Parece ser que los utilizan en sus ejercicios de entrenamiento.$b$bMañana vendrá un representante para recoger todos los melones a rayas que puedas darle.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'frFR', 'Commande : les Astres vénérables I', 'Plantez 8 Graines de melon à rayures à la Ferme Chant du Soleil.', '<L’écriture familière de Yoon noircit le papier déchiré.>$b$bLes recrues du berceau de Chi Ji ont besoin de melons à rayures. Apparemment, elles s’en servent durant leurs exercices d’entraînement.$b$bUn représentant passera demain pour prendre tous les melons à rayures que vous pourrez lui fournir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'itIT', 'Ordine: Venerabili Celestiali I', 'Pianta 8 Semi di Melone Striato alla Tenuta Cantasole.', '<La familiare scrittura di Yoon scorre sulla carta logora.>$b$bGli allievi alla Culla di Chi-Ji hanno bisogno di Meloni Striati. A quanto pare, servono per le loro sessioni di allenamento.$b$bUn rappresentante arriverà domani per prelevare ogni Melone Striato che potrai dargli.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'koKR', '주문서: 위대한 천신회 I', '태양노래 농장에 줄무늬 멜론 씨앗을 8개 심어야 합니다.', '<눈에 익은 윤의 글씨가 너덜너덜한 종이에 휘갈겨져 있습니다.>$b$b츠지의 요람에 있는 수련생들에게 줄무늬 멜론이 필요하다고 합니다. 멜론을 수련에 사용하는 모양이지요.$b$b내일 천신회에서 사람이 와서 줄무늬 멜론을 있는 대로 가져갈 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'ptBR', 'Ordem de Serviço: Os Celestiais Majestosos I', 'Plante 8 Sementes de Melão Listrado na Fazenda Sol Cantante.', '<A escrita familiar de Yoon se espraia pelo papel esgarçado.>$b$bOs aprendizes lá no Berço de Chi-Ji precisam de melões listrados. Aparentemente, eles os usam em seus exercícios.$b$bUm representante virá amanhã coletar todos os melões listrados de que você puder dispor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'ruRU', 'Заказ: Августейшие Небожители I', 'Посадите 8 семян полосатой дыни на ферме Солнечной Песни.', '<Ветхая бумага испещрена знакомым почерком Юна.>$b$bУченикам из Колыбели Чи-Цзи нужны полосатые дыни. По всей видимости, для упражнений.$b$bЗавтра от них прибудет посыльный. Отдай ему столько дынь, сколько сможешь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'zhCN', '产品订单：至尊天神I', '在日歌农场播种8颗菜瓜种子。', '<老愚熟悉的字迹出现在这张破烂的纸条上。>$b$b在赤精栖木的学徒们需要菜瓜。显然，他们在训练中要用到这种果实。$b$b明天会有代表前来收集你所能种出来的所有菜瓜。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32653, 'zhTW', '工作令：聖獸天尊I', '在日歌農莊播下8顆條紋甜瓜的種子。', '<破爛的紙張上有尹熟悉的字跡。>$b$b赤吉搖籃的受訓弟子需要條紋甜瓜，顯然是拿來練習用的。$b$b明天將有個代表會來收集你所能提供的所有條紋甜瓜。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'deDE', 'Die Mauer muss weg!', 'Sprecht mit Lady Jaina Prachtmeer und dringt dann in die untere Stadt vor, um sie für die Allianz zu erobern.', 'Die Zeit ist gekommen. Unsere Stellung ist gut genug befestigt, sodass wir es riskieren können, in die Behausung des Donnerkönigs vorzudringen. Leider sieht es nicht so aus, als ob er dieses Tor in nächster Zeit öffnen wird.$b$bIch vermute also, dass wir uns unser eigenes Tor schaffen müssen. Lasst es mich wissen, wenn Ihr bereit seid, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'esES', '¡Abajo la Muralla!', 'Habla con Jaina Valiente, entra en el Bajo Arrabal y asegúralo para la Alianza.', 'Ha llegado el momento. Nuestras fuerzas están lo bastante bien atrincheradas para arriesgarnos a avanzar hacia la guarida del Rey del Trueno. Por desgracia, no parece que vaya a abrir esa puerta en un futuro próximo.$b$bSupongo que tendremos que crear nuestra propia puerta. Avísame cuando estés $glisto:lista;, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'esMX', '¡Abajo la Muralla!', 'Habla con Jaina Valiente, entra en el Bajo Arrabal y asegúralo para la Alianza.', 'Ha llegado el momento. Nuestras fuerzas están lo bastante bien atrincheradas para arriesgarnos a avanzar hacia la guarida del Rey del Trueno. Por desgracia, no parece que vaya a abrir esa puerta en un futuro próximo.$b$bSupongo que tendremos que crear nuestra propia puerta. Avísame cuando estés $glisto:lista;, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'frFR', 'Démolissez ce mur !', 'Parlez à Dame Jaina Portvaillant, puis introduisez-vous dans la Ville basse et sécurisez-la pour l’Alliance.', 'Le moment est venu. Nos forces sont assez bien retranchées ici, nous pouvons prendre le risque d’avancer et de pénétrer dans l’antre du roi-tonnerre. Malheureusement, ce n’est sûrement pas demain la veille qu’il ouvrira cette porte.$b$bJe suppose que nous allons devoir ouvrir notre propre porte, dans ce cas. Prévenez-moi quand vous serez $gprêt:prête;, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'itIT', 'Demolizione delle Mura', 'Parla con Dama Jaina Marefiero e poi fai irruzione nella città bassa per affidarla al solido controllo dell\'Alleanza.', 'L\'ora è giunta. Le nostre forze si trovano abbastanza protette da poter rischiare un attacco frontale al covo del Re del Tuono. Sfortunatamente, lui non sembra avere intenzione di aprire quel portale.$b$bCredo che dovremo arrangiarci in qualche modo. Fammi sapere quando sei $gpronto:pronta;, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'koKR', '성벽을 무너뜨려라!', '여군주 제이나 프라우드무어와 대화하고 얼라이언스를 위해 성벽을 확보해야 합니다.', '드디어 때가 왔어요. 여기 우리 병력이 충분히 모였으니 천둥왕의 은신처로 밀어붙일 수 있을 것 같습니다. 하지만 불행하게도 가까운 시일 내에 그가 직접 문을 열어줄 생각은 없을 것 같군요.$b$b우리가 직접 문을 만들어야겠습니다. 준비가 되면 말씀하세요, $n 님.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'ptBR', 'Derrubem a Muralha!', 'Fale com a Grã-senhora Jaina Proudmore, depois infiltre-se no bairro inferior e reivindique-o para a Aliança.', 'A hora chegou. Nossas forças estão bem entrincheiradas o bastante para que possamos arriscar uma investida ao covil do Rei Trovão. Infelizmente, acho que ele não vai abrir aquele portão tão cedo.$b$bAcho que teremos que fazer nosso próprio portão. Avise quando estiver $gpronto:pronta;, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'ruRU', 'Разрушить эту стену!', 'Поговорите с леди Джайной Праудмур, а затем займите нижний город и обеспечьте там безопасность для войск Альянса.', 'Итак, время пришло. Мы достаточно укрепились здесь, и теперь можно попытаться перейти в наступление и направиться прямо в логово Властелина Грома. К сожалению, в ближайшее время он вряд ли планирует открыть для нас ворота.$b$bПолагаю, нам придется принять роль незваных гостей и открыть эти ворота самостоятельно. Дай мне знать, когда будешь $gготов:готова;, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'zhCN', '摧毁城墙！', '与吉安娜·普罗德摩尔交谈，然后攻入城市下层，为联盟夺取它。', '时机终于到了。我们的大军已在这里稳住阵脚，现在可以冒险突进，前往雷电之王的老巢了。不幸的是，他好像一时半会还没有打开大门的意思。$b$b那我们就只好自己弄出一道门来了。等你做好准备就来找我，$n。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32654, 'zhTW', '突破城牆!', '與珍娜‧普勞德摩爾女士交談，然後攻下城市的下層區，為聯盟奪回這座城池。', '時候到了。我們的軍隊屯營在這裡養精蓄銳，該朝雷王的巢穴推進了。可惜，雷王看來是不會自動打開那扇大門的。$b$b我想我們得自己打一個通道進去。你準備好的時候告訴我一聲，$n。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'deDE', 'Eine waghalsige Idee', 'Infiltriert den Sturmgepeitschten Hafen und öffnet das Tor zum Hof von innen. Sprecht mit Taoshi, um die Mission zu beginnen.', 'Kriegsschiffe der Zandalari plündern die Meere um diese Insel herum. Sie bringen Soldaten und Kriegsbestien aus Zandalar, um Lei Shens Armeen aufzustocken.$B$BWenn wir die Werft des Palastes in unseren Besitz bringen können, ist die Versorgungsroute des Donnerkönigs abgeschnitten, und Euer Standort hier ist gesichert.$B$BIch habe einen Plan, um genau das zu bewerkstelligen, und wir werden keine ganze Armee dazu brauchen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'esES', 'Una idea audaz', 'Infíltrate en el Muelle Aguaturbia y abre la puerta del patio desde dentro. Habla con Taoshi para comenzar la misión.', 'Los buques de guerra Zandalari saquean los mares alrededor de esta isla. Traen soldados y bestias de guerra de Zandalar para reforzar el ejército de Lei Shen.$B$BSi capturamos el astillero del palacio, cortaremos los suministros del Rey del Trueno y asegurarás tu posición.$B$BTengo un plan para ello, y no hará falta un ejército entero.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'esMX', 'Una idea audaz', 'Infíltrate en el Muelle Aguaturbia y abre la puerta del patio desde dentro. Habla con Taoshi para comenzar la misión.', 'Los buques de guerra Zandalari saquean los mares alrededor de esta isla. Traen soldados y bestias de guerra de Zandalar para reforzar el ejército de Lei Shen.$B$BSi capturamos el astillero del palacio, cortaremos los suministros del Rey del Trueno y asegurarás tu posición.$B$BTengo un plan para ello, y no hará falta un ejército entero.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'frFR', 'Une idée audacieuse', 'Infiltrez le Débarcadère de la mer des Tempêtes et ouvrez la porte de la cour de l’intérieur. Parlez à Taoshi pour commencer la mission.', 'Des navires de guerre zandalari pillent les mers autour de cette île. Ils amènent des soldats et des bêtes de guerre de Zandalar pour renforcer l’armée de Lei Shen.$B$BSi nous parvenons à prendre le chantier naval du palais, la voie de ravitaillement du roi-tonnerre sera coupée, et votre position ici sera sécurisée.$B$BJ’ai un plan pour atteindre ce but, et il ne nécessitera pas toute une armée.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'itIT', 'Un\'idea audace', 'Infiltrati nell\'Approdo della Tempesta e apri la porta per il cortile dall\'interno. Parla con Taoshi per iniziare la missione.', 'Le navi da guerra Zandalari solcano i mari attorno all\'isola. Trasportano soldati e bestie da guerra direttamente da Zandalar per infoltire le armate di Lei Shen.$B$BSe riuscissimo a catturare il porto del palazzo, la linea di rifornimenti del Re del Tuono sarà tagliata e il vostro avamposto qui sarà al sicuro.$B$BHo in mente un piano per riuscirci, e non sarà necessaria un\'intera armata per metterlo in atto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'koKR', '대담한 계획', '폭풍바다 선착장에 침투하여 안의 안마당 관문을 열어야 합니다. 타오스에게 말을 걸어 임무를 시작하십시오.', '잔달라 전함이 이 섬 주위 바다에서 노략질을 일삼고 있어요. 놈들은 잔달라에서 병사와 전쟁 야수들을 태우고 와선 레이 션의 군대를 강화하기도 하지요.$B$B만약 우리가 궁전의 항만을 손에 넣을 수 있다면 천둥왕의 자원줄이 끊길 거고 이곳의 당신 거점은 안전해질 겁니다.$b$b이게 제 계획입니다. 아마 전 군대가 필요할 것 같진 않군요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'ptBR', 'Uma ideia audaciosa', 'Infiltre-se no Ancoradouro do Mar Revolto e abra o portão do paço por dentro. Fale com Taoshi para começar a missão.', 'Naus de guerra Zandalari saqueiam os mares que circundam a ilha. Eles enviam soldados e feras de batalha de Zandalar para aumentar o exército de Lei Shen.$B$BSe pudermos capturar o estaleiro do palácio, os suprimentos do Rei Trovão serão cortados e poderemos nos estabelecer aqui com segurança.$B$BEu tenho um plano para conseguir exatamente isso, e não vamos precisar de um exército inteiro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'ruRU', 'Смелая идея', 'Проникните на Пристань Бушующих Волн и откройте ворота изнутри. Поговорите с Таоши, чтобы начать задание.', 'Зандаларские боевые корабли бороздят воды вокруг острова. На них перевозят солдат и боевых зверей, которые вступают в армию Лэй Шэня.$B$BЕсли нам удастся захватить пристань, то мы перекроем пути снабжения Властелина Грома и создадим вам надежный плацдарм.$B$BУ меня есть план действий, и большая армия нам вовсе не потребуется.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'zhCN', '大胆的点子', '渗透进风暴之海码头并从里面打开庭院大门。与陶矢交谈来开始任务。', '赞达拉战船在这座岛屿四周的海域上大肆掠夺。他们不断地从赞达拉运送士兵和作战野兽，来壮大雷神的军队。$B$B要是我们能夺取宫殿的船坞，雷电之王的补给线就会被截断，而你在这里的据点也会变得越发稳固。$B$B我有个计划可以实现这一目标，而且用不着劳烦整支大军出动。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32655, 'zhTW', '出奇制勝', '潛入狂濤港，從裡頭打開庭院大門。與陶氏交談以展開任務。', '贊達拉的戰艦劫掠島嶼海岸，他們從贊達拉運來士兵與戰爭猛獸來助長雷神的氣焰。$B$B如果我們能攻下宮殿的船塢，便能切斷雷王的補給線，而你們在這島的根基也會更穩固。$B$B我有個計畫，不需要派出全部兵力就能達到目的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'deDE', 'Der Sturz von Shan\'Bu', 'Besiegt Shan\'Bu.', 'Lei Shen fand zu Beginn seines Feldzuges ein Gelege seltener Sturmdracheneier. Eine dieser Kreaturen - Nalak - war besonders brutal und tötete alle seine Pfleger, also nahm Lei Shen sich seiner an.\n\nNun arbeitet Shan\'Bu, einer der treuesten Diener Lei Shens, daran, den Drachen wiederzuerwecken.\n\nEs wird höchste Zeit, gegen Shan\'Bu loszuschlagen!\n\nSprecht mich wieder an, sobald Ihr bereit seid, den Angriff zu starten!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'esES', 'La Caída de Shan Bu', 'Derrota a Shan Bu.', 'Lei Shen encontró una nidada muy poco común de huevos de dragón de tormenta en su campaña, cuando aún vivía. Uno de ellos, conocido como Nalak, era especialmente feroz y mató a todos los cuidadores que tuvo, así que lo acogió él mismo.\n\nAhora, Shan Bu, uno de los siervos más leales de Lei Shen, está intentando resucitar al dragón.\n\n¡Es hora de atacar a Shan Bu!\n\n¡Habla conmigo cuando estés $glisto:lista; para iniciar el ataque!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'esMX', 'La Caída de Shan Bu', 'Derrota a Shan Bu.', 'Lei Shen encontró una nidada muy poco común de huevos de dragón de tormenta en su campaña, cuando aún vivía. Uno de ellos, conocido como Nalak, era especialmente feroz y mató a todos los cuidadores que tuvo, así que lo acogió él mismo.\n\nAhora, Shan Bu, uno de los siervos más leales de Lei Shen, está intentando resucitar al dragón.\n\n¡Es hora de atacar a Shan Bu!\n\n¡Habla conmigo cuando estés $glisto:lista; para iniciar el ataque!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'frFR', 'La chute de Shan Bu', 'Vainquez Shan Bu.', 'Lei Shen a trouvé un nid contenant de très rares œufs de dragon des tempêtes, au tout début de sa campagne. L’un de ces dragons, connu sous le nom de Nalak, était particulièrement brutal et a tué tous ses éleveurs. Lei Shen l’a alors fait sien.\n\nAujourd’hui, Shan Bu, un de ses plus loyaux serviteurs, s’efforce de ressusciter le dragon.\n\nIl est temps pour nous de frapper !\n\nVenez me parler quand vous serez $gprêt:prête; à donner l’assaut !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'itIT', 'La caduta di Shan Bu', 'Sconfiggi Shan Bu.', 'Lei Shen trovò un raro nido di Drago della Tempesta durante una delle sue campagne. Un esemplare in particolare, conosciuto con il nome Nalak, si rivelò estremamente crudele e brutale e uccise tutti i suoi addestratori, attirando così l\'attenzione di Lei Shen, il quale decise di prenderlo per sé.\n\nOra Shan Bu, uno dei servi più fidati di Lei Shen, sta cercando di resuscitare il drago.\n\nÈ giunta l\'ora di procedere all\'attacco contro Shan Bu!\n\nParlami quando sei $gpronto:pronta; a lanciare l\'assalto!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'koKR', '샨 부의 몰락', '샨 부를 처치해야 합니다.', '레이 션은 전쟁 도중 희귀한 폭풍 용의 알을 여러 개 발견했어요. 이 알에서 태어난 것 중 나락크라고 알려진 용은 특히나 흉포했고, 자신의 조련사들을 모두 없애버렸어요. 그래서 레이 션은 자신을 위해 나락크를 데려갔어요.\n\n이제 천둥왕의 충성스러운 하인 중 하나인 샨 부가 레이 션의 용, 나락크를 부활시키려고 해요.\n\n이제 샨 부와 맞서 싸울 때예요!\n\n공격할 준비가 되면 제게 말해 주세요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'ptBR', 'A Queda de Shan Bu', 'Derrote Shan Bu.', 'Lei Shen encontrou um ninho de dragões da tempestade em sua campanha. Um deles, conhecido como Nalak, era particularmente brutal e matou todos os seus cuidadores. Então, Lei Shen resolveu tomá-lo para si.\n\nAgora um dos servos mais leais de Lei Shen, Shan Bu, quer ressuscitar o dragão.\n\nPrecisamos atacar Shan Bu!\n\nFale comigo quando estiver $gpronto:pronta; para começar o ataque!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'ruRU', 'Падение Шань-Бу', 'Победите Шань-Бу.', 'Давным-давно Лэй Шэнь обнаружил редкую кладку драконьих яиц. Из одного вылупился невероятно злобный дракон, который пожрал своих укротителей. Тогда Лэй Шэнь забрал его к себе.\n\nТеперь Шань-Бу, один из верных прислужников императора, пытается воскресить этого дракона. У нас появился шанс одолеть Шань-Бу!\n\nДай мне знать, когда мы можем выступать!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'zhCN', '山怖之死', '击败山怖。', '雷神在早年的征战途中找到了几枚罕见的风暴龙蛋。其中一条名叫纳拉克的巨龙尤为残忍，干掉了所有训练师，于是雷神将它据为己有。\n\n现在，雷神最为忠实的仆从之一——山怖，正在复活这条巨龙。\n\n我们攻击山怖的时刻到了！\n\n等你做好出击准备时，再来与我交谈。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32656, 'zhTW', '衫布敗亡', '擊敗衫布。', '雷神在早年的一場戰役中找到了一窩稀有的風暴龍蛋。孵出來的幼龍中有一隻叫納拉卡的性格特別殘暴，殺死了每個照顧他的人，因此雷神便親自飼養這隻惡龍。\n\n現在，雷神忠實的僕人衫布正在復活這隻飛龍。\n\n此刻正是討伐衫布的時機!\n\n等你準備好發動攻擊時跟我說一聲!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'deDE', 'Arbeitsauftrag: Die Himmlischen Erhabenen II', 'Erntet 8 reife Streifenmelonenpflanzen und liefert 40 Streifenmelonen an die Himmlischen Erhabenen.', 'Sobald Eure Streifenmelonen erntereif sind, müsst Ihr sie nur noch für die Abholung einpacken.$b$bEin Schüler von Chi-Jis Wiege wird in Kürze eintreffen, um die Bestellung abzuholen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'esES', 'Pedido de los Augustos Celestiales II', 'Cosecha 8 plantas de melón a rayas maduras y consigue 40 melones a rayas para los Augustos Celestiales.', 'Cuando tus melones a rayas estén listos para recogerlos, solo tienes que empaquetarlos para que se los lleven.$b$bUn aprendiz de la Cuna de Chi-Ji vendrá enseguida para recoger el pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'esMX', 'Pedido de los Augustos Celestiales II', 'Cosecha 8 plantas de melón a rayas maduras y consigue 40 melones a rayas para los Augustos Celestiales.', 'Cuando tus melones a rayas estén listos para recogerlos, solo tienes que empaquetarlos para que se los lleven.$b$bUn aprendiz de la Cuna de Chi-Ji vendrá enseguida para recoger el pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'frFR', 'Commande : les Astres vénérables II', 'Récoltez 8 plants de Melons à rayures mûrs et fournissez 40 Melons à rayures pour les Astres vénérables.', 'Quand vos melons à rayures seront prêts à être cueillis, vous n’aurez qu’à les conditionner pour la livraison.$b$bUne recrue du berceau de Chi Ji viendra bientôt chercher la commande.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'itIT', 'Ordine: Venerabili Celestiali II', 'Raccogli 8 Meloni Striati Maturi per consegnare 40 Meloni Striati ai Venerabili Celestiali.', '<Una volta che i Meloni Striati saranno pronti per essere raccolti, bisognerà solo confezionarli per il trasporto.>$b$b<Un allievo della Culla di Chi-Ji arriverà presto per prelevarli.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'koKR', '주문서: 위대한 천신회 II', '잘 익은 줄무늬 멜론을 8개 수확한 후 위대한 천신회에 줄무늬 멜론을 40개 공급해야 합니다.', '줄무늬 멜론을 수확하고 나면, 운송할 수 있도록 포장하기만 하면 됩니다.$b$b곧 츠지의 요람 수련생이 와서 주문한 물건을 가져갈 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'ptBR', 'Ordem de Serviço: Os Celestiais Majestosos II', 'Colha 8 pés de Melão Listrado Maduro e forneça 40 Melões Listrados aos Celestiais Majestosos.', 'Quando seus melões listrados estiverem prontos para serem colhidos, você só precisará embalá-los para a entrega.$b$bUm aprendiz do Berço de Chi-Ji logo estará aqui para levar o pedido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'ruRU', 'Заказ: Августейшие Небожители II', 'Соберите урожай с 8 грядок со спелыми полосатыми дынями и упакуйте 40 полосатых дынь для Августейших Небожителей.', 'Когда полосатые дыни вырастут, останется только сложить их в ящик.$b$bСкоро за ним придет ученик из Колыбели Чи-Цзи.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'zhCN', '产品订单：至尊天神II', '收获8个成熟的菜瓜，并向至尊天神提供40份菜瓜。', '等你的菜瓜长到可以采摘时，你只要将它们打包，等待提取就好了。$b$b有位来自赤精栖木的学徒很快就会来这里取货。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32657, 'zhTW', '工作令：聖獸天尊II', '採收8株成熟的條紋甜瓜，並提供40顆條紋甜瓜給聖獸天尊。', '一旦你的條紋甜瓜可以收成了，你只要把它們打包好等人來拿就可以了。$b$b一名赤吉搖籃的受訓弟子很快就會來拿甜瓜。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'deDE', 'Arbeitsauftrag: Die Klaxxi I', 'Pflanzt 8 Mogukürbissamen auf dem Gehöft Sonnensang.', '<Auch wenn Yoons Handschrift zu Wünschen übrig lässt, ist es trotzdem schön, ein wenig von ihm auf dem Gehöft zu haben.>$b$bHeute morgen ist eine Bestellung von den Klaxxi eingetroffen. Seltsamerweise bitten sie für irgendwelche Forschungszwecke um Mogukürbisse.$b$bMeiner Erfahrung nach sollte man ihnen nicht allzu viele Fragen stellen...$b$bFangt am besten mit ihrer Bestellung an. Ich würde nur ungern sehen, was ein Insekt von dieser Größe sonst mit Eurer Ernte anstellen könnte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'esES', 'Pedido de los Klaxxi I', 'Planta 8 semillas de calabaza mogu en el Rancho Cantosol.', '<Aunque la letra de Yoon deja bastante que desear, es agradable tener algo suyo en la granja.>$b$bEsta mañana ha llegado un pedido de los Klaxxi. Curiosamente, piden calabazas mogu para algún tipo de investigación.$b$bCreo que es mejor no hacerles demasiadas preguntas...$b$bSerá mejor empezar con su pedido. ¡No me gustaría ver qué haría con tus cultivos un insecto de ese tamaño!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'esMX', 'Pedido de los Klaxxi I', 'Planta 8 semillas de calabaza mogu en el Rancho Cantosol.', '<Aunque la letra de Yoon deja bastante que desear, es agradable tener algo suyo en la granja.>$b$bEsta mañana ha llegado un pedido de los Klaxxi. Curiosamente, piden calabazas mogu para algún tipo de investigación.$b$bCreo que es mejor no hacerles demasiadas preguntas...$b$bSerá mejor empezar con su pedido. ¡No me gustaría ver qué haría con tus cultivos un insecto de ese tamaño!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'frFR', 'Commande : les Klaxxi I', 'Plantez 8 Graines de citrouille mogu à la Ferme Chant du Soleil.', '<Même si l’écriture de Yoon laisse un peu à désirer, c’est agréable d’avoir encore un peu de lui à la ferme.>$b$bLes Klaxxi m’ont fait parvenir une commande ce matin. Étrangement, ils désirent des citrouilles mogu pour leurs recherches.$b$bJe pense qu’il vaut mieux ne pas leur poser trop de questions…$b$bJe serais vous, je m\'occuperais de cette commande sans tarder. Je préfère ne pas savoir ce qu’un insecte de cette taille pourrait faire à vos cultures autrement !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'itIT', 'Ordine: Klaxxi I', 'Pianta 8 Semi di Zucca dei Mogu alla Tenuta Cantasole.', '<Nonostante la scrittura di Yoon lasci un po\' a desiderare, è bello averlo qui, anche se solo in questa forma.>$b$bStamattina è giunto un ordine dai Klaxxi: stranamente stanno chiedendo alcune Zucche dei Mogu per un qualche tipo di ricerca.$b$bHo imparato che è meglio non fare troppe domande...$b$bTi consiglio di metterti subito al lavoro. Non voglio nemmeno sapere cosa potrebbe fare un insetto di quelle dimensioni ai tuoi raccolti se non lo facessi!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'koKR', '주문서: 클락시 I', '태양노래 농장에 모구 호박 씨앗을 8개 심어야 합니다.', '<윤의 필체가 보기 좋다고 하기는 어렵지만, 아직도 농장에서 윤의 흔적을 느낄 수 있다는 점은 좋습니다.>$b$b오늘 아침에 클락시에서 주문이 들어왔습니다. 이상하게도 무슨 연구에 쓴다며 모구 호박이 필요하다더군요.$b$b그들에게는 질문을 많이 하지 않는 편이 좋습니다...$b$b빨리 주문을 처리하는 게 좋을 겁니다. 그러지 않으면 그 큰 곤충이 작물에 무슨 짓을 할지 어떻게 알겠어요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'ptBR', 'Ordem de Serviço: Os Klaxxi I', 'Plante 8 Sementes de Abóbora Mogu na Fazenda Sol Cantante.', '<Embora a escrita de Yoon deixe um pouco a desejar, é bom ainda ter uma parte dele aqui na fazenda.>$b$bChegou uma ordem dos Klaxxi esta manhã. É estranho, mas eles estão requisitando abóboras mogus para alguma pesquisa.$b$bAcho melhor não fazer muitas perguntas...$b$bVamos logo atender esse pedido. Eu odiaria ver o que um inseto daquele tamanho pode fazer com nossa plantação caso a gente não atenda!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'ruRU', 'Заказ: Клакси I', 'Посадите 8 семян тыквы могу на ферме Солнечной Песни.', '<Разобрать почерк Юна не слишком просто, но приятно осознавать, что он все еще участвует в делах фермы.>$b$bСегодня утром пришел заказ от клакси. Они потребовали для каких-то исследований тыквы могу.$b$bЯ считаю, что лучше не задавать им лишних вопросов...$b$bЛучше принимайся за работу. В противном случаев не хочу даже и думать о том, что насекомые такого размера могут сделать с твоими посевами!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'zhCN', '产品订单：卡拉克西I', '在日歌农场播种8颗魔古南瓜种子。', '<尽管老愚书写的字迹潦草，但在农场上收到来自他的只言片语总是好的。>$b$b我们在今天早上接到了来自卡拉克西的订单。奇怪的是，他们要收购一批魔古南瓜来进行什么研究。$b$b我觉得最好不要问长问短……$b$b赶快完成他们的订单吧。要是没能如期交货，我可不知道像他们那么大的虫子会对你的庄稼做出什么事儿来！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32658, 'zhTW', '工作令：卡拉西I', '在日歌農莊播下8顆魔古南瓜的種子。', '<雖然農夫尹的字跡潦草得難以入目，但農場裡能有這一點來自他的東西還是令人開心的。>$b$b今早卡拉西那兒來了一張工作令，他們居然想要一些魔古南瓜來做點研究。$b$b我發現最好還是別問他們太多問題...$b$b你最好開始栽種。如果你不乖乖聽話的話，我不敢想像那樣巨大的蟲子會對你的作物做出什麼事情!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'deDE', 'Arbeitsauftrag: Die Klaxxi II', 'Erntet 8 reife Mogukürbispflanzen und liefert 40 Mogukürbisse an die Klaxxi.', 'Sobald Eure Mogukürbisse erntereif sind, müsst Ihr sie nur noch für die Abholung einpacken.$b$bIch an Eurer Stelle würde nicht trödeln. Die Klaxxi sind nicht gerade für ihre Geduld bekannt!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'esES', 'Pedido de los Klaxxi II', 'Cosecha 8 plantas de calabaza mogu maduras y consigue 40 calabazas mogu para los Klaxxi.', 'Cuando tus calabazas mogu estén listas para recogerlas, solo tienes que empaquetarlas para que se las lleven.$b$bYo que tú no perdería el tiempo. ¡Los Klaxxi no se caracterizan por su paciencia!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'esMX', 'Pedido de los Klaxxi II', 'Cosecha 8 plantas de calabaza mogu maduras y consigue 40 calabazas mogu para los Klaxxi.', 'Cuando tus calabazas mogu estén listas para recogerlas, solo tienes que empaquetarlas para que se las lleven.$b$bYo que tú no perdería el tiempo. ¡Los Klaxxi no se caracterizan por su paciencia!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'frFR', 'Commande : les Klaxxi II', 'Récoltez 8 plants de Citrouilles mogu mûres et fournissez 40 Citrouilles mogu pour les Klaxxi.', 'Quand vos citrouilles mogu seront prêtes à être cueillies, vous n’aurez qu’à les conditionner pour la livraison.$b$bJe ne lambinerais pas à votre place. Les Klaxxi ne sont pas réputés pour leur patience !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'itIT', 'Ordine: Klaxxi II', 'Raccogli 8 Zucche dei Mogu Mature per consegnare 40 Zucche dei Mogu ai Klaxxi.', '<Una volta che le Zucche dei Mogu saranno pronte per essere raccolte, bisognerà solo confezionarle per il trasporto.>$b$b<Sarà meglio sbrigarsi, i Klaxxi non sono famosi per la loro pazienza!>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'koKR', '주문서: 클락시 II', '잘 익은 모구 호박을 8개 수확한 후 클락시에 모구 호박을 40개 공급해야 합니다.', '모구 호박을 수확하고 나면, 운송할 수 있도록 포장하기만 하면 됩니다.$b$b저라면 꾸물거리지 않겠습니다. 클락시는 그리 참을성이 많지 않거든요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'ptBR', 'Ordem de Serviço: Os Klaxxi II', 'Colha 8 pés de Abóbora Mogu Madura e forneça 40 Abóboras Mogus para os Klaxxi.', 'Quando suas abóboras mogus estiverem prontas para a coleta, você só precisará embalá-las para a entrega.$b$bEu não perderia tempo se fosse você. Os Klaxxi não são exatamente pacientes!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'ruRU', 'Заказ: Клакси II', 'Соберите 8 спелых тыкв могу и упакуйте 40 тыкв могу для Клакси.', 'Когда тыквы могу вырастут, останется только сложить их в ящик.$b$bИ не стоит слишком мешкать. Клакси не отличаются терпением!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'zhCN', '产品订单：卡拉克西II', '收获8个成熟的魔古南瓜，并向卡拉克西提供40份魔古南瓜。', '等你的魔古南瓜长到可以采摘时，你只要将它们打包，等待提取就好了。$b$b如果我是你，我可不会磨磨蹭蹭的。卡拉克西从来都没什么耐心！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32659, 'zhTW', '工作令：卡拉西II', '採收8株成熟的魔古南瓜，並提供40顆魔古南瓜給卡拉西。', '一旦你的魔古南瓜長大成熟，只要把它們打包好等人來拿就可以了。$b$b如果我是你的話，我就會趕緊行動。卡拉西向來沒什麼耐心!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'deDE', 'Lernt Reiten', 'Findet Aalun.', 'Es wird Zeit, dass Ihr das Reiten erlernt! Besucht Aalun am Eingang zur Exodar auf der Azurmythosinsel. Vergesst nicht, ein Reittier zu erwerben, wenn Ihr dort seid!', '', 'Findet Aalun und lernt, wie man reitet!', 'Aalun', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'esES', 'A montar se ha dicho', 'Encuentra a Aalun.', '¡Es hora de que aprendas a montar! Busca a Aalun en la entrada de El Exodar, en Isla Bruma Azur. ¡No te olvides de comprar tu montura cuando llegues!', '', '¡Encuentra a Aalun y aprende a montar!', 'Aalun', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'esMX', 'A montar se ha dicho', 'Encuentra a Aalun.', '¡Es hora de que aprendas a montar! Busca a Aalun en la entrada de El Exodar, en Isla Bruma Azur. ¡No te olvides de comprar tu montura cuando llegues!', '', '¡Encuentra a Aalun y aprende a montar!', 'Aalun', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'frFR', 'Un cavalier débutant', 'Trouvez Aalun.', 'Il est temps que vous appreniez à monter ! Allez trouver Aalun à l’entrée de l’Exodar sur l’île de Brume-Azur. Et n’oubliez pas de vous acheter une monture tant que vous y serez !', '', 'Trouvez Aalun et apprenez à monter !', 'Aalun', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'itIT', 'Imparare a cavalcare', 'Trova Aalun.', 'È ora di imparare a cavalcare! Cerca Aalun all\'entrata di Exodar sull\'Isola Brumazzurra. Non dimenticare di acquistare la tua cavalcatura quando sarai lì!', '', 'Trova Aalun e impara a cavalcare!', 'Aalun', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'koKR', '타는 법 배우기', '알룬을 찾아야 합니다.', '이제 당신도 타는 법을 배워야겠군요! 하늘안개 섬의 엑소다르 입구에 있는 알룬을 찾아보십시오. 그곳에 가면 탈것을 사는 걸 잊지 마십시오!', '', '알룬을 찾아서 타는 법을 배우십시오!', '알룬', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'ptBR', 'Aprender a montar', 'Encontre Aalun.', 'É hora de aprender a montar! Procure Aalun na entrada de Exodar, que fica na Ilha Névoa Lazúli. Não se esqueça de comprar sua montaria quando estiver por lá!', '', 'Encontre Aalun e aprenda a montar!', 'Aalun', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'ruRU', 'Уроки верховой езды', 'Найдите Аалун.', 'Пора тебе научиться ездить верхом! Найди Аалун у входа в Экзодар, что на острове Лазурной Дымки. И не забудь купить себе скакуна, пока будешь там.', '', 'Найди Аалун и научись ездить верхом.', 'Аалун', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'zhCN', '学习骑乘', '找到埃亚伦。', '你该学习骑术了！去秘蓝岛的埃索达入口处找埃亚伦。在那别忘了买坐骑！', '', '找到埃亚伦并学习如何骑乘坐骑！', '埃亚伦', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32661, 'zhTW', '騎術訓練', '找到阿露恩。', '你學習騎術的時候到了！到藍謎島的艾克索達入口處找阿露恩。到了那裡別忘了買個坐騎！', '', '去找阿露恩，學會怎麼騎乘坐騎！', '阿露恩', '', '', '', 0);
