INSERT INTO `quest_template_locale` VALUES (32680, 'deDE', 'Der Sturm braut sich zusammen', 'Reist mit Späherhauptmann Elsia zur Insel des Donners.', 'Wir haben keine Zeit zu verlieren, $n. Jeder Moment, den wir hier vertrödeln, gibt Jaina und den Kirin Tor weitere Teile der Insel preis.$b$bLor\'themar wartet auf uns. Sprecht mit mir, wenn Ihr bereit seid, abzureisen. Ich werde Euch selbst hinbringen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32680, 'esES', 'La tormenta acecha', 'Viaja con la capitana de exploradores Elsia a la Isla del Trueno.', 'No hay tiempo que perder, $n. Con cada segundo que perdemos aquí, Jaina y el Kirin Tor refuerzan su control sobre la isla.$b$bLor\'themar nos espera. Habla conmigo cuando estés $glisto:lista; para partir. Te acompañaré hasta allí.', '', '', '', '', '', '', 0);
