INSERT INTO `quest_template_locale` VALUES (32681, 'frFR', 'La tempête se prépare', 'Rendez-vous avec Vereesa Coursevent sur l’Île du Tonnerre.', 'Le temps presse, $n. Chaque minute perdue donne plus de temps aux Saccage-Soleil pour renforcer leur emprise sur l’île.$b$bDame Jaina Portvaillant nous attend. Ne la faisons pas patienter plus longtemps.$b$bVenez me parler quand vous serez $gprêt:prête; à partir. Je vous escorterai moi-même jusque là-bas.', '', '', '', '', '', '', 0);
