INSERT INTO `quest_template_locale` VALUES (32720, 'esES', 'Elige tu tarea', 'Mata a 10 unidades o jugadores del Kirin Tor en la Isla del Trueno.', '', '', '', '', '', '', 'Vuelve con: Lor\'themar Theron. Zona: Promontorio Buscalba.', 0);
INSERT INTO `quest_template_locale` VALUES (32720, 'frFR', 'Choisissez votre affectation', 'Tuez 10 unités du Kirin Tor ou personnages-joueurs sur l’Île du Tonnerre.', '', '', '', '', '', '', 'Retournez voir Lor’themar Theron au Promontoire de Cherchelaube.', 0);
INSERT INTO `quest_template_locale` VALUES (32720, 'itIT', 'Scegli la tua assegnazione', 'Uccidi 10 membri del Kirin Tor o giocatori avversari sull\'Isola del Tuono.', '', '', '', '', '', '', 'Torna da Lor\'themar Theron al Promontorio Albafurente.', 0);
INSERT INTO `quest_template_locale` VALUES (32720, 'ptBR', 'Escolha sua missão', 'Mate 10 unidades do Kirin Tor na Ilha do Trovão.', '', '', '', '', '', '', 'Fale novamente com Lor\'themar Theron no Promontório Buscalva.', 0);
INSERT INTO `quest_template_locale` VALUES (32720, 'ruRU', 'Выбери задание', 'Убейте 10 последователей Кирин-Тора или игроков на острове Грома.', '', '', '', '', '', '', 'Вернитесь к Лор\'темару Терону на мыс Искателя Рассвета.', 0);
INSERT INTO `quest_template_locale` VALUES (32720, 'zhTW', '選擇你的任務', '殺死雷王島上的10名祈倫托成員或玩家。', '', '', '', '', '', '', '向曦尋者之岬的洛索瑪‧塞隆回報。', 0);
INSERT INTO `quest_template_locale` VALUES (32721, 'ruRU', 'Выбери задание', 'Убейте 10 последователей Кирин-Тора или игроков на острове Грома.', '', '', '', '', '', '', 'Вернитесь к Лор\'темару Терону на мыс Искателя Рассвета.', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'deDE', 'Schmiede voraus!', 'Tötet den Meister der Schmiede und sammelt ein Kodexfragment von Fleischschmied Hoku auf der Insel des Donners ein.', 'Ihr habt noch mehr zu tun. \n\nTötet Itoka, den Schmiedemeister, und findet ein Kodexfragment bei Fleischschmied Hoku, dem Torhüter des Donnerkönigs.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'esES', '¡Hacia la forja!', 'Mata al maestro de la forja y recoge un fragmento de códice del modelador de carne Hoku en la Isla del Trueno.', 'Aún hay más que hacer. \n\nMata a Itoka, maestro de la forja, y encuentra un fragmento de códice del modelador de carne Hoku, guardián de las puertas del Rey del Trueno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'esMX', '¡Hacia la forja!', 'Mata al maestro de la forja y recoge un fragmento de códice del modelador de carne Hoku en la Isla del Trueno.', 'Aún hay más que hacer. \n\nMata a Itoka, maestro de la forja, y encuentra un fragmento de códice del modelador de carne Hoku, guardián de las puertas del Rey del Trueno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'frFR', 'Forge droit devant !', 'Tuez le Maître de la forge et récupérez un Fragment de codex sur le Façonneur de chair Hoku, sur l’Île du Tonnerre.', 'Vous devez faire autre chose pour moi.\n\nTuez Itoka, le maître de la forge, et trouvez le fragment d’un codex sur Hoku le Façonneur de chair, le gardien du roi-tonnerre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'itIT', 'Verso la Forgia!', 'Uccidi il Maestro della Forgia e recupera un Frammento di Codice dei Mogu da Hoku il Carnefice sull\'Isola del Tuono.', 'C\'è dell\'altro da fare.\n\nUccidi Itoka, il Maestro della Forgia, e depreda un Frammento di Codice dei Mogu da Hoku il Carnefice, guardiano del Regno del Tuono.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'koKR', '제련소로!', '천둥의 섬에 있는 제련소 대장 코타를 처치하고 살덩이창조자 호쿠로부터 전서 조각을 수집해야 합니다.', '그대가 해야 할 일이 더 있답니다.\n\n제련소의 대장인 코타를 처치하고, 천둥왕의 문지기인 살덩이창조자 호쿠로부터 전서 조각을 수집해야 해요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'ptBR', 'Forja adiante!', 'Mate o Mestre da Forja e pegue um Fragmento de Códice do Moldacarne Hoku na Ilha do Trovão.', 'Há algo mais que você precisa fazer. \n\nMate Itoka, mestre da forja, e encontre um fragmento do códice do Moldacarne Hoku, guarda-pórtico do Rei Trovão.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'ruRU', 'Впереди кузня!', 'Убейте главного кузнеца и получите фрагмент кодекса от Плотореза Хоку на острове Грома.', 'Тебе придется сделать еще кое-что.\n\nУбей Итоку, главного кузнеца, и найди фрагмент кодекса Плотореза Хоку, хранителя врат Властелина Грома.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'zhCN', '近在眼前！', '在雷神岛上消灭锻造大师，然后从塑肌者霍古身上找到一块法典碎片。', '还有其它事情要你去做。\n\n消灭锻造大师伊多喀，然后从雷电之王的看门人——塑肌者霍古身上找到一块法典碎片。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32722, 'zhTW', '朝熔爐前進!', '在雷王島上殺死熔爐統領，並從血肉工匠郝古身上取得聖典殘片。', '還有一件事情你非做不可。\n\n殺死熔爐統領夷托卡，再從雷王守門人血肉工匠郝古身上取得聖典殘片。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32723, 'deDE', 'recycelt mich', 'Tötet den Meister der Schmiede und sammelt ein Kodexfragment vom Mogutorhüter auf der Insel des Donners ein.', 'Ihr habt noch mehr zu tun. \n\nTötet Itoka, den Meister der Schmiede, und findet ein Kodexfragment bei Fleischschmied Hoku, dem Torhüter des Donnerkönigs.', '', '', '', '', '', 'Kehrt zum Questgeber zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (32723, 'esES', 'reuse me', 'Mata al maestro de la forja y recoge un fragmento de códice del guardián mogu en la Isla del Trueno.', 'Aún hay más que hacer. \n\nMata a Itoka, maestro de la forja, y encuentra un fragmento de códice del modelador de carne Hoku, guardián de las puertas del Rey del Trueno.', '', '', '', '', '', 'Return to <the questgiver>', 0);
INSERT INTO `quest_template_locale` VALUES (32723, 'esMX', 'reuse me', 'Mata al maestro de la forja y recoge un fragmento de códice del guardián mogu en la Isla del Trueno.', 'Aún hay más que hacer. \n\nMata a Itoka, maestro de la forja, y encuentra un fragmento de códice del modelador de carne Hoku, guardián de las puertas del Rey del Trueno.', '', '', '', '', '', 'Return to <the questgiver>', 0);
INSERT INTO `quest_template_locale` VALUES (32723, 'frFR', 'reuse me', 'Tuez le Maître de la forge et récupérez un Fragment de codex sur le Gardien de la porte mogu, sur l’Île du Tonnerre.', 'Vous devez faire autre chose pour moi.\n\nTuez Itoka, le maître de la forge, et trouvez le fragment d’un codex sur Hoku le Façonneur de chair, le gardien du roi-tonnerre.', '', '', '', '', '', 'Retournez voir <le donneur de quête>.', 0);
INSERT INTO `quest_template_locale` VALUES (32723, 'itIT', 'Riusami', 'Uccidi il Maestro della Forgia e recupera un Frammento di Codice dei Mogu dal Guardiano delle Porte Mogu sull\'Isola del Tuono.', 'C\'è dell\'altro da fare.\n\nUccidi Itoka, il Maestro della Forgia, e depreda un Frammento di Codice dei Mogu da Hoku il Carnefice, guardiano del Regno del Tuono.', '', '', '', '', '', 'Torna da <committente>', 0);
INSERT INTO `quest_template_locale` VALUES (32723, 'ptBR', 'reuse me', 'Mate o Mestre da Forja e pegue um Fragmento de Códice do Guarda-pórtico Mogu na Ilha do Trovão.', 'Há algo mais que você precisa fazer. \n\nMate Itoka, mestre da forja, e encontre um fragmento do códice do Moldacarne Hoku, guarda-pórtico do Rei Trovão.', '', '', '', '', '', 'Return to <the questgiver>', 0);
INSERT INTO `quest_template_locale` VALUES (32723, 'ruRU', 'reuse me', 'Убейте главного кузнеца и получите фрагмент кодекса от стража врат могу на Острове Грома.', 'Тебе придется сделать еще кое-что.\n\nУбей Итоку, главного кузнеца, и найди фрагмент кодекса Плотореза Хоку, хранителя врат Властелина Грома.', '', '', '', '', '', 'Вернитесь к <тому, кто выдал задание>.', 0);
INSERT INTO `quest_template_locale` VALUES (32724, 'deDE', 'Schmiede voraus!', 'Tötet den Meister der Schmiede und sammelt ein Kodexfragment vom Mogutorhüter auf der Insel des Donners ein.', 'Ihr habt noch mehr zu tun. \n\nTötet Itoka, den Meister der Schmiede, und findet ein Kodexfragment bei Fleischschmied Hoku, dem Torhüter des Donnerkönigs.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32724, 'esES', '¡Hacia la forja!', 'Mata al maestro de la forja y recoge un fragmento de códice del guardián mogu en la Isla del Trueno.', 'Aún hay más que hacer. \n\nMata a Itoka, maestro de la forja, y encuentra un fragmento de códice del modelador de carne Hoku, guardián de las puertas del Rey del Trueno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32724, 'esMX', '¡Hacia la forja!', 'Mata al maestro de la forja y recoge un fragmento de códice del guardián mogu en la Isla del Trueno.', 'Aún hay más que hacer. \n\nMata a Itoka, maestro de la forja, y encuentra un fragmento de códice del modelador de carne Hoku, guardián de las puertas del Rey del Trueno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32724, 'frFR', 'Forge droit devant !', 'Tuez le Maître de la forge et récupérez un Fragment de codex sur le Gardien de la porte mogu, sur l’Île du Tonnerre.', 'Vous devez faire autre chose pour moi.\n\nTuez Itoka, le maître de la forge, et trouvez le fragment d’un codex sur Hoku le Façonneur de chair, le gardien du roi-tonnerre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32724, 'itIT', 'Verso la Forgia!', 'Uccidi il Maestro della Forgia e recupera un Frammento di Codice dei Mogu da Hoku il Carnefice sull\'Isola del Tuono.', 'C\'è dell\'altro da fare.\n\nUccidi Itoka, il Maestro della Forgia, e depreda un Frammento di Codice dei Mogu da Hoku il Carnefice, guardiano del Regno del Tuono.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32724, 'koKR', '제련소로!', '천둥의 섬에 있 제련소 대장 코타를 처치하고 모구 문지기인 살덩이창조자 호쿠로부터 전서 조각을 수집해야 합니다.', '그대가 해야 할 일이 더 있습니다.\n\n제련소의 대장인 코타를 처치하고, 천둥왕의 문지기인 살덩이창조자 호쿠로부터 전서 조각을 수집해야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32724, 'ptBR', 'Forja adiante!', 'Mate o Mestre da Forja e pegue um Fragmento de Códice do Guarda-pórtico Mogu na Ilha do Trovão.', 'Há algo mais que você precisa fazer. \n\nMate Itoka, mestre da forja, e encontre um fragmento de códice do Moldacarne Hoku, vigia do portão do Rei Trovão.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32724, 'ruRU', 'Впереди кузня!', 'Убейте главного кузнеца и получите фрагмент кодекса от стража врат могу на острове Грома.', 'Тебе придется сделать еще кое-что.\n\nУбей Итоку, главного кузнеца, и найди фрагмент кодекса Плотореза Хоку, хранителя врат Властелина Грома.', '', '', '', '', '', '', 0);
