INSERT INTO `quest_template_locale` VALUES (32730, 'itIT', 'Ihgaluk', 'Trova l\'Arcimago Aethas il Predatore del Sole fuori da Ihgaluk.', 'Aethas si è trasferito a Ihgaluk, verso sud-est, nel tentativo di costringere i Saurok a schierarsi contro gli Zandalari e i Mogu.$B$BVai da lui e aiutalo con ogni mezzo possibile.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32730, 'koKR', '이갈룩 바위산', '이갈룩 바위산 밖에 있는 대마법사 에이타스 선리버를 찾아야 합니다.', '에이타스가 남동쪽에 있는 이갈룩 바위산에서 사우록을 부추겨 잔달라, 모구와 대치하도록 하고 있소.$B$B그를 찾아가서 어떻게든 도우시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32730, 'ptBR', 'Penhasco Ihgaluk', 'Encontre o Arquimago Aethas Fendessol, perto do Penhasco Ihgaluk.', 'Aethas tomou o controle do Penhasco Ihgaluk, a sudeste daqui, continuando seus esforços de colocar os Sauroks contra os Zandalari e mogus.$B$BVá até ele e ajude-o como puder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32730, 'ruRU', 'Утесы Ихгалук', 'Разыщите верховного мага Этаса Похитителя Солнца возле утесов Ихгалук.', 'Этас сейчас на юго-востоке, на утесах Ихгалук, где он пытается натравить сауроков на зандаларов и могу.$B$BОтправляйся туда и помоги ему, чем сможешь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32730, 'zhCN', '伊瓜鲁克峭壁', '在伊瓜鲁克峭壁外面找到大法师艾萨斯·夺日者。', '艾萨斯接管了东南边的伊瓜鲁克峭壁，继续努力让蜥蜴人与赞达拉和魔古族为敌。$B$B去找他吧，尽你所能提供帮助。', '', '', '', '', '', '', 0);
