INSERT INTO `quest_template_locale` VALUES (32862, 'itIT', 'Campo di battaglia: le Savane', '[Missione settimanale] Ottieni $1oa Legna dei Kor\'kron, $2oa Petrolio dei Kor\'kron, $3oa Carne dei Kor\'kron e $4oa Pietre dei Kor\'kron.', '$n, \'o lavoro nuostr\' sta ben lungi dall\'essere concluso. \'O fuoco do\' cambiamento già arde, ma può ancora essere estinto.$B$BPuortami altri rifornimenti dei Kor\'kron e io ti ricompenserò adeguatamente.', '', '', '', '', '', '', 0);
