INSERT INTO `quest_template_locale` VALUES (32867, 'deDE', 'Vol\'jin von den Dunkelspeeren', 'Hört Ki\'ta Pfeilzahn zu.', 'Willkomm\'n in Sen\'jin. Ich weiß, Ihr kommt von weit her, um Vol\'jin zu seh\'n, den Anführer der Dunkelspeere.$B$BAber ich muss Euch bitt\'n, Euch noch \'nen Moment zu geduld\'n. Zuerst sollt\'n wir uns unterhalt\'n.', '', '', '', '', '', 'Sprecht mit Vol\'jin in Sen\'jin in Durotar.', 0);
INSERT INTO `quest_template_locale` VALUES (32867, 'esES', 'Vol\'jin de los Lanza Negra', 'Escucha a Ki\'ta Flechacerada.', '$gBienvenido:Bienvenida; al Poblado Sen\'jin, colega. Sé que has venido desde lejos pa\' ver a Vol\'jin, líder de los Lanza Negra.$B$BPero tendrás que eh\'perar un poco más. Vamos a hablar.', '', '', '', '', '', 'Habla con: Vol\'jin. Zona: Poblado Sen\'jin, Durotar.', 0);
INSERT INTO `quest_template_locale` VALUES (32867, 'esMX', 'Vol\'jin de los Lanza Negra', 'Escucha a Ki\'ta Flechacerada.', '$gBienvenido:Bienvenida; al Poblado Sen\'jin, colega. Sé que has venido desde lejos pa\' ver a Vol\'jin, líder de los Lanza Negra.$B$BPero tendrás que eh\'perar un poco más. Vamos a hablar.', '', '', '', '', '', 'Habla con: Vol\'jin. Zona: Poblado Sen\'jin, Durotar.', 0);
INSERT INTO `quest_template_locale` VALUES (32867, 'frFR', 'Vol’jin des Sombrelances', 'Écoutez Ki’ta Brocheflèche.', 'Bienvenue au village de Sen’jin, mec. Je sais que vous venez de loin pour voir Vol’jin, le chef des Sombrelances.$B$BMais il faut que vous attendiez encore un peu. On va parler.', '', '', '', '', '', 'Parlez à Vol’jin à Sen’jin, en Durotar.', 0);
INSERT INTO `quest_template_locale` VALUES (32867, 'itIT', 'Vol\'jin dei Lanciascura', 'Ascolta Ki\'ta Zannafina.', '$gBenvenuto:Benvenuta; al Villaggio di Sen\'jin, cumpà. So che hai viaggiato a lungo assai per incontrare Vol\'jin, capo dei Lanciascura, ma devo chiederti di attendere ancora nu poco. Jamme, parliamo nu momento.', '', '', '', '', '', 'Parla con Vol\'jin a Sen\'jin nella regione di Durotar.', 0);
INSERT INTO `quest_template_locale` VALUES (32867, 'ptBR', 'Vol\'jin dos Lançanegra', 'Ouça Ki\'ta Flechatusca.', '$gBem-vindo:Bem-vinda; à Aldeia Sen\'jin, $gmano:mina;. Eu sei que cê veio de longe para ver Vol\'jin, líder dos Lançanegra.$B$BMas peço que espere um pouco mais. Vamos conversar antes.', '', '', '', '', '', 'Fale com Vol\'jin na Aldeia Sen\'jin, em Durotar.', 0);
INSERT INTO `quest_template_locale` VALUES (32867, 'ruRU', 'Вол\'джин из племени Черного Копья', 'Выслушайте Ки\'ту Стрелоклыка.', 'С возвращением в Сен\'джин. Знаю, ты $gпроделал:проделала; долгий путь, чтобы поговорить с Вол\'джином, предводителем Черного Копья.$B$BПрости, придется еще чуть подождать. Надо поговорить.', '', '', '', '', '', 'Поговорите с Вол\'джином в деревне Сен\'джин в Дуротаре.', 0);
