INSERT INTO `quest_template_locale` VALUES (32895, 'esES', 'El camino hacia la guerra', 'Habla con Ki\'ta Flechacerada en Cerrotajo, en Durotar.', '¡Ey! Acabo de recibir un informe de nuestros agentes en Durotar.$B$BLa Rebelión Lanza Negra necesita tu ayuda.$B$BHe oído que te llevas bien con los trols. ¿Por qué no te acercas hasta Durotar y averiguas lo que está ocurriendo?', '', '', '', '', '', '', 0);
