INSERT INTO `quest_template_locale` VALUES (32895, 'koKR', '전쟁의 길', '듀로타의 칼바위 언덕에 있는 키타 애로우터스크와 대화해야 합니다.', '어이! 듀로타에 있는 우리 요원들로부터 소식을 받았어요.$B$B검은창 부족 저항군이 당신의 도움이 필요하다는군요.$B$B트롤들이랑 벌써 인사도 나누었다면서요? 듀로타로 가서 한 번 상황을 살펴보는 게 어때요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32895, 'zhCN', '战争之路', '在杜隆塔尔的剃刀岭与基塔·箭牙交谈。', '嘿！我刚刚收到我们远在杜隆塔尔的密探发回的情报。$B$B暗矛起义军需要你的帮助。$B$B我听说你本来就跟巨魔有点交情。那为什么不亲自去杜隆塔尔走一趟，看看那里到底发生了什么事情？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32895, 'zhTW', '戰爭之路', '到杜洛塔的剃刀嶺找奇塔·箭牙交談。', '嘿!我剛剛收到我們來自杜洛塔的密探的消息。$B$B暗矛反抗軍需要你的幫助。$B$B我聽說你已經跟食人妖他們混得很熟了。要不你跑一趟杜洛塔看看那裏有什麼動靜?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32896, 'esES', 'El camino hacia la guerra', 'Habla con Ki\'ta Flechacerada en Cerrotajo, en Durotar.', 'He recibido noticias de Baine. La Rebelión Lanza Negra necesita tu ayuda.$B$BDirígete a Cerrotajo y busca a Ki\'ta Flechacerada.$B$B¡No digas ni una palabra de esto a nadie!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32896, 'esMX', 'The Road to War', 'Speak to Ki\'ta Arrowtusk at Razor Hill in Durotar.', 'I have received word from Baine. The Darkspear Rebellion needs your help.$B$BGo to Razor Hill and seek out Ki\'ta Arrowtusk.$B$BBreathe word of this to no one!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32896, 'itIT', 'La via per la guerra', 'Parla con Ki\'ta Zannafina a Tranciacolle nella regione di Durotar.', 'Ho appena ricevuto un messaggio da Baine. La Ribellione dei Lanciascura ha bisogno del tuo aiuto.$B$BVai a Tranciacolle e cerca Ki\'ta Zannafina.$B$BNon farne parola con nessuno!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32896, 'ptBR', 'A estrada da guerra', 'Fale com Ki\'ta Flechatusca no Monte Navalha, em Durotar.', 'Tenho notícias de Baine. A Rebelião dos Lançanegra precisa de sua ajuda.$B$BVá até o Monte Navalha e procure Ki\'ta Flechatusca.$B$BNão fale sobre isso com ninguém!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (32896, 'ruRU', 'Путь к войне', 'Поговорите с Ки\'той Стрелоклыком в Колючем Холме, что в Дуротаре.', 'У меня новости от Бейна. Восстанию Черного Копья нужна твоя помощь.$B$BОтправляйся в Колючий Холм и найди Ки\'ту Стрелоклыка.$B$BИ никому ни слова!', '', '', '', '', '', '', 0);
