INSERT INTO `quest_template_locale` VALUES (33013, 'deDE', 'Feuer und Donner', 'Untersucht die Belagerungsmunition und die schwere Panzerung in der Knochenschlucht.', 'Der Eisenwolf... hat Waffen mitgebracht...$B$B<hust>$B$B...Waffen mit Feuer und Donner.$B$BNoch nie haben unsere Krieger solche Waffen gesehen. Darauf waren wir nicht vorbereitet.$B$B<keuch> Wir müssen lernen... sie zu bezwingen...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'esES', 'De fuego y truenos', 'Examina las municiones de asedio y la placa pesada en Los Despojos.', 'El Lobo de Hierro... ha traído armas...$B$B<tos>$B$B... armas de fuego y truenos.$B$BNuestros guerreros nunca habían visto cosa igual. No estábamos preparados.$B$B<resuello> Debemos aprender... a imponernos...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'esMX', 'De fuego y trueno', 'Examina las municiones de asedio y el blindaje pesado de la Escoria de Huesos.', 'El Lobo de Hierro... trajo armas...$B$B<tos>$B$B...armas de fuego y trueno.$B$BNuestros guerreros nunca habían visto tales armas. No estábamos preparados.$B$B<resuello> Debemos aprender... para triunfar...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'frFR', 'De feu et de tonnerre', 'Examinez les Munitions de siège et les Plaques lourdes au Crâne-de-Lave.', 'Le Loup de Fer… a apporté des armes…$B$B<Tousse>$B$B… des armes de feu et de tonnerre.$B$BNos guerriers n’ont jamais vu de telles armes. Nous n’étions pas prêts.$B$B<Sa respiration se fait sifflante.> Nous devons apprendre… pour vaincre…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'itIT', 'Fuoco e fulmini', 'Esamina le Munizioni d\'Assedio e le Placcature Pesanti a Ossarotte.', 'Il Lupo di Ferro... ha portato armi...$B$B<Tossisce>$B$B...armi di fuoco e tuono.$B$BI nostri guerrieri non hanno mai visto armi di questo genere, non eravamo pronti.$B$B<Ansima> Dobbiamo capire... come resistere...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'koKR', '불과 천둥으로', '뼈무더기에서 공성 탄약과 두꺼운 강철판을 조사해야 합니다.', '강철 늑대가... 무기를 가져왔소...$B$B<쿨럭>$B$B... 불과 천둥의 무기를...$B$B우리 전사들이 한 번도 본 적이 없는 무기였소... 우린... 준비가 안 되어 있었소...$B$B<쌕쌕> 알아내야 하오... 어떻게 막아야 할지...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'ptBR', 'De Fogo e Trovão', 'Examine a Munição de Cerco e a Blindagem Pesada na Escória de Ossos.', 'O Lobo de Ferro... trouxe armas...$B$B<tosse>$B$B... armas de trovão e fogo.$B$BNossos guerreiros nunca tinham visto armas desse tipo. Não estávamos preparados.$B$B<respira> Temos que aprender... a superar...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'ruRU', 'Огонь и молнии', 'Осмотрите осадные боеприпасы и тяжелую обшивку в Костегаре.', 'Железный Волк... принес оружие...$B$B<кашляет>$B$B...оружие, которые изрыгает огонь и мечет молнии.$B$BНаши воины никогда не видели подобного. Мы были не готовы.$B$B<хрипит> Надо... помешать…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'zhCN', '烈焰与雷霆', '在骨渣锻炉里检查攻城军需品和厚钢板。', '铁狼……带来了武器……$B$B<咳嗽>$B$B……烈焰与雷霆的武器。$B$B我们的战士们从没见过这样的武器。我们没有做好准备。$B$B<喘息>我们必须学会……战胜……', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33013, 'zhTW', '雷火交鋒', '檢視焚骨坑地中的攻城彈藥和重裝甲。', '鐵狼…帶了武器…$B$B<咳>$B$B…有火與雷的武器。$B$B我們的戰士從來沒有看過那種東西。我們的準備還不夠充份。$B$B<喘氣>我們必須學習…去克服…', '', '', '', '', '', '', 0);
