INSERT INTO `quest_template_locale` VALUES (33145, 'itIT', 'Collina dei Gelotetro', '', 'Libera i Lupi Bianchi tenuti prigionieri dagli Ogre Gelotetro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33145, 'koKR', '잔혹서리 언덕', '', '잔혹서리 오우거에게 붙잡힌 서리늑대 부족을 풀어줘야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33145, 'ptBR', 'Colina Calafrio', '', 'Liberte os Lobo do Gelo aprisionados pelos ogros Calafrio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33145, 'ruRU', 'Холм Жестокого Холода', '', 'Освободите северных волков, которых удерживают в плену огры клана Жестокого Холода.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33145, 'zhCN', '肃霜丘陵', '', '释放被肃霜食人魔囚禁的霜狼成员。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33145, 'zhTW', '嚴霜之丘', '', '救出被嚴霜巨魔囚禁的霜狼。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'deDE', 'Nudelzeit', 'Reiht Euch für eine Schicht am Nudelstand von Halbhügel ein und beendet sie.', 'Hallo, $n. Möchtet Ihr wieder einmal am Nudelstand arbeiten?$B$BSagt mir einfach Bescheid, wenn Ihr noch eine Schicht übernehmen könnt, dann bereite ich alles vor.', '', '', '', 'Gute Arbeit! Alle sind immer ganz begeistert, wenn sie hören, dass Ihr die Nudelsuppe zubereitet.', 'Sungshin Eisentatz', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'esES', 'La hora de los fideos', 'Haz cola y completa un turno en el puesto de fideos de El Alcor.', 'Hola, $n. ¿Te apetece volver a trabajar en el puesto de fideos?$B$BAvísame cuando estés $glisto:lista; para trabajar otro turno y lo prepararé todo.', '', '', '', '¡Buen trabajo! La gente se pone muy contenta cuando se entera de que vas a preparar sopa de fideos. ', 'Sungshin Zarpa Férrea', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'esMX', 'La hora de los fideos', 'Haz cola y completa un turno en el puesto de fideos de El Alcor.', 'Hola, $n. ¿Te apetece volver a trabajar en el puesto de fideos?$B$BAvísame cuando estés $glisto:lista; para trabajar otro turno y lo prepararé todo.', '', '', '', '¡Buen trabajo! La gente se pone muy contenta cuando se entera de que vas a preparar sopa de fideos. ', 'Sungshin Zarpa Férrea', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'frFR', 'L’heure des nouilles', 'Mettez-vous dans la file et effectuez un service au Stand de nouilles de Micolline.', 'Bonjour, $n. Ça vous dirait de vous charger à nouveau du stand de nouilles ?$B$BPrévenez-moi quand vous serez $gprêt:prête; pour faire un autre service et je mettrai tout en place.', '', '', '', 'Beau travail ! Tout le monde aime entendre qu’une bonne soupe de nouille est en préparation.', 'Sung Shin Patte de Fer', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'itIT', 'Spaghetti a volontà', 'Mettiti in coda e completa un turno al Chiosco degli Spaghetti di Mezzocolle.', 'Salve, $n. Sei $ginteressato:interessata; a lavorare ancora al Chiosco degli Spaghetti?$B$BFammi sapere quando desideri fare un nuovo turno e io mi occuperò di organizzare tutto.', '', '', '', 'Ottimo lavoro! Tutti adorano una buona zuppa di spaghetti.', 'Sungshin Zampa Forte', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'koKR', '국수 시간', '언덕골 국수 장사 대기열에 등록한 후 완료해야 합니다.', '안녕하세요, $n 님. 또 국수 장사를 하고 싶으세요?$B$B일할 준비가 됐을 때 말씀만 하시면 수레를 준비해 드릴게요.', '', '', '', '훌륭해요! 당신이 국수 장사를 한다고 하면 모두들 아주 좋아하죠.', '성쉰 아이언포우', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'ptBR', 'Hora do Macarrão', 'Entre na fila e complete um turno na barraquinha de macarrão de Meia Colina.', 'Olá, $n. Quer trabalhar na barraquinha de macarrão de novo?$B$BMe diga quando estiver $gpronto:pronta; para começar um novo turno e preparo tudo para você.', '', '', '', 'Ótimo trabalho! Todo mundo se anima quando fica sabendo que é você quem vai preparar a sopa de macarrão.', 'Sungshin Pata de Ferro', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'ruRU', 'Час лапши', 'Встаньте в очередь на рабочую смену и отработайте ее у стойки с лапшой в Полугорье.', 'Привет, $n. Не желаешь ли приготовить очередную порцию лапши?$B$BДай знать, когда будешь $gготов:готова; к смене, и я все подготовлю.', '', '', '', 'Отличная работа! Все обожают, когда ты готовишь суп с лапшой.', 'Суншинь Железная Лапа', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'zhCN', '汤面时间', '在半山面档排队并完成一次上工。', '你好，$n。想再经营一次面档吗？$B$B等你准备好再次上工时就来找我吧，我会将一切都准备好。', '', '', '', '干得好！人们一听说你要制作汤面，就马上变得胃口大开。', '孙行·铁掌', '', 0);
INSERT INTO `quest_template_locale` VALUES (33146, 'zhTW', '湯麵時刻', '在半丘拉麵攤加入並完成拉麵攤的佇列。', '你好啊，$n還想擺拉麵攤嗎?$B$B如果你想再擺一輪攤子的話跟我說，我會把東西全部準備好。', '', '', '', '做得好!每次聽說你要煮湯麵，大家都很高興。', '宋辛‧鐵爪', '', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'deDE', 'Y\'Shaarjs letzter Atemzug', 'Sprecht mit Lehrensucher Cho in der Schlacht um Orgrimmar.', 'Eine dunkle, dampfende Substanz befleckt den Boden des Raums. Das ist alles, was vom Alten Gott Y\'Shaarj übrig bleibt.', '', '', '', '', '', 'Sprecht mit Lehrensucher Cho in der Schlacht um Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'esES', 'El último aliento de Y\'Shaarj', 'Habla con el eremita Cho en el Asedio de Orgrimmar.', 'Una sustancia oscura y vaporosa cubre el suelo de la sala. Es todo lo que queda del dios antiguo Y\'Shaarj.', '', '', '', '', '', 'Habla con: Eremita Cho. Zona: Asedio de Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'esMX', 'El último aliento de Y\'Shaarj', 'Habla con el eremita Cho en el Asedio de Orgrimmar.', 'Una sustancia oscura y vaporosa cubre el suelo de la sala. Eso es todo lo que queda del antiguo dios Y\'Shaarj.', '', '', '', '', '', 'Habla con: Eremita Cho. Zona: Asedio de Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'frFR', 'Le dernier souffle d’Y’Shaarj', 'Parlez au Chroniqueur Cho au Siège d’Orgrimmar.', 'Une substance noire et vaporeuse macule le sol de la salle. C’est tout ce qu’il reste du dieu très ancien Y’Shaarj.', '', '', '', '', '', 'Parlez au Chroniqueur Cho au Siège d’Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'itIT', 'L\'ultimo respiro di Y\'shaarj', 'Parla con il Ramingo della Sapienza Cho nell\'Assedio di Orgrimmar.', '<Una sostanza oscura e vaporosa macchia il pavimento della stanza. È tutto ciò che rimane del Dio Antico Y\'shaarj.>', '', '', '', '', '', 'Parla con il Ramingo della Sapienza Cho nell\'Assedio di Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'koKR', '이샤라즈의 마지막 숨결', '오그리마 공성전에서 전승지기 초와 대화해야 합니다.', '어둡고 수증기 같은 물질로 방 바닥이 얼룩졌습니다. 고대 신 이샤라즈의 마지막 흔적입니다.', '', '', '', '', '', '오그리마 공성전에서 전승지기 초와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'ptBR', 'O último suspiro de Y\'Shaarj', 'Fale com o Andarilho das Lendas Cho no Cerco a Orgrimmar.', 'Uma substância escura e vaporosa mancha o chão da sala. É tudo o que restou do deus antigo Y\'Shaarj.', '', '', '', '', '', 'Fale com o Andarilho das Lendas Cho no Cerco a Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'ruRU', 'Последний вздох И\'Шараджа', 'Поговорите с хранителем истории Чо во время осады Оргриммара.', 'Темная, похожая на пар субстанция покрывает пол комнаты. Это все, что осталось от древнего бога И\'Шараджа.', '', '', '', '', '', 'Поговорите с хранителем истории Чо во время осады Оргриммара.', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'zhCN', '垂死的亚煞极', '在决战奥格瑞玛中与游学者周卓谈一谈。', '一种黑暗的雾状物质玷污着房间的地板。古神亚煞极的所有残留物都在这里了。', '', '', '', '', '', '在决战奥格瑞玛中与游学者周卓谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (33147, 'zhTW', '亞煞拉懼的臨終之息', '在圍攻奧格瑪中與博學行者阿洲交談。', '漆黑的揮發物質髒污了地面。這就是上古之神亞煞拉懼僅存的殘骸。', '', '', '', '', '', '在圍攻奧格瑪中與博學行者阿洲交談。', 0);
