INSERT INTO `quest_template_locale` VALUES (33263, 'deDE', 'Schummergehölz', 'Sucht im Schummergehölz nach Hinweisen.', '<Das scheint eines der Lastmaultiere aus Eurer Garnison zu sein. Seltsam, dass es hier draußen allein herumwandert.$b$bIhr solltet dem vielleicht auf den Grund gehen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'esES', 'Arboleda Sombra Umbría', 'Busca pistas en la Arboleda Sombra Umbría.', '<Se parece a una de las mulas de carga de tu ciudadela. Qué raro que ande deambulando sola por aquí.$b$bHarías bien en investigarlo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'esMX', 'Arboleda Penumbra', 'Inspeccionar la Arboleda Penumbra en busca de pistas', '<Esta parece una de las mulas de carga de tu fortaleza. Es raro que ande sola deambulando por aquí.$b$bDeberías investigar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'frFR', 'Le bosquet des Ténombres', 'Cherchez des indices dans le bosquet des Ténombres.', '<On dirait l’une des mules de bât de votre fief. Elle n’a aucune raison de se promener seule dans les parages.$b$bVous devriez peut-être aller voir ça de plus près.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'itIT', 'Boschetto di Ombracupa', 'Cerca indizi sulla squadra scomparsa.', '<Sembra essere uno dei Muli da Soma della tua Guarnigione. È strano che si trovi qui in giro a vagare da solo.>$b$b<Decidi di investigare.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'koKR', '어둠그늘 숲', '어둠그늘 숲으로 가서 단서를 찾아야 합니다.', '<이 노새는 당신의 주둔지에서 떠난 짐노새 중 하나인 듯합니다. 여기서 혼자 어슬렁거리고 있다니, 이상한 일입니다.$b$b조사를 해보는 편이 좋겠습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'ptBR', 'Bosque da Penumbra', 'Procure pistas no Bosque da Penumbra.', '<Esta parece uma das mulas da sua guarnição. Estranho ela estar aqui sozinha.$b$bTalvez seja melhor investigar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'ruRU', 'Сумрачная роща', 'Обыщите Сумрачную рощу и выясните, откуда здесь взялся мул.', '<Кажется, это вьючный мул из вашего гарнизона. Странно, что он бродит здесь в одиночку.$B$BНужно выяснить, что случилось.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'zhCN', '幽影林地', '在幽影林地寻找线索。', '<这像是来自你要塞的骡子。它独自在这游荡可真奇怪。$b$b你也许应该调查清楚。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33263, 'zhTW', '幽蔭林地', '在幽蔭林地尋找線索。', '<這頭運貨馱騾像是從你的要塞來的。牠獨自在外徘徊實在太奇怪了。$b$b你應該調查一下。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33264, 'deDE', 'Ein ordentliches Begräbnis', 'Verbrennt die Leichname der Vorhut der Frostwölfe.', '<Der Orc ist sichtlich erschöpft. Eine große Blutlache sammelt sich zu seinen Füßen.>$B$BWir waren auf der Jagd, als wir auf die Wölfe getroffen sind. Sie wurden angegriffen.$b$bWir haben mit unseren Klingen viele Oger getötet... aber ihre Zahl war zu groß.$b$bIch muss nur kurz zu Atem kommen, dann werde ich hinausgehen und den Rest von ihnen töten.$b$bHier, nehmt diese Fackel. Gebt meinen Brüdern und Schwestern ein anständiges Begräbnis und kehrt dann zu mir zurück.', '', '', '', '', '', 'Kehrt zu Urukag am Grimmfrostberg im Frostfeuergrat zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (33264, 'esES', 'Un sepelio adecuado', 'Quema los cadáveres de los escoltas Lobo Gélido muertos.', '<El orco está claramente sin aliento. A sus pies, hay un gran charco de sangre>.$B$BEstábamos de caza cuando descubrimos que estaban atacando a los lobos.$b$bMasacramos a muchos ogros... pero eran demasiado numerosos.$b$bTengo que recobrar el aliento antes de salir a matar a los que queden.$B$BToma esta antorcha. Otorga a mis hermanos un sepelio adecuado y vuelve a verme.', '', '', '', '', '', 'Vuelve con: Urukag. Zona: Cerro Siniescarcha, Cresta Fuego Glacial.', 0);
INSERT INTO `quest_template_locale` VALUES (33264, 'esMX', 'A Proper Burial', 'Quema los cadáveres de los escoltas Lobo Gélido muertos.', '<The orc is visibly winded. A large pool of blood is gathering at his feet.>$B$BWe were out hunting when we found the wolves under attack.$b$bMany ogres were slaughtered by our blades... but their numbers were too great.$b$bI\'m going to catch my breath before I go out and kill the rest of them.$B$BHere, take this torch. Go give my brothers and sister a proper burial and return to me.', '', '', '', '', '', 'Vuelve con Urukag en la colina Siniescarcha, en la Cresta Pirofrío.', 0);
INSERT INTO `quest_template_locale` VALUES (33264, 'frFR', 'Des funérailles adéquates', 'Brûlez les corps des Voltigeurs loups-de-givre morts.', '<L’orc est manifestement épuisé. Une grande mare de sang s’étale à ses pieds.>$B$BNous chassions quand nous avons découvert que les loups étaient attaqués.$b$bBeaucoup d’ogres ont péri sous nos coups… mais ils étaient trop nombreux.$b$bJe vais reprendre mon souffle avant d’aller éliminer les derniers d’entre eux.$B$BTenez, prenez cette torche. Allez enterrer dignement mes frères et ma sœur, puis revenez me voir.', '', '', '', '', '', 'Retournez voir Urukag près de la Colline de Triste-Givre, sur la Crête de Givrefeu.', 0);
INSERT INTO `quest_template_locale` VALUES (33264, 'itIT', 'Una giusta sepoltura', 'Brucia i cadaveri dei Perlustratori dei Lupi Bianchi.', '<L\'Orco è visibilmente esausto. Una grossa pozza di sangue si sta formando ai suoi piedi.>$B$BEravamo fuori a caccia quando abbiamo trovato i lupi sotto attacco.$b$bMolti Ogre sono caduti sotto le nostre armi... ma erano in troppi.$b$bMi riposerò un po\' prima di tornare là fuori e uccidere quelli rimasti.$B$BEcco, prendi questa torcia. Onora i miei fratelli caduti e torna da me.', '', '', '', '', '', 'Torna da Urukag alla Collina dei Gelotetro nella Landa di Fuocogelo.', 0);
INSERT INTO `quest_template_locale` VALUES (33264, 'ptBR', 'A Proper Burial', 'Burn the corpses of dead Frostwolf Outriders.', '<The orc is visibly winded. A large pool of blood is gathering at his feet.>$B$BWe were out hunting when we found the wolves under attack.$b$bMany ogres were slaughtered by our blades... but their numbers were too great.$b$bI\'m going to catch my breath before I go out and kill the rest of them.$B$BHere, take this torch. Go give my brothers and sister a proper burial and return to me.', '', '', '', '', '', 'Return to Urukag by Grimfrost Hill in Frostfire Ridge.', 0);
INSERT INTO `quest_template_locale` VALUES (33264, 'ruRU', 'A Proper Burial', 'Burn the corpses of dead Frostwolf Outriders.', '<The orc is visibly winded. A large pool of blood is gathering at his feet.>$B$BWe were out hunting when we found the wolves under attack.$b$bMany ogres were slaughtered by our blades... but their numbers were too great.$b$bI\'m going to catch my breath before I go out and kill the rest of them.$B$BHere, take this torch. Go give my brothers and sister a proper burial and return to me.', '', '', '', '', '', 'Return to Urukag by Grimfrost Hill in Frostfire Ridge.', 0);
