INSERT INTO `quest_template_locale` VALUES (33267, 'deDE', 'Zurückschneiden der Schotlinge', 'Nehmt Rache an den Schotlingen.', 'The blood soaked body of Kenny is slummped up against the cave wall. The podlings clearly made a feast of him.', '', '', '', '', '', '', 0);
