INSERT INTO `quest_template_locale` VALUES (33267, 'esMX', '[Deprecated]', 'Véngate de las vainitas.', 'El cuerpo ensangrentado de Kenny yace desplomado contra la pared de la cueva. Las vainitas se hicieron un festín con él.', '', '', '', '', '', '', 0);
