INSERT INTO `quest_template_locale` VALUES (33269, 'deDE', 'Säuberung von Gul\'var', 'Tötet $1oa Orcs des Schattenrats in Gul\'var.', 'Khadgar möchte, dass Ihr Euch auf Gul\'dan konzentriert, aber das heißt noch lange nicht, dass wir dabei nicht auch den Schattenrat zu Fall bringen.$B$BGul\'var stinkt geradezu nach Handlangern des Schattenrats. Wir müssen etwas gegen sie unternehmen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'esES', 'Limpiar Gul\'var', 'Mata a $1oa orcos del Consejo de la Sombra en Gul\'var.', 'Khadgar quiere que te concentres en Gul\'dan, pero eso no significa que no vayamos a aprovechar para acabar con el Consejo de la Sombra.$B$BGul\'var está contaminada por culpa de los villanos del Consejo de la Sombra. Hay que hacer algo al respecto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'esMX', 'Limpiando Gul\'var', 'Mata a $1oa orcos del Consejo de la Sombra en Gul\'var.', 'Khadgar quiere que te concentres en Gul\'dan, pero eso no significa que, entretanto, no vayamos a deshacernos del Consejo de la Sombra.$B$BGul\'var está lleno de villanos del Consejo. Hagamos algo al respecto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'frFR', 'Nettoyer Gul’var', 'Tuez $1oa orcs du Conseil des ombres à Gul’var.', 'Khadgar veut que vous vous concentriez sur Gul’dan, mais rien ne nous empêche d’éliminer le Conseil des ombres en même temps.$B$BGul’var est infesté de scélérats du conseil. Occupons-nous d’eux.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'itIT', 'Ripulire Gul\'var', 'Uccidi $1oa Orchi del Concilio dell\'Ombra a Gul\'var.', 'Khadgar vuole che ti concentri su Gul\'dan, ma questo non significa che non ci possiamo occupare anche del Concilio dell\'Ombra allo stesso tempo.$B$BGul\'var pullula di membri del Concilio dell\'Ombra; dobbiamo fare qualcosa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'koKR', '굴바르 청소', '굴바르에 있는 어둠의 의회 오크 $1oa명을 처치해야 합니다.', '카드가 님께선 우리가 굴단에게 집중하길 바라시지만, 그 과정에서 어둠의 의회 놈들을 좀 잡아서 안 될 것 없죠.$B$B굴바르엔 어둠의 의회 악당들이 득시글거립니다. 뭔가 조치를 취해 봅시다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'ptBR', 'Limpando Gul\'var', 'Mate $1oa orcs do Concílio das Sombras em Gul\'var.', 'Hadggar quer que você se concentre em Gul\'dan, mas isso não significa que não vamos derrubar o Concílio das Sombras.$B$BGul\'var está repleta de vilões do Concílio das Sombras. Vamos cuidar deles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'ruRU', 'Освобождение Гул\'вара', 'Убейте $1oa |4орка:орков:орков; Совета Теней в Гул\'варе.', 'Кадгар хочет, чтобы ты $gсосредоточился:сосредоточилась; на Гул\'дане, но это не значит, что одновременно мы не можем уничтожить и Совет Теней.$B$BВ Гул\'варе полно приспешников Совета Теней. Нужно разобраться с ними.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'zhCN', '清理古尔瓦', '在古尔瓦消灭$1oa个暗影议会兽人。', '卡德加要你集中精力对付古尔丹，但那并不意味着我们不能顺便消灭暗影议会。$B$B古尔瓦被暗影议会的恶棍占领了。让我们对他们采取些行动吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33269, 'zhTW', '整頓古爾瓦', '殺死$1oa個在古爾瓦的暗影議會獸人。', '卡德加要你把注意力集中在古爾丹身上，並不表示我們不能順手把暗影議會解決掉。$B$B古爾瓦到處都是暗影議會的走狗。我們來解決掉他們吧。', '', '', '', '', '', '', 0);
