INSERT INTO `quest_template_locale` VALUES (33271, 'deDE', 'Ränkespiele', 'Tötet König Todesblüte.', '<Jarrod ist eindeutig verletzt. Er zuckt vor Schmerz zusammen, als er anfängt, zu berichten.>$b$bMeine Waldtruppführerin Shelly... sie wird nicht weit von hier gefangen gehalten.', '', '', 'König Todesblüte', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'esES', 'A dos metros bajo tierra', 'Mata al rey Flormuerte.', '<Es evidente que Jarrod está herido. Hace una mueca de dolor al comenzar a hablar.>$b$bMi operadora jefe del bosque, Shelly... está cautiva cerca de aquí...', '', '', 'Rey Flormuerte', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'esMX', 'Juego de espinas', 'Mata al Rey Flor de Muerte.', '<Jarrod está claramente herido. Se dobla de dolor cuando empieza a hablar.>$b$bMi operadora de bosque líder, Shelly... la tienen no muy lejos de aquí...', '', '', 'Rey Flor de Muerte', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'frFR', 'Qui s’y frotte s’y pique', 'Tuez le Roi Mortefleur.', '<Jarrod est manifestement blessé. Il grimace de douleur lorsqu’il commence à parler.>$b$bShelly, la responsable des opérations forestières... Elle est prisonnière, pas loin d’ici…', '', '', 'Roi Mortefleur', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'itIT', 'Il trono di spine', 'Uccidi Re Fiormortale.', '<Jarrod è chiaramente ferito. Si contorce dal dolore non appena inizia a parlare.>$b$bIl capo delle operazioni nella foresta, Shelly... è tenuta prigioniera non lontano da qui...', '', '', 'Re Fiormortale', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'koKR', '가시왕좌의 게임', '대왕 죽음꽃잎을 처치해야 합니다.', '<자로드는 한눈에 봐도 중상을 입었습니다. 그가 고통으로 움찔거리며 말하기 시작합니다.>$b$b우리 대장, 셸리가... 여기서 멀지 않은 곳에 붙잡혀 있습니다...', '', '', '대왕 죽음꽃잎', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'ptBR', 'A Guerra dos Espinhos', 'Mate o Rei Florescer da Morte.', '<Jarrod está claramente ferido. Ele geme de dor antes de falar.>$b$bA líder da operação florestal, Conchita... está presa perto daqui...', '', '', 'Rei Florescer da Morte', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'ruRU', 'Песнь шипов и кореньев', 'Убейте короля Бутона Смерти.', '<Джаррод ранен. Морщась от боли, он обращается к вам.>$b$bШелли, командир отряда... ее держат где-то поблизости...', '', '', 'Король Бутон Смерти', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'zhCN', '棘花的游戏', '击败枯蕾大王。', '<加罗德显然受了伤。他说话的时候面孔因剧痛而抽搐着。>$b$b我的首席看林人雪莉……就被关在不远处……', '', '', '枯蕾大王', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33271, 'zhTW', '權棘遊戲', '殺死亡花國王。', '<傑拉德明顯受了傷。在他開口的同時，臉部肌肉痛苦地抽搐。>$b$b帶領森林工作者的雪莉…就被關在離此地不遠之處…', '', '', '亡花國王', '', '', '', 0);
