INSERT INTO `quest_template_locale` VALUES (33431, 'koKR', '엘윈 장거리 시험: 30', '아치를 통과한 뒤, 빨간색 푯대에 닿고 보라색 푯대에 닿은 후, 강철엘프 게임장으로 돌아와야 합니다.', '도전을 완료하시려면 아치를 통과한 후에 빨간색 푯대, 보라색 푯대에 닿은 다음 다시 여기로 돌아오시면 돼요. 종이 30번 울리기 전에 돌아오셔야 한답니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33431, 'ptBR', 'Tomada de tempo de distância ao sul de Elwynn: 30', 'Atravesse o arco correndo, passe pelo marcador vermelho, depois pelo marcador roxo e volte ao acampamento dos Jogos Élficos.', 'Nessa prova, você tem que atravessar o arco correndo, passar pelo marcador vermelho, depois pelo marcador roxo e voltar para cá antes que o sino toque 30 vezes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33431, 'ruRU', 'Элвиннская южная трасса: 30', 'Пробегите под аркой, добегите до красной отметки, затем до фиолетовой и возвращайтесь в игровой лагерь Железных Эльфов.', 'B этом испытании тебе нужно будет пробежать под аркой, затем добежать до красной отметки, потом до фиолетовой отметки и успеть вернуться сюда до тридцатого удара колокола.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33431, 'zhCN', '艾尔文南部长跑试炼之钟响30下', '跑过拱门，通过红色标记和紫色标记，然后返回钢铁精灵游戏营地。', '在这项挑战中：你需要跑过拱门，先后抵达红色标记和紫色标记，最后在钟敲响30下之前返回这里。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33431, 'zhTW', '艾爾文南段賽程考驗：30', '跑過這道拱門，碰到紅色標記，再碰到紫色標記，再回到鋼鐵精靈比賽營地。', '比賽規則是這樣的：跑過這道拱門，碰到紅色標記，再來是紫色標記，最後在30響以內回到這裡來。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33432, 'deDE', 'Elwynn South Distance Practice', 'Rennt durch den Bogen, erreicht die rote Markierung, dann die violette Markierung und kehrt dann zum Lager der Eisenelfspiele zurück.', 'Ihr wollt etwas üben?$B$BHier gilt: Rennt durch den Bogen, erreicht die rote Markierung, dann die violette Markierung und kehrt schließlich hierher zurück.$B$Bhr könnt Euch so viel Zeit nehmen, wie Ihr braucht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33432, 'esES', 'Elwynn South Distance Practice', 'Pasa por el arco, activa el marcador rojo, luego el marcador morado y regresa al campamento de juegos Ferrelfo.', '¿Quieres practicar un poco?$B$BPara este reto: pasa por el arco, luego ve al marcador rojo, al marcador morado, y finalmente vuelve aquí.$B$BDispones de todo el tiempo que necesites.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33432, 'esMX', 'Práctica de distancia sur de Elwynn', 'Corre a través del arco, golpea el marcador rojo, luego el púrpura y regresa al campamento de juegos Elférreo.', '¿Quieres practicar un poco?$B$BPara este reto, corre a través del arco, luego ve al marcador rojo, luego al púrpura y finalmente vuelve aquí.$B$BTienes todo el tiempo que necesites.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33432, 'frFR', 'Elwynn South Distance Practice', 'Passez en courant sous l’arche, touchez le marqueur rouge, puis le marqueur violet, et retournez au camp de jeu d’Elfefer.', 'Vous voulez vous entraîner ?$B$BPour réussir ce défi, passez en courant sous l’arche, puis allez jusqu’au marqueur rouge, au marqueur violet, et enfin revenez ici.$B$BVous avez tout le temps nécessaire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33432, 'itIT', 'Allenamento sulla distanza del sud di Elwynn', 'Corri attraverso l\'arcata, raggiungi il punto di controllo rosso e quello viola, quindi torna al Campo Sportivo degli Elfi di Ferro.', 'Vuoi allenarti?$B$BPer questa sfida, corri attraverso l\'arcata, raggiungi il punto di controllo rosso e quello viola, quindi torna qui.$B$BHai tutto il tempo che ti serve.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33432, 'koKR', '엘윈 남쪽 장거리 연습', '아치를 통과한 뒤, 빨간색 푯대에 닿고 보라색 푯대에 닿은 후, 강철엘프 게임장으로 돌아와야 합니다.', '연습해 보고 싶으세요?$B$B도전을 완료하시려면 아치를 통과한 후에 빨간색 푯대, 보라색 푯대에 닿은 다음 다시 여기로 돌아오시면 돼요.$B$B시간 제한은 없답니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33432, 'ptBR', 'Treino para distância ao sul de Elwynn', 'Atravesse o arco correndo, passe pelo marcador vermelho, depois pelo marcador roxo e volte ao acampamento dos Jogos Élficos.', 'Você quer treinar um pouco?$B$BNessa prova, você tem que atravessar o arco correndo, passar pelo marcador vermelho, depois pelo marcador roxo e voltar para cá.$B$BNão há limite de tempo.', '', '', '', '', '', '', 0);
