INSERT INTO `quest_template_locale` VALUES (33898, 'deDE', 'Vertreibung der Sklaventreiber', 'Tötet $1oa Oger im Rußgeschwärzten Bergbaulager.', 'Die Oger nehmen immer wieder Mitglieder meines Klans gefangen und versklaven sie!\n\nTötet ein paar Oger. Ich werde sie aufhalten, wenn sie versuchen, zu entkommen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'esES', 'Asesinar esclavistas', 'Mata a $1oa ogros Tiznado.', '¡Estos ogros siguen capturando a los de mi clan y esclavizándolos!\n\nMata algunos ogros y yo contendré a los que intenten huir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'esMX', 'Matar esclavos', 'Mata $1oa ogros Manchollín', '¡Estos ogros siguen metiéndose con mi clan y convirtiéndolos en esclavos!\n\nMata algunos ogros; y yo los detendré si intentan escapar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'frFR', 'Extermination des esclavagistes', 'Tuez $1oa Ogres de Noir-de-Suie.', 'Ces Ogres ne cessent de capturer des membres de mon clan pour en faire leurs esclaves !$B$BTuez quelques Ogres, je les retiendrai s’ils tentent de fuir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'itIT', 'Eliminare gli schiavisti', 'Uccidi $1oa Ogre di Nerofumo.', 'Questi Ogre continuano ad attaccare il mio clan e a trasformarci in schiavi!\n\nUccidi quegli Ogre e tienili a bada se cercano di scappare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'koKR', '노예사냥꾼 처치', '검댕투성이 오우거 $1oa마리를 처치해야 합니다.', '저 오우거들이 자꾸 우리 부족 동료들을 끌고 가서 노예로 삼고 있습니다!\n\n오우거들을 처치해 주십시오. 달아나는 놈들은 제가 막겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'ptBR', 'Morte aos escravagistas', 'Mate $1oa Ogros Fuliginosos.', 'Os ogros não param de escravizar membros do meu clã!\n\nMate alguns ogros. Eu pego os que tentarem fugir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'ruRU', 'Долой рабовладельцев', 'Убейте $1oa |4огра:огров:огров; Закоптелой шахты.', 'Огры похищают орков моего клана и обращают их в рабство!\n\nУбей для меня парочку, а я буду ждать тут... на случай, если они попытаются бежать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'zhCN', '消灭奴贩', '消灭$1oa个污煤食人魔。', '这些食人魔一直在掠夺我的族人，并把他们变为奴隶！\n\n去消灭一些食人魔，要是他们想要逃跑的话，我会拖住他们。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (33898, 'zhTW', '殺死奴隸商人', '殺死$1oa隻煤污巨魔。', '這些巨魔一直抓走我的族人，讓他們當奴隸！\n\n宰掉一些巨魔，如果他們想逃走的話，我來擋住他們。', '', '', '', '', '', '', 0);
