INSERT INTO `quest_template_locale` VALUES (34054, 'deDE', 'Die dunkle Seite des Mondes', 'Sucht Ner\'zhul.', '<Prophet Velens Stimme fährt fort.>$B$BYrel und ich haben Kriegsherr Ner\'zhul aufgespürt. Er trifft sich mit Grommash Höllschrei am höchsten Altar, der die Festung der Pein überblickt.$B$BTrefft Euch dort mit uns. Wir müssen ihn aufhalten.', '', '', 'Prophet Velen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'esES', 'La cara oculta de la luna', 'Busca a Ner\'zhul.', '<La voz del profeta Velen sigue hablando.>$b$bYrel y yo hemos localizado al señor de la guerra Ner\'zhul. Se reúne con Grommash Grito Infernal en el altar más alto sobre la Fortaleza de la Angustia.$b$bVe a vernos allá. Hay que pararle los pies.', '', '', 'Profeta Velen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'esMX', 'El lado oscuro de la luna', 'Encuentra a Ner\'zhul.', '<La voz del profeta Velen continúa.>$B$BYrel y yo encontramos al Señor de la Guerra Ner\'zhul. Se encontrará con Grommash Grito Infernal en la cima del altar más alto con vistas a la Fortaleza Angustia.$B$BReúnete con nosotros allí. Debemos detenerlo.', '', '', 'Profeta Velen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'frFR', 'La face cachée de la lune', 'Trouvez Ner’zhul.', '<La voix du prophète Velen poursuit.>$B$BYrel et moi avons trouvé le seigneur de guerre Ner’zhul. Il s’entretient avec Grommash Hurlenfer au sommet du plus haut des autels surplombant la forteresse de l’Angoisse.$B$BRejoignez-nous sur place. Nous devons l’arrêter.', '', '', 'Le prophète Velen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'itIT', 'Il lato oscuro della luna', 'Trova Ner\'zhul.', '<La voce del Profeta Velen continua.>$B$BYrel e io abbiamo scoperto la posizione del Signore della Guerra Ner\'zhul. Sta per incontrare Grommash Malogrido in cima all\'altare più alto che domina la Fortezza del Tormento.$B$BIncontriamoci lì, dobbiamo fermarlo.', '', '', 'Profeta Velen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'koKR', '달의 어두운 이면', '넬쥴을 찾아야 합니다.', '<예언자 벨렌의 목소리가 계속 이어집니다.>$B$B이렐과 내가 넬쥴의 위치를 알아냈네. 그는 고뇌의 요새를 굽어볼 수 있는 가장 높은 제단에서 그롬마쉬 헬스크림과 회합을 가지고 있네.$B$B거기서 만나세. 놈을 막아야 하네.', '', '', '예언자 벨렌', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'ptBR', 'O lado escuro da lua', 'Encontre Ner\'zhul.', '<A voz do Profeta Velen.>$B$BYrel e eu localizamos o Senhor da Guerra Ner\'zhul. Ele está reunido com Grommash Grito Infernal no topo do altar mais elevado, que dá para a Fortaleza da Angústia.$B$BEncontra-nos lá. Temos que detê-lo.', '', '', 'Profeta Velen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'ruRU', 'Темная сторона Луны', 'Найдите Нер\'зула.', '<голос Пророка Велена продолжает>$B$BНам с Ирель удалось найти Нер\'зула. Он встречается с Громмашем Адским Криком на вершине самого высокого алтаря около Крепости Страданий.$B$BПриходи туда. Нам надо остановить его.', '', '', 'Пророк Велен', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'zhCN', '月之阴暗面', '找到耐奥祖。', '<先知维伦的声音还在继续。>$B$B我和伊瑞尔发现了督军耐奥祖的下落。他正在俯瞰痛楚堡垒的那座最高的祭坛上跟格罗玛什·地狱咆哮会面。$B$B到那找我们吧。我们必须阻止他。', '', '', '先知维伦', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34054, 'zhTW', '月亮的陰暗面', '找到耐祖奧。', '<預言者費倫的聲音繼續說道。>$B$B我和伊芮爾已經知道耐祖奧的下落了。他正要前往那座能俯瞰苦怨堡壘，位於最高處的祭壇，與葛羅瑪許‧地獄吼會面。$B$B到那裡和我們會合，我們必須阻止他。', '', '', '預言者費倫', '', '', '', 0);
