INSERT INTO `quest_template_locale` VALUES (34242, 'ptBR', 'Encontro marcado', 'Fale com a Atalmas Tuulani fora de Telmor.', 'Tu te provaste mais que capaz de lutar, $n. Pode haver forças do Concílio das Sombras por toda Auchindoun, inclusive perto de Telmor.$B$BPreciso que tu acompanhes a atalmas Tuulani em Telmor para garantir que o cristal chegue em segurança a Auchindoun. Sei que posso contar contigo.', '', '', '', '', '', '', 0);
