INSERT INTO `quest_template_locale` VALUES (34439, 'deDE', 'Schlacht um die Schmiede', 'Tötet 15 Orcs des Schwarzfelsklans und 3 Ogronkriegsschläger.', 'Unsere Truppen sind voll bewaffnet. Zeit, anzugreifen!$b$bSchlagt ohne zu zögern und zielsicher zu, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'esES', 'La batalla de la forja', 'Mata a 15 orcos Roca Negra y a 3 aplastaguerras ogrones.', 'Nuestras tropas están armadas, ¡ahora es el momento de atacar!$b$b¡Ataca rápido y de verdad, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'esMX', 'La batalla de la Forja', 'Mata a 15 orcos Roca Negra y a 3 Triturados de Guerra ogron.', 'Nuestras tropas están armadas, ¡el momento de golpear es ahora!$b$b¡Golpea fuerte y duro, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'frFR', 'La bataille de la forge', 'Tuez 15 Orcs rochenoires et 3 Brise-guerres ogrons.', 'Nos troupes sont prêtes… il est temps d’attaquer !$b$bFrappez vite et fort, $n !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'itIT', 'La battaglia della forgia', 'Uccidi 15 Orchi Roccianera e 3 Ogron Spaccaguerra.', 'Le nostre truppe sono armate, è arrivato il momento di dare il via all\'assalto!$b$bColpisci $grapido e preciso:rapida e precisa;, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'koKR', '제련소 전투', '검은바위 오크 15명과 오그론 전쟁분쇄자 3마리를 처치해야 합니다.', '우리 동료들이 모두 제대로 무장을 했으니, 이제 공격할 때가 됐어요!$b$b온 힘을 다해 빠르게 공격하세요, $n 님!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'ptBR', 'A batalha da forja', 'Mate 15 Orcs da Rocha Negra e 3 Esmaguerras Ogrons.', 'Nossas tropas estão armadas... este é o momento de atacar!$B$BUm ataque rápido e firme, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'ruRU', 'Битва за кузницу', 'Убейте 15 орков из клана Черной горы и 3 огронов-сокрушителей.', 'Наши воины получили оружие – самое время нанести удар! $b$b$n, бей быстро и точно!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'zhCN', '锻炉大战', '消灭15个黑石兽人和3个独眼魔战地碾压者。', '我们的军队已经武装完毕——攻击时间到了！$b$b迅速出击，决不留情，$n！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34439, 'zhTW', '熔爐之戰', '殺死15個黑石獸人以及3個歐格隆碎戰者。', '我們的部隊已經全副武裝，現在是攻擊的好機會！$b$b要快、狠、準。攻擊，$n！', '', '', '', '', '', '', 0);
