INSERT INTO `quest_template_locale` VALUES (34455, 'deDE', 'Befreit ihre Geister', 'Benutzt das Rylakgedankenkontrollgerät bei gedankenkontrollierten Rylaks.', 'Dieses Gerät verwenden die Goblins, um die Rylaks zu kontrollieren.$B$BEs gibt auf der Steuerung einen Knopf, an dem \'nicht berühren\' steht.$B$BBegebt Euch in die Reichweite eines gedankenkontrollierten Rylaks und drückt auf den Knopf.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'esES', 'Abre sus mentes', 'Usa el controlador mental de rylaks en rylaks controlados.', 'Este dispositivo es lo que los goblins están utilizando para controlar a los rylaks.$b$bEn un botón del controlador pone \"No tocar\".$b$bAcércate a un rylak controlado y pulsa el botón.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'esMX', 'Libera sus mentes', 'Usa el controlador de mentes de rylaks en los rylaks controlados.', 'Este dispositivo es lo que los goblin están usando para controlar a los rylak.$b$bHay un botón en el controlador que dice \"no tocar\".$b$bAcércate a un rylak controlado y presiona el botón.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'frFR', 'Libérer leur esprit', 'Utilisez le Contrôleur d’esprit rylak sur les Rylaks contrôlés.', 'C’est avec ce dispositif que les gobelins contrôlent les rylaks.$b$bUn des boutons sur le contrôleur indique : « Ne pas toucher ».$b$bApprochez à portée d’un rylak contrôlé et appuyez sur ce bouton.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'itIT', 'Liberare le menti', 'Usa il Controllore Mentale di Rylak sugli Alacupa Sotto Controllo.', '<Questo dispositivo è quello usato dai Goblin per controllare i rylak.>$B$B<C\'è un bottone sul telecomando che reca la scritta \"Non toccare\".>$b$b<Avvicinati a uno di quei rylak sotto controllo e premi il pulsante.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'koKR', '정신 해방', '지배당한 라일라크에게 라일라크 정신 지배기를 사용해야 합니다.', '이 장치는 고블린들이 라일라크를 제어하기 위해 사용하는 물건입니다.$b$b\"만지지 마시오\"라고 쓰인 단추가 하나 있습니다.$b$b지배당한 라일라크 근처에 가서 이 단추를 눌러 봐야겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'ptBR', 'Liberte as mentes deles', 'Use o Controlador Mental de Rylak nos Rylaks Controlados.', 'Este dispositivo é o que os goblins estão usando para controlar os rylaks.$b$bNele, há um botão com um aviso para não tocar.$b$bChegue perto de um Rylak Controlado e... isso mesmo, aperte o botão.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'ruRU', 'Свободу узникам разума', 'Используйте устройство для управления разумом рилака на контролируемых рилаках.', 'Этот прибор гоблины используют для того, чтобы управлять рилаками.$b$bТам есть кнопка, на которой написано \"Не трогать\".$b$bПодберитесь к контролируемому рилаку и нажмите кнопку.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'zhCN', '放飞它们的心灵', '对被控制的双头飞龙使用双头飞龙精神控制仪。', '地精们就是用这个装置来控制双头飞龙的。$b$b在控制器上有个按钮，标签写着“请勿触摸”。$b$b到被控制的双头飞龙身边去，按下这个按钮。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34455, 'zhTW', '解放心志', '在受控的萊拉克身上使用萊拉克心靈控制器。', '這個裝置是哥布林用來控制萊拉克的。$b$b在控制器上面有一個按鈕，標示著千萬別碰。$b$b進入受控萊拉克的有效範圍內並按下按鈕。', '', '', '', '', '', '', 0);
