INSERT INTO `quest_template_locale` VALUES (34729, 'koKR', '나쉬라의 피의 맹세', '검귀 나쉬라의 피의 맹세를 받아들여야 합니다.', '자네는 내게 큰 도움을 주었네. 내 검은 이제 자네의 검이기도 해.$b$b자네에게 피의 맹세를 바치네. 내 검술을 자네의 뜻에 따라 쓰고, 채광을 도와주겠네.$b$B나를 받아들여 주겠나?', '', '최근에 검귀라는 칭호를 얻게 된 나쉬라는 용맹한 전사이자 무기의 달인입니다.', '검귀 나쉬라', '', '', '', 0);
