INSERT INTO `quest_template_locale` VALUES (34817, 'frFR', 'Altercation à l’autel', '', 'Secourez les prisonniers de l’autel.$b$b[NOTE: THIS QUEST WILL GET SIGNIFICANT REVAMP ONCE WE GET THE ALTAR IN]', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34817, 'itIT', 'Alterco all\'altare', '', 'Salva i prigionieri all\'Altare.$b$b[NOTE: THIS QUEST WILL GET SIGNIFICANT REVAMP ONCE WE GET THE ALTAR IN]', '', '', '', '', '', '', 0);
