INSERT INTO `quest_template_locale` VALUES (34897, 'deDE', 'Rangari gesucht', 'Findet die $1oa verborgenen Rangari.', 'Ihr habt ein gutes Auge, $C.$B$BIn der Nachtmarsch verbergen sich noch mehr Rangari wie ich.$B$BIch brauche Berichte von ihnen, um sicherzustellen, dass die Gegend sicher ist.$B$BKönntet Ihr mir dabei helfen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'esES', 'Redada Rangari', 'Encuentra a los $1oa Rangari ocultos.', 'Tienes buen ojo, $c.$b$bHay más Rangari como yo escondidos por la Marisma de las Pesadillas.$b$bNecesito informes de todos ellos para garantizar que la zona es segura.$b$b¿Podrías ayudarme?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'esMX', 'Redada Rangari', 'Encuentra $1oa Rangari ocultos.', 'Tienes buen ojo, $c.$b$bHay otros Rangari como yo escondidos en el Pantano Nocturno.$b$bNecesito informes de cada uno de ellos para asegurarme de que el área es segura.$b$b¿Quizás puedes ayudarme?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'frFR', 'Rassemblement des rangari', 'Trouvez les $1oa Rangari dissimulés.', 'Vous avez le regard perçant, $c.$b$bDans le Cauchemarais se cachent différents rangari comme moi.$b$bJ’ai besoin d’un compte-rendu de chacun d’entre eux afin de déterminer si la région est sûre.$b$bPeut-être pourriez-vous m’aider ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'itIT', 'Raduno dei Guardaselve', 'Trova i $1oa Guardaselve Nascosti.', 'Non ti sfugge niente, $C.$b$bCi sono Guardaselve come me nascosti lungo tutta la Palude dell\'Incubo.$b$bHo bisogno dei rapporti di ognuno di loro per assicurarmi che la zona sia al sicuro.$b$bForse puoi aiutarmi?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'koKR', '랑가리의 보고', '숨은 랑가리 $1oa명을 찾아야 합니다.', '당신은 날카로운 눈을 지녔죠, $c님.$b$b저 같은 랑가리들이 이 어둠늪 곳곳에 숨어 있습니다.$b$b이 지역이 안전한지 그들에게서 보고를 듣고 싶은데 말이죠.$b$b혹시 절 도와 주실 수 있나요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'ptBR', 'O Relatório Rangari', 'Encontre $1oa Rangari Escondidos.', 'Você tem olhos afiados, $c.$b$bHá outros Rangari como eu escondidos pelo Pântano da Noite.$b$bPreciso do relatório de cada um deles para saber se a área está segura.$b$bVocê poderia ajudar?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'ruRU', 'Среди рангари', 'Отыщите $1oa |4скрывшегося:скрывшихся:скрывшихся; рангари.', 'Ты весьма $gнаблюдательный:наблюдательная:c;, |3-6($c).$b$bВ Ночнотопи скрываются многие рангари вроде меня.$b$bМне нужно получить рапорты от каждого из них, чтобы удостовериться в том, что там безопасно.$b$bМожет быть, ты мне поможешь?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'zhCN', '集结游侠', '找到$1oa名潜伏的游侠。', '你的目光很是锐利，$c。$b$b在暗夜沼泽各处都潜伏着像我一样的游侠。$b$b我需要从他们每个人那里搜集报告，来确保地区的安全。$b$b也许你能帮帮我？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (34897, 'zhTW', '遊俠報告總結', '找出$1oa個隱匿的遊俠。', '你的眼力很好，$c。$b$b有一些像我這樣的遊俠就躲在夜夢澤裡。$b$b我必須得到他們的報告，確認這個地區是安全的。$b$b或許你幫得上忙？', '', '', '', '', '', '', 0);
