INSERT INTO `quest_template_locale` VALUES (35072, 'koKR', '통통 망둑어', '막진의 부탁에 따라 나그란드에서 통통 망둑어 알 10개를 모아야 합니다.', '주둔지의 연못에 이 지역 물고기를 채우려고 합니다. 나그란드 수원에 주둔지에서 기를 만한 물고기가 있는지 확인해 주십시오. 물고기를 낚고 나서, 배를 갈라 알이 있는지 찾아 보시고요.', '', '', '', '', '', '', 0);
