INSERT INTO `quest_template_locale` VALUES (35240, 'deDE', 'Ausgeblutet', 'Befreit die Sklaven aus den 2 Käfigen des Blutenden Auges in Herzblut.', '<Der Draenei stößt seine letzten Worte hervor.>$b$bSie hielten uns in Käfigen gefangen. Sie... haben uns das Blut entzogen... für Rituale...$b$b<Er hustet stark.>$b$b...noch viele...$b$b... mein Volk...$b$b<Der Draenei stirbt.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'esES', 'Desangrado', 'Libera a los esclavos de las 2 jaulas Foso Sangrante, en Sangre Vital.', '<El draenei intenta decir algo mientras agoniza.>$b$bNos encerraban en jaulas. Nos drenaban... la sangre... para rituales.$b$b<Tose violentamente.>$b$bQuedan muchos...$b$bMi gente...$b$b<El draenei muere.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'esMX', 'Desangrado', 'Libera a los esclavos de las 2 jaulas Foso Sangrante en Corazón de Sangre.', '<El draenei escupe sus últimas palabras.>$b$bNos encerraban en jaulas. Dre... drenaban nuestra sangre para realizar... rituales...$b$b<Tose con violencia.>$b$b... muchos se fueron...$b$b... mi... pueblo...$b$b<El draenei muere.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'frFR', 'Perte de sang', 'Libérez les esclaves des 2 Cages de l’Orbite-Sanglante au Sang-du-Cœur.', '<Le Draeneï souffle ses derniers mots.>$b$bIls nous ont enfermés dans des cages. Ils… nous vident de notre sang, pour… des rituels…$b$b<Il est pris d’une violente quinte de toux.>$b$b… Beaucoup se sont enfuis…$b$bMon peuple…$b$b<Le draeneï rend l’âme.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'itIT', 'Dissanguato', 'Libera gli schiavi dalle 2 Gabbie dei Guerci Insanguinati a Cuorsangue.', '<Il Draenei sussurra le sue ultime parole.>$b$bCi hanno tenuti in gabbia. Ci hanno... succhiato il sangue per i loro... rituali...$b$b<Tossisce violentemente.>$b$bMolti ci hanno lasciato...$b$bLa mia... gente...$b$b<Il Draenei muore.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'koKR', '뽑아낸 피', '심장피에 있는 피눈물 우리 2개에서 노예를 풀어줘야 합니다.', '<드레나이가 마지막 말을 내뱉습니다.>$b$b적은 우리 안에 동료들을 가뒀소. 그리고 우리... 피를 빼내서... 의식에 사용하려 했고...$b$b<드레나이가 거칠게 기침을 합니다.>$b$b... 많은 이들이 세상을... 떠났소...$b$b내... 동료들이...$b$b<드레나이가 목숨을 잃습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'ptBR', 'Sangrar até a morte', 'Liberte os escravos das 2 Jaulas dos Olhos Sangrentos em Cordissangue.', '<O draenei murmura suas últimas palavras.>$B$BEles nos prenderam em jaulas. Eles... drenaram nosso sangue para fazer... rituais...$B$B<Tosse violenta.>$B$B... tem... muitos lá...$B$B    ... meu... povo...$B$B<O draenei morre.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'ruRU', 'До последней капли', 'Освободите рабов из двух клеток клана Кровавой Глазницы в Крови Сердца.', '<Дреней буквально выплевывает свои последние слова.>$b$bОни бросили нас в клетки. Они высасывают из нас кровь... для своих ритуалов...$b$b<Он заходится кашлем.>$b$b...осталось...$b$b...моих... собратьев...$b$b<Дреней умирает.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'zhCN', '鲜血流尽', '从血环之心的2个血环牢笼里救出奴隶。', '<这名德莱尼人吐出了他的遗言。>$B$B他们把我们关在笼子里，抽……我们的血……来举行仪式……$B$B<他剧烈地咳嗽起来。>$B$B很多人……还在——$B$B——我的同胞们……$B$B<这名德莱尼人死去了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35240, 'zhTW', '鮮血乾涸', '從心之血的2個血之谷牢籠中釋放奴隸。', '<德萊尼人說出最後遺言。>$b$b他們將我們囚禁在牢籠裡。他們…抽取我們的血液，以供…儀式之用…$b$b<他劇烈咳嗽。>$b$b…許多人還在那裡…我的…同胞…$b$b<德萊尼人死了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'deDE', 'Rache für die Gefallenen', 'Tötet 6 Orcs des Blutenden Auges.', 'Der Klan des Blutenden Auges hat schon zu viele meiner Leute ermordet. Ihre Grausamkeiten müssen gerächt werden.$b$b<Der trauernde Orc sieht Euch an.>$b$bLasst sie dafür bezahlen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'esES', 'Venganza por los caídos', 'Mata a 6 orcos Foso Sangrante.', 'Ya han sucumbido demasiados a manos de los Foso Sangrante. No podemos dejar impune su crueldad.$b$b<La orco afligida alza la vista para mirarte.>$b$bHaz que paguen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'esMX', 'Venganza para los caídos', 'Mata 6 orcos Foso Sangrante.', 'Demasiados de los míos han caído a manos de los Foso Sangrante. Su crueldad no puede ser perdonada.$b$b<La orco afligida te mira.>$b$bHazlos pagar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'frFR', 'Au nom des disparus', 'Tuez 6 Orcs de l’Orbite-Sanglante.', 'Les monstres de l’Orbite-Sanglante ont fait bien trop de victimes parmi nous. Leur cruauté est impardonnable.$b$b<L’Orque éplorée vous fixe du regard.>$b$bFaites-leur payer leurs crimes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'itIT', 'Vendetta per i caduti', 'Uccidi 6 Orchi Guerci Insanguinati.', 'In molti del mio popolo sono caduti per mano dei Guerci Insanguinati. La loro crudeltà non può essere perdonata.$b$b<L\'Orchessa addolorata ti guarda.>$b$bFagliela pagare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'koKR', '망자에 대한 복수', '피눈물 오크 6명을 처치해야 합니다.', '피눈물 부족의 손에 너무 많은 형제들이 죽었네. 놈들의 잔인한 행동은 결코 용서할 수 없어.$b$b<슬픔에 빠진 오크가 당신을 바라봅니다.>$b$b복수를 해 주게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'ptBR', 'Vingança pelos que caíram', 'Mate 6 orcs do clã Olhos Sangrentos.', 'Inúmeros irmãos meus sucumbiram nas mãos dos Olhos Sangrentos. A crueldade deles não será perdoada.$B$B<A orquisa ergue os olhos pesarosos para você.>$B$BFaça-os pagar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'ruRU', 'Месть за павших', 'Убейте 6 орков клана Кровавой Глазницы.', 'Слишком много моих сородичей было убито кланом Кровавой Глазницы. Такую жестокость простить нельзя.$b$b<Скорбящая орчиха останавливает взгляд на вас.>$b$bЗаставь их заплатить за это.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'zhCN', '为逝者复仇', '消灭6名血环兽人。', '血环氏族残忍地杀害了我们如此多的同胞。他们的暴行绝不能获得原谅。$B$B<这名哀伤的兽人抬头看着你。>$B$B让他们付出代价。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35241, 'zhTW', '為死者復仇', '殺死6個血之谷獸人。', '太多同胞死在血之谷獸人的手裡，絕對不能饒恕這些殘酷無情的傢伙。$b$b<悲傷的獸人抬頭看著你。>$b$b讓他們付出代價。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35242, 'deDE', 'Rache für die Gefallenen', 'Tötet 6 Orcs des Blutenden Auges.', 'Der Klan des Blutenden Auges hat schon zu viele meiner Leute ermordet. Ihre Grausamkeiten müssen gerächt werden.$b$b<Die trauernde Draenei schaut Euch an.>$b$bLasst sie dafür bezahlen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35242, 'esES', 'Venganza por los caídos', 'Mata a 6 orcos Foso Sangrante.', 'Ya han sucumbido demasiados a manos de los Foso Sangrante. No podemos dejar impune su crueldad.$b$b<La draenei afligida alza la vista para mirarte.>$b$bHaz que paguen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35242, 'esMX', 'Venganza para los caídos', 'Mata 6 orcos Foso Sangrante.', 'Demasiados de los míos han caído a manos de los Foso Sangrante. Su crueldad no puede ser perdonada.$b$b<La afligida draenei te mira.>$b$bHazlos pagar.', '', '', '', '', '', '', 0);
