INSERT INTO `quest_template_locale` VALUES (35243, 'deDE', 'Das Geschenk der Dunklen Fürstin', '', '', '', 'Die frischgebackene Klingenmeisterin Na\'Shra ist eine hervorragende Kriegerin und Waffenmeisterin.', 'Klingenmeisterin Na\'Shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'esES', 'El regalo de la Dama Oscura', '', '', '', 'A pesar de no llevar mucho tiempo como maestra del acero, Na\'Shra es una excelente guerrera y maestra armera.', 'Maestra del acero Na\'Shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'esMX', 'El regalo de la Dama Oscura', '', '', '', 'Novata en las filas de Maestro del acero, Na\'Shra es una guerrera formidable y maestra de las armas.', 'Maestro del acero Na\'Shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'frFR', 'Le cadeau de la Dame noire', '', '', '', 'Récemment promue au titre de maître-lame, Na’shra est une guerrière et un maître d’armes redoutable.', 'Maître-lame Na’shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'itIT', 'Il dono della Dama Oscura', '', '', '', 'Con il suo nuovo rango di Maestra di Spade, Na\'shra è una formidabile Guerriera e specialista di armi.', 'Maestra di Spade Na\'shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'koKR', '어둠의 여왕의 선물', '', '', '', '최근에 검귀라는 칭호를 얻게 된 나쉬라는 용맹한 전사이자 무기의 달인입니다.', '검귀 나쉬라', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'ptBR', 'A dádiva da Dama Sombria', '', '', '', 'Mestra de Espadas novata, Na\'Shra é uma guerreira formidável, verdadeira mestra das armas.', 'Mestra de Espadas Na\'shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'ruRU', 'Дар Темной Госпожи', '', '', '', 'На\'Шра, только что получившая звание мастера клинка, – грозная воительница.', 'Мастер клинка На\'Шра', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'zhCN', '黑暗女王的礼物', '', '', '', '作为一名新晋的剑圣，纳什拉无疑是一位强大的战士和武器大师。', '剑圣纳什拉', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35243, 'zhTW', '黑暗女王的贈禮', '', '', '', '新升任為大劍師的奈許菈是一位武藝高強的戰士和武具大師。', '大劍師奈許菈', '', '', '', 0);
