INSERT INTO `quest_template_locale` VALUES (35380, 'deDE', 'Zweiter Befehlshaber', 'Sprecht mit Sir Edward in der Garnison von Admiral Taylor.', 'Wartet... Ich erinnere mich noch an etwas.$B$BSir Edward... Er hat gerufen... Er hat jemanden gesucht.$B$BIch... Ich weiß nicht mehr, wen. Da waren so viele Schreie...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'esES', 'Segundo al mando', 'Habla con Sir Edward en la Ciudadela del almirante Taylor.', 'Espera... recuerdo algo más.$B$BSir Edward... estaba gritando... buscaba a alguien.$B$BNo... no recuerdo a quién. Había tantos gritos...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'esMX', 'Segundo al mando', 'Habla con Sir Edward en la Fortaleza del almirante Taylor.', 'Espera... Recuerdo algo más.$B$BSir Edward... estaba gritando... buscando a alguien.$B$BNo... no puedo recordar a quién. Se escuchaban muchos gritos...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'frFR', 'Officier en second', 'Parlez à Monseigneur Edward au Fief de l’amiral Taylor.', 'Attendez… il me revient autre chose.$B$BMonseigneur Edward… je l’ai entendu crier… il cherchait quelqu’un.$B$BJe… je ne me souviens plus de qui il s’agissait. Difficile d’y prêter attention, au milieu de tous ces hurlements…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'itIT', 'Secondo in comando', 'Parla con Ser Edward alla Guarnigione dell\'Ammiraglio Taylor.', 'Aspetta... mi è venuta in mente un\'altra cosa.$B$BSer Edward... stava urlando... cercando qualcuno.$B$BIo... non riesco a ricordare chi. C\'erano troppe urla...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'koKR', '주둔지의 이인자', '제독 테일러의 주둔지에 있는 에드워드 경과 대화해야 합니다.', '잠깐만요... 또 기억나는 게 있어요.$B$B에드워드 경이... 누군가를 찾으며... 소리를 질렀어요.$B$B아... 누군지는 기억이 나질 않아요. 사방에서 비명이 터져서...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'ptBR', 'Segundo em comando', 'Fale com Sir Edward na Guarnição do Almirante Taylor.', 'Espere... eu me lembrei de mais uma coisa.$B$BSir Edward... ele estava gritando... procurando alguém.$B$BNão... não me lembro quem. Foram tantos gritos...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'ruRU', 'Заместитель командира', 'Поговорите с сэром Эдвардом в гарнизоне адмирала Тейлора.', 'Подожди... Я припоминаю кое-что еще...$B$BСэр Эдвард... кричал... искал кого-то.$B$BЯ... Не помню, кого именно. Все вокруг так кричали...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'zhCN', '二把手', '在海军上将泰勒的要塞与爱德华爵士对话。', '等等……我还记得……$B$B爱德华爵士……当时他在喊……要去找谁。$B$B我……我不记得了。当时的惨叫声太多了……', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35380, 'zhTW', '副手', '到泰勒上將的要塞與艾德華爵士交談。', '等等…我還想到一件事。$B$B艾德華爵士…他在大叫…在找一個人。$B$B我…我不記得是誰了。有好多人在叫喊…', '', '', '', '', '', '', 0);
