INSERT INTO `quest_template_locale` VALUES (35406, 'deDE', 'Supersamen', 'Sammelt angereicherte Samen.', 'Diese Samen wurden von den Botani mit größter Sorgfalt beschützt.$b$bDie kleinen Lebenskapseln strahlen eine große Kraft aus.$b$bSie könnten sich noch als nützlich erweisen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'esES', 'Supersemillas', 'Recoge semillas enriquecidas.', 'Los botani han protegido estas semillas con cuidado.$b$bSe puede sentir todo el poder que desprenden estos pequeños recipientes de vida.$b$bPueden sernos útiles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'esMX', 'Supersemillas', 'Reúne semillas enriquecidas.', 'Los botani han guardado estas semillas con cuidado.$b$bSe puede sentir un gran poder en estos pequeños contenedores de vida.$b$bSerán muy útiles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'frFR', 'Super-graines', 'Récupérez des Graines enrichies.', 'Les botani ont abrité ces graines avec soin.$b$bOn sent un grand pouvoir filtrer de ces minuscules vaisseaux de vie.$b$bIls pourraient s’avérer utiles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'itIT', 'Supersemi', 'Recupera i Semi Arricchiti.', '<Questi semi sono difesi con cura dai Botani.>$b$b<Riesci a sentire un grande potere emanare da queste piccole fonti di vita.>$b$b<Forse potrebbero tornarci molto utili.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'koKR', '초능력 씨앗', '농축된 씨앗을 입수해야 합니다.', '이 씨앗은 신록지기들이 공들여 지키던 것입니다.$b$b이 작은 생명의 그릇에서 엄청난 힘이 약동하는 것이 느껴집니다.$b$b언젠가 요긴하게 쓰일지도 모르겠군요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'ptBR', 'Supersementes', 'Colete Sementes Enriquecidas.', 'Essas sementes foram protegidas com cuidado pelos botanis.$B$BDá para sentir um enorme poder emanando desses pequenos receptáculos de vida.$B$BElas podem acabar sendo úteis.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'ruRU', 'Суперсемена', 'Соберите обогащенные семена.', 'Ботани заботливо защищали эти семена.$B$BЭти маленькие сосуды жизни излучают великую энергию.$B$BОни могут нам пригодиться.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'zhCN', '超级种子', '收集浓缩之种。', '木精小心地保护着这些种子。$b$b在这些微小的生命体里涌动着巨大的能量。$b$b这些也许很有用处。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35406, 'zhTW', '超級種子', '收集營養的種子。', '這些種子被波塔尼小心的保護著。$b$b從這些小小的生命之器中，可以感受到強大的力量。$b$b這可能會派上用場。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'deDE', 'Todesstrafe', 'Tötet $1oa Garnisonsverräter.', 'Verräter, allesamt!$B$BWas Ihr hier seht, sind die letzten Überbleibsel von Truppen, die Admiral Taylor und der Allianz treu ergeben sind.$B$BIch werde nicht zulassen, dass diese Verräter ungestraft davonkommen.$B$BIhr seht aus, als wärt Ihr bereit für diese Aufgabe. Zeigt ihnen, wie man mit ihresgleichen in Kriegszeiten verfährt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'esES', 'Pena de muerte', 'Mata a $1oa traidores de la ciudadela.', '¡Traidores, todos ellos!$B$BEstás viendo el último bastión de las fuerzas que aún son leales al almirante Taylor y a la Alianza.$B$BNo pienso dejar que los desertores se vayan de rositas.$B$BParece que tú estás $glisto:lista;. Demuéstrales cómo tratamos a los de su calaña en tiempos de guerra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'esMX', 'Sancionable con la muerte', 'Mata $1oa traidores de la fortaleza.', '¡Todos traidores!$B$BEstás viendo al último bastión de fuerzas que aún le es fiel al almirante Taylor y a la Alianza.$B$BQue me muera si los desertores se salen con la suya.$B$BParece que estás $glisto:lista;. Muéstrales cómo lidiamos con los de su clase en tiempos de guerra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'frFR', 'Sous peine de mort', 'Tuez $1oa Traîtres du Fief.', 'Des traîtres, tous autant qu’ils sont !$B$BVous contemplez le dernier groupe de soldats loyaux à l’amiral Taylor et à l’Alliance.$B$BPlutôt mourir que laisser ces renégats s’en tirer impunément.$B$BVous semblez $gprêt:prête; à passer à l’action, alors allez donc leur montrer comment nous profilons les individus de leur espèce, en temps de guerre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'itIT', 'Punibile con la morte', 'Uccidi $1oa traditori della Guarnigione.', 'Traditori, tutti loro!$B$BStai osservando l\'ultimo bastione di forze ancora leali all\'Ammiraglio Taylor e all\'Alleanza.$B$BChe io sia dannato se lascerò che quei disertori la passino liscia.$B$BTu sembri essere $gpronto:pronta;: mostragli cosa ne facciamo di quelli come loro in tempo di guerra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'koKR', '배신의 대가', '주둔지 배신자 $1oa명을 처치해야 합니다.', '모두 배신자일세!$B$B테일러 제독과 얼라이언스에 변함없이 충성하는 최후의 수호자는 이게 전부일세.$B$B배신자들이 죗값을 치르지 않고 빠져나가게 하지는 않을 걸세.$B$B자네는 준비가 된 것 같군. 우리가 전시에 배신자를 어떻게 처리하는지 녀석들에게 보여 주게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'ptBR', 'Pena de morte', 'Mate $1oa traidores da guarnição.', 'Traidores, todos eles.$B$BVocê está olhando para o último bastião de tropas ainda leais ao Almirante Taylor e a Aliança.$B$BEu não vou deixar que os desertores escapem impunes.$B$BVocê parece estar $gpronto:pronta;. Mostre a eles como lidamos com vermes em tempos de guerra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'ruRU', 'Полевой суд', 'Убейте $1oa |4предателя:предателей:предателей; из гарнизона.', 'Они предатели – все до одного!$b$bПеред тобой – последний оплот, все еще верный адмиралу Тейлору и Альянсу.$B$BИ будь я проклят, если позволю перебежчикам уйти безнаказанно.$B$BТы, похоже, $gготов:готова; действовать. Покажи им, как мы поступаем с такими, как они, в военное время.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'zhCN', '处死', '消灭$1oa名要塞叛徒。', '那帮叛徒！$B$B在你眼前的是依然忠于联盟和海军上将泰勒的最后一批战士。$B$B堵上我的名誉，绝不能让那帮叛贼逃走。$B$B你看起来已经准备充分，让他们瞧瞧战争时期我们是怎么对待叛徒的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35407, 'zhTW', '處以極刑', '殺掉$1oa個要塞叛徒。', '叛徒，都是叛徒！$B$B在你面前的是最後一群對泰勒上將和聯盟忠心不貳的軍人。$B$B我絕對不會讓那些叛徒逍遙法外。$B$B看樣子你已經整裝待發，讓他們看看我們在戰時是怎麼處置他們這種敗類的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'deDE', 'Ladet die Kanonen', 'Holt $1oa Arsenalkanonenkugeln und $2oa Arsenalkanone zurück.', 'Unsere Waffenvorräte wurden von den Verrätern geplündert. Wir haben nur noch ein paar Kanonen ohne Munition.$B$BIch will, dass ihr ein paar Kanonenkugeln zusammensammelt, damit wir sie wieder in Betrieb nehmen können.$B$BUnd eine weitere Kanone wäre auch nicht schlecht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'esES', 'Preparar los cañones', 'Recupera $1oa bolas de cañón recubiertas de hierro y $2oa cañón de armería.', 'Los traidores han tomado la armería. Tan solo nos quedan estos cañones sin munición.$B$BSoldado, necesito que consigas bolas de cañón para que podamos utilizar los cañones.$B$BLa verdad es que algún cañón más tampoco vendría mal.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'esMX', 'Preparen los cañones', 'Recupera $1oa bolas de cañón Capahierro y $2oa cañón de armería.', 'Los traidores se han apoderado de la armería. Todo lo que nos queda son estos pocos cañones sin munición.$B$BSoldado, necesito que recuperes bolas de cañón para poder usar estas cosas.$B$BEn realidad, un cañón más tampoco sobraría.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'frFR', 'Canons et boulets', 'Récupérez $1oa Boulets de canon en fer et $2oa Canon de l’armurerie.', 'L’armurerie est tombée aux mains des traîtres. Il ne nous reste plus que quelques canons, et nous sommes à court de munitions.$B$BSoldat, j’ai besoin que vous alliez récupérer des boulets, histoire de refaire notre stock.$B$BEn fait, ça ne nous ferait pas de mal d’avoir un canon supplémentaire aussi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'itIT', 'Preparare i cannoni', 'Recupera $1oa Palle di Cannone dell\'Armeria e $2oa Cannone dell\'Armeria.', 'L\'armeria è caduta in mano ai traditori. Tutto ciò che ci rimane sono questi pochi cannoni senza munizioni.$B$BSoldato, ho bisogno che recuperi delle palle di cannone per far funzionare queste armi.$B$BA dire il vero, anche un altro cannone sarebbe molto utile.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'koKR', '발사 준비', '무기고 포탄 $1oa개와 무기고 대포 $2oa개를 입수해야 합니다.', '무기고가 배신자 일당에게 넘어갔어요. 남은 거라곤 탄약도 없는 대포 몇 문뿐이에요.$B$B당신이 포탄을 구해 오면 이 대포를 사용할 수 있을 거예요.$B$B물론 대포가 하나 더 있어도 좋겠죠.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'ptBR', 'Preparar os canhões', 'Obtenha $1oa Balas de Canhão revestidas de ferro e $2oa Canhões da Armaria.', 'A armaria foi perdida para os traidores. Só nos restam esses poucos canhões sem munição.$B$BSoldado, preciso que você recupere as balas de canhão para que a gente possa botar essas coisas para atirar.$B$BNa verdade, mais um canhão seria bem útil.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'ruRU', 'Забить снаряды в пушку', 'Добудьте $1oa |4железное ядро:железных ядра:железных ядер; и $2oa |4пушку:пушки:пушек; арсенала.', 'Оружейную захватили предатели. Все, что у нас есть, – это пара пушек без боеприпасов.$B$BСолдат, тебе нужно добыть ядра, тогда мы сможем начать обстрел.$B$BИ, если честно, еще одно орудие нам тоже не помешает.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'zhCN', '装填弹药', '获得$1oa枚铁舰炮弹和$2oa门军械库火炮。', '叛军占领了军械库。我们手头只剩下几门没有炮弹的大炮。$B$B士兵，我要你去夺取弹药，好让这些玩意儿动起来。$B$B另外，要是你能再弄到一两门炮，那就再好不过了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35408, 'zhTW', '裝填火砲', '取回$1oa顆鐵皮砲彈和$2oa座軍械庫火砲。', '軍械庫被叛徒佔據了。我們僅剩這幾座火砲，而且沒有彈藥可以發射。$B$B士兵，我要你去取得砲彈，讓火砲繼續運作。$B$B事實上，再多弄一座火砲來也不錯。', '', '', '', '', '', '', 0);
