INSERT INTO `quest_template_locale` VALUES (35642, 'deDE', 'Mysteriöse Kapsel', 'Bringt die leuchtende rote Kapsel zu Khaano im Blutroten Sumpf in Gorgrond.', 'Diese Kapsel ähnelt denen, die auf oder bei den Körpern hier in der Gegend wachsen, allerdings pulsiert sie seltsam.$b$bKhaano dürfte sich dafür interessieren.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'esES', 'Vaina misteriosa', 'Lleva la vaina roja resplandeciente a Khaano en el Pantano Carmesí, en Gorgrond.', 'Esta vaina es igual que las que crecían junto a los cadáveres por aquí, pero esta palpita de una forma extraña.$b$bSeguro que a Khaano le interesa ver esto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'esMX', 'Vaina misteriosa', 'Llévale la vaina roja brillante a Khaano en el Pantano Carmesí en Gorgrond.', 'Esta vaina es parecida a las que están creciendo cerca de los cuerpos de por aquí, solo que esta está pulsando peculiarmente.$b$bEsto es algo que le interesaría a Khaano.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'frFR', 'La bogue mystérieuse', 'Rapportez la Cosse rouge luminescente à Khaano au Marécage écarlate, en Gorgrond.', 'Cette cosse ressemble à celles qui poussaient sur les cadavres et aux alentours, mais celle-ci palpite bizarrement.$b$bCela pourrait intéresser Khaano.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'itIT', 'Baccello Misterioso', 'Porta il Baccello Rosso Luminoso a Khaano presso il Pantano Cremisi nella regione di Gorgrond.', '<Questo baccello è simile a quelli cresciuti nei pressi dei cadaveri qua attorno, a eccezione del fatto che sta pulsando in modo strano.>$b$b<Khaano potrebbe essere interessato ad analizzarlo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'koKR', '기묘한 깍지', '고르그론드의 진홍빛 늪지대에 있는 카노에게 빛나는 붉은색 깍지를 가져가야 합니다.', '이 콩깍지처럼 생긴 것은 이 주변에 있는 사체에서 자라는 것과 비슷하게 생겼지만, 괴이하게도 고동치고 있습니다.$b$b카노가 관심을 보일지도 모릅니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'ptBR', 'Vagem misteriosa', 'Leve a Vagem Vermelha Pulsante a Khaano no Charco Carmesim.', 'Esta vagem é parecida com aquelas que crescem nos corpos por aqui, ou perto deles, só que ela pulsa de um jeito estranho$b$bKhaano deve se interessar por ela.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'ruRU', 'Таинственный стручок', 'Отнесите светящийся красный стручок Хаано в Багровую топь, что в Горгронде.', 'Этот стручок похож на все остальные стручки, растущие на трупах и возле них, только он как-то странно пульсирует.$b$bХаано определенно заинтересуется им.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'zhCN', '神秘的豆荚', '把发光的红色豆荚带给戈尔隆德赤红沼泽的卡诺。', '这颗豆荚和在这附近其他尸体周围所生长的极为相似，但它却诡异地搏动着。卡诺可能会对它感兴趣的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35642, 'zhTW', '神秘的豆莢', '把發光的紅色豆莢帶到格古隆德的緋紅沼澤交給坎諾。', '這個豆莢跟其他長在這裡或是屍體上的十分相似，只不過這個有著相當詭異的脈動。坎諾可能會對它感興趣。', '', '', '', '', '', '', 0);
