INSERT INTO `quest_template_locale` VALUES (35807, 'esES', 'Trofeo de la gloria: Erosian', 'Llévale el trofeo a Durotan en Avanzada de las Bestias, en Gorgrond.', 'Derrotar a Erosian te ha hecho ganar un trofeo digno de reconocimiento.$b$bLleva esto a Durotan en Avanzada de las Bestias.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35807, 'esMX', 'Trofeo de gloria: Erosian', 'Llévale el trofeo a Durotan en el Observatorio de Bestias, Gorgrond.', 'Derrotar a Erosian te dejó un trofeo digno de aclamación.$b$bLlévale esto a Durotan en el Observatorio de Bestias.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35807, 'frFR', 'Trophée glorieux : Erosian', 'Apportez le trophée à Durotan au Guet des Bêtes, en Gorgrond.', 'En triomphant d’Erosian, vous avez obtenu un trophée prouvant votre valeur.$b$bApportez-le à Durotan, au guet des Bêtes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35807, 'itIT', 'Trofeo della gloria: Erosian', 'Porta il trofeo a Durotan presso Roccabestia nella regione di Gorgrond.', 'Con la vittoria su Erosian ti sei $gaggiudicato:aggiudicata; un trofeo di grande valore.$b$bConsegnalo a Durotan a Roccabestia.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35807, 'koKR', '영광의 전리품: 에로시안', '고르그론드의 야수 감시터에 있는 듀로탄에게 전리품을 가져가야 합니다.', '에로시안을 처치한 후 자부심을 느낄 만한 전리품을 손에 넣었습니다.$b$b야수 감시터에 있는 듀로탄에게 이걸 가져가십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35807, 'ptBR', 'Troféu do Triunfo: Erosian', 'Leve o troféu a Durotan, que está em Guarda das Feras, em Gorgrond.', 'Derrotar Erosian lhe rendeu um troféu digno de aclamação.$b$bLeve isto a Durotan em Guarda das Feras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35807, 'ruRU', 'Почетный трофей: Эрозиан', 'Отнесите трофей Дуротану на Звериную заставу в Горгронде.', 'Вы победили Эрозиана и завоевали достойный восхищения трофей.$b$bОтнесите его Дуротану на Звериную заставу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35807, 'zhCN', '荣耀的证明：艾罗萨恩', '把战利品带到戈尔隆德的蛮兽岗哨，交给杜隆坦。', '击败艾罗萨恩后，你获得了一件值得拾取的战利品。$b$b把它带到蛮兽岗哨交给杜隆坦。', '', '', '', '', '', '', 0);
