INSERT INTO `quest_template_locale` VALUES (35819, 'ptBR', 'Troféu do Triunfo: Khargax, o Devorador', 'Leve o troféu a Durotan, que está em Guarda das Feras, em Gorgrond.', 'Derrotar Kahrgax lhe rendeu um troféu digno de aclamação.$b$bLeve isto a Durotan em Guarda das Feras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35819, 'ruRU', 'Почетный трофей: Каргакс Пожиратель', 'Отнесите трофей Дуротану на Звериную заставу в Горгронде.', 'Вы победили Каргакса и завоевали достойный восхищения трофей.$b$bОтнесите его Дуротану на Звериную заставу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35819, 'zhCN', '荣耀的证明：吞噬者卡加克斯', '把战利品带到戈尔隆德的蛮兽岗哨，交给杜隆坦。', '击败卡加克斯后，你获得了一件值得拾取的战利品。$b$b把它带到蛮兽岗哨交给杜隆坦。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35819, 'zhTW', '光榮戰利品：『吞噬者』卡爾賈斯', '帶著戰利品到格古隆德的獸望崗哨找杜洛坦。', '擊敗卡爾賈斯讓你獲得一件值得表揚的戰利品。$b$b將戰利品交給獸望崗哨的杜洛坦。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'deDE', 'Ruhmestrophäe: Khargax der Verschlinger', 'Bringt die Trophäe zu Maraad am Hochpass in Gorgrond.', 'Der Sieg über Khargax hat Euch eine Trophäe eingebracht, die es würdig ist, in Eurem Außenposten zur Schau gestellt zu werden. $b$bBringt sie zu Maraad, um Euren Ruhm zu mehren!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'esES', 'Trofeo de la gloria: Khargax el Devorador', 'Llévale el trofeo a Maraad en Paso Alto, en Gorgrond.', 'Derrotar a Khargax te ha hecho ganar un trofeo digno de ser mostrado en tu avanzada.$b$b¡Devuélvelo esto a Maraad para que tu gloria sea aún mayor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'esMX', 'Trofeo de gloria: Khargax El Devorador', 'Llévale el trofeo a Maraad en el Paso Alto, Gorgrond.', 'Derrotar a Khargax te dejó un trofeo digno de alardeo en tu puesto de avanzada.$b$b¡Devuélvele esto a Maraad para aumentar tu gloria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'frFR', 'Trophée glorieux : Khargax le Dévoreur', 'Apportez le trophée à Maraad à la Haute-Route, en Gorgrond.', 'En triomphant de Khargax, vous avez obtenu un trophée digne d’être exhibé dans votre avant-poste.$b$bApportez-le à Maraad pour vous couvrir de gloire !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'itIT', 'Trofeo della gloria: Khargax il Divoratore', 'Porta il trofeo a Maraad presso Altopasso nella regione di Gorgrond.', 'Con la vittoria su Khargax ti sei $gaggiudicato:aggiudicata; un trofeo di grande valore da sfoggiare con orgoglio al tuo avamposto.$b$bRiportalo a Maraad per incrementare la tua gloria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'koKR', '영광의 전리품: 포식자 카르각스', '고르그론드의 오름길에 있는 마라아드에게 전리품을 가져가야 합니다.', '카르각스를 처치한 후 자부심을 느낄 만한 전리품을 손에 넣었습니다.$b$b오름길에 있는 마라아드에게 이걸 가져가십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'ptBR', 'Troféu do Triunfo: Khargax, o Devorador', 'Leve o troféu a Maraad, que está no Passoalto, em Gorgrond.', 'Derrotar Khargax lhe rendeu um troféu digno de aclamação.$b$bLeve isto a Maraad em Passoalto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'ruRU', 'Почетный трофей: Каргакс Пожиратель', 'Отнесите трофей Марааду на Верхний Путь в Горгронде.', 'Вы победили Каргакса и завоевали трофей, которым не стыдно похвалиться на своем аванпосте. $b$bОтнесите трофей Марааду – это прославит вас еще больше!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'zhCN', '荣耀的证明：吞噬者卡加克斯', '把战利品带到戈尔隆德的高山走廊，交给玛尔拉德。', '击败卡加克斯后，你获得了一件值得去岗哨炫耀的战利品。$b$b把它交还给玛尔拉德，来提升你的荣耀！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35820, 'zhTW', '光榮戰利品：『吞噬者』卡爾賈斯', '帶著戰利品到格古隆德的高地之路找瑪銳德。', '擊敗卡爾賈斯讓你獲得一件值得在哨站炫耀的戰利品。$b$b將戰利品交給瑪銳德以增加你的榮譽！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'deDE', 'Aggrokultur vom Feinsten', 'Trefft Euch mit Thaelin Schwarzamboss am Wildwuchsbett.', '$p? Bin ich froh, Euch zu sehen.$b$bAls ich versucht hab\', den Genesaurier zu erledigen, bin ich über dieses Rangarilager gestolpert.$b$bUnd plötzlich brach der Boden auf und spuckte alle möglichen zornigen Pflanzen aus.$b$bIch schaffte es gerade noch so aus meinem Panzer, bevor ihn der Genesaurier wie einen leeren Bierkrug durch die Gegend warf.$b$bIch geh\' zurück zur Straße und such\' dort nach meinem Panzer.$b$bDa können wir uns treffen, sobald Ihr fertig seid.', '', '', '', '', '', 'Trefft Euch mit Thaelin Schwarzamboss am Wildwuchsbett.', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'esES', 'Generé agro... cultura', 'Reúnete con Thaelin Yunquenegro en el Manglar Boscaraz.', '¿$p? Caramba, cómo me alegro de verte.$b$bEstaba intentando deshacerme de ese genosaurio cuando me topé con un campamento Rangari.$b$bDe repente, el suelo se abrió y se puso a escupir todo tipo de plantas furiosas.$b$bLogré salir a duras penas del tanque antes de que el genosaurio lo tirara como una jarra de cerveza vacía.$b$bVoy a volver al camino a buscar mi tanque.$b$bNos veremos allí cuando hayas terminado.', '', '', '', '', '', 'Reúnete con: Thaelin Yunquenegro. Zona: Manglar Boscaraz.', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'esMX', 'Thaelin Agro... cultor', 'Reúnete con Thaelin Yunqueoscuro en la Estela de Madera Salvaje.', '¿$p? Cuánto me alegro de verte.$b$bEstaba tratando de deshacerme de ese genesaurio cuando me topé con este campamento Rangari.$b$bDe pronto, el suelo empieza a escupir todo tipo de plantas iracundas.$b$bPor un pelito logré salirme del tanque antes de que ese genesaurio lo arrojara como si fuera una jarra de cerveza vacía.$b$bMe vuelvo al camino para buscar mi tanque.$b$bTe veo por ahí cuando termines.', '', '', '', '', '', 'Reúnete con Thaelin Yunqueoscuro en la Estela de Madera Salvaje.', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'frFR', 'J’ai pris l’agro… culture', 'Retrouvez Thaelin Sombrenclume au Reflux du Bois sauvage.', '$p ? Mazette, je suis content de vous voir.$b$bJ’essayais de larguer ce génésaure quand je suis tombé sur ce campement rangari.$b$bL’instant d’après, le sol s’est mis à s’agiter et à cracher toutes sortes de plantes agressives.$b$bJ’ai à peine eu le temps de sortir de mon char avant que le génésaure le balance comme une chope de bière vide.$b$bJe vais retourner jusqu’à la route chercher ma machine.$b$bOn s’y retrouve quand vous en aurez terminé.', '', '', '', '', '', 'Retrouvez Thaelin Sombrenclume au Reflux du Bois sauvage.', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'itIT', 'Un nemico troppo forte per me', 'Incontra Thaelin Forgiacupa alla Riva di Selvabrada.', '$p? Sono così felice di vederti.$b$bStavo cercando di sbarazzarmi di quel Genesauro quando mi sono imbattuto in questo accampamento di Guardaselve.$b$bNon ho avuto nemmeno il tempo di ambientarmi che il terreno ha iniziato a tremare e a sputare ogni genere di pianta furiosa.$b$bSono riuscito a malapena a uscire dal mio carro prima che quel Genesauro lo scagliasse via come fosse una pinta di birra vuota.$b$bIo tornerò sulla strada in cerca del mio carro.$b$bCi vediamo lì quando avrai finito.', '', '', '', '', '', 'Incontra Thaelin Forgiacupa alla Riva di Selvabrada.', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'koKR', '내가 잘못했네', '야생림 사잇길에 있는 테일린 다크앤빌을 만나야 합니다.', '$p 아닌가? 세상에, 이렇게 반가울 데가.$b$b나는 그 제네사우루스를 따돌리려 하다가 이 랑가리 야영지를 발견했다네.$b$b다음으로 기억나는 건, 땅이 갈라지면서 온갖 성난 식물을 뱉어 내는 광경이었지.$b$b내가 간신히 전차에서 빠져나오자마자, 제네사우루스가 그걸 빈 맥주잔처럼 던져 버렸지 뭔가.$b$b난 다시 그 길로 돌아가서 전차를 찾아 봐야겠네.$b$b일이 끝나면 거기로 오게.', '', '', '', '', '', '야생림 사잇길에 있는 테일린 다크앤빌을 만나야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'ptBR', 'Meu agro (é um péssimo) negócio', 'Encontre Thaelin Forjanegra no Prado Matagreste.', '$p? Rapaz, estou feliz em te ver.$B$BEu tava tentando me livrar daquele genessauro quando esbarrei nesse acampamento rangari.$B$BQuando eu menos espero, começa a brotar do chão todo tipo de planta furiosa.$B$BEu mal tinha saído do meu tanque quando o genessauro arremessou ele como se fosse uma caneca de cerveja vazia.$B$BVou voltar para a estrada e procurar meu tanque.$B$BTe vejo lá quando terminar.', '', '', '', '', '', 'Encontre Thaelin Forjanegra no Prado Matagreste.', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'ruRU', 'Я сорвал агро... культуру', 'Встретьтесь с Телином Темной Наковальней в Диколесье.', '$p? Как же я рад тебя видеть.$b$bЯ тут пытался отвязаться от генезотавра, но наткнулся на лагерь рангари.$b$bГлазом моргнуть не успел – земля задрожала, и из нее поперли злобные растения.$b$bЯ едва успел выбраться из танка, как проклятый генезотавр отшвырнул его, точно пустое ведро.$b$bЯ собираюсь вернуться на дорогу и поискать танк.$b$bУвидимся там, когда закончишь.', '', '', '', '', '', 'Встретьтесь с Телином Темной Наковальней в Диколесье.', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'zhCN', '我惹上了……植物怪', '到荒林洼地找塞林·暗砧。', '$p？见到你真是太好了。$b$b我当时正想甩掉那个原祖荆兽，结果就闯到这个游侠营地里来了。$b$b还没等我反应过来，大地就开始震颤，不知打哪冒出来好多愤怒的植物。$b$b我好不容易才从坦克里爬出来，眼睁睁地看着那个原祖荆兽把坦克像空啤酒杯那样抛了出去。$b$b我得沿路返回，找找我的坦克。$b$b等你完成这里的任务后，我们到那见吧。', '', '', '', '', '', '到荒林洼地找塞林·暗砧。', 0);
INSERT INTO `quest_template_locale` VALUES (35828, 'zhTW', '我是天生嘲諷臉', '到荒林小徑找塞林‧暗砧。', '$p？真是高興看到你啊。$b$b當我誤闖這座遊俠營地時，我正努力想甩掉那隻傑那索。$b$b回過神來我才發現天地似乎顛倒了，而整窩的憤怒植物傾巢而出。$b$b就在那頭傑那索將我的坦克如同空酒杯般摔出去前，我才好不容易從坦克爬出來。$b$b我會回去路上找我的坦克。$b$b你事情辦完就來找我吧。', '', '', '', '', '', '到荒林小徑找塞林‧暗砧。', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'deDE', 'Nach dem Genesaurier', 'Vernichtet $1oa wilde Überwucherungen.', 'Erst walzt eine große Metallbestie in unser Lager, dann ein Genesaurier, der eine Welle aus reinem chaotischem Leben auf uns hetzt.$b$bWir müssen die Manifestationen beseitigen, die der Genesaurier hinterlassen hat, damit wir vor unserem Aufbruch möglichst viel bergen können.$b$bFalls Ihr uns helfen wollt, dann tötet bitte so viele der gewalttätigen Pflanzen, die uns angreifen, wie ihr nur könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'esES', 'El despertar del genosaurio', 'Mata a $1oa sobrecrecimientos salvajes.', 'Primero vimos una enorme bestia de metal entrar en el campamento, y justo después viene un genosaurio detrás y nos manda una oleada de pura vida caótica.$b$bHay que reducir el número de manifestaciones que dejó el genosaurio a su paso si queremos rescatar lo que podamos antes de irnos.$b$bSi has venido a ayudar, ponte a matar a todas las plantas violentas que puedas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'esMX', 'El despertar del genesaurio', 'Mata $1oa sobrecrecimientos salvajes.', 'Un minuto vemos una bestia enorme de metal rodando hacia nuestro campamento, al siguiente, un genesaurio la sigue y despide una ola de pura vida caótica hacia nosotros.$b$bTenemos que sacrificar las manifestaciones que dejó el genesaurio para poder salvar lo que podamos antes de irnos.$b$bSi quieres ayudar, empieza a matar a todas las plantas violentas que puedas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'frFR', 'L’éveil des génésaures', 'Tuez $1oa spécimens de Flore sauvage.', 'Nous avons vu une grande bête de métal rouler jusque dans notre camp, et l’instant suivant un génésaure la suivait et nous ensevelissait sous une vague de pure vie chaotique.$b$bIl faut juguler les effets du passage du génésaure pour que nous ayons le temps de sauver ce qui peut l’être avant de partir.$b$bSi vous êtes ici pour nous aider, commencez par tuer un maximum des plantes violentes qui s’en prennent à nous.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'itIT', 'Il risveglio del Genesauro', 'Elimina $1oa Piante Assassine.', 'Prima una grossa bestia di metallo è entrata nel nostro accampamento, poi è arrivato un Genesauro e ha scatenato un\'ondata di caos su di noi.$b$bDobbiamo sbarazzarci delle creature lasciate indietro dal Genesauro per riuscire a recuperare quel poco che si è salvato e fuggire da qui.$b$bSe vuoi aiutarci, elimina tutte quelle piante assassine che ci stanno attaccando.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'koKR', '제네사우루스의 발자국', '난폭한 식물 $1oa개를 처치해야 합니다.', '거대한 금속 괴물이 우리 야영지를 덮치는가 싶더니, 제네사우루스가 그 뒤를 따라와 이곳을 혼란의 도가니로 만들었어.$b$b여길 뜨기 전에 쓸 만한 물자를 수습하려면, 우선 제네사우루스가 지나간 자리에 솟아난 식물을 처리해야 해.$b$b우릴 도와주러 온 거라면, 우릴 공격하는 난폭한 식물을 가급적 많이 처치해 줘.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'ptBR', 'O despertar do genessauro', 'Mate $1oa Exuberâncias Silvestres.', 'Vimos uma enorme fera de metal entrando no nosso acampamento. Um genessauro passou logo atrás e manda uma onda de vida caótica contra nós.$B$BAs manifestações que o genessauro deixou em seu rastro devem ser dizimadas para termos tempo de recuperar o que pudermos antes de partir.$B$BSe estiveres aqui para ajudar, então podes começar matando o máximo de plantas violentas possível.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'ruRU', 'След генезотавра', 'Уничтожьте $1oa |4агрессивное растение:агрессивных растения:агрессивных растений;.', 'Сначала в наш лагерь въехала здоровенная железная зверюга, а за ней следом примчался генезотавр и устроил тут дендрарий.$b$bОставленные генезотавром проявления необходимо истребить, чтобы мы смогли собрать свои пожитки перед бегством.$b$bЕсли хочешь помочь, убей как можно больше агрессивных растений, которые не дают нам покоя.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'zhCN', '原祖荆兽的破坏力', '消灭$1oa个野性生长物。', '一开始有一台铁疙瘩开进我们的营地，接着就有一只原祖荆兽紧随其后，派了一大波混乱的生物来攻击我们。$b$b快去收拾掉原祖荆兽留下的烂摊子，好让我们能在离开之前抓紧时间回收物资。$b$b如果你是来帮忙的，那就快去消灭那些袭击我们的暴力植物吧，越多越好。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35834, 'zhTW', '傑那索之醒', '殺掉$1oa棵狂亂植物。', '前一刻我們才看到一具金屬巨怪開進我們營地，下一秒一頭傑那索就尾隨而來，還派一群狂亂生物攻擊我們。$b$b我們必須收拾傑那索留下來的爛攤子，才能在離開前回收可用的物資。$b$b如果你是來協助我們的，那就開始盡可能殺掉這些攻擊我們的植物吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'deDE', 'Der schwarze Jäger', 'Besorgt den organischen Laserapparat des schwarzen Jägers.', 'Wir sind bereit, den Laser aufzustellen, aber um durch dieses Zeug zu kommen, werden wir mehr Feuerkraft brauchen.$B$BDer schwarze Jäger geht im Delta direkt südöstlich von hier auf die Pirsch. Auf dem Gesicht trägt er einen Apparat, der einen Laser abfeuert, der weitaus stärker ist als der, den wir hier haben.$B$BGenau den brauchen wir.$B$BBesorgt ihn, während wir den Laser einrichten.', '', '', 'Der schwarze Jäger', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'esES', 'El cazador de ébano', 'Consigue un aparato láser orgánico del cazador de ébano.', 'Ya estamos listos para preparar el láser, pero nos va a hacer falta más poder de fuego para perforar esto.$B$BEl cazador de ébano deambula por el delta que hay justo al sureste de aquí. Tiene un aparato en la cara que dispara un láser mucho más potente que el nuestro.$B$BTiene que ser nuestro.$B$BDeberías ir allí y conseguirlo mientras que preparamos el láser.', '', '', 'El cazador de ébano', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'esMX', 'El Cazador de Ébano', 'Obtén un aparato de láser orgánico del Cazador de ébano.', 'Estamos listos para ajustar el láser, pero vamos a necesitar más poder para abrirnos paso.$B$BEl Cazador de ébano acecha el delta al sudeste de aquí. Tiene un aparato en su cara que puede disparar un láser mucho más poderoso que el que tenemos aquí.$B$BNecesitamos eso.$B$BDeberías ir a buscarlo mientras ajustamos el láser.', '', '', 'El Cazador de Ébano', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'frFR', 'Le chasseur d’ébène', 'Obtenez un Appareil à laser organique sur le Chasseur d’ébène.', 'On est prêts à mettre le laser en route, mais il nous faut plus de puissance de feu pour découper ce machin.$B$BLe chasseur d’ébène traque ses proies dans le delta au sud-est d’ici. Sur sa tête, il a un appareil capable de tirer un rayon laser bien plus puissant que celui dont nous disposons.$B$BIl nous le faut.$B$BVous devriez aller le chercher pendant qu’on installe le laser.', '', '', 'Le chasseur d’ébène', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'itIT', 'Cacciatore d\'Ebano', 'Ottieni un Apparato Laser Organico dal Cacciatore d\'Ebano.', 'Siamo quasi pronti con il laser, ma ci servirà più potenza di fuoco per passare attraverso questa roba.$B$BIl Cacciatore d\'Ebano vaga nei pressi del delta poco a sud-est di qui. È dotato di un particolare apparato sulla sua testa in grado di sparare un laser ben più potente del nostro.$B$BÈ esattamente quello che ci serve: tu pensa a recuperarlo mentre noi ci prepariamo a utilizzarlo.', '', '', 'Cacciatore d\'Ebano', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'koKR', '칠흑의 사냥꾼', '칠흑의 사냥꾼에게서 유기체 광선 기계장치를 구해야 합니다.', '레이저를 설치할 준비는 끝났지만, 이 암석을 뚫으려면 화력이 더 필요해요.$B$B여기에서 바로 남동쪽에 있는 삼각주에 칠흑의 사냥꾼이 출몰하는데, 그 녀석의 얼굴에는 강력한 레이저를 발사할 수 있는 장치가 있어요. 그런데 우리가 설치한 것보다 훨씬 더 강력하지 뭐예요.$B$B그래요, 그 레이저 장치가 필요해요.$B$B우리가 설치 준비를 할 동안 그 장치를 좀 구해 주세요.', '', '', '칠흑의 사냥꾼', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'ptBR', 'O Caçador de Ébano', 'Obtenha um Aparato Laser Orgânico do Caçador de Ébano.', 'Estamos prontos para montar o laser, mas vamos precisar de mais poder de fogo para cortar uma passagem por esse troço.$B$BO Caçador de Ébano espreita o delta logo a sudeste daqui. Ele tem um aparato no rosto capaz de disparar um laser muito mais forte do que isso que temos aqui.$B$BPrecisamos desse aparato.$B$BVá lá buscá-lo enquanto nós montamos o laser.', '', '', 'O Caçador de Ébano', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'ruRU', 'Эбеновый охотник', 'Добудьте органический лазер, победив эбенового охотника.', 'Мы готовы установить лазер, но нам потребуется увеличить мощность, чтобы пробиться через породу.$B$BК юго-востоку отсюда в устье реки живет эбеновый охотник. У него в голове есть орган, способный стрелять гораздо более мощным лучом, чем наш лазер.$B$BНам нужна эта штуковина.$B$BПопробуй добыть ее, а мы пока подготовим лазер к работе.', '', '', 'Эбеновый охотник', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'zhCN', '乌木猎手', '从乌木猎手身上取得活体激光球。', '我们要开始设置激光炮台了，但火力不够是穿透不了这玩意儿的。$B$B一个叫乌木猎手的孢子爬行者就在东南海岸的三角洲处游荡。它脸上有一个器官可以发射激光，比我们目前在用的激光发射器的火力要强很多。$B$B我们需要它。$B$B你负责去把它弄来，我们继续在这里设置炮台。', '', '', '乌木猎手', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35835, 'zhTW', '黯黑獵人', '從黯黑獵人身上收集一個有機雷射器官。', '我們準備要架設雷射裝置了，但我們還要更強的火力才有辦法切割這東西。$B$B有個「黯黑獵人」潛伏在這東南方的三角洲，它的臉上有個能發射強力雷射的器官，威力比我們所裝設過的裝置都還強。$B$B我們需要那東西。$B$B你應該趁我們做好架設時將那東西拿回來。', '', '', '黯黑獵人', '', '', '', 0);
