INSERT INTO `quest_template_locale` VALUES (35921, 'deDE', 'Brot und Spiele', 'Sprecht mit Dexyl Todesklinge im Ring der Prüfung.', 'He, $ggnä\' Herr:gnä\' Frau;, dieser Taugenichts will mich nicht einlassen. Würdet Ihr Euch gerne etwas Bares verdienen?$B$BMein Boss veranstaltet Kämpfe im Ring der Prüfung. Ihr seht aus, als hättet Ihr das Zeug zu so was.$B$BIhr solltet einen Kampf wagen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'esES', 'Pan y circo', 'Habla con Dexyl Filoletal en El Círculo de los Retos.', 'Hola, $gseñor:señora;, este piltrafilla no me deja entrar. ¿Te interesaría ganar dinero contante y sonante?$B$BMi jefe está organizando combates en El Círculo de los Retos. Por tu porte, diría que podrías apañártelas.$B$BVe y lucha.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'esMX', 'Pan y circos', 'Habla con Dexyl Hojamuerta en el Círculo de los Retos.', 'Ey, $gdon:doña;, este mequetrefe no me deja entrar. ¿Te interesa un poco de efectivo en bruto?$B$BMi jefe promociona peleas en el Círculo de los Retos. Tú pareces $gun buen luchador:una buena luchadora;.$B$BDeberías ir a pelear.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'frFR', 'Du pain et des jeux', 'Parlez à Dexyl Défuntelame à l’Arène des Épreuves.', 'Hé, $gm’sieur:m’dame;, cette andouille ne veut pas me laisser passer. Un beau paquet d’argent, ça vous tente ?$B$BMon supérieur organise des combats à l’arène des Épreuves. Vous avez l’air capable de vous débrouiller.$B$BVous devriez aller combattre là-bas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'itIT', 'Pane e giochi circensi', 'Parla con Dexyl Lamamorta al Circolo delle Sfide.', 'Ehi, $gamico:amica;, questo simpaticone non vuole farmi entrare. Ti va di guadagnare un po\' di soldi facili?$B$BIl mio capo sta organizzando dei combattimenti al Circolo delle Sfide. Tu hai l\'aria di essere $guno:una; che non si tira indietro di fronte a certe cose.$B$BDovresti partecipare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'koKR', '빵이 아니면 결투를 달라', '시험의 투기장에 있는 덱실 데드블레이드와 대화해야 합니다.', '이봐요, 저 건달 녀석들이 날 들여보내 주지 않아서 그런데. 당신 혹시 돈벌이에 관심 있나요?$B$B우리 대장은 시험의 투기장에서 결투를 주관하는 엄청난 사람이에요. 당신 몸뚱이 하나는 좀 튼튼해 보이네요.$B$B한 번 싸워보는 게 어때요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'ptBR', 'Pão e circo', 'Fale com Dexyl Mortegume no Ringue da Provação.', 'Fala aí, gente fina. Esse prego não quer me deixar entrar. Quer ganhar uma mixaria?$B$BMeu chefe tá promovendo lutas no Ringue da Provação. Você tem cara de que é $gbom:boa; de briga.$B$BPor que não vai lutar?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'ruRU', 'Хлеб и зрелища', 'Поговорите с Дексилом Клинком Ужаса в Круге Испытаний.', 'Эй, $Gгосподин:госпожа;, этот недоумок меня не пропускает. Ты деньжат заработать не хочешь?$B$BМой босс устраивает бои в Круге Испытаний. А по тебе видно, что драться ты умеешь.$B$BСоветую принять участие.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'zhCN', '竞技场的宣传', '与试炼竞技场的戴克希尔·死刃交谈。', '嗨，$g先生:女士;，这个榆木脑袋不放我进去。想要赚些外快吗？$B$B我的老板在给试炼竞技场的角斗比赛做宣传。$B$B你瞧着挺耐揍的，何不去参加比赛呢？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35921, 'zhTW', '麵包與競技', '到試煉競技場，與德賽爾‧亡刃談話。', '嗨，$g先生:女士;，這笨蛋不讓我進去。對白花花的鈔票有興趣嗎？$B$B我的老闆正在宣傳試煉競技場的打鬥。你看來有兩下子。$B$B你應該去試試看。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35922, 'deDE', 'Brot und Spiele', 'Sprecht mit Dexyl Todesklinge im Ring der Prüfung.', 'He, $ggnä\' Herr:gnä\' Frau;, dieser Taugenichts will mich nicht einlassen. Würdet Ihr Euch gerne etwas Bares verdienen?$B$BMein Boss veranstaltet Kämpfe im Ring der Prüfung. Ihr seht aus, als hättet Ihr das Zeug zu so was.$B$BIhr solltet einen Kampf wagen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35922, 'esES', 'Pan y circo', 'Habla con Dexyl Filoletal en El Círculo de los Retos.', 'Hola, $gseñor:señora;, este piltrafilla no me deja entrar. ¿Te interesaría ganar dinero contante y sonante?$B$BMi jefe está organizando combates en El Círculo de los Retos. Por tu porte, diría que podrías apañártelas.$B$BVe y lucha.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35922, 'esMX', 'Pan y circos', 'Habla con Dexyl Hojamuerta en el Círculo de los Retos.', 'Ey, $gdon:doña;, este mequetrefe no me deja entrar. ¿Te interesa un poco de efectivo en bruto?$B$BMi jefe promociona peleas en el Círculo de los Retos. Tú pareces $gun buen luchador:una buena luchadora;.$B$BDeberías ir a pelear.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35922, 'frFR', 'Du pain et des jeux', 'Parlez à Dexyl Défuntelame à l’Arène des Épreuves.', 'Hé, $gm’sieur:m’dame;, cette andouille ne veut pas me laisser passer. Un beau paquet d’argent, ça vous tente ?$B$BMon supérieur organise des combats à l’arène des Épreuves. Vous avez l’air capable de vous débrouiller.$B$BVous devriez aller combattre là-bas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35922, 'itIT', 'Pane e giochi circensi', 'Parla con Dexyl Lamamorta al Circolo delle Sfide.', 'Ehi, $gamico:amica;, questo simpaticone non vuole farmi entrare. Ti va di guadagnare un po\' di soldi facili?$B$BIl mio capo sta organizzando dei combattimenti al Circolo delle Sfide. Tu hai l\'aria di essere $guno:una; che non si tira indietro di fronte a certe cose.$B$BDovresti partecipare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35922, 'koKR', '빵이 아니면 결투를 달라', '시험의 투기장에 있는 덱실 데드블레이드와 대화해야 합니다.', '이봐요, 저 건달 녀석들이 날 들여보내 주지 않아서 그런데, 당신 혹시 돈벌이에 관심 있나요?$B$B우리 대장은 시험의 투기장에서 결투를 주관하는 고블린이에요. 당신 몸뚱이 하나는 튼튼해 보이네요.$B$B한번 싸워보는 게 어때요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35922, 'ptBR', 'Pão e circo', 'Fale com Dexyl Mortegume no Ringue da Provação.', 'Fala aí, gente fina. Esse prego não quer me deixar entrar. Quer ganhar uma mixaria?$B$BMeu chefe tá promovendo lutas no Ringue da Provação. Você tem cara de que é $gbom:boa; de briga.$B$BPor que não vai lutar?', '', '', '', '', '', '', 0);
