INSERT INTO `quest_template_locale` VALUES (35944, 'esES', 'Prueba de fuerza: diente de goren', 'Lleva la prueba al Rangari Jonaa en Paso Alto, en Gorgrond.', '¡Este diente es prueba de tu fuerza a la hora de matar goren y tu amigo gladiador es testigo de ello!$b$b¡Llévale el diente a Jonaa y recibe el premio que te mereces!', '', '', '', '', '', '', 0);
