INSERT INTO `quest_template_locale` VALUES (35944, 'frFR', 'Preuve de force : dent de goren', 'Apportez la preuve au Rangari Jonaa à la Haute-Route, en Gorgrond.', 'Cette dent prouve votre habileté à tuer des gorens et votre ami gladiateur en est témoin !$b$bApportez la dent à Jonaa et préparez-vous à recevoir les honneurs !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35944, 'itIT', 'Prova di forza: Dente di Goren', 'Porta la prova al Guardaselve Jonaa presso Altopasso nella regione di Gorgrond.', 'Questo dente è la prova della tua forza nell\'uccidere i Goren, e il tuo amico gladiatore ne è testimone!$b$bPorta il dente a Jonaa e reclama la tua ricompensa!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35944, 'koKR', '힘의 증거: 고렌 이빨', '승리의 증거를 고르그론드의 오름길에 있는 랑가리 조나에게 가져가야 합니다.', '이 이빨은 당신이 고렌을 처치했으며, 당신의 검투사 친구가 그 광경을 목격했다는 힘의 증거입니다!$b$b이 이빨을 랑가리 조나에게 가져가면 포상을 받을 수 있습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35944, 'ptBR', 'Prova de Força: Dente de Goren', 'Leve a prova ao Rangari Jonaa, que está no Passoalto, em Gorgrond.', 'Este dente é prova da sua força ao matar gorens, e seu amigo gladiador foi testemunha!$B$BLeve este dente para Jonaa e reivindique suas honrarias!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35944, 'ruRU', 'Доказательство силы: зуб горена', 'Отнесите доказательство рангари Йонаа на Верхний Путь в Горгронде.', 'Этот зуб – доказательство твоего мастерства в охоте на горенов, твой друг гладиатор может это подтвердить.$b$bОтнеси этот зуб Йонаа, ты $gзаслужил:заслужила; награду.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35944, 'zhCN', '力量的证明：鬣蜥人牙齿', '把证明带到戈尔隆德的高山走廊，交给游侠乔纳。', '这颗牙齿证明了你有消灭鬣蜥人的能力，你的角斗士朋友目击了你的战斗！把这颗牙齿带回去给乔纳，换取你应得的嘉奖！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35944, 'zhTW', '力量之證：戈倫之牙', '把證據帶到格古隆德的高地之路交給遊俠喬納。', '這顆牙齒是你殺掉戈倫的力量之證，你的格鬥士朋友是見證人！$b$b將牙齒帶回給喬納，取得你的稱號！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'deDE', 'Greblin Flinkzünd', 'Helft Greblin Flinkzünd nach unten.', 'Ich hänge hier schon ewig rum!$b$bIch bin hier rumgeflogen, hab das Gebiet für Nixxie vermessen und dann... BÄMM! Einer dieser blöden Oger hat einen Stein nach mir geworfen. Hat mich direkt getroffen!$b$bMein Gleiter ist abgestürzt und ich sitz hier fest... und, Mann, ist das unbequem.$b$bHelft mir bitte runter, ja? Ich sorge auch dafür, dass es sich für Euch lohnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'esES', 'Greblin Velochispa', 'Ayuda a Greblin Velochispa a bajar.', '¡Llevo aquí colgado una eternidad!$b$bEstaba volando por ahí, buscando a Nixxie, y de repente... ¡pam! ¡Uno de esos estúpidos ogros me tira una roca!$b$bMe rompió el parapente, y aquí estoy... Y no veas lo incómodo que es esto.$b$bAyúdame a bajar, ¿quieres? Te lo recompensaré.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'esMX', 'Greblin Silbaveloz', 'Ayuda a bajar a Greblin Silbaveloz.', '¡Llevo horas colgado aquí!$b$bEstaba volando sobre el área en busca de Nixxie cuando... ¡bum! ¡Uno de esos estúpidos ogros me lanza una roca!$b$bMi planeador se rompió y aquí estoy... y guau, esto sí que es incómodo.$b$bAyúdame a bajar, ¿quieres? Te lo recompensaré.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'frFR', 'Greblin Pétivif', 'Aidez Greblin Pévitif à descendre.', 'Je suis pendu ici depuis une éternité !$b$bJe survolais la zone, vaquant à mes affaires quand… blam ! Un de ces stupides Ogres m’a jeté une pierre !$b$bMon planeur s’est cassé et me voilà… dans une position très inconfortable.$b$bAidez-moi à descendre ? Vous n’aurez pas affaire à un ingrat.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'itIT', 'Greblin Frizzalesto', 'Aiuta Greblin Frizzalesto a scendere.', 'È una vita che sono qui!$b$bStavo sorvolando l\'area per conto di Nixxie, quando... kabooom! Uno di quegli stupidi Ogre mi ha lanciato addosso una grossa roccia!$b$bIl mio aliante è stato seriamente danneggiato ed eccomi qui... Diamine quanto sono scomodo.$b$bMi aiuteresti a scendere? Ti ricompenserò profumatamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'koKR', '그레블린 패스트피즐', '그레블린 패스트피즐을 도와야 합니다.', '나 여기 온종일 매달려 있었어!$b$b하늘을 날면서 닉시를 위해서 이 지역을 정찰하고 있는데, 갑자기 쾅하는 거야. 저 멍청한 오우거가 나한테 바위를 던진 거야!$b$b그래서 내 글라이더가 고장 나고 난 여기에 이 꼴로... 그런데 지금 좀 힘이 들거든.$b$b날 좀 내려줘! 답례는 할게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'ptBR', 'Greblin Chiadeira', 'Ajude Greblin Chiadeira a descer.', 'Estou plantado aqui há séculos!$b$bEu estava sobrevoando a região para avaliá-la a pedido de Nixxie quando BUM! Um daqueles ogros idiotas jogou uma pedra em mim!$b$bMeu planador caiu e cá estou eu... Vou te contar, viu, está bem desconfortável.$b$bFaça o favor de me descer, que eu ofereço algo em troca.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'ruRU', 'Греблин Убыстрямс', 'Помогите Греблину Убыстрямсу спуститься.', 'Я тут сто лет уже торчу!$b$bСпокойно летел себе, вел разведку для Никсии, и вдруг... бац! Какой-то тупой огр швырнул в меня камень!$b$bПланер сломался, и вот я здесь... и как же тут неуютно!$b$bПоможешь спуститься? Я в долгу не останусь! ', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'zhCN', '格莱布林·迅爆', '把格莱布林·迅爆放下来。', '我吊在这很久了！$B$B我正从这片区域上空飞过，思考着我的财路，谁知道——砰！有个傻冒食人魔拿石头丢我！$B$B我的滑翔机坏了，然后我就在这里了……伙计，这实在太不舒服了。$B$B能放我下去吗？我会给你报酬的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35945, 'zhTW', '格雷布林‧菲索費斯', '幫格雷布林‧菲索費斯下來。', '我吊在這不曉得多久了！$b$b我本來在這區專心飛行，為妮可希執行偵察任務，結果碰！一個白癡巨魔向我丟了一顆石頭！$b$b我的滑翔機壞了，所以我就在這了…這裡還真是不舒服。$b$b你可以幫我下來嗎？我會好好報答你的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35946, 'deDE', 'Picker der Majestätische', 'Nehmt den Blutschwur von Klingenmeisterin Na\'Shra an.', 'Ihr habt mir einen großen Dienst erwiesen. Diese Klinge gehört Euch jetzt ebenso wie mir.$b$bIch biete Euch einen Blutschwur an: Ich werde Euch dienen und Euren Truppen mein Wissen über Klingen und den Bergbau zur Verfügung stellen.$b$BSeid Ihr damit einverstanden?', '', '', 'Klingenmeisterin Na\'Shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35946, 'esES', 'Peckers el Majestuoso', 'Acepta el juramento de sangre de la maestra del acero Na\'Shra.', 'Me has prestado un gran servicio. Esta espada es tan tuya como mía.$b$bTe ofrezco este juramento de sangre: serviré a tu causa y compartiré mis conocimientos de espadas y minería con tu ejército.$b$B¿Aceptas?', '', '', 'Maestra del acero Na\'Shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35946, 'esMX', 'Picotero el Majestuoso', 'Aceptar juramento de sangre de la maestra del acero Na\'Shra.', 'Me hiciste un gran servicio. Esta cuchilla es tan tuya como mía.$b$bTe ofrezco este juramento de sangre: serviré a tu causa y prestaré mi conocimiento sobre cuchillas y minería a tus fuerzas.$b$B¿Lo aceptarás?', '', '', 'Maestro del acero Na\'Shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35946, 'frFR', 'Pic-Pic le Majestueux', 'Acceptez le Serment de sang du Maître-lame Na’Shra.', 'Vous m’avez rendu un grand service. Cette lame vous appartient autant qu’à moi.$b$bJe suis disposée à prêter le serment de sang suivant : je soutiendrai votre cause et mettrai ma connaissance des lames et des minerais au service de vos troupes.$b$BCela vous convient-il ?', '', 'Récemment promue au titre de maître-lame, Na’Shra est une guerrière et un maître d’armes redoutable.', 'Maître-lame Na’shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35946, 'itIT', 'Struzz il Magnifico', 'Accetta il Giuramento di Sangue di Na\'shra.', 'Mi hai reso un grande servigio. Questa spada ora è tua quanto mia.$b$bTi offro questo giuramento di sangue: servirò la tua causa e metterò a tua disposizione le mie conoscenze di forgiatura ed estrazione.$b$BAccetti?', '', '', 'Maestra di Spade Na\'shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35946, 'ptBR', 'Peckers the Majestic', 'Aceite a Jura de Sangue da Mestra de Espadas Na\'shra.', 'Você me prestou um grande serviço. Essa lâmina é tão sua quanto minha.$b$bEu ofereço este juramento de sangue a você: eu servirei à sua causa e usarei meu conhecimento de lâminas e mineração a favor das suas forças.$b$bVocê aceita?', '', '', 'Mestra de Espadas Na\'shra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35946, 'ruRU', 'Стукс Великолепный', 'Примите клятву крови мастера клинков На\'шры.', 'Ты мне очень $gпомог:помогла;. Теперь этот клинок принадлежит не только мне, но и тебе.$b$bЯ клянусь на крови служить тебе и использовать все свои знания о клинках и руде, чтобы помогать тебе.$b$bПримешь ли ты мою клятву?', '', '', 'Мастер клинков На\'Шра', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'deDE', 'Die Macht des Giftes', 'Befragt Orcs der Zerschmetterten Hand in der Ausschlachterei und findet den Namen und den Aufenthaltsort von \"G.G.\" heraus.', 'G.G.? Wir brauchen einen Namen und eine Ortsangabe. Keine verdammten Initialen.$B$B<Velonara holt eine kleine Phiole hervor.>$B$BDas ist mein letztes Fläschchen Foltergift. Wir benutzen es, um Gefangenen Informationen zu entlocken. Es ist... wirksam.$B$BBestreicht Eure Waffe damit und geht zum Lager der Zerschmetterten Hand ein Stück weiter. Verwickelt sie einfach in einen Kampf und verlangt Antworten... den Rest wird das Gift übernehmen. Ich habe genug von diesen Orcs.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'esES', 'El poder del veneno', 'Interroga a los orcos Mano Destrozada en las Fosas de Cosecha para averiguar el nombre y la ubicación de \"G. V.\".', '¿G. V.? Necesitamos un nombre y una ubicación, no malditas iniciales.$B$B<Velonara saca un pequeño vial.>$B$BEste es mi último vial de veneno convulso. Lo usaremos para obtener información de los prisioneros. Es bastante... potente.$B$BImpregna tu arma con él y dirígete al campamento Mano Destrozada de ahí delante. Enfréntate a ellos como siempre y exige respuestas. El veneno se encargará del resto. Ya estoy cansada de tanto orco.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'esMX', 'El poder del veneno', 'Interroga a los orcos Mano Destrozada en las fosas para cosechas y descubre el nombre y la ubicación de \"G.V.\".', '¿G.V.? Necesitamos un nombre y una ubicación. No unas malditas iniciales.$B$B<Velonara saca un pequeño vial.>$B$BEste es mi último vial de veneno destructivo. Lo usamos para obtener información de los prisioneros. Es... potente.$B$BCubre tu arma con él y dirígete al campamento Mano Destrozada un poco más adelante. Enfréntate con ellos normalmente y busca respuestas; el veneno se encargará del resto. Ya me cansé de estos orcos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'frFR', 'Le pouvoir du poison', 'Interrogez des Orcs de la Main-Brisée dans les Fosses des Moissons pour découvrir le nom de « G.K. » et l’endroit où il se trouve.', 'G.K. ? Il nous faut un nom et un lieu, pas des initiales.$B$B<Velonara sort une petite fiole.>$B$BVoilà ma dernière fiole de poison douloureux. Il nous sert à obtenir des informations de nos prisonniers. C’est… plutôt efficace.$B$BImprégnez-en votre arme et partez pour le camp de la Main-Brisée, juste devant. Combattez comme d’habitude et exigez des réponses. Le poison se chargera du reste. J’ai assez vu ces Orcs.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'itIT', 'Il potere del veleno', 'Interroga gli Orchi Manomozza nelle Fosse di Raccolta per scoprire il nome e la posizione di \"G.D.\".', 'G.D.? Ci serve un nome e un luogo, non delle dannate iniziali.$B$B<Velonara estrae una piccola fiala.>$B$BQuesta è la mia ultima fiala di Veleno Devastante. Lo usiamo per estrarre informazioni dai nostri prigionieri. È... potente.$B$BSpargilo sulla tua arma e vai all\'accampamento dei Manomozza poco più avanti. Affrontali come hai sempre fatto e interrogali... il veleno penserà al resto. Ne ho avuto abbastanza di questi Orchi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'koKR', '독약의 효과', '수확의 구덩이에서 으스러진 손 오크들을 심문해, \"G.V.\"의 이름과 위치를 알아내야 합니다.', 'G.V.? 머리글자는 소용이 없어요. 이름과 위치를 알아야죠.$B$B<작은 약병을 꺼내는 벨로나라>$B$B이건 딱 하나 남은 고문용 독약이에요. 포로에게서 정보를 빼낼 때 쓰죠. 효과가... 아주 뛰어나요.$B$B이 독을 무기에 바르고 곧장 으스러진 손 야영지로 가세요. 그냥 평소처럼 싸운 다음에 질문에 대한 대답을 요구하면, 나머지는 이 독이 알아서 할 거예요. 오크라면 이제 진절머리가 난다고요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'ptBR', 'O poder do veneno', 'Interrogue os orcs da Mão Despedaçada nas Fossas da Colheita para descobrir o nome e a localização de \"G.P.\".', 'G.P.? Droga! Precisamos de um nome e de lugar, não de iniciais.$B$B<Velonara saca um vidrinho.>$B$BÉ o meu último frasco de veneno destruidor. Usamos quando precisarmos extrair informações de prisioneiros. É... forte.$B$BCubra sua arma com ele e vá ao acampamento da Mão Despedaçada logo adiante. Enfrente-os como de costume e exija respostas. O veneno cuidará do resto. Cansei desses orcs.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'ruRU', 'Сила яда', 'Допросите орков клана Изувеченной Длани в Ядовитых ямах, чтобы узнать, кто такой Г.Я.Н. и где его искать.', 'Г.Я.Н.? Нам бы имя и место, а не какие-то инициалы.$B$B<Велонара достает небольшой флакон.>$B$BЭто мой последний флакон терзающего яда. Мы его используем, чтобы разговорить пленных. Он... довольно мощный.$B$BСмажь им свое оружие и отправляйся в лагерь клана Изувеченной Длани, вон он – прямо перед тобой. Вступи, как обычно, в бой с врагами и потребуй ответов – об остальном позаботится яд. Довольно с меня орков и их глупости.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'zhCN', '毒药的力量', '审讯收割围栏里的碎手兽人，问出“G.V.”的名字和所在地。', 'G.V.？我们必须找到他的全名和所在！$B$B对此我倒是有个主意。$B$B<维罗娜拉拿出一个小瓶子。>$B$B这是我最后一瓶凌虐毒素。我们经常用它来从囚犯身上逼问出我们想要的情报。$B$B它的毒性……相当强。$B$B在你的武器上涂一些这个，然后去前方的碎手营地。用你平常的方式招呼他们，并向他们询问答案——剩下的部分，就交给毒药来解决。我已经受够这些兽人了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35947, 'zhTW', '毒藥之力', '審問收割場的破碎之手獸人，找出「加毒」的全名與下落。', '「加毒」？我們需要全名跟地點，而不是該死的縮寫或外號。$B$B<薇蘿娜拉拿出一個小藥瓶。>$B$B這是我最後一瓶殘痛毒藥，用來逼問囚犯的。藥性…很強。$B$B將毒藥塗抹在你的武器上，然後去前方的破碎之手營地。如同以往一樣與他們交戰，逼問答案。毒藥的藥效會幫你問出答案。我受夠這些獸人了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'deDE', 'Stärkebeweis: Gorenzahn', 'Bringt den Beweis zu Schinder an der Wildwacht in Gorgrond.', 'Dieser Zahn ist ein Beweis für Eure Fähigkeit im Töten von Goren, und Euer Gladiatorfreund kann es bezeugen!$b$bBringt den Zahn zu Schinder und nehmt Eure Anerkennung entgegen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'esES', 'Prueba de fuerza: diente de goren', 'Lleva la prueba a Desollabrazos en Avanzada de las Bestias, en Gorgrond.', '¡Este diente es prueba de tu fuerza a la hora de matar goren y tu amigo gladiador es testigo de ello!$b$b¡Llévale el diente a Desollabrazos y recibe el premio que te mereces!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'esMX', 'Prueba de fuerza: diente de goren', 'Llévale la prueba a Despellejamiembros en el Observatorio de Bestias en Gorgrond.', '¡Este diente es una prueba de tu fuerza para matar goren y tu amigo gladiador es testigo!$b$bLlévale el diente a Despellejamiembros y reclama tus premios!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'frFR', 'Preuve de force : dent de goren', 'Apportez la preuve à Fouettard au Guet des Bêtes en Gorgrond.', 'Cette dent prouve votre habileté à tuer des gorens et votre ami gladiateur en est témoin !$b$bApportez la dent à Fouettard et préparez-vous à recevoir les honneurs !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'itIT', 'Prova di forza: Dente di Goren', 'Porta la prova a Squartabraccia a Roccabestia nella regione di Gorgrond.', 'Questo dente è la prova della tua forza nell\'uccidere i Goren, e il tuo amico gladiatore ne è testimone!$b$bPorta il dente a Squartabraccia e reclama la tua ricompensa!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'koKR', '힘의 증거: 고렌 이빨', '승리의 증거를 고르그론드의 야수 감시터에 있는 사지절단자에게 가져가야 합니다.', '이 이빨은 당신이 고렌을 처치했으며, 당신의 검투사 친구가 그 광경을 목격했다는 힘의 증거입니다!$b$b이 이빨을 사지절단자에게 가져가면 포상을 받을 수 있습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'ptBR', 'Prova de Força: Dente de Goren', 'Leve a prova ao Esfolador de Membros, que está em Guarda das Feras, que fica em Gorgrond.', 'Este dente é prova da sua força ao matar gorens, e seu amigo gladiador foi testemunha!$B$BLeve este dente para o Esfolador de Membros e reivindique suas honrarias!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'ruRU', 'Доказательство силы: зуб горена', 'Отнесите доказательство Ногорвачу на Звериную заставу в Горгронде.', 'Этот зуб – доказательство твоего мастерства в охоте на горенов, твой друг гладиатор может это подтвердить.$b$bОтнеси этот зуб Ногорвачу, ты $gзаслужил:заслужила; награду.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'zhCN', '力量的证明：鬣蜥人牙齿', '把证明带到戈尔隆德的蛮兽岗哨，交给断肢。', '这颗牙齿证明了你有消灭鬣蜥人的能力，你的角斗士朋友目击了你的战斗！把这颗牙齿带回去给断肢，换取你应得的嘉奖！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (35948, 'zhTW', '力量之證：戈倫之牙', '把證據帶到格古隆德的獸望崗哨交給裂肢者。', '這顆牙齒是你殺掉戈倫的力量之證，你的格鬥士朋友是見證人！$b$b將牙齒帶回給喬納，取得你的稱號！', '', '', '', '', '', '', 0);
