INSERT INTO `quest_template_locale` VALUES (36119, 'esES', 'Bienvenida a Ashran', 'Habla con la comandante Anne Dunworthy en Escudo de Tormenta.', 'Saludos. Te damos la bienvenida a Ashran.$B$BPreséntate ante la comandante Anne Dunworthy en Escudo de Tormenta para recibir instrucciones.', '', '', '', '', '', '', 0);
