INSERT INTO `quest_template_locale` VALUES (36119, 'itIT', 'Benvenuti ad Ashran', 'Parla con il Comandante Anne Dunworthy a Scudo del Vento.', 'Salve e $gbenvenuto:benvenuta; ad Ashran.$B$BFai rapporto al Comandante Anne Dunworthy presso Scudo del Vento per ricevere istruzioni.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (36119, 'koKR', '아쉬란 첫 방문', '폭풍방패에 있는 지휘관 앤 던월디와 대화해야 합니다.', '아쉬란에 오신 걸 환영합니다. $B$B폭풍방패에 있는 지휘관 앤 던월디에게 보고하십시오.', '', '', '', '', '', '', 0);
